界国家森林公园门票  【旺季门票  245元/人，24周岁以下学生、60岁至70岁的老人门票是 163元/人，70岁及以上老人 85元/人，淡季门票 136元/人，学生及60岁至70岁的老人门票 85元/人，现役军人和70岁及以上老人免票】',4,'http://pad.pinhai.com.cn:8081/zjjtour-admin/attached/image/20160128/20160128232519_223.jpg',NULL,1058,1,1,'1','0',NULL,NULL),(1063,'地标美景',NULL,NULL,1,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1012),(1064,'奇山秀水',NULL,NULL,2,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1012),(1065,'溶洞幽谷',NULL,NULL,3,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1012),(1066,'历史建筑',NULL,NULL,1,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1013),(1067,'名村古寨',NULL,NULL,2,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1013),(1068,'文化演艺',NULL,NULL,1,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1014),(1070,'红色经典',NULL,NULL,3,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1013),(1071,'艺术精粹',NULL,NULL,2,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1014),(1072,'民俗风情',NULL,NULL,3,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1014),(1073,'户外行走',NULL,NULL,1,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1015),(1074,'当地休闲',NULL,NULL,2,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1015),(1076,'生活指南',NULL,NULL,1,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1016),(1077,'美食小吃',NULL,NULL,2,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1016),(1078,'旅游攻略',NULL,NULL,1,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1017),(1079,'游记精选',NULL,NULL,1,'/res/images/leftico01.png',NULL,0,2,1,'1',NULL,NULL,1018),(1080,'诚信中心',NULL,NULL,NULL,NULL,'/securitynews/toChengxin.htm',1,0,1,NULL,NULL,NULL,NULL);

/*Table structure for table `security_news` */

DROP TABLE IF EXISTS `security_news`;

CREATE TABLE `security_news` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(100) DEFAULT NULL COMMENT '通告标题',
  `litter_title` varchar(100) DEFAULT NULL,
  `content` text COMMENT '通告内容',
  `userid` int(11) DEFAULT NULL COMMENT '用户id',
  `status` int(11) DEFAULT NULL COMMENT '是否启用',
  `ctime` datetime DEFAULT NULL COMMENT '创建时间',
  `smallimg` varchar(200) DEFAULT NULL COMMENT '缩略图',
  `indeximg` varchar(200) DEFAULT NULL COMMENT '首页图',
  `url` varchar(200) DEFAULT NULL COMMENT 'url',
  `orderlist` int(11) DEFAULT NULL COMMENT '排序',
  `abstract_content` varchar(200) DEFAULT NULL COMMENT '摘要',
  `read_type` varchar(10) DEFAULT NULL COMMENT '0会员阅读 1全部阅读',
  `index_status` varchar(10) DEFAULT NULL COMMENT '是否首页',
  `top_status` varchar(10) DEFAULT NULL COMMENT '是否置顶',
  `jump_type` varchar(10) DEFAULT NULL COMMENT '0页面 1页外',
  `menu_id` int(11) DEFAULT NULL,
  `review_status` varchar(5) DEFAULT NULL COMMENT '0未处理 1通过 2 失败',
  `review_userid` int(11) DEFAULT NULL,
  `review_time` datetime DEFAULT NULL,
  `review_reason` varchar(200) DEFAULT NULL,
  `news_type` varchar(10) DEFAULT NULL COMMENT '类型 -article -viedo',
  `out_status` varchar(10) DEFAULT NULL COMMENT '是否外部调用',
  `out_js` varchar(200) DEFAULT NULL COMMENT '外部调用js',
  `video_url` varchar(200) DEFAULT NULL,
  `maodian_status` varchar(10) DEFAULT NULL,
  `quanjing_url` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`id`)
