/*Table structure for table `member_activity` */

DROP TABLE IF EXISTS `member_activity`;

CREATE TABLE `member_activity` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(100) DEFAULT NULL COMMENT '活动标题',
  `litter_title` varchar(100) DEFAULT NULL,
  `content` text COMMENT '通告内容',
  `userid` int(11) DEFAULT NULL COMMENT '用户id',
  `activity_status` int(11) DEFAULT NULL COMMENT '活动状态全部、未开始、进行中、已结束',
  `status` int(11) DEFAULT NULL COMMENT '是否启用',
  `ctime` datetime DEFAULT NULL COMMENT '创建时间',
  `smallimg` varchar(200) DEFAULT NULL COMMENT '缩略图',
  `indeximg` varchar(200) DEFAULT NULL COMMENT '首页图',
  `url` varchar(200) DEFAULT NULL COMMENT 'url',
  `orderlist` int(11) DEFAULT NULL COMMENT '排序',
  `abstract_content` varchar(200) DEFAULT NULL COMMENT '摘要',
  `join_type` varchar(10) DEFAULT NULL COMMENT '0会员 1全部',
  `index_status` varchar(10) DEFAULT NULL COMMENT '是否首页',
  `top_status` varchar(10) DEFAULT NULL COMMENT '是否置顶',
  `jump_type` varchar(10) DEFAULT NULL COMMENT '0页面 1页外',
  `menu_id` int(11) DEFAULT NULL,
  `review_status` varchar(5) DEFAULT NULL COMMENT '0未处理 1通过 2 失败',
  `review_userid` int(11) DEFAULT NULL,
  `review_time` datetime DEFAULT NULL,
  `review_reason` varchar(200) DEFAULT NULL,
  `support_status` varchar(10) DEFAULT NULL COMMENT '是否支持报名',
  `start_time` datetime DEFAULT NULL COMMENT '开始时间',
  `end_time` datetime DEFAULT NULL COMMENT '截止时间',
  `usercount` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8;

/*Data for the table `member_activity` */

insert  into `member_activity`(`id`,`title`,`litter_title`,`content`,`userid`,`activity_status`,`status`,`ctime`,`smallimg`,`indeximg`,`url`,`orderlist`,`abstract_content`,`join_type`,`index_status`,`top_status`,`jump_type`,`menu_id`,`review_status`,`review_userid`,`review_time`,`review_reason`,`support_status`,`start_time`,`end_time`,`usercount`) values (1,'第四届世界翼装飞行锦标赛','第四届世界翼装飞行锦标赛','<p class=\"eventsdaytxt\" style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#333333;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">活动时间：</span><span class=\"eventsday\" style=\"color:#039FEB;font-family:\'Microsoft YaHei\';font-size:14px;\">2015年10月16日-18日</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">活动介绍：：10月16日-18日代表全球翼装飞行运动最高水平的2015第四届翼装飞行世锦赛将于在张家界天门山举办。届时，由预选赛产生的来自全球12个国家16位顶级“翼装侠”将聚在一起挑战空中无动力翼装飞行的竞速极限，角逐最快翼装侠。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Ft2rHHgDxcKUf5fQQBXpe_P3Pztl\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">两两PK 生死竞速</span></strong> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">翼装飞行世锦赛已经是第四次在张家界天门山举行。这里保存着完整的原始次森林，古树参天、藤蔓缠绕，被誉为人间仙境，也成为冒险家和挑战者的天堂。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">据世界翼装联盟介绍，2015第四届翼装飞行世界锦标赛仍将采用两人同时出发PK的赛制，比赛的赛道仍为直道。从海拔约1400米的张家界天门山高点跃下，竞速赛道飞行距离长达2公里，垂直落差1100米，参赛选手将在约40秒至50秒左右的时间里完成比赛，最高时速可达200公里/小时(第三届翼装飞行世锦赛选手最快成绩为23秒284)。比赛在比拼速度的同时，也考验选手处理复杂自然环境能力，需要全面的飞行技术以及胆量。</span> \r\n</p>\r\n<p style=\"text-align:center;font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqfSX9a7U6TTdMbpWoH2qcECiwfm\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">玩命挑战 高手迭出</span></strong> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">翼装飞行是人类无动力飞行的极限。由于飞行高度低，运动的危险性和难度极大，全球只有大约500人尝试，而真正称得上是翼装运动员的不到一百人。尤其是高山挑战，对翼装侠分析和掌控云层、风力等飞行条件的能力有着苛刻的要求，任何细节上的失误，都有可能让挑战者殒命山崖。因此，翼装飞行世锦赛对参赛选手设置的门槛极高。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">世界翼装联盟主席伊罗曾表示：“不是买了翼装等装备就能飞了，参赛之前选手首先需要高空跳伞几百次，完成后才能进行低空飞行，再进行数百次的循环练习，到最终成为翼装飞行员，是一个漫长的积累过程。”以连续三年参加翼装飞行世锦赛的南非翼装侠朱力安·布勒为例，他有13年低空跳伞经验，经历了约15000次跳伞，1700次低空跳伞和超过2000次从飞机或固定建筑上起跳的翼装飞行经历。他参加过三次翼装飞行世锦赛，并在2012年和2014年摘得两届冠军。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">随着世界翼装飞行运动的发展，越来越多的年轻好手涌现，来自斯洛文尼亚的黑马卢卡·弗南杰里克、曾参加去年第三届翼装世锦赛的美国选手卡尔森·克莱恩、来自荷兰的\"新人”亚诺·科迪亚在翼装飞行世锦赛预选赛中均展露出了惊人的实力。其中，卢卡·弗南杰里克更在海拔1300米落差中飞出了26秒337的成绩，夺得预选赛冠军。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">来自克罗地亚的罗伯特·佩兹尼克是年龄最大的选手，也是优秀的翼装飞行服首席设计师。他15岁开始练习跳伞并尝试修补跳伞飞行服，在定点跳伞17年中，完成定点跳伞1540次，翼装飞行950次。今年参加世锦赛，他有个朴素的愿望，那就是希望所有运动员都能健康平安地回家。</span> \r\n</p>\r\n<p style=\"text-align:center;font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fr4yBUuFs1-xlp58B0dmYlQpGoQ5\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">中国“翼装侠”现身</span></strong> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">翼装飞行世锦赛创办于2012年，尽管到今年连续四年举办以来还没有中国的翼装飞行运动员参与到正式比赛中，但已经在中国爱挑战的勇士们心中埋下种子。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">继今年4月成功挑战8100米无氧翼装飞行世界纪录后，被称为“亚洲翼装第一人”的中国翼装运动员张树鹏将在10月14-15日择机进行翼装飞行表演，在天门山挑战全新线路，同时为2015第四届红牛翼装飞行世锦赛揭幕。</span> \r\n</p>',1,2,0,'2016-01-15 14:45:11','http://7xr35w.com2.z0.glb.qiniucdn.com/Fpy6dIRwukwD2q7pXIg-_agEu0mp','http://7xr35w.com2.z0.glb.qiniucdn.com/Fpy6dIRwukwD2q7pXIg-_agEu0mp','444',2,'10月16日-18日代表全球翼装飞行运动最高水平的2015第四届翼装飞行世锦赛将于在张家界天门山','0','1','0','0',NULL,'1',1,'2016-01-15 15:27:28','adfsd','1','2015-10-16 00:00:00','2015-10-18 00:00:00',1),(2,'张家界“欢乐潇湘”系列活动','张家界“欢乐潇湘”系列活动','<p class=\"eventsdaytxt\" style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#333333;text-indent:2em;\">\r\n	活动时间：<span class=\"eventsday\" style=\"font-size:16px;color:#039FEB;\">2015年4月1日-10月31日</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	活动介绍：<span style=\"line-height:1.5;\"></span><span style=\"line-height:1.5;\">6月9日，从张家界召开的2015年“欢乐潇湘”系列活动推进会上获悉，各项突出地域特色与亮点的活动正如火如荼开展。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"line-height:1.5;\">为展示群众文艺成果、丰富群众文化生活，张家界“欢乐潇湘”系列活动从4月开始至10月底结束，以“文化广场”为中心，以“我们的中国梦·世界的张家界”为主题，组织开展群众美术、书法、摄影创作展览评比活动，展现张家界民俗文化、大山大水文化、红色文化和农耕文化等，将独特的文化视角与旅游发展有效结合，助力“提质张家界·打造升级版”。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"line-height:1.5;\">目前，张家界已完成“欢乐潇湘”群众美术、书法、摄影活动部分作品的收集工作，“三湘群星奖”、“三湘群文之星”的选拔与申报，创作出了《土家农耕歌》、永定摆手舞《舍巴耶》、桑植民歌《九岭十八岗》、慈利《打硪舞》等多首“张家界本土原创广场舞”优秀曲目。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	举办单位：省委宣传部、省文化厅、省文联等联合举办\r\n</p>',1,1,1,'2016-01-15 15:00:59','http://7xr35w.com2.z0.glb.qiniucdn.com/Fs3WL9AWPibZ9s6aRXl6FFo8xRKM','http://7xr35w.com2.z0.glb.qiniucdn.com/Fs3WL9AWPibZ9s6aRXl6FFo8xRKM',NULL,NULL,'6月9日，张家界召开的2015年“欢乐潇湘”系列活动推进会上获悉，各项突出地域特色与亮点活动...','0','1','1','1',NULL,'1',1,'2016-01-28 22:06:08','123','1','2016-01-04 00:00:00','2016-01-29 00:00:00',10),(4,'“心动瞬间 微爱相伴”7月邂逅张家界','“心动瞬间 微爱相伴”7月邂逅张家界','<p class=\"eventsdaytxt\" style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#333333;text-indent:2em;\">\r\n	活动时间：<span class=\"eventsday\" style=\"font-size:16px;color:#039FEB;\">2015年6月26日-7月31日</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"line-height:1.5;\">活动介绍：旅行是爱情的试金石，即将在湘西这块神秘而秀美的土地展开的特别寻爱之旅，不仅仅是简单相亲而已。男女嘉宾必须经过多次趣向随机选择配对成约会搭档，每次配对要双方或集体共同完成一些指定任务方可配对成功。大家在选择搭档、挑战任务的过程中增进了解，但，在挑战的过程中会擦出什么样的火花，就只有在节目现场才能得知了。</span><span style=\"line-height:1.5;\">《心动瞬间》现面向全国优质单身男女发出粉色邀请，食宿费用全免，更有机会免费周游全国不同的景点，体验真实版的“在风景中恋爱”。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"line-height:1.5;\">举办单位：湖南日报报业集团、华声在线主办</span> \r\n</p>',1,0,1,'2016-02-20 15:02:09','http://7xr35w.com2.z0.glb.qiniucdn.com/FkO_Zhl84kzRZeS2nYfUIykyjEZA','http://7xr35w.com2.z0.glb.qiniucdn.com/FkO_Zhl84kzRZeS2nYfUIykyjEZA',NULL,3,'旅行是爱情的试金石，即将在湘西这块神秘而秀美的土地展开的特别寻爱之旅，不仅仅是简单相亲而已。','1','0','0','0',NULL,'1',1,'2016-02-20 15:02:14','','0','2016-02-20 00:00:00','2016-03-31 00:00:00',0),(5,' “2015’写意中国”在张家界拉开序幕',' “2015’写意中国”在张家界拉开序幕','<p class=\"eventsdaytxt\" style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#333333;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>活动时间：</strong></span><span class=\"eventsday\" style=\"font-size:14px;color:#039FEB;font-family:\'Microsoft YaHei\';\">2015年4月11日-4月21日</span>\r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>活动介绍：</strong>油画艺术作为西风东渐的文化精神产物，从泊来、生根，而至成为现当代中国与传统并行的绘画艺术，已走过百余年历史。目前它已深深扎根中国文化和精神土壤之中，一部中国100余年油画史走过了油画的故乡-欧洲，自文艺复兴起约500年的历史沧桑，从“以欧为师”到文化自觉，中国的油画在民族性、现当代性和个性的探索和创造活动中，部分有志的油画家们在“写意油画”的探索中，日渐成为一个自觉而有为的艺术分流，在此背景下“写意油画”的概念顺势而出。</span>\r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">此次活动的目的在于，依托著名的张家界山水环境和理想的文化场，以相对长的时间安排，为中国写意油画学派主力阵容研究和推进写生创作提供一个对各自思考和实践可能“破局”和超越的契机；依托具有千年文脉的岳麓书院、并与诸多著名文人学者和美术史论家的清谈对语，对深刻认识和清醒把握中国写意油画的文化根性和当代课题，提供一个确认与思辨的契机；依托力美术馆的实力与展览场所，对深入研究中国油画写生与写意关系的艺术实践，提供一个再认识再实践的交流和展示平台；依托媒体宣传和后继文献性画册的编辑发行，推介此次不同于常规的“写生创作研究和文化清谈”活动，在中国油画界和广大青年油画家中倡导冷静稳健的艺术实践和文化植入。</span>\r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>举办单位：</strong>这次写意油画学派创作研究暨《千年文脉·雅集岳麓》系列活动由著名策展人范迪安总策划，联合湖南大学岳麓书院、中国政协文史馆油画研究院以及力美术馆共同主办</span>\r\n</p>',1,2,1,'2016-02-20 15:05:28','http://7xr35w.com2.z0.glb.qiniucdn.com/Fm3bJ0AmNrFnFs-QbgdRuM4JAzVv','http://7xr35w.com2.z0.glb.qiniucdn.com/Fm3bJ0AmNrFnFs-QbgdRuM4JAzVv',NULL,4,'油画艺术作为西风东渐的文化精神产物，从泊来、生根，而至成为现当代中国与传统并行的绘画艺术，已走过百余年历史。','1','0','0','0',NULL,'0',1,'2016-02-20 15:07:28','','0','2016-02-20 00:00:00','2016-03-11 00:00:00',0),(6,'张家界送福利，百万门票免费抢','张家界送福利，百万门票免费抢','<p class=\"eventsdaytxt\" style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#333333;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>活动时间：</strong></span><span class=\"eventsday\" style=\"font-size:14px;color:#039FEB;font-family:\'Microsoft YaHei\';\">2015年5月15日16:30-17:30</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>活动介绍：</strong>套票抢到手，免费去旅游!1000套张家界必玩景区门票，每套门票含：张家界国家森林公园门票、杨家界索道(单程)、十里画廊观光电车(双程)、宝峰湖、魅力湘西演艺门票各1张，限时免费开抢!只要需微信搜索“张家界游客中心”，关注“张家界游客中心”微信公众号。活动时进入&nbsp;“免费抢门票”栏，点击“幸运大转盘”，不限次数，抢到为止!现在拿出手机，动下手指，门票就是你的!</span><span style=\"font-size:14px;line-height:1.5;\"></span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">“<strong>世界那么大，张家界这么近”泛长株潭2015张家界产品说明会</strong>在株洲成功举办，正式启动了“张家界百万门票免费抢”的优惠活动。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">说明会上，主办方在推介张家界武陵源核心景区、宝峰湖、杨家界、魅力湘西等优质旅游资源的同时，更注重与参与者的互动。现场介绍了“三羊送吉，‘码’上有奖”、“mini cooper找主人，快乐自游张家界”等景区营销活动，并敲锣启动了“张家界送福利·百万门票免费抢”活动，游客通过关注“张家界游客中心”官方微信号，就可参加以上优惠活动。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">据悉，近年来张家界围绕 “提质张家界、打造升级版”的发展战略，全市上下朝着“把张家界建设成为国内外知名的旅游胜地”目标迈进，着力对景区提质升级。张家界市人民政府副市长胡志文介绍说，张家界拥有“中国首批世界自然遗产”、“全球首批世界地质公园”、“世界‘张家界地貌’命名地”、“中国第一个国家森林公园”、“国家首批5A级旅游景区”、“全国文明风景区”这六张“烫金的名片”，标志着张家界是湖南旅游的龙头、开放的窗口、迎宾的客厅和湖南的名片。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">希望通过本次泛长株潭产品说明会，进一步增进与兄弟省、市，与各界朋友建立良好的互通、交流与合作，为全省及周边旅游的发展创造更多机会，最大限度实现合作共赢目标。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>换票地址：</strong>张家界市高速三出口门店，即：（</span><span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';font-size:14px;\">&nbsp;高速东出口官网特别营销活动指定服务门店、高速西出口官网特别营销活动指定服务门店、高速阳和出口官网特别营销活动指定服务门店</span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">）。</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>咨询电话：</strong>4008831111</span> \r\n</p>\r\n<p style=\"font-size:medium;font-family:\'Microsoft YaHei\';color:#666666;text-indent:2em;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>举办单位：</strong>张家界市人民政府主办，张家界市旅游局、携程旅行网、张家界旅游集团股份有限公司承办</span> \r\n</p>',1,0,1,'2016-02-20 15:07:24','http://7xr35w.com2.z0.glb.qiniucdn.com/Fi0imdOh-6K5E5vV7_v8Opy4IYxy','http://7xr35w.com2.z0.glb.qiniucdn.com/Fi0imdOh-6K5E5vV7_v8Opy4IYxy',NULL,5,'套票抢到手，免费去旅游!1000套张家界必玩景区门票。','1','0','1','0',NULL,'0',1,'2016-02-20 15:07:28','','0','2015-05-15 00:00:00','2015-12-31 00:00:00',0),(7,'2016年中国张家界国际乡村音乐周即将开启','2016年中国张家界国际乡村音乐周即将开启','<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>活动时间：</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">2016年9月12日至18日</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';\"><span style=\"font-size:14px;\"><strong>活动地点：</strong></span><span style=\"font-size:14px;\">湖南省张家界市</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';\"><span style=\"font-size:14px;\"><strong>活动介绍：</strong></span><span><strong></strong><span style=\"font-size:14px;\"> 中国张家界国际乡村音乐周活动从2009年开始举办，2011年获准永久落户张家界，从迄今已成功举办三届，是目前我国中西部地区唯一由省、部联合主办的国际性文化品牌活动，曾被誉为“巧用文化软实力提升国家形象的成功实践”。</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';\"><span style=\"font-size:14px;\"><strong>活动主题：</strong></span><span style=\"font-size:14px;\">世界的自然遗产，我们的乡村音乐！</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>活动内容：</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">2016年中国张家界国际乡村音乐周采用开幕式、闭幕式和巡展演方式。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';\"><span style=\"font-size:14px;\"><strong>举办单位：</strong></span><span style=\"font-size:14px;\">中华人民共和国文化部、湖南省人民政府主办，</span></span><span style=\"font-family:\'Microsoft YaHei\';line-height:1.5;font-size:14px;\">湖南省文化厅、湖南省旅游局、张家界市人民政府承办，</span><span style=\"font-family:\'Microsoft YaHei\';line-height:1.5;font-size:14px;\">张家界旅游集团股份有限公司执行承办</span> \r\n</p>',16,0,1,'2016-04-07 13:52:17','http://7xr35w.com2.z0.glb.qiniucdn.com/Fjxyg7Nf3aKJTUgNamJTswfVQC-t','http://7xr35w.com2.z0.glb.qiniucdn.com/Fjxyg7Nf3aKJTUgNamJTswfVQC-t',NULL,NULL