/*Table structure for table `member_complain` */

DROP TABLE IF EXISTS `member_complain`;

CREATE TABLE `member_complain` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `member_id` int(11) DEFAULT NULL COMMENT '会员ID',
  `member_mobile` varchar(50) DEFAULT NULL COMMENT '会员手机',
  `member_name` varchar(50) DEFAULT NULL COMMENT '会员姓名',
  `complain_title` varchar(200) DEFAULT NULL,
  `complain_message` text COMMENT '投诉内容',
  `complain_time` datetime DEFAULT NULL COMMENT '投诉时间',
  `replay_status` int(11) DEFAULT NULL COMMENT '回复状态',
  `replay_userid` int(11) DEFAULT NULL COMMENT '回复人id',
  `replay_time` datetime DEFAULT NULL COMMENT '回复时间',
  `replay_message` varchar(200) DEFAULT NULL COMMENT '回复内容',
  `review_status` int(11) DEFAULT NULL COMMENT '审核状态',
  `review_userid` int(11) DEFAULT NULL COMMENT '审核人id',
  `review_time` datetime DEFAULT NULL COMMENT '审核时间',
  `review_message` varchar(200) DEFAULT NULL COMMENT '审核意见',
  `member_img` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;

/*Data for the table `member_complain` */

insert  into `member_complain`(`id`,`member_id`,`member_mobile`,`member_name`,`complain_title`,`complain_message`,`complain_time`,`replay_status`,`replay_userid`,`replay_time`,`replay_message`,`review_status`,`review_userid`,`review_time`,`review_message`,`member_img`) values (3,273,'15951984655','杨海龙','旅游投诉','2016年2月14日，我在网络购票到黄龙洞售票点换票，网络票窗口的女工作人员，只顾玩手机，一句三不答，只摇晃手指，连头都没抬起来。我问她这是什么工作态度，她说我们就这样，然后站起骂了粗口！我认为她这种行为是在抹黑张家界，希望主管部门引起重视！','2016-02-21 10:16:30',1,1,'2016-02-21 10:17:30','我局已责成该单位进行整改，给您带来的不便，请您谅解！',1,1,'2016-02-21 10:17:34','',NULL),(4,273,'15951984655','杨海龙','酒店投诉','原本携程网定的70多元，手机接收到短信就去看房了，老板娘就说房子今天开放价200元，我们好说的就加了20就我，就勉强住下，可还没算完～我们出去玩了 晚上回来还要我们加90元，老板还拍桌子凶我们骂我们。真心不知道张家界的酒店业是怎么管理的，严重有损张家界酒店业的形象。','2016-02-21 10:22:24',0,NULL,NULL,NULL,1,1,'2016-02-21 10:26:12','',NULL),(5,273,'15951984655','杨海龙','酒店投诉','原本携程网定的70多元，手机接收到短信就去看房了，老板娘就说房子今天开放价200元，我们好说的就加了20就我，就勉强住下，可还没算完～我们出去玩了 晚上回来还要我们加90元，老板还拍桌子凶我们骂我们。真心不知道张家界的酒店业是怎么管理的，严重有损张家界酒店业的形象。','2016-02-21 10:27:15',1,1,'2016-02-21 10:27:32','我局已责成该单位进行整改，给您带来的不便，请您谅解！',1,1,'2016-02-21 10:27:36','',NULL),(6,279,'18930189100','18930189100','哈哈哈','庄晓毅','2016-03-01 13:06:14',0,NULL,NULL,NULL,1,1,'2016-03-07 22:58:44','123',NULL),(7,290,'18074477955','游客','test','test','2016-03-15 14:38:27',0,NULL,NULL,NULL,2,1,'2016-03-17 10:30:34','测试OK',NULL),(8,290,'18074477955','tester','test','test','2016-03-15 14:49:37',0,NULL,NULL,NULL,2,1,'2016-03-17 10:32:19','驳回',NULL),(9,290,'18074477955','tester','test','test','2016-03-15 14:49:43',0,NULL,NULL,NULL,2,1,'2016-03-17 10:32:19','驳回',NULL),(10,290,'18074477955','tester','张家界好玩','人太多，排队太久','2016-03-17 10:39:57',0,NULL,NULL,NULL,2,1,'2016-03-21 16:21:54','这是投诉吗？','/headimg/ab96608e-c87c-4822-b101-7e228bcc31fe.png'),(11,290,'18074477955','tester','这是投诉标题','这是投诉内容','2016-03-23 09:25:57',0,NULL,NULL,NULL,0,NULL,NULL,NULL,NULL);

/*Table structure for table `news_maodian` */

DROP TABLE IF EXISTS `news_maodian`;

CREATE TABLE `news_maodian` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `news_id` int(11) DEFAULT NULL,
  `orderlist` int(11) DEFAULT NULL,
  `maodian_name` varchar(200) DEFAULT NULL,
  `maodian_content` text,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1891 DEFAULT CHARSET=utf8;

/*Data for the table `news_maodian` */

insert  into `news_maodian`(`id`,`news_id`,`orderlist`,`maodian_name`,`maodian_content`) values (95,61,1,'地质公园','																		<p >\r\n	“中国首批世界自然遗产、世界首批地质公园、国家首批5A级旅游景区”【武陵源核心景区】为是来<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>游人的必玩景区，因独特的“张家界石英砂岩峰林地貌”而闻名于世，由<a href=\"http://www.travelzjj.com/panorama/detail/1051.html\" target=\"_blank\" class=\"keywords\">张家界国家森林公园</a>、<a href=\"http://www.travelzjj.com/panorama/detail/1068.html\" target=\"_blank\" class=\"keywords\">天子山</a>风景区、索溪峪风景区（部分）共同组成，门票为通票制，下辖7个分景区，分别为【<a href=\"http://www.travelzjj.com/panorama/detail/1060.html\" target=\"_blank\" class=\"keywords\">杨家界</a>】、【天子山】、【<a href=\"http://www.travelzjj.com/panorama/detail/1065.html\" target=\"_blank\" class=\"keywords\">袁家界</a>】、【<a href=\"http://www.travelzjj.com/panorama/detail/1066.html\" target=\"_blank\" class=\"keywords\">十里画廊</a>】、【<a href=\"http://www.travelzjj.com/panorama/detail/1069.html\" target=\"_blank\" class=\"keywords\">金鞭溪</a>】、【<a href=\"http://www.travelzjj.com/panorama/detail/1067.html\" target=\"_blank\" class=\"keywords\">黄石寨</a>】、【<a href=\"http://www.travelzjj.com/panorama/detail/1070.html\" target=\"_blank\" class=\"keywords\">鹞子寨</a>】，自然风光各不相同。\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	 奇山异水张家界，举世闻名，是湖南核心旅游景点，还有世界溶洞之冠的<a href=\"http://www.travelzjj.com/panorama/detail/1079.html\" target=\"_blank\" class=\"keywords\">黄龙洞</a>，神秘幽深。有峰林之称的天子山，不到黄石寨，枉到张家界，还有世界上最美丽的峡谷金鞭溪。\r\n</p>\r\n									\r\n									'),(96,61,2,'美景风光','																		<p >\r\n	<span style=\"line-height:1.5;\">上亿年前的地壳运动，大自然的鬼斧神工，造就了<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>不可复制的神奇和美丽。于是，无数游过张家界的人赞叹：人生不到张家界，访尽千山也枉然，人生不到张家界 百岁何能称老翁？张家界是“扩大的盆景.缩小的仙境”，随着四季的气象不同变幻莫测，是大自然鬼斧神功美的让人心动。张家界集桂林之秀、黄山之奇、华山之险、泰山之雄于一体，藏赂、桥、洞、湖、瀑于一身，有“扩大的盆景、缩小的仙山”之美称。</span>\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界杨家界峰墙\" src=\"http://manage.zjji.com/uploads/201506/261039042596.jpg\" width=\"730\" height=\"450\" title=\"张家界杨家界峰墙\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                                <a href=\"http://www.travelzjj.com/panorama/detail/1060.html\" target=\"_blank\" class=\"keywords\">杨家界</a>峰墙\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<a href=\"http://www.travelzjj.com/panorama/detail/1051.html\" target=\"_blank\" class=\"keywords\">张家界国家森林公园</a>位于湖南省西北部张家界市境内，是1982年由国务院委托国家计委批准成立的中国第一个国家森林公园，1992年12月因奇特的石英砂岩大峰林被联合国列入《世界自然遗产名录》，2004年2月被列入世界地质公园。公园自然风光以峰称奇、以谷显幽、以林见秀。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界天子山卧龙岭\" src=\"http://manage.zjji.com/uploads/201506/261040580484.jpg\" width=\"730\" height=\"450\" title=\"张家界天子山卧龙岭\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                             <a href=\"http://www.travelzjj.com/panorama/detail/1068.html\" target=\"_blank\" class=\"keywords\">天子山</a>卧龙岭                                                  摄影者：刘君\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	公园属于中亚热带气候区，高山峡谷，茂密的森林，使公园形成了独特的森林小气候——冬暖夏凉，年平均气温12.8℃，夏天平均最高气温16.8℃，冬天平均最低气温9.6℃，公园空气清新，含负氧离子高，身临其中，人体感觉特别舒适，是休闲避暑的理想之地。\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<img alt=\"张家界袁家界\" src=\"http://manage.zjji.com/uploads/201506/261043149132.jpg\" width=\"730\" height=\"450\" title=\"张家界袁家界\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                                    <a href=\"http://www.travelzjj.com/panorama/detail/1065.html\" target=\"_blank\" class=\"keywords\">袁家界</a>\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	绝美自然景观。张家界地处于云贵高原隆起区与洞庭湖沉降区之间，既受隆起的影响，又受沉降的牵制 ,加上地表水切割强烈和岩溶地貌极其发育，形成了当今这种高低悬殊、奇峰林立、万石峥嵘、溪谷纵横的自然奇观。是户外爱好者的旅行天堂。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界十里画廊小火车\" src=\"http://manage.zjji.com/uploads/201506/271601423959.jpg\" width=\"730\" height=\"450\" title=\"张家界十里画廊小火车\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                                 <a href=\"http://www.travelzjj.com/panorama/detail/1066.html\" target=\"_blank\" class=\"keywords\">十里画廊</a>\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	独特的人文景观。土家族、白族、苗族、侗族和瑶族等少数民族占整个张家界市的96%。具有浓烈的民族风情，是人文摄影爱好者的必去之地。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界金鞭溪\" src=\"http://manage.zjji.com/uploads/201506/261044519598.jpg\" width=\"730\" height=\"450\" title=\"张家界金鞭溪\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                                <a href=\"http://www.travelzjj.com/panorama/detail/1069.html\" target=\"_blank\" class=\"keywords\">金鞭溪</a>                                                        摄影者： 汤东涛\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	及其优良的气候条件。四季分明，冬 长夏短。每年3月起，气温日渐升高，春意盎然，兽鸟活跃，山峦田野，充满着生机与活力，同时此时降雨较多，可看到云海。到了6月下旬，武陵源开始进入夏季，直至9月上旬。武陵源夏季的平均气温在22至26摄氏度之间，最高时也不过32摄氏度左右。此时，许多地区酷热难熬，而这里却一派清凉，是避暑消夏的理想去处之一。冬季景区山顶与市区截然不同，张家界虽然地处南方，但还是有雪景的出现！是冬季摄影的好去处！\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界黄石寨\" src=\"http://manage.zjji.com/uploads/201506/261046028952.jpg\" width=\"730\" height=\"450\" title=\"张家界黄石寨\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                             <a href=\"http://www.travelzjj.com/panorama/detail/1067.html\" target=\"_blank\" class=\"keywords\">黄石寨</a>日出\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	 张家界以岩称奇。这里三千座奇峰拔地而起，形态各异，有的似玉柱神鞭，立地顶天；有的像铜墙铁壁，巍然屹立；有的如晃板垒卵，摇摇欲坠；有的若盆景古董，玲珑剔透......神奇而又真实，迷离而又实在，不是艺术创造胜以艺术创造，令人叹为观止，张家界奇独特的风景，丰富的旅游资源，唯美的自然风光，无一不向世人展示着它雄伟壮丽。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界鹞子寨\" src=\"http://manage.zjji.com/uploads/201506/261046371955.jpg\" width=\"730\" height=\"450\" title=\"张家界鹞子寨\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                             <a href=\"http://www.travelzjj.com/panorama/detail/1070.html\" target=\"_blank\" class=\"keywords\">鹞子寨</a>                                                          摄影者：郑亚平\r\n</p>\r\n									\r\n									'),(97,61,3,'推荐理由','																		<p >\r\n	<span>其间有奇峰3000多座，这些石峰如人如兽、如器如物，形象逼真，气势壮观。峰间峡谷，溪流潺潺，浓荫蔽日。有“三千奇峰，八百秀水”之美称。公园不仅自然风光壮美绝伦，而且森林植物和野生动物资源极为丰富，森林覆盖率达98％，是一座巨大的生物宝库和天然氧吧，被称为“自然博物馆和天然植物园”。草木禽兽与奇山异水，同生共荣，形成完美的自然生态系统。</span>\r\n</p>\r\n<p >\r\n	<span></span> \r\n</p>\r\n<p >\r\n	<span>境内峰奇岩险，谷深涧幽，水秀林碧，云缭雾绕。集雄、奇、幽、野、秀于一体，汇峰、谷、壑、林、水为一色。<a href=\"http://www.travelzjj.com/panorama/detail/1051.html\" target=\"_blank\" class=\"keywords\">张家界国家森林公园</a>主要景点有：<a href=\"http://www.travelzjj.com/panorama/detail/1069.html\" target=\"_blank\" class=\"keywords\">金鞭溪</a>，<a href=\"http://www.travelzjj.com/panorama/detail/1060.html\" target=\"_blank\" class=\"keywords\">杨家界</a>景区，乌龙寨，<a href=\"http://www.travelzjj.com/panorama/detail/1070.html\" target=\"_blank\" class=\"keywords\">鹞子寨</a>景区，<a href=\"http://www.travelzjj.com/panorama/detail/1068.html\" target=\"_blank\" class=\"keywords\">天子山</a>景区</span>\r\n</p>\r\n									\r\n									'),(98,61,4,'交通路线','																		<p >\r\n	<span style=\"line-height:1.5;\">在<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>市中心汽车站（火车站旁50米）就可搭乘去往森林公园、武陵源的线路巴士，每十分钟发班。</span>\r\n</p>\r\n<p >\r\n	公园交通十分方便，距市城区、飞机场、火车站仅28公里。景区内已建成58公里的国际标准游道、安全舒适的索道和开通了环保车。\r\n</p>\r\n									\r\n									'),(99,61,5,'游览须知','																		<p >\r\n	<span style=\"line-height:1.5;\">游览景区一般为2到3天。</span>\r\n</p>\r\n<p >\r\n	景区有5个门票站，其中森林公园门票站、吴家峪门票站（武陵源标志门）是游人最常的选择。\r\n</p>\r\n<p >\r\n	景区游览路线可简要形容为山下游览路线、山上游览路线；\r\n</p>\r\n<p >\r\n	山下游览路线为：森林公园门票站-大氧吧广场-<a href=\"http://www.travelzjj.com/panorama/detail/1067.html\" target=\"_blank\" class=\"keywords\">黄石寨</a>-<a href=\"http://www.travelzjj.com/panorama/detail/1069.html\" target=\"_blank\" class=\"keywords\">金鞭溪</a>-水绕四门-<a href=\"http://www.travelzjj.com/panorama/detail/1066.html\" target=\"_blank\" class=\"keywords\">十里画廊</a>-吴家峪门票站。山上游览路线为：<a href=\"http://www.travelzjj.com/panorama/detail/1065.html\" target=\"_blank\" class=\"keywords\">袁家界</a>-<a href=\"http://www.travelzjj.com/panorama/detail/1060.html\" target=\"_blank\" class=\"keywords\">杨家界</a>-<a href=\"http://www.travelzjj.com/panorama/detail/1068.html\" target=\"_blank\" class=\"keywords\">天子山</a>。另有<a href=\"http://www.travelzjj.com/panorama/detail/1070.html\" target=\"_blank\" class=\"keywords\">鹞子寨</a>，可选择单独游览。\r\n</p>\r\n									\r\n									'),(100,58,1,'峰林奇观','																		<p>\r\n	<p >\r\n		<span>位于武陵源核心景区中心位置，平均海拔1074米，为一方山台地，背依岩峰山峦，面临幽谷群峰，自东向西延伸，是武陵源核心景区的风景集中地。袁家界名称相传来源于后唐时期，黄巢起义失败后，有一名姓“袁”的义军将士，为躲避朝廷追捕，便来到了这远离人世的深山野岭隐居生活，并以自己的姓氏为这里命名，起名“袁家界”。</span>\r\n	</p>\r\n	<p >\r\n		<span><br />\r\n</span>\r\n	</p>\r\n	<p >\r\n		<img alt=\"张家界袁家界峰林\" src=\"http://www.travelzjj.com/uploads/201506/271413305495.jpg\" width=\"730\" height=\"450\" title=\"张家界袁家界峰林\" align=\"\" />\r\n	</p>\r\n	<p >\r\n		                                                                         峰林                                                                   摄影者：张建国\r\n	</p>\r\n	<p >\r\n		<br />\r\n	</p>\r\n	<p >\r\n		袁家界面积约1200公顷，平均海拔1074米，它东邻<a href=\"http://www.travelzjj.com/panorama/detail/1069.html\" target=\"_blank\" class=\"keywords\">金鞭溪</a>、远眺<a href=\"http://www.travelzjj.com/panorama/detail/1070.html\" target=\"_blank\" class=\"keywords\">鹞子寨</a>；南望<a href=\"http://www.travelzjj.com/panorama/detail/1067.html\" target=\"_blank\" class=\"keywords\">黄石寨</a>，连接天波府；西通<a href=\"http://www.travelzjj.com/panorama/detail/1068.html\" target=\"_blank\" class=\"keywords\">天子山</a>；北距索溪峪。它位于<a href=\"http://www.travelzjj.com/panorama/detail/1051.html\" target=\"_blank\" class=\"keywords\">张家界国家森林公园</a>北部，是镶嵌在武陵源核心景区的一颗明珠。袁家界位于杉刀沟北麓，是以石英岩为主构成的一座巨大而较平缓的山岳。袁家界是<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>公园又一处风景集中地。自金鞭溪紫草潭左入杉刀沟可上袁家界；顺袁家界台地边沿，绕荒径小道亦可下至金鞭溪千里相会处；亦可从袁家界去天子山、杨家寨。\r\n	</p>\r\n	<p >\r\n		<br />\r\n	</p>\r\n	<p >\r\n		<span style=\"line-height:1.5;\"><img alt=\"张家界袁家界阿凡达\" src=\"http://www.travelzjj.com/uploads/201506/271400220155.jpg\" width=\"730\" height=\"450\" title=\"张家界袁家界阿凡达\" align=\"\" /><br />\r\n</span>\r\n	</p>\r\n	<p >\r\n		<br />\r\n	</p>\r\n	<p >\r\n		<span style=\"line-height:1.5;\">袁家界景区虽然在地域上隶属于中国第一个国家森林公园——张家界国家森林公园，但从地理位置上来说，却被众星捧月般地位居张家界武陵源风景名胜区的核心，与世界上最美丽的峡谷金鞭溪一衣带水，东接索溪峪，南眺鹞子寨，西望黄石寨，北靠天子山，总面积约1200公顷，平均海拔1074米。</span>\r\n	</p>\r\n	<p >\r\n		<span style=\"line-height:1.5;\"><br />\r\n站在袁家界景区的观景台上，三千奇峰尽收眼底，雄险秀野，神态各异。峡谷深处，千百根石峰石柱奇伟突立。峻峭之石，如英武将帅；嵯峨之峰，似勇猛壮士。有声有色，若静若动，形象逼真，呼之欲出。</span>\r\n	</p>\r\n</p>\r\n									\r\n									'),(101,58,2,'风光美景','																		<p>\r\n	<p >\r\n		<span style=\"line-height:1.5;\"><img alt=\"哈利路亚山\" src=\"http://www.travelzjj.com/uploads/201506/271400533245.jpg\" width=\"730\" height=\"450\" title=\"哈利路亚山\" align=\"\" /></span>\r\n	</p>\r\n	<p >\r\n		<span style=\"line-height:1.5;\">                                                                                                                                                      摄影者：宋志光</span>\r\n	</p>\r\n	<p >\r\n		<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n	</p>\r\n	<p >\r\n		<span style=\"line-height:1.5;\"><a href=\"http://www.travelzjj.com/panorama/detail/1065.html\" target=\"_blank\" class=\"keywords\">袁家界</a>景色之美有“<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>后花园”之称，后来因美国科幻大片《阿凡达》在此取景而名声大噪。来到袁家界，一步一景美妙绝伦，原来，电影中的潘多拉星球真的存在。悬浮山的原型“南天一柱”现被更名为 “哈利路亚山”，“潘多拉太远，张家界很近”的广告词更是脍炙人口，传播甚广。</span>\r\n	</p>\r\n	<p >\r\n		<span style=\"line-height:1.5;\"></span> \r\n	</p>\r\n	<p >\r\n		<img alt=\"空中田园\" src=\"http://www.travelzjj.com/uploads/201506/271406422244.jpg\" width=\"730\" height=\"450\" title=\"空中田园\" align=\"\" />\r\n	</p>\r\n	<p >\r\n		                                                                                                                                                  摄影者：陈明明\r\n	</p>\r\n	<p >\r\n		<br />\r\n	</p>\r\n	<p >\r\n		<span style=\"line-height:1.5;\">景区内的代表性景点有：百龙天梯，世界上最高、载重量最大、运行速度最快的户外观光电梯；天下第一桥，桥面宽两米，厚四米，跨度为二十多米，高度为三百多米，是世界是迄今为止所发现落差最高的天然石桥。</span>\r\n	</p>\r\n</p>\r\n									\r\n									'),(102,58,3,'推荐理由','																		<p>\r\n	<span style=\"color:#666666;font-family:arial;font-size:14px;line-height:30px;\">最具特色的百龙天梯，垂直高差335米，运行高度326米，由154米山体内坚井和172米贴山钢结构井架等组成，采用三台双层全暴露观光电梯并列分体运行，每台次载客50人次，运行速度3米/秒，三台同时运行每小时往返量达4000人次。百龙天梯的修建实现“山上游，山下住”的目标，提供了交通保障，将</span><a href=\"http://www.travelzjj.com/panorama/detail/1065.html\" target=\"_blank\" class=\"keywords\">袁家界</a><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:30px;\">天下第一桥、迷魂台、天悬白练以及乌龙寨、</span><a href=\"http://www.travelzjj.com/panorama/detail/1060.html\" target=\"_blank\" class=\"keywords\">杨家界</a><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:30px;\">等绝世美景从幕后推向了前台，并将</span><a href=\"http://www.travelzjj.com/panorama/detail/1068.html\" target=\"_blank\" class=\"keywords\">天子山</a><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:30px;\">、袁家界、</span><a href=\"http://www.travelzjj.com/panorama/detail/1069.html\" target=\"_blank\" class=\"keywords\">金鞭溪</a><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:30px;\">等精品旅游线路连为一体，解决了多年困扰景区的交通瓶颈问题。</span>\r\n</p>\r\n									\r\n									'),(103,58,4,'交通路线','																		<p>\r\n	<p >\r\n		在<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>市中心汽车站（火车站旁50米）就可搭乘去往森林公园、武陵源的线路巴士。\r\n	</p>\r\n	<p >\r\n		武陵源吴家峪门票站进入：乘景区环保车至百龙天梯下站，乘电梯上山。\r\n	</p>\r\n	<p >\r\n		森林公园门票站进入：步行至<a href=\"http://www.travelzjj.com/panorama/detail/1069.html\" target=\"_blank\" class=\"keywords\">金鞭溪</a>千里相会景点处，从乱窜坡游道步行上山。\r\n	</p>\r\n	<p >\r\n		或者从<a href=\"http://www.travelzjj.com/panorama/detail/1060.html\" target=\"_blank\" class=\"keywords\">杨家界</a>索道、<a href=\"http://www.travelzjj.com/panorama/detail/1068.html\" target=\"_blank\" class=\"keywords\">天子山</a>索道上山后，乘景区环保车前往。\r\n	</p>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n									\r\n									'),(104,58,5,'游览须知','																		<p >\r\n	武陵源核心景区游览路线可简要形容为山下游览路线、山上游览路线；\r\n</p>\r\n<p >\r\n	<a href=\"http://www.travelzjj.com/panorama/detail/1065.html\" target=\"_blank\" class=\"keywords\">袁家界</a>属于山上游览线，可作为游览的起点，1日游推荐线路为：武陵源区-吴家峪门票站-景区环保车-百龙天梯上山-袁家界-景区环保车-<a href=\"http://www.travelzjj.com/panorama/detail/1060.html\" target=\"_blank\" class=\"keywords\">杨家界</a>-景区环保车-<a href=\"http://www.travelzjj.com/panorama/detail/1068.html\" target=\"_blank\" class=\"keywords\">天子山</a>-天子山索道下山-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需7小时。\r\n</p>\r\n<p >\r\n	袁家界为半环形游览线，迷魂台停车场进入景区-沿游道步行-天下第一桥停车场处景区，游览时间约1.5小时。\r\n</p>\r\n									\r\n									'),(115,59,1,'456','																											456\r\n									\r\n									\r\n									'),(116,59,2,'789','																											789\r\n									\r\n									\r\n									'),(125,36,1,'观风情园','									<p >\r\n	<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a><a href=\"http://www.travelzjj.com/panorama/detail/1086.html\" target=\"_blank\" class=\"keywords\">土家风情园</a>座落在湖南张家界市城区，交通便利。占地80余亩,总投资近亿元,是以湘西人的土司城堡为基础，以最完整的土家山寨旧址原貌为风格，于1997年原址修复后向游客开放，是与张家界自然山水交相辉映的一颗璀璨的文化明珠，是土家人朝拜、祭祀、祈祷和节庆的“圣地”，这里保存着土家族千年奇风异俗，园中的土司城堡、九重天世袭堂、土家山寨汇集了整个大湘西土家人历史的精粹，是集土家族的农耕文化、兵战文化、土司文化、建筑文化、民俗风情为一体的民族文化景点,开园以来接待了以国务院前总理朱镕基为代表的30多位党和国家领导人，中央电视台、省级电视台多次对风情园进行报道，2003年被列入了国家AAAA级景区，现每年接待中外游客60多万人次，构成了“山上观风景、山下看风情”行程精品线路。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	 <img alt=\"张家界土家风情园\" src=\"http://www.travelzjj.com/uploads/201506/261103596630.jpg\" width=\"730\" height=\"450\" title=\"张家界土家风情园\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	土家风情园是原土司城所在地，土司城建于清庸正八年（公元1730年）。土家风情园占地达120余亩，建筑群主要有走马转角楼和吊脚楼民居群两部分。\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<img alt=\"土家风情九重天吊脚楼\" src=\"http://www.travelzjj.com/uploads/201506/261103415393.jpg\" width=\"730\" height=\"450\" title=\"土家风情九重天吊脚楼\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	九重天吊脚楼为全木质结构，飞檐翘角，土家族民居里面的建筑元素在这里都可以找到，这里也是目前国内最大、最壮观的吊脚楼，有民间艺人设计建筑，高48米，九重十二层，层叠有致，从侧面看尤其壮丽。\r\n</p>\r\n									'),(126,36,2,'民俗风情','									<p >\r\n	民风民俗展示主要有土家婚俗、头饰服饰、蜡染、织锦、银匠、石匠以及奇石根雕，珍贵文物展等，还有整台原汁原味的土家文艺节目，如表现湖南土家原始劳作与生活情形的茅古斯舞、铜铃舞、<a href=\"http://www.travelzjj.com/panorama/detail/1121.html\" target=\"_blank\" class=\"keywords\">摆手舞</a>、打溜子等表演。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"土家风情毛古斯\" src=\"http://www.travelzjj.com/uploads/201506/261103105639.jpg\" width=\"730\" height=\"450\" title=\"土家风情毛古斯\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">这里是集土家族农耕文化、兵战文化、土司历史、建筑艺术、工艺美术、表演艺术、民俗风情及饮食起居为一体的大型民族文化景点，有醉人的土家拦门酒，神秘的土家祭祖<a href=\"http://www.travelzjj.com/active/\" target=\"_blank\" class=\"keywords\">活动</a>，土家历史文物展示，土家民俗表演，还有头饰服装、蜡染、织锦、银器、石器以及奇石根雕珍贵文物展等。拥有“九重天”、“千人茅古斯”两项世界吉尼斯之最纪录。 </span>\r\n</p>\r\n									'),(127,36,3,'推荐理由','									<span style=\"color:#666666;font-family:arial;font-size:14px;line-height:21px;\">园内建筑多为木石结构，雕梁画栋，金壁辉煌，飞檐翘角，玲珑雅致。代表作有土司城堡、摆手堂、湖南土家山寨、垕王宫等。特别是冲天楼，依山造阁，气势恢弘，楼高48米，九重十二层，曲径回复，独具匠心。</span>\r\n									'),(128,36,4,'交通路线','									<span style=\"color:#666666;font-family:arial;font-size:14px;line-height:21px;\">到</span><a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a><a href=\"http://www.travelzjj.com/panorama/detail/1086.html\" target=\"_blank\" class=\"keywords\">土家风情园</a><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:21px;\">，可以先坐火车到张家界火车站下，然后市内驱车5分钟到,即市南庄坪五子坡.车费不贵，</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">公交</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">5</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">路、</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">10</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">路、</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">1</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">路可达。</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:21px;\">包车去最多也不过20元。游客可以选择在离开张家界之前游玩土家风情园，既不用担心赶车或者飞机，又可以给自己在张家界的行程画上一个完美的句号！</span>\r\n									'),(133,62,1,'寺庙奇观','<p >\r\n	<img alt=\"张家界天门山寺\" src=\"http://www.travelzjj.com/uploads/201506/281140537775.jpg\" width=\"730\" height=\"450\" title=\"张家界天门山寺\" align=\"\" />\r\n</p>\r\n<p >\r\n	&nbsp;\r\n</p>\r\n<p >\r\n	<a href=\"http://www.travelzjj.com/panorama/detail/1071.html\" target=\"_blank\" class=\"keywords\">天门山</a>寺始建于明朝，历经儒、道、佛三教的融会变通，建寺以来就香火鼎盛，信士遍及辰、永、澧、常，是湘西地区的佛教中心，天门山也由此得名“湘西第一神山”。复建后的天门山寺占地两万余平方米，是目前湖南省境内海拔最高的佛教建筑群。\r\n</p>\r\n<p >\r\n	&nbsp;\r\n</p>\r\n<p >\r\n	<img alt=\"张家界天门山寺\" src=\"http://www.travelzjj.com/uploads/201506/281141199582.jpg\" width=\"730\" height=\"450\" title=\"张家界天门山寺\" align=\"\" />\r\n</p>\r\n<p >\r\n	&nbsp;\r\n</p>\r\n<p >\r\n	寺庙坐落山窝，视野开阔，极目东南，庄严端丽，尽显天界佛国雄姿。\r\n</p>'),(134,62,2,'天界佛国','<p >\r\n	<span><img alt=\"天门山李娜小屋\" src=\"http://www.travelzjj.com/uploads/201506/281145298984.jpg\" width=\"730\" height=\"450\" title=\"天门山李娜小屋\" align=\"\" /></span>\r\n</p>\r\n<p >\r\n	<span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 李娜小屋</span>\r\n</p>\r\n<p >\r\n	<span><br />\r\n</span>\r\n</p>\r\n<p >\r\n	<span>“承天地之灵气，接山水之精华，佛祖舍利给众生带来庇佑，也使天门奇景结无上佛缘。”<a href=\"http://www.travelzjj.com/panorama/detail/1071.html\" target=\"_blank\" class=\"keywords\">天门山</a>寺方丈怀梵法师引领着众人走进一个“天界佛国”，以最华丽的方式展现着湘西地区的佛教文化。1997年，李娜剃度受戒，法号释昌圣，从此遁迹空门。2009年有释迦摩尼故乡尼泊尔蓝毗尼迎请的释迦摩尼舍利正式安防在天门山寺，天门山寺成为湖南省内唯一一座拥有佛舍利的寺庙。</span>\r\n</p>'),(135,62,3,'交通路线','<span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">在市区可乘</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">5</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">、</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">6</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">、</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">10</span><span style=\"color:#666666;font-family:arial;font-size:14px;line-height:1.5;\">路等公交至市区<a href=\"http://www.travelzjj.com/panorama/detail/1071.html\" target=\"_blank\" class=\"keywords\">天门山</a>索道下站，乘缆车至天门山国家森林公园。</span>'),(136,62,4,'游览须知','<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">该景点位于<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a><a href=\"http://www.travelzjj.com/panorama/detail/1071.html\" target=\"_blank\" class=\"keywords\">天门山</a>国家森林内。乘索道终点下，山顶线路分东、西线，任走一条都会途经此地。</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	相关信息：天门山门票258元（含往返索道、天门山寺门票）\r\n</p>'),(171,71,1,'简介','<p >\r\n	<img alt=\"张家界崇山\" src=\"http://www.travelzjj.com/uploads/201506/291340163364.jpg\" width=\"730\" height=\"450\" title=\"张家界崇山\" align=\"\" />\r\n</p>\r\n<p >\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;摄影者：李东群\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	崇山——仙人溪之旅，既可以被定义为休闲赏红叶、体验农家乐的经典线路，也可以被户外资深人士定义为接受更大挑战——七星山的必经之路。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界崇山\" src=\"http://www.travelzjj.com/uploads/201506/291340479608.jpg\" width=\"730\" height=\"450\" title=\"张家界崇山\" align=\"\" />\r\n</p>\r\n<p >\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 摄影者：李东群\r\n</p>'),(196,68,1,'剧情介绍','									<p >\r\n	<img alt=\"魅力湘西追爱\" src=\"http://www.travelzjj.com/uploads/201506/261117217884.jpg\" width=\"730\" height=\"450\" title=\"魅力湘西追爱\" align=\"\" />\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">魅力湘西剧场的舞台演出持续了十余年，在国内、港澳台、日韩、东南亚均拥有极高的知名度，为武陵源核心景区享誉盛名的大剧院。</span><span style=\"line-height:1.5;\">2012年龙年春晚的杂技歌舞节目《追爱》，便是出自魅力湘西剧场的作品。《追爱》原名叫《爬楼》，是魅力湘西剧场经久不衰的经典剧目。</span>\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\"> </span><img alt=\"春晚节目追爱\" src=\"http://www.travelzjj.com/uploads/201506/261117590242.jpg\" width=\"729\" height=\"450\" title=\"春晚节目追爱\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	而在魅力湘西剧场原址，《爬楼》的效果完全超越春晚舞台的表演。灯光亮出，少数民族的阿哥从观众席中突然跑出来，通过借力用力的配合，身手不凡的演员们，瞬间窜上数米高的阁楼，展现演员们极佳的弹跳力和柔韧性。舞台上的演员们更表现出极好的平衡，能几个人同时在一张双脚落地的梯子上作出惊险的动作，引得观众惊呼连连。\r\n</p>\r\n									'),(197,68,2,'演出特色','									<p >\r\n	<img alt=\"魅力湘西\" src=\"http://www.travelzjj.com/uploads/201506/261119006345.jpg\" width=\"730\" height=\"450\" title=\"魅力湘西\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	魅力湘西的节目取材，全都来自于湘西的民间艺术和生活：《边城》中的翠翠，划着小船出场，动如雷震的苗鼓，敲出了大山的力量。人们熟悉的桑植民歌《郎在高山打一望》、《马桑树儿搭灯台》在舞台上有了全新的演绎；哭嫁也被搬上了舞台，让人们欣赏到濒临失传的《哭嫁歌》。原始的祭祀舞茅古斯，延续着湘西人原始的信仰；神秘可怖的湘西赶尸，转成了一个令人感伤落泪的故事。湘西侗寨的《合拢宴》，在节目尾声将气氛推向高潮，强烈的节奏感，牵动着每个观众的神经。\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<img alt=\"魅力湘西\" src=\"http://www.travelzjj.com/uploads/201506/261119359094.jpg\" width=\"730\" height=\"450\" title=\"魅力湘西\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	室内演出之后，室外还有一场原生态的表演。苗族芦笙，侗族大歌，上刀山下火海，鬼谷神功轮番上阵，还有令人捧腹的互动游戏。\r\n</p>\r\n									'),(198,68,3,'交通路线','									<p class=\"MsoNormal\" >\r\n	市中心汽车站乘“市区<span>-</span>武陵源”专线车直达<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<b>联系方式<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	电话：<span>0744-5667777</span>\r\n</p>\r\n									'),(199,68,4,'观演须知','									<p class=\"MsoNormal\" >\r\n	<b>票价<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	挂牌价<span>268</span>元<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<b>演出详情</b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	时间：<span>17</span>：<span>30,19</span>：<span>30</span>，<span>21:30</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	地点：张家界魅力湘西国际文化广场\r\n</p>\r\n									'),(200,67,1,'剧情介绍','									<p >\r\n	<img alt=\"室外演出天门狐仙\" src=\"http://www.travelzjj.com/uploads/201506/271945442698.jpg\" width=\"730\" height=\"450\" title=\"室外演出天门狐仙\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                                                                                                     摄影者：李钢\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	《天门狐仙》是全世界首部以高山峡谷为背景和舞台的实景音乐剧演绎的是包括张家界内的大湘西流传的神话爱情故事。纵深5公里、海拔高差1100米的峡谷舞台，纳入了大自然的雄奇俊美。最高处是豁然悬廊的天门洞，山坡上建造了土家吊脚楼、飞瀑流泉、溜索、飞桥。这部音乐剧完全突破了人们熟知的舞台效果，融入了歌舞、武术、魔术、杂技等舞台艺术，宏大的灯光效果，营建出一个如梦如幻的世界。\r\n</p>\r\n									'),(201,67,2,'演出特色','									<p >\r\n	<img alt=\"天门狐仙\" src=\"http://www.travelzjj.com/uploads/201506/271946402946.jpg\" width=\"730\" height=\"450\" title=\"天门狐仙\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	《天门狐仙》又名《新刘海砍樵》。《刘海砍樵》是湖南著名的花鼓戏，主要流传在湘西一带。这出花鼓戏源起于神话传说，发源地在常德，讲述了樵夫刘海和千年狐狸精胡秀英的爱情故事。\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<img alt=\"天门狐仙刘海哥\" src=\"http://www.travelzjj.com/uploads/201506/271947011280.jpg\" width=\"730\" height=\"450\" title=\"天门狐仙刘海哥\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                              刘海哥                                                          摄影者：李东群\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	本剧通过对这个经典故事的重新构思、巧妙编排，并在音乐旋律的引领下，层层展开一幅幅绚丽优美、哀婉生动的舞台场景，重新讲述了以砍柴为生的青年樵夫刘海和在天门山中修炼千年的白狐仙之间曲折而浪漫的爱情经历。融入了桑植等地的民歌和土家的民风、民俗，在大型的真实场景中，表演的歌舞剧。以灯光和背景的大型营造以及歌曲独特风格为胜。\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<img alt=\"狐仙\" src=\"http://www.travelzjj.com/uploads/201506/271948171555.jpg\" width=\"730\" height=\"449\" title=\"狐仙\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                             狐仙                                                              摄影者：李东群\r\n</p>\r\n									'),(202,67,3,'交通路线','									<p class=\"MsoNormal\" >\r\n	地点：天门山峡谷大剧场<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<b>联系方式<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	电话：<span>0744-8369999</span>\r\n</p>\r\n									'),(203,67,4,'观演须知','									<p class=\"MsoNormal\" >\r\n	<b>票价<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	根据不同的座次，有<span>238</span>元、<span>580</span>元、<span>880</span>元。（演出基本不会受天气影响，每年冬季停演，时间大致在<span>12</span>月中下旬<span>-</span>次年中上旬）<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<b>演出详情</b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	时间：每天<span>20:15</span>\r\n</p>\r\n									'),(204,66,1,'剧情介绍','									<p >\r\n	<img alt=\"烟雨张家界\" src=\"http://www.travelzjj.com/uploads/201506/271938084039.jpg\" width=\"730\" height=\"450\" title=\"烟雨张家界\" align=\"\" />\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">《烟雨张家界》讲述了远古时代在烟雨缥缈的张家界大山深处，土家族英俊青年洛巴冲和苗族美丽姑娘阿依朵之间缠绵悱恻、美丽动人、荡气回肠的爱情故事。</span>\r\n</p>\r\n									'),(205,66,2,'演出特色','									<p >\r\n	<img alt=\"烟雨张家界\" src=\"http://www.travelzjj.com/uploads/201506/271938314437.jpg\" width=\"730\" height=\"450\" title=\"烟雨张家界\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	《烟雨张家界》时长80分钟，10个选段围绕洛巴冲和阿依朵的爱情主线，展示了张家界地区历史悠久的农耕文化、巫傩文化、建筑文化、服饰文化、饮食文化、婚俗文化，其中桑植民歌、茅古斯舞蹈、土家织锦、打溜子、哭嫁等更是列入了国家非物质文化遗产名录。\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<img alt=\"烟雨张家界\" src=\"http://www.travelzjj.com/uploads/201506/271938489921.jpg\" width=\"730\" height=\"450\" title=\"烟雨张家界\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	这台节目是中国首台3D视觉大型民俗文化演出，也是中国首个模拟实景剧场演出，利用现代声、光、电等科技设施，配合美妙而震撼的配乐以及逼真的超大视频特效，真人与视频的完美结合，演员在屏幕间来回穿梭，让人产生亦真亦幻、如烟似雨的感觉，向世人演绎魅力、神秘的张家界民族风情。整台大戏时长只有80分钟，穿越的却是张家界几千年的辉煌灿烂文化。\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<img alt=\"烟雨张家界\" src=\"http://www.travelzjj.com/uploads/201506/271939090230.jpg\" width=\"730\" height=\"450\" title=\"烟雨张家界\" align=\"\" />\r\n</p>\r\n									'),(206,66,3,'交通路线','									<p class=\"MsoNormal\" >\r\n	<b>交通<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	自驾线路<span>:</span>长沙至张家界高速公路在阳和收费站下高速，到黄龙洞只有<span>17</span>公里。<span><br />\r\n</span>公交线路<span>:</span>张家界火车站距黄龙洞<span>48</span>公里有直达武陵源的大巴，或者从火车站打的至张家界汽车站再转车到武陵源。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<b>联系方式<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	电话：<span>400-883-1111</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	         0744-5628756\r\n</p>\r\n									'),(207,66,4,'观演须知','									<p class=\"MsoNormal\" >\r\n	<b>票价</b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	挂牌价<span>198</span>元<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<b>演出详情<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	时间：<span>11:00</span>、<span>17:00</span>，每场<span>35</span>分钟<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	地点：哈利路亚音乐厅\r\n</p>\r\n									'),(208,72,1,'简介','									<p >\r\n	<img alt=\"张家界崇山\" src=\"http://www.travelzjj.com/uploads/201506/291340163364.jpg\" width=\"730\" height=\"450\" title=\"张家界崇山\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                                                                                                 摄影者：李东群\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	崇山——仙人溪之旅，既可以被定义为休闲赏红叶、体验农家乐的经典线路，也可以被户外资深人士定义为接受更大挑战——七星山的必经之路。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界崇山\" src=\"http://www.travelzjj.com/uploads/201506/291340479608.jpg\" width=\"730\" height=\"450\" title=\"张家界崇山\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                                                                                                摄影者：李东群\r\n</p>\r\n									'),(209,69,1,'简介','									<p >\r\n	<img alt=\"八大公山\" src=\"http://www.travelzjj.com/uploads/201506/272047340897.jpg\" width=\"730\" height=\"450\" title=\"八大公山\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	八大公山自然保护区总面积2万公顷，分为天平山、杉木界、斗篷山三大林区，主峰斗篷山海拔1898.4米，为武陵山脉南麓之巅。气候属北亚热带山地湿润季风气候，夏秋季短，春冬季长，最冷月平均气温0.1℃，最热月平均气温22.8℃，相对湿度90%以上。区内群山起伏，山脉纵横交错，599座山峰星罗棋布，奇峰突起，溪流、瀑布众多。独特的地理环境，孕育和保存了亚热带最完整、面积最大原生型常绿阔叶林。\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<img alt=\"张家界八大公山\" src=\"http://www.travelzjj.com/uploads/201506/272047553808.jpg\" width=\"730\" height=\"450\" title=\"张家界八大公山\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n									'),(210,69,2,'户外攻略','									<p class=\"MsoNormal\" >\r\n	【资源种类】原生性森林、瀑布、山峦\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	【户外项目】徒步山地穿越、溯溪、岩降、瀑降、水上抱石、定向越野、野营等\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	【海拔范围】346m至2003m\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">【线路难度】5星</span><span style=\"line-height:1.5;\">               </span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">【线路强度】4星</span><span style=\"line-height:1.5;\">                               </span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">【线路风景】4星</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">【装备要求】速干衣裤、冲锋衣、防寒衣、登山包、登山鞋、登山杖、水袋、手套、头巾、野炊用具、露营装备、头盔、药品等</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">【周边资源】贺龙故居、廖汉生故居、桑鹤土司结盟遗址、澧水源头七眼泉等。</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 <strong>【交通指引】</strong>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;font-size:14px;font-family:arial;color:#666666;text-indent:0cm;\">\r\n	1.进入：桑植汽车西站至芭茅溪乡（20元），然后租车进入辽竹湾村（穿越的起点）。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;font-size:14px;font-family:arial;color:#666666;text-indent:0cm;\">\r\n	2.回程：下山后再沙园联系芭茅溪车主接至芭茅溪，原路返回桑植县。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 \r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<strong>行程</strong>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	DAY1\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	桑植县-芭茅溪-四门岩林场\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	[备注]桑植县至芭茅溪乡后中餐，推荐参观：圆梦饭庄（价格20-30元/人，联系电话：13974426576）。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	中餐后租车往辽竹湾，在辽竹湾村部下车后沿小路步行10km左右至四门岩林场，宿营四门岩林场场部。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	DAY2\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	四门岩林场-长坪-大顶坪\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 \r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	[备注]\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	从四门岩林场出发，翻山至长坪峡谷入口，沿长坪沟溯溪而上，长坪午休，午休后顺干谷河床一路向上提升面，直达大顶坪宿营。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	DAY3\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	大顶坪-瞭望塔-小庄坪（天平山接待站）\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	DAY4\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	小庄坪-连三台-毛垭半坡\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	[备注]沿山腰猎户小道环山至青龙湾，青龙湾对面有一小溪沟，顺沟而下可见三棵枯树，再往下见干枯河床，顺河床而下至漆水塔休息。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	顺峡谷而下行至毛垭半坡农户家宿营。全程无手机信号，需9小时。晚上可住宿当地农家，体验土家族的日常生活。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	DAY5\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	毛垭半坡-黄连台（李家河）\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	[备注]\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	早餐后从毛垭半坡至毛垭村，有小卖部补结，从毛垭村小路下山至黄连台村长宿营，全天行程需7小时。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	黄连台村村长杨志山15074408816\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	            书记刘伟华13407443072\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	DAY6\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	黄连台-小溪口-天梯人家-七湾-芭茅溪\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	[备注]\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	至黄连台村部，沿溪边土公路往下到达小溪口，公路可达芭茅溪镇，公路旁小溪进山约40分钟山路到达天梯人家，此处可观60m瀑布。天梯人家徒步七湾下山到达天平山公路，可联系包车来接至芭茅溪，车费30元/车（司机张师傅13707446937）。<span style=\"line-height:1.5;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\"><img alt=\"张家界八大公山地图\" src=\"http://www.travelzjj.com/uploads/201506/272049474905.jpg\" width=\"730\" height=\"450\" title=\"张家界八大公山地图\" align=\"\" /><br />\r\n</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n									'),(211,69,3,'安全提示','									<p class=\"MsoNormal\" >\r\n	【安全提示】该线路需要扁带、对讲机、急救药品等公共装备方可成行，切勿孤身或私下结伴前往，需联系当地户外俱乐部提供专业向导和保护。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	此行程路途较长，而且进入谷底，手机信号不好，也有可能没有；又出于隐雷区，雨天建议慎重出行。谷中暂时没有小商品出售，进入景区要带足食物和饮水保持体力，可以带些高能量的食物，如巧克力、饼干、肉类等；景区中有许多飞禽走兽，请远离并保护它们，切勿伤害它们；山中雨雾笼罩，有时豁然开朗，横穿石壁，自然温服比较低，风力也会大些，请务必注意保暖，适当带些衣服，防止温度突变，引起身体不适。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	特别提醒：八大公山毒蛇（五步蛇）出没，要防止被毒蛇咬伤。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	遇险求助电话：<span style=\"line-height:1.5;\">八大公山乡政府0744-6730001</span>\r\n</p>\r\n									'),(215,75,1,'凉面','									<p >\r\n	<img alt=\"张家界小吃凉面\" src=\"http://www.travelzjj.com/uploads/201506/281345216088.jpg\" width=\"730\" height=\"450\" title=\"张家界小吃凉面\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	张家界凉面算的上是一道独特的风景，走在大街小巷，随处都可见卖凉面小吃摊。卖的人简单，一个小小的两轮车就解决了店面的问题。吃的人形态各异，或坐、货站、或蹲、或边走边吃，吃凉面的人也不分男女老少，季节不分春夏秋冬。\r\n</p>\r\n									'),(216,75,2,'酸萝卜','									<p >\r\n	<img alt=\"张家界小吃酸萝卜\" src=\"http://www.travelzjj.com/uploads/201506/281345342755.jpg\" width=\"730\" height=\"450\" title=\"张家界小吃酸萝卜\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	土家腌酸萝卜别具特色，味道特别鲜脆。在张家界，只要有人群来往的地方，就会有卖腌酸萝卜的摊子。甜萝卜汁水很多，香脆可口、甜中带麻；甜萝卜皮，嚼起来“嘎嘣嘎嘣”响！萝卜本身的清甜和那香浓的辣味在嘴里融为一体，十分美味，是开胃的绝佳美食。\r\n</p>\r\n									'),(217,75,3,'草帽面','									<p >\r\n	<img alt=\"张家界特色小吃草帽面\" src=\"http://www.travelzjj.com/uploads/201506/281353337630.jpg\" width=\"730\" height=\"450\" title=\"张家界特色小吃草帽面\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	这个名字的由来是因为老板常年戴着草帽卖面，店铺内现仍挂着戴了30多年的草帽。草帽面的面全手工自擀，猪面皮，熟猪油，臊子非常香，其特点吃要一个快，热面热胃肠。\r\n</p>\r\n									'),(218,75,4,'社饭','									<p >\r\n	<img alt=\"张家界特色小吃社饭\" src=\"http://www.travelzjj.com/uploads/201506/281345496253.jpg\" width=\"730\" height=\"450\" title=\"张家界特色小吃社饭\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	社饭自古有之，是中国汉、土家、苗、侗族等民族祭祀社稷的一种食品。土家人做社饭不光是自家人吃，还把它作为馈赠亲友的佳品，故有民谚说：“送完了自家的，吃不完别家的。”充分显示出土家人淳朴、亲和的民风。由于社蒿（香蒿）具有很好的药理作用，其性苦寒，能治疗和预防伤、肿痛、痨、疟、痢、痔等多种疾病，所以社饭是土家人传统药膳中的一个常用品种。社饭既有糯米的甜香，又有腊肉的熏味，还有社菜的清香，饭一开锅，香味四溢，直令人馋涎欲滴，食欲大振。<span style=\"line-height:1.5;\"> </span>\r\n</p>\r\n									'),(219,75,5,'糖油粑粑','									<p >\r\n	<img alt=\"张家界特色小吃糖油粑粑\" src=\"http://www.travelzjj.com/uploads/201506/281346073341.jpg\" width=\"730\" height=\"450\" title=\"张家界特色小吃糖油粑粑\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	糖油粑粑算的上是湖南传统名吃，主要原料是糯米粉和糖，其制造工艺精细讲究，有特殊的制造过程。它虽不能登大雅之堂，更不能与山珍海味、鱼翅熊掌相媲美，但正是因其廉价的身份，它能出入平常百姓家，受到民众的厚爱，成为民间吃不厌的小吃。\r\n</p>\r\n									'),(220,74,1,'土家扣肉','									<p >\r\n	<img alt=\"张家界特色菜梅菜扣肉\" src=\"http://www.travelzjj.com/uploads/201506/281623458582.jpg\" width=\"730\" height=\"450\" title=\"张家界特色菜梅菜扣肉\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	张家界土家扣肉是土家人待客的一道主菜，一般只有在当地的人办酒席或大型活动的时候才会上用作主菜。土家扣肉的做法均采用五花猪肉，经过煮、炸、炸、蒸等多道工艺制成，上席时肉的下面一般都会用干咸菜铺底。虽说是肥肉制成，但吃起来一点也不腻口，味道极其鲜嫩。目前张家界一般餐馆及酒店都备有这道菜，以供旅游者享用。\r\n</p>\r\n									'),(221,74,2,'娃娃鱼','									<p >\r\n	<img alt=\"张家界娃娃鱼\" src=\"http://www.travelzjj.com/uploads/201506/281613045884.jpg\" width=\"730\" height=\"450\" title=\"张家界娃娃鱼\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">“娃娃鱼是国家二级保护动物，怎么可以吃呢？”别称“娃娃鱼”的大鲵真的可以吃，因为它们是在仿野生环境的饲养条件下，人工培育的。但是，只有拿到了大鲵特许经营利用证的酒店才能经营娃娃鱼系列餐饮。</span>\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	娃娃鱼肉嫩味鲜，营养丰富，有抗衰老、驻颜的功效。用娃娃鱼鱼头跟爪子炖汤，可健胃理气、祛风除湿，是老年人最好的滋补品。\r\n</p>\r\n									'),(222,74,3,'土家十大碗','									<p >\r\n	<img alt=\"张家界名菜十大碗\" src=\"http://www.travelzjj.com/uploads/201506/281429421045.jpg\" width=\"730\" height=\"450\" title=\"张家界名菜十大碗\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	土家十大碗来源于当地民间节庆及办酒席时惯用的方式，其最大的特点是半荤半素、一菜两味，餐桌上看不到盘子，吃饭及装菜全部是蓝边大口碗。久而久之，土家族人就给宴席取名为“土家十大碗”。除十大碗菜外，桌上还要配腌菜碟两个，为客人备用，另配上土家字自酿的苞谷烧酒。上菜的程序与方式也很有讲究，在土家民间每上一道菜，打盘子的人高喊拖腔的号子直到菜上完，优势还要鸣炮，吹响欢快的菜调子。\r\n</p>\r\n									'),(223,74,4,'三下锅','									<p >\r\n	<img alt=\"张家界三下锅\" src=\"http://www.travelzjj.com/uploads/201506/281429282509.jpg\" width=\"730\" height=\"450\" title=\"张家界三下锅\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	说到张家界的食物，绝大多数的人会立马想到三下锅。相传明代嘉靖年间，朝廷征调湘鄂西土司兵上前线抗倭，恰好赶上年关。为了不耽误保家卫国的大事，土司王下令提前一天过年，也是腊肉、豆腐、萝卜一锅煮，叫吃“合菜”，以后演变成“三下锅”。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	土家三下锅的主材有腊肉、核桃肉（猪头）、肥肠、猪肚、牛肚、猪脚等，食客可从其中任意选择三样。辅以萝卜干、西红柿、土豆、豆腐、千张皮（豆皮）等配菜，以及花椒、桂皮等香料，味道又香又辣。\r\n</p>\r\n									'),(224,73,1,'岩耳炖土鸡','									<p >\r\n	<img src=\"http://www.travelzjj.com/uploads/201506/281412228957.jpg\" alt=\"张家界特色菜岩耳炖鸡\" width=\"730\" height=\"450\" title=\"张家界特色菜岩耳炖鸡\" align=\"\" />\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	岩耳生峭壁，形状和木耳相似，但比木耳大，表面呈黑色，有细刺，一般要六七年才能长成。土鸡养农家。岩耳炖土鸡，具有极高的营养价值，曾是土司进贡给皇上的御菜。\r\n</p>\r\n									'),(225,73,2,'擂辣椒','									<p >\r\n	<img src=\"http://www.travelzjj.com/uploads/201506/281622331223.jpg\" alt=\"张家界特色菜了擂辣椒\" width=\"730\" height=\"450\" title=\"张家界特色菜了擂辣椒\" align=\"\" />\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	擂辣椒是有名的张家界常菜，传统做法是用油锅煸炒大尖椒至表皮糊焦，再泡入冷开水中剥皮去籽，放入擂钵中捣碎，拌入调料。这道擂辣椒综合了川菜的干煸厨艺和虎皮尖椒的做法，最后成品貌似钵中捣碎。适合家庭操作，口味也相当不错！很多擂辣椒的菜谱用大青椒做这道菜，不过大尖椒辣味不足。小尖椒辣味劲道，塞在馒头里也很好吃！\r\n</p>\r\n									'),(226,73,3,'合渣','									<p >\r\n	<img src=\"http://www.travelzjj.com/uploads/201506/281413554256.jpg\" alt=\"张家界必点菜合渣\" width=\"730\" height=\"450\" title=\"张家界必点菜合渣\" align=\"\" />\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	土家极喜欢的菜肴。在土家族的民间歌谣中，有这么几句：辣椒当盐，合渣过年，一条裤子穿它几十年····这几句话夸张地描述了土家人在解放前的辛酸生活。合渣的制作比较简单，只是在开头“推”的阶段辛苦些，因此，土家人称制作合渣为“推合渣”—将黄豆用水泡胀后，在石磨上一转一转地磨成豆浆，再将豆浆兑水放进锅，架火煮开，然后放进切好的菜丝，又煮开，就制成了一锅乳白带绿的合渣。由此可见，“推合渣”比起打豆腐来，要容易的多，难怪土家人又称合渣为“懒豆腐”。合渣最好的吃法是把合渣和米饭拌在一起吃，比酸菜都要下饭。\r\n</p>\r\n									'),(227,73,4,'土家腊味','									<p >\r\n	<img src=\"http://www.travelzjj.com/uploads/201506/281414093232.jpg\" alt=\"张家界必点菜腊肉\" width=\"730\" height=\"450\" title=\"张家界必点菜腊肉\" align=\"\" />\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	湖南盛产腊味，以土家腊味为最，只有土家腊味是真正的通过柴火熏烤而成，腊味品种很多，有腊鱼、腊肉、腊肠、腊猪肚、腊猪肝、腊野味等。这些腊味黑中透红、肥而不腻、焦而不脆，闻之腊香扑鼻，食之满口留香。\r\n</p>\r\n<p >\r\n	每年腊月间，人们先将猪肉切成条条块块，然后将肉抹上盐，再放进坛子里或木桶中，腌制一个星期后取出风干，然后用柴火熏烤。吃腊肉时，吧腊肉放在锅里烹煮，蒸熟的腊肉，香飘十里，勾人食欲，是土家人平时招待客人摆在席上的主菜。\r\n</p>\r\n<p >\r\n	来湘西如不食腊味，定会让你遗憾不已！\r\n</p>\r\n									'),(228,76,1,'星级酒店','																		<p class=\"MsoNormal\" >\r\n	<span>张家界星级酒店、商务宾馆、度假村、民俗客栈、家庭旅馆一应俱全，来张家界旅游，您无论选择何种住宿设施，都会得到土家人民热情、温馨而周到的服务。</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 \r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<img alt=\"张家界五星级阳光酒店\" src=\"http://www.travelzjj.com/uploads/201506/281253303835.jpg\" width=\"730\" height=\"450\" title=\"张家界五星级阳光酒店\" align=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 \r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">星级酒店43家设施完善、舒适的星级酒店，房间标准、价格从100元/间—2000元/间不等，酒店通常会根据淡旺季上下浮动。大部分酒店提供个性化服务，您可根据自己的需求进行选择。<span id=\"__kindeditor_bookmark_end_7__\"></span></span>\r\n</p>\r\n									\r\n									'),(229,76,2,'商务宾馆','																		<p class=\"MsoNormal\" >\r\n	<img alt=\"张家界商务酒店\" src=\"http://www.travelzjj.com/uploads/201506/281253107088.jpg\" width=\"730\" height=\"449\" title=\"张家界商务酒店\" align=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 \r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	商务宾馆其特点是设施简单、整洁、经济、互联网系统让您在旅行休息中能了解更多的信息。张家界商务宾馆价格一般在100元/间—200元/间，大部分商务宾馆不提供餐食服务，根据需要您可到周边的餐馆选择就餐。\r\n</p>\r\n									\r\n									'),(230,76,3,'度假村','																		<p class=\"MsoNormal\" >\r\n	<img alt=\"张家界禾田居度假酒店\" src=\"http://www.travelzjj.com/uploads/201506/281252577085.jpg\" width=\"730\" height=\"450\" title=\"张家界禾田居度假酒店\" align=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 \r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	如果您想将自己完全置身于放松状态，不为工作、学习所累，那就选择度假村吧！张家界有温泉度假村、别墅式旅游度假村，不同的类型，不同的感受。由于服务内容的不同，所以度假村的价格也有很大差别。\r\n</p>\r\n									\r\n									'),(231,76,4,'名俗客栈','																		<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\"><img alt=\"张家界民俗客栈\" src=\"http://www.travelzjj.com/uploads/201506/281311173621.jpg\" width=\"730\" height=\"450\" title=\"张家界民俗客栈\" align=\"\" /></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">民俗客栈外观如百年老宅，为土家吊脚楼结构，秉承“精致古朴、高压不俗、内敛不简”设计理念，融汇了神秘湘西土风苗韵之精髓。有土家新娘床、土家阿公房、复式阁楼，如果您想体验土家人对居住的温馨和浪漫您可以在这些民俗客栈体验一把。</span>\r\n</p>\r\n									\r\n									'),(232,76,5,'家庭旅馆','																		<p class=\"MsoNormal\" >\r\n	<img alt=\"张家界家庭旅馆\" src=\"http://www.travelzjj.com/uploads/201506/281252349260.jpg\" width=\"730\" height=\"450\" title=\"张家界家庭旅馆\" align=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 \r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	家庭旅馆一般都在武陵源核心景区内，是一些背包客、学生、摄影爱好者的首选。因为它价格便宜、位置条件优越，但在深山里家庭旅馆住宿条件有限。住在这里夏天您可以不用开空调，早上您可以起的早早的带上您的摄影设备找好机位可以欣赏到美丽的日出。\r\n</p>\r\n									\r\n									'),(233,65,1,'峰林奇观','																		<p class=\"MsoNormal\" >\r\n	<img alt=\"张家界天子山\" src=\"http://www.travelzjj.com/uploads/201506/271456235998.jpg\" width=\"730\" height=\"450\" title=\"张家界天子山\" align=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	                                                                                                                                                      摄影者：文绍军\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span>天子山位于武陵源核心景区东北面，海拔</span><span>1200</span><span>多米，景区内石峰林立，沟壑纵横，被誉为“扩大的盆景，缩小的仙境”。相传，土家民族英雄“向王天子”，带领土家族人反对朝廷暴政。在此与前来围剿的官兵殊死搏斗，兵败后宁死不屈，跳下了神堂湾。人们为了</span><span>念这位土家族英雄，而取名“天子山”。即使后来清王朝采用各种压抑措施。强令取消“天子”二字。但土家族人民却一直称“天子山”。</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span></span> \r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<img alt=\"张家界天子山雪景\" src=\"http://www.travelzjj.com/uploads/201506/271456524049.jpg\" width=\"730\" height=\"450\" title=\"张家界天子山雪景\" align=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	                                                                                                                                                        摄影者：赵敏\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span>因景区内峰林耸立如海，故被游人赞为“峰林之王”。那些遗世而独立的石峰，屹立在岁月的长河中，以至于每一座孤峰都拥有着生命，向您无言地倾诉着他们积攒下的无数故事。不会腾云的我们，只能在路可以相连的山壁上行走。而那些雄壮的山体，也许再过</span><span>十万载，又演变成了新的峰林。</span>\r\n</p>\r\n									\r\n									'),(234,65,2,'风光美景','																		<p >\r\n	<img alt=\"张家界天子山云海\" src=\"http://www.travelzjj.com/uploads/201506/271259359819.jpg\" width=\"730\" height=\"450\" title=\"张家界天子山云海\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                                                                                                     摄影者：孙建华\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<span>天子山还有“云雾、月夜、霞日、冬雪”四大奇观。天子山最多见的云雾奇观，景象万千，有云雾、云海、云涛、云瀑、云彩等。久雨初晴之后或霏霏细雨之中，先是朦胧大雾，继而形成白云，环绕飘浮。群峰在无边无际的云海中时隐时现，使人仿佛置身于蓬莱仙境之中。霞日，晴天的早晨，一轮红日在朵朵红云的衬托下，从奇山异峰中冉冉升起。千万座石塔，婷婷玉立，披上一层红霞，十分迷人。</span>\r\n</p>\r\n<p >\r\n	<span><span></span><br />\r\n </span>\r\n</p>\r\n<p >\r\n	<img alt=\"张家界天子山雪景\" src=\"http://www.travelzjj.com/uploads/201506/271259482692.jpg\" width=\"730\" height=\"450\" title=\"张家界天子山雪景\" align=\"\" />\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">                                                                                                                                                  摄影者：孙建华</span>\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">冬雪，入冬，天子山银装素裹，青松和峰顶被大雪覆盖。此时的天子山冰柱倒立，冰花四散，只有一些山鹿、野猪，才不为银白世界所征服。</span>\r\n</p>\r\n									\r\n									'),(235,65,3,'交通路线','																		<p >\r\n	在张家界市中心汽车站（火车站旁50米）就可搭乘去往武陵源的线路巴士。\r\n</p>\r\n<p >\r\n	武陵源吴家峪门票站进入：乘景区环保车至天子山索道下站，乘缆车上山，或从卧龙岭步行上山。\r\n</p>\r\n<p >\r\n	或者从杨家界索道、百龙天梯上山后，乘景区环保车前往。\r\n</p>\r\n									\r\n									'),(236,65,4,'游览须知','																		<p >\r\n	武陵源核心景区游览路线可简要形容为山下游览路线、山上游览路线；\r\n</p>\r\n<p >\r\n	天子山属于山上游览线，可作为游览的起点，1日游推荐线路为：武陵源区-吴家峪门票站-景区环保车-天子山索道上山-天子山-景区环保车-杨家界-景区环保车-袁家界-百龙天梯下山-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需7小时。\r\n</p>\r\n<p >\r\n	天子山常规游览多在贺龙公园游览区（时间约2小时）。如果时间允许，体力充沛，可选择去茶盘塔游览区（时间约1.5小时）、老屋场游览区游览（时间约3小时）。\r\n</p>\r\n<p >\r\n	老屋场云海是最直接推荐的拍摄地，云雾缭绕，仙气逼人。从丁香榕路口至老屋场，尚有4公里余山路，可乘当地面包车、三轮车前往。若是选择徒步，要考虑此道是一路下坡，路上布满石子，晴天三尺土，雨天一身泥。\r\n</p>\r\n<p >\r\n	相关信息\r\n</p>\r\n<p >\r\n	门票：已含在武陵源核心景区门票内（票价248元/3天有效）\r\n</p>\r\n<p >\r\n	地址：张家界市武陵源区天子山风景区\r\n</p>\r\n									\r\n									'),(242,48,1,'山水画卷','																																				<p >\r\n	<span style=\"line-height:1.5;\">山水画卷位于武陵源核心景区中部，来回全长10里。峡谷两旁千峰耸立，林木葱笼，野花飘香；奇峰异石，千姿百态，像一幅巨大的山水画卷，并排悬挂在千韧绝壁之上，使秀美绝伦的自然奇观溶进仙师画工的水墨丹青之中；人行其间，三步一景，如在画中遨游，故有“十里画廊”之美誉。</span>\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界十里画廊\" src=\"http://www.travelzjj.com/uploads/201506/271341425442.jpg\" width=\"730\" height=\"450\" title=\"张家界十里画廊\" align=\"\" />\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<p >\r\n	十里画廊是<a href=\"http://www.travelzjj.com/panorama/detail/1051.html\" target=\"_blank\" class=\"keywords\">张家界国家森林公园</a>景区内的旅游精华，在这条长达十余里的山谷两侧，有着丰富的自然景观，人行其间如在画中。沟旁黛峰屏列，山上的岩石形成了200来尊似人似物、似鸟似兽的石景造型，其中“孔雀开屏”、“采药老人”、“寿星迎宾”、“猛虎啸天”等最为著名。\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(243,48,2,'美景风光','																																				<p >\r\n	<img alt=\"张家界十里画廊小火车\" src=\"http://www.travelzjj.com/uploads/201506/271346472423.jpg\" width=\"730\" height=\"450\" title=\"张家界十里画廊小火车\" align=\"\" />\r\n</p>\r\n<p >\r\n	                       \r\n</p>\r\n<p >\r\n	 <a href=\"http://www.travelzjj.com/panorama/detail/1066.html\" target=\"_blank\" class=\"keywords\">十里画廊</a>是核心景区内，目前唯一可乘交通工具全程游览的景区，观光电车不仅是游客朋友以车代步、轻松观景的美妙体验载体，更是游览十里画廊最佳的观景方式，改变了游客欣赏风景的角度和视野，被誉为“<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>最美的流动风景线”。 \r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界十里画廊\" src=\"http://www.travelzjj.com/uploads/201506/271342241657.jpg\" width=\"730\" height=\"450\" title=\"张家界十里画廊\" align=\"\" />\r\n</p>\r\n<p >\r\n	                                                                             星海采药                                                         摄影者：贾长江\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(244,48,3,'推荐理由','																																				<p >\r\n	有人说，<a href=\"http://www.travelzjj.com/panorama/detail/1066.html\" target=\"_blank\" class=\"keywords\">十里画廊</a>是在大自然有无穷灵感的时候，恣意描绘出的一幅神奇绝美的画卷！诗人黎盛鸣游毕赋诗:“奇峰争起闹长空,百态千姿造化功。秀谷清溪十里路,游人多少画廊中。”雕塑家钱绍武称其为“一条天然雕塑杰作的群像陈列长廊”。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	十里画廊内可谓步步有景，处处如画。在这条长达十余里的山谷两侧，有着丰富的自然景观，人行其间如在画中。沟旁黛峰屏列，山上的岩石形成了200来尊似人似物、似鸟似兽的石景造型，沿途主要景点有寿星迎宾、采药老人、猛虎啸天、夫妻抱子、转阁楼、三姐妹等。\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(245,48,4,'交通路线','																																				<p >\r\n	在<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>市中心汽车站（火车站旁50米）就可搭乘去往森林公园、武陵源的线路巴士。\r\n</p>\r\n<p >\r\n	武陵源吴家峪门票站进入：乘景区环保车至<a href=\"http://www.travelzjj.com/panorama/detail/1066.html\" target=\"_blank\" class=\"keywords\">十里画廊</a>。\r\n</p>\r\n<p >\r\n	森林公园门票站进入：步行至<a href=\"http://www.travelzjj.com/panorama/detail/1069.html\" target=\"_blank\" class=\"keywords\">金鞭溪</a>尽头水绕四门停车场，乘景区环保车至十里画廊。\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(246,48,5,'游览须知','																																				<p >\r\n	<span style=\"line-height:1.5;\">游览<a href=\"http://www.travelzjj.com/panorama/detail/1066.html\" target=\"_blank\" class=\"keywords\">十里画廊</a>，时间约1.5小时。</span>\r\n</p>\r\n<p >\r\n	武陵源核心景区游览路线可简要形容为山下游览路线、山上游览路线；\r\n</p>\r\n<p >\r\n	十里画廊属于山下游览线，1日游推荐线路为：市区-森林公园门票站-徒步<a href=\"http://www.travelzjj.com/panorama/detail/1069.html\" target=\"_blank\" class=\"keywords\">金鞭溪</a>-水绕四门-景区环保车-十里画廊-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需6小时。\r\n</p>\r\n<p >\r\n	此外，穿过十里画廊，沿尽头卧龙岭游道，可徒步登上<a href=\"http://www.travelzjj.com/panorama/detail/1068.html\" target=\"_blank\" class=\"keywords\">天子山</a>。\r\n</p>\r\n<p >\r\n	相关信息\r\n</p>\r\n<p >\r\n	门票：已含在武陵源核心景区门票内（票价248元/3天有效）\r\n</p>\r\n<p >\r\n	地址：<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>市武陵源区十里画廊风景区\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(247,70,1,'简介','																		<p class=\"MsoNormal\" >\r\n	<img alt=\"张家界户外清风峡\" src=\"http://www.travelzjj.com/uploads/201506/291347053576.jpg\" width=\"730\" height=\"450\" title=\"张家界户外清风峡\" align=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	                                                                                                                                                 摄影者：李东群\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<a href=\"http://www.travelzjj.com/panorama/detail/1129.html\" target=\"_blank\" class=\"keywords\">清风峡</a>在<span>2009</span>年前后被大批户外爱好者发现，在这里除了当地乡民就地取材、逐年垒砌而成的简易游道和一处似土家院落般的游客服务点和露营营地，几乎没有人工雕凿的痕迹。可以说是一处世外桃源。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<img alt=\"张家界户外清风峡\" src=\"http://www.travelzjj.com/uploads/201506/291347502277.jpg\" width=\"730\" height=\"450\" title=\"张家界户外清风峡\" align=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	                                                                                                                                                 摄影者：李东群\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span>清风峡谷沿途可见森林茂密、石峰林立，骏马奋蹄、屈子行吟、 指点江山、五指迎宾、金鞭直立等景点形态各异、活灵活现、栩栩如生，还有高山流水、鸳鸯洞穴、龙泉瀑布、千年古藤满绝壁、五色花树等绝景，这些风景在不同的角度观赏给人不同的感觉。真有：“横看成岭侧成峰，远近高低各不同”之感。</span>\r\n</p>\r\n									\r\n									'),(248,70,2,'户外攻略','																		<p class=\"MsoNormal\" >\r\n	资源种类】张家界地貌\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	【户外项目】徒步、摄影、度假、攀岩、溯溪、露营、涉潭、探瀑等\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	【海拔范围】470m至800m\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	【线路难度】2星\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	【线路强度】2星\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	【线路风景】5星\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">【装备要求】登山包、登山溯溪鞋、登山杖、防寒衣、路餐、对话机、露营装备（停车场和峡谷中段服务站可露营）等</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	【交通指引】市区--武陵源中湖乡--杨家界景区门票站（大门左侧入峡谷）。中湖乡距市区约39km。自驾：约50分钟，为市区公路+省道S228+县道X020；骑行：市区公路+省道S228+县道X020，路况尚可；班车：从张家界中心汽车站乘坐中湖专线车直达中湖乡。抵达中湖乡后，步行10分钟或乘坐观光车3分钟，自驾约2分钟即可到杨家界景区门票站，再从其大门外左侧步行入清风峡。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<strong>行程</strong>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 A.M\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	杨家界景区门票站（大门左侧）--1号营地（可停车、露营）--洗尘池--高山流水--2号营地（可露营、用餐）\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	[备注]峡谷长3km，在铁索桥景点，可以溯溪而行。在高山流水景点，可稍作歇息并留影拍照。再往前步行30分钟就到了2号营地，即画里江山营地与中餐厅。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	 P.M\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	2号营地--五色花（春季）--千年滕王--龙泉瀑布--将军座--2号营地--杨家界景区门票站\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	[备注]清风峡整个行程往返约需4-5个小时，途中野人谷可以看到野猴，并有溯溪、登山、涉潭、探瀑等多种户外体验，在1、2号营地可以开展露营等营地休闲活动。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">【联系方式】张家界清风峡户外运动基地暨画里江山营地</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	                  15387449799（九九香）\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	                  13297440222（兽兽）\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\"><img alt=\"张家界清风峡地图\" src=\"http://www.travelzjj.com/uploads/201506/272112127396.jpg\" width=\"730\" height=\"450\" title=\"张家界清风峡地图\" align=\"\" /></span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n									\r\n									'),(249,70,3,'安全提示','																		<p class=\"MsoNormal\" >\r\n	<span style=\"line-height:1.5;\">【安全提示】该线路难度不大，满峡幽潭飞瀑峰林山泉，景色怡人。攀岩、溯降、瀑降等需有专业资质的户外俱乐部方可进行，游客及户外爱好者最好结伴而行，切莫孤身前往，定要注意安全。</span>\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	遇险求助电话：\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	中湖乡政府0744-5517003\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	【其他】\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	峡谷属于户外人的天堂，起环境卫生靠大家自觉维护。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	因景区生态原野，沿路枯枝败叶较多，凡进景区者未到指定地点严禁抽烟生火，千万要注意防止火灾。\r\n</p>\r\n									\r\n									'),(250,64,1,'凌空观景','																		<p >\r\n	俗话说“不上<a href=\"http://www.travelzjj.com/panorama/detail/1067.html\" target=\"_blank\" class=\"keywords\">黄石寨</a>，枉到<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>”。黄石寨，位于张家界市的武陵源，是国家5条精品游览线之一。相传汉朝张良，看破红尘，辞官不做，隐居江湖。在云游这里时，被官兵围困。后来得师父黄石公的帮助脱险，因而把这里叫作黄石寨。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界黄石寨索道\" src=\"http://www.travelzjj.com/uploads/201506/271323212601.jpg\" width=\"730\" height=\"450\" title=\"张家界黄石寨索道\" align=\"\" />\r\n</p>\r\n									\r\n									'),(251,64,2,'美景风光','																		<p >\r\n	<a href=\"http://www.travelzjj.com/panorama/detail/1067.html\" target=\"_blank\" class=\"keywords\">黄石寨</a>海拔1200米，占地面积250亩，为<a href=\"http://www.travelzjj.com/panorama/detail/1051.html\" target=\"_blank\" class=\"keywords\">张家界森林公园</a>最大、 最集中的观景台，主要观景点有20余处，故有“不上黄石寨，枉到<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>”之说。有南北两条步行登寨游道，还有后山车道自老磨湾通通寨后卡门，系砂石路面；从南面登寨，沿途有天书宝匣、定海神针、南天门、南天一柱、摘星台、天桥遗墩、六奇阁等绝佳景点。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<img alt=\"张家界黄石寨\" src=\"http://www.travelzjj.com/uploads/201506/271323578413.jpg\" width=\"730\" height=\"450\" title=\"张家界黄石寨\" align=\"\" />\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<span>游人这样评价黄石寨：五步称奇，七步叫绝；十步之外，目瞪口呆。黄石寨这里的每一座岩峰都是一件古老的艺术品，它们如剑、如针、如龙首、如老龟、如现代化都市的摩天大厦……曾有诗曰：“千峰竟秀，万木葱笼，琳琅满目，美不胜数。虽非大上，不似人间，借以喻此，不为过也。”</span>\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	<img alt=\"张家界黄石寨雪景\" src=\"http://www.travelzjj.com/uploads/201506/271324124611.jpg\" width=\"730\" height=\"450\" title=\"张家界黄石寨雪景\" align=\"\" />\r\n</p>\r\n									\r\n									'),(252,64,3,'交通路线','																		<p >\r\n	在张家界市中心汽车站（火车站旁50米）就可搭乘去往森林公园的线路巴士。\r\n</p>\r\n<p >\r\n	森林公园门票站进入：从老磨湾广场乘景区环保车至黄石寨索道下站，乘缆车上山。或从登山游道步行上山（时间约1.5小时）。\r\n</p>\r\n									\r\n									'),(253,64,4,'游览须知','																		<p >\r\n	武陵源核心景区游览路线可简要形容为山下游览路线、山上游览路线；\r\n</p>\r\n<p >\r\n	黄石寨归属于山下游览线，1日游推荐线路为：市区-森林公园门票站-老磨湾广场-黄石寨索道上山-黄石寨-黄石寨索道下山-徒步金鞭溪-水绕四门-景区环保车-十里画廊-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需7小时。\r\n</p>\r\n<p >\r\n	如果选择步行上下山，时间增加约3小时。\r\n</p>\r\n<p >\r\n	黄石寨山顶为环形游道，有小环线（1.5小时）、大环线（3小时）之分。\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">相关信息</span>\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">门票：已含在武陵源核心景区门票内（票价</span><span style=\"line-height:1.5;\">248</span><span style=\"line-height:1.5;\">元</span><span style=\"line-height:1.5;\">/3</span><span style=\"line-height:1.5;\">天有效）</span>\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">地址：张家界市武陵源区黄石寨风景区</span>\r\n</p>\r\n									\r\n									'),(255,128,1,'锚点1','									content1\r\n									'),(256,128,2,'test2','conten2'),(257,128,3,'test3','content3'),(269,131,1,'锚点1名称','																											锚点1内容\r\n									\r\n									\r\n									'),(270,131,2,'锚点2名称','																		锚点2内容\r\n									\r\n									'),(271,131,3,'锚点3名称','																		锚点3内容\r\n									\r\n									'),(272,34,1,'历史文化','																											<p >\r\n	玉皇洞位于<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>城西约7公里的麻空山南壁，为清代本地乡绅李京开独资捐修。李京开又名叫李五瑞，别名九洞乐，号识破生。他生于乾隆七年（公元1742），道光乙酉年（1825年）去世，享年83岁。李先生家道殷实，富甲一方。少时勤奋读书，颇有才华，数十年奔波仕途，但屡试不第，官场无望，于是看破红尘，另寻安慰，请工匠，耗巨资，凭借麻空山天然溶洞，按上、中、下方位布局，巧妙构思，开凿成“天堂”、“人间”、“地府”三层共八个洞。另外，在离此约3公里的峰泉洞另凿一洞，故有“九乐洞”别名。\r\n</p>\r\n<p >\r\n	<br />\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\"><img alt=\"张家界玉皇石窟\" src=\"http://www.travelzjj.com/uploads/201506/281156353643.jpg\" width=\"730\" height=\"450\" title=\"张家界玉皇石窟\" align=\"\" /></span>\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\"> </span>\r\n</p>\r\n<p >\r\n	<span style=\"line-height:1.5;\">玉皇洞石窟是一雕塑造像甚多，工艺亦较精美的清代艺术石窟。石窟上层“天堂”为雷电洞；“人间”居中，自东向西依次排列为玉金、毫笔、墨池、虎龙、狮子、孔圣六洞；下怪为因果洞，即所谓“地府”。从“天堂”到“人间”，自“人间”至“地府”，上下三层距离分别为60米和70米，其间由宽不盈尺的曲径于悬崖峭壁上相沟连，气势十分险峻。</span>\r\n</p>\r\n									\r\n									\r\n									'),(273,34,2,'石窟奇观','																											<p >\r\n	<span style=\"line-height:1.5;\"><img alt=\"张家界玉皇石窟\" src=\"http://manage.zjji.com/uploads/201506/281156495858.jpg\" width=\"730\" height=\"450\" title=\"张家界玉皇石窟\" align=\"\" /><br />\r\n</span>\r\n</p>\r\n<p >\r\n	 \r\n</p>\r\n<p >\r\n	玉皇洞8个石窟，反映的内容十分丰富，且各具特色，虽杂而不乱。它将石刻、雕塑、文学、书法等艺术溶铸于一炉，是古代劳动人民智慧的结晶。1959年，湖南省政府将玉皇洞正式列为重点文物保护单位。玉皇洞石窟风景区集观光、休闲与宗教<a href=\"http://www.travelzjj.com/active/\" target=\"_blank\" class=\"keywords\">活动</a>于一体，景点内相应地开设了珍石文化区（天然石景陈列，考古石器、石雕艺术欣赏）、园林休闲区（画眉园、斗鸡场、重檐亭、水景、密林）、盆花、苗木观赏区，是永定区难得的历史人文景观，是<a href=\"http://www.travelzjj.com/\" target=\"_blank\" class=\"keywords\">张家界</a>精品旅游景点。\r\n</p>\r\n									\r\n									\r\n									'),(274,34,3,'交通路线','																											<p class=\"MsoNormal\" >\r\n	从市区打车往<a href=\"http://www.travelzjj.com/panorama/detail/1088.html\" target=\"_blank\" class=\"keywords\">玉皇石窟</a>20元左右。\r\n</p>\r\n<p class=\"MsoNormal\" >\r\n	乘坐公交车11路在枫香岗乡下。票价3元。\r\n</p>\r\n									\r\n									\r\n									'),(275,34,4,'游览须知','																											<span style=\"color:#666666;font-family:arial;font-size:14px;line-height:30px;\">该景点属于免费景点，该线路为具有一定危险的户外线路，游客切莫孤身前往建议聘请专业向导。</span>\r\n									\r\n									\r\n									'),(276,214,1,'地质公园','<p>\r\n	界石英砂岩峰林地貌”而闻名于世，由张家界国家森林公园、天子山风景区、索溪峪风景区（部分）共同组成，门票为通票制，下辖7个分景区，分别为【杨家界】、【天子山】、【袁家界】、【十里画廊】、【金鞭溪】、【黄石寨】、【鹞子寨】，自然风光各不相同\r\n</p>\r\n<p>\r\n	界石英砂岩峰林地貌”而闻名于世，由张家界国家森林公园、天子山风景区、索溪峪风景区（部分）共同组成，门票为通票制，下辖7个分景区，分别为【杨家界】、【天子山】、【袁家界】、【十里画廊】、【金鞭溪】、【黄石寨】、【鹞子寨】，自然风光各不相同\r\n</p>'),(358,330,1,'d f','fd &nbsp;'),(385,342,1,'测试11','1111'),(386,342,2,'测试22','222'),(387,343,1,'测试11','111'),(388,343,2,'222','222'),(395,345,1,'t31','t31'),(396,345,2,'t32','t32'),(397,346,1,'测试2','测试2'),(398,346,2,'测试3','测试3'),(399,347,1,'测试41','测试41'),(400,347,2,'测41','测是'),(401,348,1,'测试5','test'),(402,348,2,'5','66'),(403,348,3,'666','66'),(404,349,1,'t1','11'),(405,349,2,'t2','t2'),(406,349,3,'t3','t3'),(407,349,4,'t4','t4'),(408,350,1,'t8','111'),(409,351,1,'9','9'),(410,351,2,'9','999999'),(411,352,1,'11','11'),(412,352,2,'12','12'),(418,354,1,'df d ','f dsf'),(419,354,2,'fd fd ','fd s'),(420,355,1,'df d ','f dsf'),(421,355,2,'fd fd ','fd s'),(422,356,1,'df d ','f dsf'),(423,356,2,'fd fd ','fd s'),(424,357,1,'df d ','fg dfad g'),(429,360,1,'SDAF','SDAFDSA'),(430,361,1,'111','111'),(431,361,2,'222','222'),(432,363,1,'1','111'),(437,366,1,'建筑奇观','<p>\r\n	田家大院也称老院子，原名紫荆堂，始建于清雍正初年，是田氏族人的祖居，其建筑风格为四井封火墙式土家建筑，融土家园林、土家吊脚楼于一体，是典型的毕兹卡（土家族）民居。也是整个湘西乃至全国幸存下来，保存最为完好的土家古宅，堪称土家建筑的活化石，被誉为“土家第一宅”，他的外表看起来朴实无华，其实它神奇得很，可以说是藏而不露，深隐玄机。\r\n</p>\r\n<p>\r\n	&nbsp;江南名联“世上几百年旧家，莫非积德；天下第一件好事，还是读书”在田家大院及其适用。晚晴的抗法保台将领土家军主帅刘明灯、民国总理熊希龄、曾任孙中山先生保镖的南北大侠杜心武、地质专家田奇隽、国务院前总理朱镕基等名人都曾在田家大院修习。\r\n</p>\r\n<p>\r\n	学堂内有一幅朱镕基总理的手迹碑刻。众所周知，朱镕基总理书法功力深厚，但题字寥寥，仅在自己认为非常重要的场合才会挥毫。这幅字是他多年以后回到田家大院参观留下的一首七绝，十足珍贵。\r\n</p>'),(438,366,2,'交通路线','位于城区永定大道彭家巷，鹭鸶湾大桥东端，市内乘1、2、7、9路公交车直达田家大院（老院子）。'),(439,366,3,'游览须知','票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年<br />\r\n证购老人票。<br />\r\n开放时间：8:00-17:00<br />'),(440,367,1,'建筑奇观','<p>\r\n	田家大院也称老院子，原名紫荆堂，始建于清雍正初年，是田氏族人的祖居，其建筑风格为四井封火墙式土家建筑，融土家园林、土家吊脚楼于一体，是典型的毕兹卡（土家族）民居。也是整个湘西乃至全国幸存下来，保存最为完好的土家古宅，堪称土家建筑的活化石，被誉为“土家第一宅”，他的外表看起来朴实无华，其实它神奇得很，可以说是藏而不露，深隐玄机。\r\n</p>\r\n<p>\r\n	&nbsp;江南名联“世上几百年旧家，莫非积德；天下第一件好事，还是读书”在田家大院及其适用。晚晴的抗法保台将领土家军主帅刘明灯、民国总理熊希龄、曾任孙中山先生保镖的南北大侠杜心武、地质专家田奇隽、国务院前总理朱镕基等名人都曾在田家大院修习。\r\n</p>\r\n<p>\r\n	学堂内有一幅朱镕基总理的手迹碑刻。众所周知，朱镕基总理书法功力深厚，但题字寥寥，仅在自己认为非常重要的场合才会挥毫。这幅字是他多年以后回到田家大院参观留下的一首七绝，十足珍贵。\r\n</p>'),(441,367,2,'交通路线','位于城区永定大道彭家巷，鹭鸶湾大桥东端，市内乘1、2、7、9路公交车直达田家大院（老院子）。'),(442,367,3,'游览须知','票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年<br />\r\n证购老人票。<br />\r\n开放时间：8:00-17:00<br />'),(446,369,1,'建筑奇观','<p>\r\n	田家大院也称老院子，原名紫荆堂，始建于清雍正初年，是田氏族人的祖居，其建筑风格为四井封火墙式土家建筑，融土家园林、土家吊脚楼于一体，是典型的毕兹卡（土家族）民居。也是整个湘西乃至全国幸存下来，保存最为完好的土家古宅，堪称土家建筑的活化石，被誉为“土家第一宅”，他的外表看起来朴实无华，其实它神奇得很，可以说是藏而不露，深隐玄机。\r\n</p>\r\n<p>\r\n	&nbsp;江南名联“世上几百年旧家，莫非积德；天下第一件好事，还是读书”在田家大院及其适用。晚晴的抗法保台将领土家军主帅刘明灯、民国总理熊希龄、曾任孙中山先生保镖的南北大侠杜心武、地质专家田奇隽、国务院前总理朱镕基等名人都曾在田家大院修习。\r\n</p>\r\n<p>\r\n	学堂内有一幅朱镕基总理的手迹碑刻。众所周知，朱镕基总理书法功力深厚，但题字寥寥，仅在自己认为非常重要的场合才会挥毫。这幅字是他多年以后回到田家大院参观留下的一首七绝，十足珍贵。\r\n</p>'),(447,369,2,'交通路线','位于城区永定大道彭家巷，鹭鸶湾大桥东端，市内乘1、2、7、9路公交车直达田家大院（老院子）。'),(448,369,3,'游览须知','票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年<br />\r\n证购老人票。<br />\r\n开放时间：8:00-17:00<br />'),(449,370,1,'建筑奇观','<p>\r\n	田家大院也称老院子，原名紫荆堂，始建于清雍正初年，是田氏族人的祖居，其建筑风格为四井封火墙式土家建筑，融土家园林、土家吊脚楼于一体，是典型的毕兹卡（土家族）民居。也是整个湘西乃至全国幸存下来，保存最为完好的土家古宅，堪称土家建筑的活化石，被誉为“土家第一宅”，他的外表看起来朴实无华，其实它神奇得很，可以说是藏而不露，深隐玄机。\r\n</p>\r\n<p>\r\n	&nbsp;江南名联“世上几百年旧家，莫非积德；天下第一件好事，还是读书”在田家大院及其适用。晚晴的抗法保台将领土家军主帅刘明灯、民国总理熊希龄、曾任孙中山先生保镖的南北大侠杜心武、地质专家田奇隽、国务院前总理朱镕基等名人都曾在田家大院修习。\r\n</p>\r\n<p>\r\n	学堂内有一幅朱镕基总理的手迹碑刻。众所周知，朱镕基总理书法功力深厚，但题字寥寥，仅在自己认为非常重要的场合才会挥毫。这幅字是他多年以后回到田家大院参观留下的一首七绝，十足珍贵。\r\n</p>'),(450,370,2,'交通路线','位于城区永定大道彭家巷，鹭鸶湾大桥东端，市内乘1、2、7、9路公交车直达田家大院（老院子）。'),(451,370,3,'游览须知','票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年<br />\r\n证购老人票。<br />\r\n开放时间：8:00-17:00<br />'),(452,371,1,'建筑奇观','<p>\r\n	田家大院也称老院子，原名紫荆堂，始建于清雍正初年，是田氏族人的祖居，其建筑风格为四井封火墙式土家建筑，融土家园林、土家吊脚楼于一体，是典型的毕兹卡（土家族）民居。也是整个湘西乃至全国幸存下来，保存最为完好的土家古宅，堪称土家建筑的活化石，被誉为“土家第一宅”，他的外表看起来朴实无华，其实它神奇得很，可以说是藏而不露，深隐玄机。\r\n</p>\r\n<p>\r\n	&nbsp;江南名联“世上几百年旧家，莫非积德；天下第一件好事，还是读书”在田家大院及其适用。晚晴的抗法保台将领土家军主帅刘明灯、民国总理熊希龄、曾任孙中山先生保镖的南北大侠杜心武、地质专家田奇隽、国务院前总理朱镕基等名人都曾在田家大院修习。\r\n</p>\r\n<p>\r\n	学堂内有一幅朱镕基总理的手迹碑刻。众所周知，朱镕基总理书法功力深厚，但题字寥寥，仅在自己认为非常重要的场合才会挥毫。这幅字是他多年以后回到田家大院参观留下的一首七绝，十足珍贵。\r\n</p>'),(453,371,2,'交通路线','位于城区永定大道彭家巷，鹭鸶湾大桥东端，市内乘1、2、7、9路公交车直达田家大院（老院子）。'),(454,371,3,'游览须知','票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年<br />\r\n证购老人票。<br />\r\n开放时间：8:00-17:00<br />'),(455,372,1,'建筑奇观','<p>\r\n	田家大院也称老院子，原名紫荆堂，始建于清雍正初年，是田氏族人的祖居，其建筑风格为四井封火墙式土家建筑，融土家园林、土家吊脚楼于一体，是典型的毕兹卡（土家族）民居。也是整个湘西乃至全国幸存下来，保存最为完好的土家古宅，堪称土家建筑的活化石，被誉为“土家第一宅”，他的外表看起来朴实无华，其实它神奇得很，可以说是藏而不露，深隐玄机。\r\n</p>\r\n<p>\r\n	&nbsp;江南名联“世上几百年旧家，莫非积德；天下第一件好事，还是读书”在田家大院及其适用。晚晴的抗法保台将领土家军主帅刘明灯、民国总理熊希龄、曾任孙中山先生保镖的南北大侠杜心武、地质专家田奇隽、国务院前总理朱镕基等名人都曾在田家大院修习。\r\n</p>\r\n<p>\r\n	学堂内有一幅朱镕基总理的手迹碑刻。众所周知，朱镕基总理书法功力深厚，但题字寥寥，仅在自己认为非常重要的场合才会挥毫。这幅字是他多年以后回到田家大院参观留下的一首七绝，十足珍贵。\r\n</p>'),(456,372,2,'交通路线','位于城区永定大道彭家巷，鹭鸶湾大桥东端，市内乘1、2、7、9路公交车直达田家大院（老院子）。'),(457,372,3,'游览须知','票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年<br />\r\n证购老人票。<br />\r\n开放时间：8:00-17:00<br />'),(458,373,1,'建筑奇观','<p>\r\n	田家大院也称老院子，原名紫荆堂，始建于清雍正初年，是田氏族人的祖居，其建筑风格为四井封火墙式土家建筑，融土家园林、土家吊脚楼于一体，是典型的毕兹卡（土家族）民居。也是整个湘西乃至全国幸存下来，保存最为完好的土家古宅，堪称土家建筑的活化石，被誉为“土家第一宅”，他的外表看起来朴实无华，其实它神奇得很，可以说是藏而不露，深隐玄机。\r\n</p>\r\n<p>\r\n	&nbsp;江南名联“世上几百年旧家，莫非积德；天下第一件好事，还是读书”在田家大院及其适用。晚晴的抗法保台将领土家军主帅刘明灯、民国总理熊希龄、曾任孙中山先生保镖的南北大侠杜心武、地质专家田奇隽、国务院前总理朱镕基等名人都曾在田家大院修习。\r\n</p>\r\n<p>\r\n	学堂内有一幅朱镕基总理的手迹碑刻。众所周知，朱镕基总理书法功力深厚，但题字寥寥，仅在自己认为非常重要的场合才会挥毫。这幅字是他多年以后回到田家大院参观留下的一首七绝，十足珍贵。\r\n</p>'),(459,373,2,'交通路线','位于城区永定大道彭家巷，鹭鸶湾大桥东端，市内乘1、2、7、9路公交车直达田家大院（老院子）。'),(460,373,3,'游览须知','票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年<br />\r\n证购老人票。<br />\r\n开放时间：8:00-17:00<br />'),(461,374,1,'建筑奇观','<p>\r\n	田家大院也称老院子，原名紫荆堂，始建于清雍正初年，是田氏族人的祖居，其建筑风格为四井封火墙式土家建筑，融土家园林、土家吊脚楼于一体，是典型的毕兹卡（土家族）民居。也是整个湘西乃至全国幸存下来，保存最为完好的土家古宅，堪称土家建筑的活化石，被誉为“土家第一宅”，他的外表看起来朴实无华，其实它神奇得很，可以说是藏而不露，深隐玄机。\r\n</p>\r\n<p>\r\n	&nbsp;江南名联“世上几百年旧家，莫非积德；天下第一件好事，还是读书”在田家大院及其适用。晚晴的抗法保台将领土家军主帅刘明灯、民国总理熊希龄、曾任孙中山先生保镖的南北大侠杜心武、地质专家田奇隽、国务院前总理朱镕基等名人都曾在田家大院修习。\r\n</p>\r\n<p>\r\n	学堂内有一幅朱镕基总理的手迹碑刻。众所周知，朱镕基总理书法功力深厚，但题字寥寥，仅在自己认为非常重要的场合才会挥毫。这幅字是他多年以后回到田家大院参观留下的一首七绝，十足珍贵。\r\n</p>'),(462,374,2,'交通路线','位于城区永定大道彭家巷，鹭鸶湾大桥东端，市内乘1、2、7、9路公交车直达田家大院（老院子）。'),(463,374,3,'游览须知','票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年<br />\r\n证购老人票。<br />\r\n开放时间：8:00-17:00<br />'),(516,413,1,'山寨特色','王家坪镇是张家界市最典型的土家族聚居区，不仅土家族占总人口的90%左右，更保存了多达2198栋的土家吊脚楼，19个极具特色的土家山寨，错落在奇山异水间，流传着多姿多彩、古朴浓郁的民俗风情。&nbsp;'),(517,413,2,'民俗风情','其中在王家坪镇土家族聚居村落最为盛行的，是一年一度的“糊仓文化”。糊仓文化是一种区域性的农耕文化，最初属农事祭祀活动，经过长时间的继承和演变，成为土家人非常重视的年度喜事。'),(518,413,3,'交通路线','张家界市中心汽车站有往返王家坪镇的班车，发车时间8:00-10:00，每日两班，里程60余公里，车程2小时，价格15元；建议自驾前往。'),(519,413,4,'游览须知','前往王家坪镇建议慎选农时，以芒种、夏至、秋收等节气为好，可以接触到土家人的田园劳作场景，寓劳于乐。盛夏之前的湘西山乡气候较为宜人,万物生长，一派生机，见欣欣向荣之感。'),(520,414,1,'山寨特色','王家坪镇是张家界市最典型的土家族聚居区，不仅土家族占总人口的90%左右，更保存了多达2198栋的土家吊脚楼，19个极具特色的土家山寨，错落在奇山异水间，流传着多姿多彩、古朴浓郁的民俗风情。&nbsp;'),(521,414,2,'民俗风情','其中在王家坪镇土家族聚居村落最为盛行的，是一年一度的“糊仓文化”。糊仓文化是一种区域性的农耕文化，最初属农事祭祀活动，经过长时间的继承和演变，成为土家人非常重视的年度喜事。'),(522,414,3,'交通路线','张家界市中心汽车站有往返王家坪镇的班车，发车时间8:00-10:00，每日两班，里程60余公里，车程2小时，价格15元；建议自驾前往。'),(523,414,4,'游览须知','前往王家坪镇建议慎选农时，以芒种、夏至、秋收等节气为好，可以接触到土家人的田园劳作场景，寓劳于乐。盛夏之前的湘西山乡气候较为宜人,万物生长，一派生机，见欣欣向荣之感。'),(524,415,1,'山寨特色','王家坪镇是张家界市最典型的土家族聚居区，不仅土家族占总人口的90%左右，更保存了多达2198栋的土家吊脚楼，19个极具特色的土家山寨，错落在奇山异水间，流传着多姿多彩、古朴浓郁的民俗风情。&nbsp;'),(525,415,2,'民俗风情','其中在王家坪镇土家族聚居村落最为盛行的，是一年一度的“糊仓文化”。糊仓文化是一种区域性的农耕文化，最初属农事祭祀活动，经过长时间的继承和演变，成为土家人非常重视的年度喜事。'),(526,415,3,'交通路线','张家界市中心汽车站有往返王家坪镇的班车，发车时间8:00-10:00，每日两班，里程60余公里，车程2小时，价格15元；建议自驾前往。'),(527,415,4,'游览须知','前往王家坪镇建议慎选农时，以芒种、夏至、秋收等节气为好，可以接触到土家人的田园劳作场景，寓劳于乐。盛夏之前的湘西山乡气候较为宜人,万物生长，一派生机，见欣欣向荣之感。'),(528,416,1,'山寨特色','王家坪镇是张家界市最典型的土家族聚居区，不仅土家族占总人口的90%左右，更保存了多达2198栋的土家吊脚楼，19个极具特色的土家山寨，错落在奇山异水间，流传着多姿多彩、古朴浓郁的民俗风情。&nbsp;'),(529,416,2,'民俗风情','其中在王家坪镇土家族聚居村落最为盛行的，是一年一度的“糊仓文化”。糊仓文化是一种区域性的农耕文化，最初属农事祭祀活动，经过长时间的继承和演变，成为土家人非常重视的年度喜事。'),(530,416,3,'交通路线','张家界市中心汽车站有往返王家坪镇的班车，发车时间8:00-10:00，每日两班，里程60余公里，车程2小时，价格15元；建议自驾前往。'),(531,416,4,'游览须知','前往王家坪镇建议慎选农时，以芒种、夏至、秋收等节气为好，可以接触到土家人的田园劳作场景，寓劳于乐。盛夏之前的湘西山乡气候较为宜人,万物生长，一派生机，见欣欣向荣之感。'),(532,417,1,'山寨特色','王家坪镇是张家界市最典型的土家族聚居区，不仅土家族占总人口的90%左右，更保存了多达2198栋的土家吊脚楼，19个极具特色的土家山寨，错落在奇山异水间，流传着多姿多彩、古朴浓郁的民俗风情。&nbsp;'),(533,417,2,'民俗风情','其中在王家坪镇土家族聚居村落最为盛行的，是一年一度的“糊仓文化”。糊仓文化是一种区域性的农耕文化，最初属农事祭祀活动，经过长时间的继承和演变，成为土家人非常重视的年度喜事。'),(534,417,3,'交通路线','张家界市中心汽车站有往返王家坪镇的班车，发车时间8:00-10:00，每日两班，里程60余公里，车程2小时，价格15元；建议自驾前往。'),(535,417,4,'游览须知','前往王家坪镇建议慎选农时，以芒种、夏至、秋收等节气为好，可以接触到土家人的田园劳作场景，寓劳于乐。盛夏之前的湘西山乡气候较为宜人,万物生长，一派生机，见欣欣向荣之感。'),(536,418,1,'山寨特色','王家坪镇是张家界市最典型的土家族聚居区，不仅土家族占总人口的90%左右，更保存了多达2198栋的土家吊脚楼，19个极具特色的土家山寨，错落在奇山异水间，流传着多姿多彩、古朴浓郁的民俗风情。&nbsp;'),(537,418,2,'民俗风情','其中在王家坪镇土家族聚居村落最为盛行的，是一年一度的“糊仓文化”。糊仓文化是一种区域性的农耕文化，最初属农事祭祀活动，经过长时间的继承和演变，成为土家人非常重视的年度喜事。'),(538,418,3,'交通路线','张家界市中心汽车站有往返王家坪镇的班车，发车时间8:00-10:00，每日两班，里程60余公里，车程2小时，价格15元；建议自驾前往。'),(539,418,4,'游览须知','前往王家坪镇建议慎选农时，以芒种、夏至、秋收等节气为好，可以接触到土家人的田园劳作场景，寓劳于乐。盛夏之前的湘西山乡气候较为宜人,万物生长，一派生机，见欣欣向荣之感。'),(540,419,1,'山寨特色','王家坪镇是张家界市最典型的土家族聚居区，不仅土家族占总人口的90%左右，更保存了多达2198栋的土家吊脚楼，19个极具特色的土家山寨，错落在奇山异水间，流传着多姿多彩、古朴浓郁的民俗风情。&nbsp;'),(541,419,2,'民俗风情','其中在王家坪镇土家族聚居村落最为盛行的，是一年一度的“糊仓文化”。糊仓文化是一种区域性的农耕文化，最初属农事祭祀活动，经过长时间的继承和演变，成为土家人非常重视的年度喜事。'),(542,419,3,'交通路线','张家界市中心汽车站有往返王家坪镇的班车，发车时间8:00-10:00，每日两班，里程60余公里，车程2小时，价格15元；建议自驾前往。'),(543,419,4,'游览须知','前往王家坪镇建议慎选农时，以芒种、夏至、秋收等节气为好，可以接触到土家人的田园劳作场景，寓劳于乐。盛夏之前的湘西山乡气候较为宜人,万物生长，一派生机，见欣欣向荣之感。'),(544,420,1,'山寨特色','王家坪镇是张家界市最典型的土家族聚居区，不仅土家族占总人口的90%左右，更保存了多达2198栋的土家吊脚楼，19个极具特色的土家山寨，错落在奇山异水间，流传着多姿多彩、古朴浓郁的民俗风情。&nbsp;'),(545,420,2,'民俗风情','其中在王家坪镇土家族聚居村落最为盛行的，是一年一度的“糊仓文化”。糊仓文化是一种区域性的农耕文化，最初属农事祭祀活动，经过长时间的继承和演变，成为土家人非常重视的年度喜事。'),(546,420,3,'交通路线','张家界市中心汽车站有往返王家坪镇的班车，发车时间8:00-10:00，每日两班，里程60余公里，车程2小时，价格15元；建议自驾前往。'),(547,420,4,'游览须知','前往王家坪镇建议慎选农时，以芒种、夏至、秋收等节气为好，可以接触到土家人的田园劳作场景，寓劳于乐。盛夏之前的湘西山乡气候较为宜人,万物生长，一派生机，见欣欣向荣之感。'),(594,438,1,'红色历史','张家界烈士公园位于张家界市城区东面迎宾路旁回龙观, 占地面积200 亩。烈士公园修建于1985年，是湖南省第一批省级重点革命烈士纪念设施保护单位，湖南省首批爱国主义教育基地，湖南省第二批国防教育基地。2002 年实行免费对外开放。<br />\r\n张家界市永定区具有光荣的革命历史，在第二次国内革命战争时期，是中华苏维埃共和国湘鄂川黔省委和省军委的所在地。在长期的革命斗争中，涌现了无数的英雄儿女，永定区人民付出了惨重的代价，先后共认定了革命烈士2800 人，为昭烈士功勋于青史，垂英雄风范于后世，原大庸县委、县政府于1985 年作出修建烈士纪念塔的决定，烈士纪念塔于1985 年3 月3 日奠基动工兴建，10 月落成。<br />'),(595,438,2,'建筑特点','烈士纪念塔位于烈士公园山丘之顶，纪念塔正面刻有原全国人大常务委员会副委员会长廖汉生的题字“革命烈士纪念塔”，原中顾问委员会常务委员肖克的题词“革命烈士永垂不朽”。纪念塔背面刻有仿毛泽东主席题词“为人民而死，虽死犹荣”。塔座正面镌刻烈士碑文，其他三面分别刻有鸡公垭战役、百姓欢迎解放军入城和工农兵建设新大庸三幅浮雕。纪念塔背面建有六角纪念亭，亭内设烈士英名录石碑，上刻烈士英名1662人。纪念塔东南面立资助陵园修建的功德碑。纪念塔右侧有烈士公墓，安葬烈士遗骨15 人，老红军2 人。烈士陵园北面，有本籍抗日阵亡的陆军少将覃子斌将军墓。<br />\r\n除了扫墓、瞻仰烈士英灵外，烈士公园树木葱郁，从山脚至山顶有一条石阶铺就的游道，因此这里也是人们健身、纳凉的极好选择。<br />\r\n<div>\r\n	<br />\r\n</div>'),(596,438,3,'交通路线','距 离：位于市区，距武陵源核心景区约35 公里<br />\r\n公共交通：可在市区乘坐1、7、10 路公交车至烈士公园站下车<br />\r\n自 驾：可驾车沿市区迎宾路至烈士公园<br />\r\n<div>\r\n	<br />\r\n</div>'),(597,438,4,'游览须知','建议穿合适的鞋，此经典免费。'),(602,441,1,'红色经典','贺龙元帅一生忠诚革命，军中大将且高风亮节，一直受到国人的爱戴。贺龙是张家界的骄傲，其故居位于桑植洪家关，目前得到较完整的恢复；故居斜对面，另建有贺龙纪念馆。前往桑植旅游，不可遗漏贺龙纪念馆。'),(603,441,2,'建筑奇观','馆舍由八个展厅组成，陈列着两把菜刀闹革命、南昌起义、创建革命根据地、抗日战争、解放战争、国家体育事业奠基者、国家军队重要领导人、贺龙同志永远活在人民心中、中小学生思想教育展厅几大块内容。贺龙故居和纪念馆是全国爱国主义教育示范基地和革命传统教育基地。贺龙纪念馆展厅共辟贺龙同志及其任弼时、关向应等我军领导人的历史照片387张，文献文物190件。纪念馆于1993年3月筹建，1996年3月在纪念贺龙同志诞辰100周年时建成并对外开放，由时任中共中央总书记的江泽民同志题写馆名。'),(604,441,3,'交通路线','从张家界中心汽车站乘车至桑植汽车站（西站），然后步行至附近的北门汽车站，在高架桥下，桥下有一些停泊的巴士，乘坐往洪家关方向的车，终点即是贺龙纪念馆和贺龙故居。返程的末班车在18:00发车。从桑植县返回张家界市区的班车最晚是17:00-18:00.'),(605,441,4,'游览须知','此景点为免费景点，营业时间为淡季9:00-16:00，旺季8:00-17:00。'),(610,443,1,'红色经典','贺龙公园 是为纪念贺龙元帅转战天子山而建，坐落湖南省张家界市天子山上，在1200米的千层岩左侧，一般归类于石家檐游览线。公园内主要游览点有贺龙元帅铜像、兵器馆、贺龙元帅陈列馆、将军碑林（建设中）等。'),(611,443,2,'人文景观','<p>\r\n	屹立在“云青岩”上的贺龙铜像与大自然连为一体，形成独特的艺术风格。建铸于1986年，高6.5米,重9吨,为我国近百年塑造的最大的一尊铜像。铜像背负青山，面壁神堂湾，凝重、庄严、和谐地统一在青山绿水的画图中，逼真再现了贺龙元帅的鲜明形象：浓眉大眼，八字胡须，右手小臂提起，握着烟斗，微笑注视着家乡石峰峭壁。身旁一匹好征的战马，昂起头颅，似乎在等待主人扬鞭踏上征途。\r\n</p>\r\n<p>\r\n	1995年3月28日，江泽民总书记为公园题写园名。胡耀邦同志亦曾为云青崖题字。公园内有数处观景台，是天子山观景最集中的地方。\r\n</p>\r\n&nbsp;<br />'),(612,443,3,'交通路线','在张家界市中心汽车站（火车站旁50米）就可搭乘去往武陵源的线路巴士。<br />\r\n武陵源吴家峪门票站进入：乘景区环保车至天子山索道下站，乘缆车上山，或从卧龙岭步行上山。<br />\r\n或者从杨家界索道、百龙天梯上山后，乘景区环保车前往。<br />'),(613,443,4,'游览须知','此景点在武陵源核心景区内天子山景区，门票已经包含在武陵源核心景区门票内（248元/人）。铜像背后的不远处，便是“云青岩”观景台，从这里可以观赏到“西海石林”。西海是名副其实的山的海洋，上千座山峰风起云涌般展现在眼底，鳞次栉比，巍峨壮观。这里还可以根据不同的季节和天气而欣赏到天子山的“四大奇观”-石林、日出、云瀑、冬雪。<br />\r\n<div>\r\n	<br />\r\n</div>'),(614,444,1,'红色经典','贺龙公园 是为纪念贺龙元帅转战天子山而建，坐落湖南省张家界市天子山上，在1200米的千层岩左侧，一般归类于石家檐游览线。公园内主要游览点有贺龙元帅铜像、兵器馆、贺龙元帅陈列馆、将军碑林（建设中）等。'),(615,444,2,'人文景观','<p>\r\n	屹立在“云青岩”上的贺龙铜像与大自然连为一体，形成独特的艺术风格。建铸于1986年，高6.5米,重9吨,为我国近百年塑造的最大的一尊铜像。铜像背负青山，面壁神堂湾，凝重、庄严、和谐地统一在青山绿水的画图中，逼真再现了贺龙元帅的鲜明形象：浓眉大眼，八字胡须，右手小臂提起，握着烟斗，微笑注视着家乡石峰峭壁。身旁一匹好征的战马，昂起头颅，似乎在等待主人扬鞭踏上征途。\r\n</p>\r\n<p>\r\n	1995年3月28日，江泽民总书记为公园题写园名。胡耀邦同志亦曾为云青崖题字。公园内有数处观景台，是天子山观景最集中的地方。\r\n</p>\r\n&nbsp;<br />'),(616,444,3,'交通路线','在张家界市中心汽车站（火车站旁50米）就可搭乘去往武陵源的线路巴士。<br />\r\n武陵源吴家峪门票站进入：乘景区环保车至天子山索道下站，乘缆车上山，或从卧龙岭步行上山。<br />\r\n或者从杨家界索道、百龙天梯上山后，乘景区环保车前往。<br />'),(617,444,4,'游览须知','此景点在武陵源核心景区内天子山景区，门票已经包含在武陵源核心景区门票内（248元/人）。铜像背后的不远处，便是“云青岩”观景台，从这里可以观赏到“西海石林”。西海是名副其实的山的海洋，上千座山峰风起云涌般展现在眼底，鳞次栉比，巍峨壮观。这里还可以根据不同的季节和天气而欣赏到天子山的“四大奇观”-石林、日出、云瀑、冬雪。<br />\r\n<div>\r\n	<br />\r\n</div>'),(618,445,1,'红色经典','贺龙公园 是为纪念贺龙元帅转战天子山而建，坐落湖南省张家界市天子山上，在1200米的千层岩左侧，一般归类于石家檐游览线。公园内主要游览点有贺龙元帅铜像、兵器馆、贺龙元帅陈列馆、将军碑林（建设中）等。'),(619,445,2,'人文景观','<p>\r\n	屹立在“云青岩”上的贺龙铜像与大自然连为一体，形成独特的艺术风格。建铸于1986年，高6.5米,重9吨,为我国近百年塑造的最大的一尊铜像。铜像背负青山，面壁神堂湾，凝重、庄严、和谐地统一在青山绿水的画图中，逼真再现了贺龙元帅的鲜明形象：浓眉大眼，八字胡须，右手小臂提起，握着烟斗，微笑注视着家乡石峰峭壁。身旁一匹好征的战马，昂起头颅，似乎在等待主人扬鞭踏上征途。\r\n</p>\r\n<p>\r\n	1995年3月28日，江泽民总书记为公园题写园名。胡耀邦同志亦曾为云青崖题字。公园内有数处观景台，是天子山观景最集中的地方。\r\n</p>\r\n&nbsp;<br />'),(620,445,3,'交通路线','在张家界市中心汽车站（火车站旁50米）就可搭乘去往武陵源的线路巴士。<br />\r\n武陵源吴家峪门票站进入：乘景区环保车至天子山索道下站，乘缆车上山，或从卧龙岭步行上山。<br />\r\n或者从杨家界索道、百龙天梯上山后，乘景区环保车前往。<br />'),(621,445,4,'游览须知','此景点在武陵源核心景区内天子山景区，门票已经包含在武陵源核心景区门票内（248元/人）。铜像背后的不远处，便是“云青岩”观景台，从这里可以观赏到“西海石林”。西海是名副其实的山的海洋，上千座山峰风起云涌般展现在眼底，鳞次栉比，巍峨壮观。这里还可以根据不同的季节和天气而欣赏到天子山的“四大奇观”-石林、日出、云瀑、冬雪。<br />\r\n<div>\r\n	<br />\r\n</div>'),(622,446,1,'红色经典','贺龙公园 是为纪念贺龙元帅转战天子山而建，坐落湖南省张家界市天子山上，在1200米的千层岩左侧，一般归类于石家檐游览线。公园内主要游览点有贺龙元帅铜像、兵器馆、贺龙元帅陈列馆、将军碑林（建设中）等。'),(623,446,2,'人文景观','<p>\r\n	屹立在“云青岩”上的贺龙铜像与大自然连为一体，形成独特的艺术风格。建铸于1986年，高6.5米,重9吨,为我国近百年塑造的最大的一尊铜像。铜像背负青山，面壁神堂湾，凝重、庄严、和谐地统一在青山绿水的画图中，逼真再现了贺龙元帅的鲜明形象：浓眉大眼，八字胡须，右手小臂提起，握着烟斗，微笑注视着家乡石峰峭壁。身旁一匹好征的战马，昂起头颅，似乎在等待主人扬鞭踏上征途。\r\n</p>\r\n<p>\r\n	1995年3月28日，江泽民总书记为公园题写园名。胡耀邦同志亦曾为云青崖题字。公园内有数处观景台，是天子山观景最集中的地方。\r\n</p>\r\n&nbsp;<br />'),(624,446,3,'交通路线','在张家界市中心汽车站（火车站旁50米）就可搭乘去往武陵源的线路巴士。<br />\r\n武陵源吴家峪门票站进入：乘景区环保车至天子山索道下站，乘缆车上山，或从卧龙岭步行上山。<br />\r\n或者从杨家界索道、百龙天梯上山后，乘景区环保车前往。<br />'),(625,446,4,'游览须知','此景点在武陵源核心景区内天子山景区，门票已经包含在武陵源核心景区门票内（248元/人）。铜像背后的不远处，便是“云青岩”观景台，从这里可以观赏到“西海石林”。西海是名副其实的山的海洋，上千座山峰风起云涌般展现在眼底，鳞次栉比，巍峨壮观。这里还可以根据不同的季节和天气而欣赏到天子山的“四大奇观”-石林、日出、云瀑、冬雪。<br />\r\n<div>\r\n	<br />\r\n</div>'),(898,540,1,'ceshi','ceshiceshi'),(1081,476,1,'民俗风情','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fro-42K43up8VDseZpIIWtIxuQ9g\" alt=\"\" />\r\n</p>\r\n<p>\r\n	过赶年是土家族人最重要的节日，从内容到形式比土家族的其他节日都要隆重，持续时间也最长。土家族过年比汉族过年要早一天，即月大过腊月二十九，月小过腊月二十八，是为赶年。赶年为纪念土家族的勇士，也为期待来年的好光景。\r\n</p>'),(1082,476,2,'赶年习俗','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fjhto-cg3CvDxfGOE9tS8UtK82GK\" alt=\"\" />\r\n</p>\r\n<p>\r\n	民间有这个传说：相传明嘉靖三十三年（1555），由于朝政腐败，倭寇在我国东南沿海地区不断大肆袭扰，朝廷曾多次派大军抗倭，都惨败告终。尚书张经上奏朝廷，请征湘鄂西土兵平倭，明世宗准奏，派经略使胡宗宪督办。永定卫茅岗土司覃尧之与儿子覃承坤及桑植司向鹤峰、永顺司彭翼南、容美司（今湖北鹤峰）田世爵等奉指率土兵出征。时值阴历年关，覃尧之深知一去难返，决定与亲人过最后一个年，于是下令：“蒸甑子饭，切砣子肉，斟大碗酒，提前一天过年再出征。”因时间紧，来不及作许多菜，就来个腊肉、豆腐、萝卜一锅炖，叫作“合菜”，吃了好上路。这道菜以后演变成“三下锅”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvofeaSGVlRww9pwQQjuDODitoKQ\" alt=\"\" />\r\n</p>\r\n<p>\r\n	士兵上前线后，很快打败倭寇，收复失地，世宗亲赐匾额，上书“东南战功第一”。志书记下了这段历史：“于十二月二十九日大犒将士，除夕，侯不备，遂大捷。后人沿之，遂成家风。”“赶年”习俗体现了土家人民识大体顾大局，甘愿为国捐躯的博大胸怀，是土家人民的骄傲。\r\n</p>'),(1085,474,1,'民俗风情','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fs7_MzkFLp-ZiOoaT8VhRCbazg2H\" alt=\"\" />\r\n</p>\r\n<p>\r\n	土家姑娘在接到男方通报结婚的日子前十天半月，就不再出门做活。先是在吊脚楼闺房架一方桌，置茶十碗，邀亲邻九女依次围坐，哭起嫁歌来，新娘居中，叫“包席”，右女为“安席”，左女为“收席”。新娘起声，“安席”接腔，依次哭去，不分昼夜。哭有规矩：母女哭，姑侄哭，姊妹哭，舅甥哭，姑嫂哭，骂媒人……哭三五天，有长达十天半月的。主要内容有回忆母女情，诉说分别苦，感谢养育恩，托兄嫂照护年迈双亲，教女为人处世等 。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fj0SBoQcT3A_Pm4yxMffwFcfLiMk\" alt=\"\" />\r\n</p>\r\n<p>\r\n	土家女哭嫁俗流传久远。清代土家诗人彭潭秋记载说：“十姊妹歌，恋亲恩，伤别离，歌为曼声，甚哀，泪随声下，是‘竹枝’遗意也。”古竹枝词里有首咏哭嫁诗：桃夭时节卜佳期，无限伤心叙别离。哭娘哭嫂哭姐妹，情意绵缠泪如丝。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn-iQ0wdHFfkIj_upNdQO2igbdwo\" alt=\"\" />\r\n</p>\r\n<p>\r\n	用哭声来庆贺欢乐的出嫁，用歌舞来祭祀死去的亲人，看似不可思议，却充分反映了土家族独特的禀性及文化意识。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhlQKJ49cbhHS_KFgX7qmb-sEIwM\" alt=\"\" />\r\n</p>'),(1088,435,1,'民俗风情','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn280k1qAh0ys8MKZUN2nsVHXCDj\" alt=\"\" />\r\n</p>\r\n<p>\r\n	阳戏，早先叫扬花柳，为民间艺人行乞或风尘女子卖唱糊口的表演艺术，后经茅岗司覃家班及若干代艺术人的加工改造，才形成生旦净觉丑末行当齐全的地方戏剧种。在重庆、湖南、贵州湖北等省市农村广泛流传。那里居住着汉、土家、苗、侗、白、回、瑶、壮等多种民族，残存着大量巴、楚、湘、黔文化的余绪，少数民族的灿烂文化，踩堂戏或傩堂戏都给阳戏以特别的艺术滋养。流行于西南各省的樵歌、秧歌、炉歌、船歌、傩歌、采茶歌以及其他民族民间歌舞和一些地方戏曲剧种都对阳戏产生了巨大影响。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FrICwYxVGLZQhJUUWw1OdNIcGvXj\" alt=\"\" />\r\n</p>\r\n<p>\r\n	在张家界市城区的普光禅寺戏院，是听阳戏的绝好景点，上午九点就有老人蹒跚来到戏院听戏，这里每天都上演阳戏，只要花上十块钱，保证你拍手叫好。有些观众遇上台上精彩的唱段，索性闭上眼睛，手里打着拍眼，细细咀嚼演员的一腔一调，听到高兴处，会提起嗓子，大声喝一个彩。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgXzGuyT6O3_Hza2pxD3w6lJdke0\" alt=\"\" />\r\n</p>\r\n<p>\r\n	听完阳戏后，我已经醉了，我爱那胡琴吓得悲欢离合，爱那小旦的轻盈舞步….\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpzAsgZkSWGIXgIinLNkq_KiSQ-w\" alt=\"\" />\r\n</p>'),(1089,434,1,'民俗风情','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fh5TzfzIHVJmBe51xnkWl0qVWlPK\" alt=\"\" />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;摄影：郑亚平\r\n</p>\r\n<p>\r\n	花灯，又名灯笼。灯笼是起源于中国的一种汉族传统民俗工艺品，在古代，其主要作用是照明，由纸或者绢作为灯笼的外皮，骨架通常使用竹或木条制作，中间放上蜡烛或者灯泡，成为照明工具。受汉族文化影响，在亚洲华人地区，许多国家的庙宇中，灯笼也是相当常见的物品。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fq_V5077-S9_2wfXghZOSARlxc-v\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span>摄影：郑亚平</span>\r\n</p>\r\n<p>\r\n	花灯是中国传统农业时代的文化产物，兼具生活功能与艺术特色。花灯是汉民族数千年来重要的娱乐文化，它酬神娱人，既有\"傩戏\"酬神的功能，又有娱人娱乐的价值，现代社会多于春节、元宵等节日悬挂，为佳节喜日增光添彩，祈求平安。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fna4wT2mV_tbQBQpZkmJfa-5_07Q\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span>摄影：郑亚平</span>\r\n</p>'),(1090,434,2,'民俗特色','<p>\r\n	说起花灯，你还想起什么？是一群人热闹的载歌载舞？还是年轻男女在花灯下互诉情思？不管怎么想，都是在品读时光，避开生活的压力，寻找到自己最贴切的温暖。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpXIZBaTRwLYhIPxWk13VQYA40Gs\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span>摄影：郑亚平</span>\r\n</p>'),(1093,432,1,'民俗风情','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiGI9Y9BuX_3zcQHvMTY95grkyCf\" alt=\"\" />\r\n</p>\r\n<p>\r\n	摆手舞是土家族古老的传统舞蹈，主要流传在鄂、湘、渝交界的酉水河流域，以重庆市秀山县、酉阳县、湖北恩施自治州的来凤、湖南湘西自治州的龙山、永顺为主要传承地，共有四节。摆手舞它分大摆手和小摆手两种。小摆手，土家语叫\"Sevbax(舍巴)\"或\"Sevbaxbax(舍巴巴)\"。大摆手，土家语称为Yevtixhhex(叶梯黑)。它集舞蹈艺术与体育健身于一体，有\"东方迪斯科\"之称。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiMA8M5kjPJ-EJSbNyNnGZuSpMke\" alt=\"\" />\r\n</p>'),(1094,432,2,'民俗特色','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuHihhSwYpXCuhWqMEmzEbRcPO-i\" alt=\"\" />\r\n</p>\r\n<p>\r\n	摆手舞反映土家人的生产生活。如狩猎舞表现狩猎活动和摹拟禽兽活动姿态。包括\"赶猴子\"、\"拖野鸡尾巴\"、\"犀牛望月\"、\"磨鹰闪翅\"、\"跳蛤蟆\"等十多个动作。列中国第一批国家级非物质文化遗产名录。\r\n</p>'),(1188,554,1,'油王','<p>\r\n	山茶油又名茶籽油，是我国特有的传统食用植物油，其生产和发展的历史源远流长，已有3000年的食用历史。据公元前三世纪的《山海经》记载：“员木，南方油食也”。这里所说的“员木”即油茶。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fmc0d8WHAHwqJWmTa0T7NuLaCiA0\" alt=\"\" />\r\n</p>\r\n<p>\r\n	山茶油是湘西客家山村中必不可少之物，它可食用，可药用。李时珍在《本草纲目》中提到茶油的食疗作用：“茶籽，苦寒香毒，主治喘急咳嗽，去痰垢”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fq-M92h8ov_TOVL-NtghmXXlPaR4\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界山茶油产自没有污染的山区，油茶树整个生长过程中不施农药、化肥等。山茶油的制作过程可分为去壳、晒干、粉碎、榨油、过滤，全过程均为物理方法，因此它是真正的纯天然绿色食用油。油茶中不含芥酸，胆固醇、黄曲霉素和其它添加剂。油茶中不饱和脂肪酸高达90%以上，油酸达到80-83%，亚油酸达到7-13%，并富含蛋白质和维生素A、B、D、E等，尤其是它所含的丰富亚麻酸是人体必需而又不能合成的。经科学鉴定，山茶油的油酸及亚油酸含量均高于橄榄油。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fvc8VWozr0eTdAmKsSOwKQlYaIWy\" alt=\"\" />\r\n</p>\r\n<p>\r\n	&nbsp;张家界山茶油色泽呈金黄或浅黄，品质纯净，澄清透明，气味清香，味道纯正。一直被视为山珍贡品，素有“油王”之美誉。\r\n</p>'),(1231,550,1,'天子绿茶','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlLyEQ4F7LhOSGjkClJ1QO9QZf25\" alt=\"\" />\r\n</p>\r\n<p>\r\n	天子绿茶，张家界国家森林公园的生态绿茶，茶园建在袁家界，海拔1200米，一年四季低温，云雾缭绕，土壤肥沃，不需要农药、施化肥。茶树生长呼吸的都是天然氧吧里的清新空气，茶叶采用千百年土家人传统技术和制茶新技术、新工艺有机结合的方法，不仅能增进健康，还有一定延年益寿的功效。天子绿茶条索紧细、圆直，外形白毫显露，色泽常翠润绿，形如松针，汤色清澈明亮，香气清鲜，滋味醇爽。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvncgWcM-l8ntWNqgxk8Q0MYjfhv\" alt=\"\" />\r\n</p>\r\n<p>\r\n	天子绿茶内含500多种人体所需的微量元素，其质位居湖南之首，冲泡数次，仍回味甘甜，沁人心肺。经专家测试鉴定“天子银毫”绿茶，具有防癌之功效，属于正宗纯天然、无污染的绿色保健饮品，有“茶中极品”、“湖南茶叶味精”之称誉。常饮该茶，可强身保健，益寿延年，增强记忆等多种神效。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtvNC-nBHxErrrCbjOpiwotrqmph\" alt=\"\" />\r\n</p>\r\n									'),(1245,542,1,'皇家御菜','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Flc-USkhgtOmkgerAE5wIB50k2Ym\" alt=\"\" />\r\n</p>\r\n<p>\r\n	岩耳生峭壁，形状和木耳相似，但比木耳大，表面呈黑色，有细刺，一般要六七年才能长成。土鸡养农家。岩耳炖土鸡，具有极高的营养价值，曾是土司进贡给皇上的御菜。\r\n</p>'),(1246,543,1,'开胃常菜','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlhDwpHQDi6AmtnLLxjl0bYJ6fXW\" alt=\"\" />\r\n</p>\r\n<p>\r\n	擂辣椒是有名的张家界常菜，传统做法是用油锅煸炒大尖椒至表皮糊焦，再泡入冷开水中剥皮去籽，放入擂钵中捣碎，拌入调料。这道擂辣椒综合了川菜的干煸厨艺和虎皮尖椒的做法，最后成品貌似钵中捣碎。适合家庭操作，口味也相当不错！很多擂辣椒的菜谱用大青椒做这道菜，不过大尖椒辣味不足。小尖椒辣味劲道，塞在馒头里也很好吃！\r\n</p>\r\n									'),(1247,544,1,'土家特色','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnVgF2MrLgWhAt47YOAYMaIh7V_n\" alt=\"\" />\r\n</p>\r\n<p>\r\n	土家十大碗来源于当地民间节庆及办酒席时惯用的方式，其最大的特点是半荤半素、一菜两味，餐桌上看不到盘子，吃饭及装菜全部是蓝边大口碗。久而久之，土家族人就给宴席取名为“土家十大碗”。除十大碗菜外，桌上还要配腌菜碟两个，为客人备用，另配上土家字自酿的苞谷烧酒。上菜的程序与方式也很有讲究，在土家民间每上一道菜，打盘子的人高喊拖腔的号子直到菜上完，优势还要鸣炮，吹响欢快的菜调子。\r\n</p>\r\n									'),(1248,545,1,'土家特色','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fr9c8pgPAI0et9P2BQ34uBNtlfWX\" alt=\"\" />\r\n</p>\r\n<p>\r\n	土家极喜欢的菜肴。在土家族的民间歌谣中，有这么几句：辣椒当盐，合渣过年，一条裤子穿它几十年····这几句话夸张地描述了土家人在解放前的辛酸生活。合渣的制作比较简单，只是在开头“推”的阶段辛苦些，因此，土家人称制作合渣为“推合渣”—将黄豆用水泡胀后，在石磨上一转一转地磨成豆浆，再将豆浆兑水放进锅，架火煮开，然后放进切好的菜丝，又煮开，就制成了一锅乳白带绿的合渣。由此可见，“推合渣”比起打豆腐来，要容易的多，难怪土家人又称合渣为“懒豆腐”。合渣最好的吃法是把合渣和米饭拌在一起吃，比酸菜都要下饭。\r\n</p>\r\n									'),(1250,539,1,'待客主菜','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuHrw3747XyH4dRlB8PB_WQa31rV\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界土家扣肉是土家人待客的一道主菜，一般只有在当地的人办酒席或大型活动的时候才会上用作主菜。土家扣肉的做法均采用五花猪肉，经过煮、炸、炸、蒸等多道工艺制成，上席时肉的下面一般都会用干咸菜铺底。虽说是肥肉制成，但吃起来一点也不腻口，味道极其鲜嫩。目前张家界一般餐馆及酒店都备有这道菜，以供旅游者享用。\r\n</p>'),(1251,538,1,'土家腊味','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fo7f7xmUdrjX82ceBAMTG9g-Fns7\" alt=\"\" />\r\n</p>\r\n<p>\r\n	腊血豆腐是土家族人杀年猪时以猪膛血、豆腐、肉丁及多种佐料拌和熏制而成，切片炒吃。血豆腐外观别致，独具特色，腊香味重，山区农家招待客人多用此菜，在酒席桌上，客人品尝此菜时，视之会误认为猪肝，食之则鲜美可口。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjC-KidddxITjoajOwBiNkM1Fh6Y\" alt=\"\" />\r\n</p>'),(1304,507,1,'星级酒店','<p>\r\n	张家界星级酒店、商务宾馆、度假村、民俗客栈、家庭旅馆一应俱全，来张家界旅游，您无论选择何种住宿设施，都会得到土家人民热情、温馨而周到的服务。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlNaWsXLWv8yj7qp9vBG6sawJR3E\" alt=\"\" />\r\n</p>\r\n<p>\r\n	星级酒店43家设施完善、舒适的星级酒店，房间标准、价格从100元/间—2000元/间不等，酒店通常会根据淡旺季上下浮动。大部分酒店提供个性化服务，您可根据自己的需求进行选择。\r\n</p>'),(1305,507,2,'商务宾馆','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Ft9sc3BR2Qmy1oKAb8hKG99wCMEF\" alt=\"\" />\r\n</p>\r\n<p>\r\n	商务宾馆其特点是设施简单、整洁、经济、互联网系统让您在旅行休息中能了解更多的信息。张家界商务宾馆价格一般在100元/间—200元/间，大部分商务宾馆不提供餐食服务，根据需要您可到周边的餐馆选择就餐。\r\n</p>'),(1306,507,3,'度假村','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvKB4zT3g_9dAUCJ_tNpHrQFfNPe\" alt=\"\" />\r\n</p>\r\n<p>\r\n	如果您想将自己完全置身于放松状态，不为工作、学习所累，那就选择度假村吧！张家界有温泉度假村、别墅式旅游度假村，不同的类型，不同的感受。由于服务内容的不同，所以度假村的价格也有很大差别。\r\n</p>'),(1307,507,4,'民俗客栈','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuMln_6uJ95kuZjlfwGOy-Yuq2Os\" alt=\"\" />\r\n</p>\r\n<p>\r\n	民俗客栈外观如百年老宅，为土家吊脚楼结构，秉承“精致古朴、高压不俗、内敛不简”设计理念，融汇了神秘湘西土风苗韵之精髓。有土家新娘床、土家阿公房、复式阁楼，如果您想体验土家人对居住的温馨和浪漫您可以在这些民俗客栈体验一把。\r\n</p>'),(1308,507,5,'家庭旅馆','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FrqvDRVCY8rxxrzP74cfTUG7AEYR\" alt=\"\" />\r\n</p>\r\n<p>\r\n	家庭旅馆一般都在武陵源核心景区内，是一些背包客、学生、摄影爱好者的首选。因为它价格便宜、位置条件优越，但在深山里家庭旅馆住宿条件有限。住在这里夏天您可以不用开空调，早上您可以起的早早的带上您的摄影设备找好机位可以欣赏到美丽的日出。\r\n</p>'),(1369,512,1,'土特产','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuahWODLl7Jbua9lo3zlqpio9c-9\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	葛根粉，也称葛粉。葛粉内富含黄酮类化合物和人体必须的铁、钙、铜、硒等矿物质，是老少皆宜的名贵滋补品，营养价值丰富，有“千年人参”至美誉。葛根粉具有清热解毒、生津止渴、补肾健脾、益胃安神、清心明目、美容养颜及醒酒等功能。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FscqXtWF7We1RSG_dxQX9t9JksXw\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	岩耳，又名石耳,生长在张家界市境内靠北朝南，湿润的绝壁上，有滋补、消炎、降压的功能，可以“明目益精”，能延年益寿。因为摘十分困难，被公认为山中之极品，不仅是山民餐桌山上的上等菜，而且是国宴中的佳肴。有滋阴补肾、祛热祛火、降脂降压之功效，炖鸡、炖排骨、炖猪蹄、其汤味鲜美，俗有“一家黄汤，十家闻香”之赞誉。\r\n</p>\r\n<p>\r\n	吃岩耳必须先将干岩耳放在30-70℃的温水侵泡7-8个小时，待岩耳舒展以后，再用淘米水轻轻揉搓，将沙与灰尘洗净，再滤干食用。岩耳炖鸡汤，其味鲜美，但必须等鸡肉基本炖熟以后，再放入岩耳，切忌同鸡肉一起炒炖。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtlOelV7RftaJNy0lOE3P84QGAu2\" alt=\"\" />\r\n</p>\r\n<p>\r\n	剁辣椒又名剁辣子，是以张家界市永定区沅古坪镇盛产的七姊妹辣椒为主要原料，与野生茶油、冰糖、食盐、生姜等配料，用传统手艺精制而成的课直接使用的辣椒制品。\r\n</p>'),(1370,512,2,'张家界的茶','																																				<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn-ogrCyuiSBRbMLnK1q44GwwDeV\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界杜仲茶精选优质杜仲鲜嫩叶、运用土家制茶工艺精制而成，富含十多种氨基酸、黄酮素、木脂素、维生素及人体所需的锌、硒等微量元素，具有养肝滋肾、双向调节血压、强筋骨、消除疲劳、提高免疫、抵御乙肝病毒、祛脂养颜、美体瘦腰的功效。张家界慈利县有国内最大的野生杜仲林，可冲茶、泡酒，实乃真正的绿色健康饮品。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkqNURVYZf95X_LIc6dvYWtmu42M\" alt=\"\" />\r\n</p>\r\n<p>\r\n	茅岩莓，又名土家甘露茶、土家神茶，也俗称藤。茅岩莓既有药品的疗效，又有食品的营养，是世界上黄酮成分含量最高、营养最丰富的野生植物。经科学鉴定，富含17种氨基酸和14中微量元素。茅岩莓主要分布在张家界茅岩河流域的崇山峻岭、云雾山中的红砂岩中，表层含有一层天然植物蛋白霜，能增强人体免疫力，祛病强身，延年益寿。\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(1371,512,3,'艺术品','																																				<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgVfFW5O8kPhDSZR7lFhQ2WIb7gD\" alt=\"\" />\r\n</p>\r\n<p>\r\n	砂石画是由李军声创作的以砂石、植物等自然材料为原材料，全方位展示张家界自热风光和风土民情的一种全新绘画种类，具有中国画的神韵，水彩画的清新，油画的凝重，工艺画的精巧，又有半浮雕的立体感，因其取材于自然而享有“绿色画种”和“环保画种”的美誉。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpDY3jlgKI4fDTQFkfaqM7i5kWr7\" alt=\"\" />\r\n</p>\r\n<p>\r\n	西兰卡普，土家语，意为土家族人的花铺盖，是一种土家织锦。以红、蓝、黑、白、黄、紫等丝线作经纬，通过手织挑打交织而成。团丰富多样，色彩鲜明，造型美观，被称为“土家之花”。\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(1372,512,4,'娃娃鱼产品','																																				<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn9bi9PqNqls_0vOSFpPOC0T1KSB\" alt=\"\" />\r\n</p>\r\n<p>\r\n	大鲵俗称“娃娃鱼”，肉质鲜嫩可口，富含18种氨基酸，17种不饱和脂肪和多不饱和脂肪酸，以及50多种对人体血液循环，增强免疫力，延年益寿，被专家誉为“水中人参”和“软黄金”。张家界金鲵生物科技有限公司经过科学研究，培养出可食用的子二代娃娃鱼，并开发出娃娃鱼汉堡、娃娃鱼面、大鲵熟食产品等营养产品。\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(1373,512,5,'小提示','																																													1.购物时请尽量到正规的商家去购买，不要轻信路边的小商贩。<br />\r\n2.购买商品，特别是价值大的高档商品时，要注意“货比三家”。<br />\r\n3.购买食品时，请查看包装上是否印有生产日期、保质期、保存期。<br />\r\n4.旅游景区内有很多小商贩，购物时一定要认清物品的品质、价值，自愿购买。<br />\r\n5.在景点购物时，对贵重物品遵循眼看手勿动的原则，一般物品在讲好价格后，让售卖方拿给您看，大家千万别跳入购物陷阱。<br />\r\n6.工艺品需轻拿轻放，一定要注意不要发生争执。<br />\r\n7.在正规商家购物时一定要开发票，以便做退货及投诉的依据。<br />\r\n									\r\n									\r\n									\r\n									\r\n									'),(1375,513,1,'简介','<span style=\"font-family:Microsoft YaHei;\">江垭温泉毗邻武陵源自然遗产保护区，是集旅游、会议培训、休闲、娱乐酒店、餐厅、户外拓展、保健等功能为一体的中高级温泉度假村，是张家界第一个休闲度假型国家4A 级旅游景点，湘西目前唯一一家仿古式半露天温泉。江垭温泉水质清澈碧透，富含对人体有益的矿物质元素，能够舒筋活络、强身健体，适合彻底放松身体，享受美好生活！<span style=\"font-family:Microsoft YaHei;\"></span></span>'),(1376,513,2,'特色温泉池','									同心泉：形似一颗红心，寓意同心同德，万众一心，众志成城。同心泉可供200 人同时共浴，水温38-41 度，可躺可坐，沐浴其中，仰望蓝天白云，远眺雄山奇峰，伴随着典雅的乡村音乐，周身便觉温暧如春、惬意万分。<br />\r\n石板温泉：又叫“地热带”，设计成为下面有温泉水加热的平滑石台，游人可躺在上面接受热石疗，从而改善血液循环。<br />\r\n瀑布温泉：哗哗流水从造型独特的假山上飞泻而下，给温泉区带来生机与活力。青春少女最喜欢此泉，立在水帘中，珠雨漂洒、视野朦胧，幽黑的秀发与银色的瀑布，仿如仙女降临人间仙境。<br />\r\n六福汤：位于温泉区的右方，集中了6 个特色小温池，这里能让您感受到人参、芦荟、灵芝、当归、薄荷、艾叶6 种温泉的神奇魅力和中医药文化的博大精深。<br />\r\n咖啡温泉：象一杯热气腾腾的咖啡，能提神醒脑，润肤养颜。<br />\r\n红酒温泉：水如陈年老酒，酒香缭绕，浸在酒温泉里，酒从毛孔渗入，浴在酒温泉里，您也会有诗仙李白一样的豪放，一样的产生“天若不爱酒，天上无酒仙，地上无酒泉”的感慨。<br />\r\n<div>\r\n	<br />\r\n</div>\r\n									'),(1377,513,3,'交通路线','									距 离：距武陵源核心景区约38 公里，距市区约70 公里<br />\r\n公共交通：武陵源区在306 省道（百丈峡，黄龙洞等处）乘坐江垭温泉专线车<br />\r\n自 驾：武陵源区沿省道S306、清李线至江垭温泉<br />\r\n									'),(1378,513,4,'基础信息','									地 址：张家界市慈利县江垭镇<br />\r\n咨询电话：0744-3355623<br />\r\n温泉开放时间: 9:00-22:00, 节假日至22:30<br />\r\n( 在此时间后, 游客不能再进入, 已进入的仍可淋浴温泉）<br />\r\n<br />\r\n									'),(1379,536,1,'土家腊味','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FoS-4nHsQtodwV5GOIS0vtsiaiQM\" alt=\"\" />\r\n</p>\r\n<p>\r\n	熏腊肉土家语叫“卡梯克”，先将鲜猪肉抹上盐，配以一定比例的花椒、大茴、八角、桂皮、丁香等香料，腌入瓦缸中3-5天，取出晾干，以锯末、木柴、橘皮、谷壳、柚皮等燃烟熏烤。土家腊肉色彩金红，味道香美，可食至来年伏日不坏。\r\n</p>'),(1380,535,1,'上好炖品','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fm3-4iILSyTG-yPAeCmILLeldsx4\" alt=\"\" />\r\n</p>\r\n<p>\r\n	将腊肉洗净，肥瘦切片完后，放置锅内，炒至双面发黄，再放入生姜、盐、葱、八角等味料，翻炒，.加水烧开，再将炒好的肉和汤一起倒入罐炖，即可。\r\n</p>'),(1397,533,1,'淡水上品','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fg5Y9XYN1hnL9s6Z3F1iptEsIyaU\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界山清水秀，渔业生产基地水质纯净清澈，盛产青鱼，肉质细嫩鲜美，白而充实，蛋白质含量超过鸡肉，是淡水鱼中的上品。可红烧、清炖、糖醋或切段熏制。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsEToutvgSsGfAyCzJSbjSHgdbg_\" alt=\"\" />\r\n</p>'),(1398,532,1,'水库特产','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FulCtOZyCP7PauXIPPgaqPZlzHBp\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界草鱼产自张家界空气、水质俱佳的各大水库，无泥土腥气，肉质嫩而不腻，紧硬爽脆，不易煮烂，口感独特。含有丰富的不饱和脂肪酸，可促进血液循环，美容养颜。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fgb3usF1_M7TKNCo6jcVJ7UR1vgZ\" alt=\"\" />\r\n</p>'),(1399,531,1,'淡水美味','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fhg9GncH5Pbx95ur4m8qSnZD9Y1_\" alt=\"\" />\r\n</p>\r\n<p>\r\n	黄鸭叫肉质细嫩，味道鲜美，多脂肪，营养丰富，浑身只有一根长鱼骨，无肌间刺，是淡水鱼中难得的美味。适合清炖和红烧，汤奶白色，加紫苏、辣椒等调料后妙不可言。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgxacZE9WsVh9aOA-4HbZdJM3FBi\" alt=\"\" />\r\n</p>'),(1403,530,1,'清水虾类','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Frpnb88zs64CjRTxwIDQDFEiTbVg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	生活在澧水河上游，水质清澈、无污染。肉质细嫩松软，易消化，味道鲜美，营养丰富，是高蛋白低脂肪的水产食品。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtqxxF7czkm674ltUIRDxWW2cVxT\" alt=\"\" />\r\n</p>'),(1410,529,1,'清水溪鱼','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlDgilkW_0SBIsxH0JX3xLUfi23G\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">生活在</span><span style=\"line-height:1.5;\">桑植县五道水镇水质清澈、没有污染的山溪里，浑圆修长，平时都紧贴在石头下面，肉厚而鲜嫩，入口松脆微甜，富含脂肪，油炸或清炖，味甚鲜美。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhCVgWhJnQ00gUxtFiJgWnfa6LVW\" alt=\"\" /><br />\r\n</span>\r\n</p>'),(1411,528,1,'地方名鱼','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fs3X6EMc8kytfUcoS_bie4JzwWYm\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界市慈利县的岩泊渡镇，该镇地处湖南四大水系“湘资沅澧”之澧水上游，这里水面开阔、青山绿水、景色优美，自古就是有名的产好鱼之地。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuosPVkthxjPJx41u3dQcR5Z-5Hv\" alt=\"\" />\r\n</p>\r\n鳜鱼是世界上一种名贵淡水鱼类，味道鲜美，富含蛋白质，肉刺少，可补五脏、益脾胃、充气胃。岩泊鱼的鱼也一直是张家界特色美食之一。<br />'),(1412,527,1,'休闲佳品','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FoEtiEPnH4O9JEtc0p8NMDoQKHQP\" alt=\"\" />\r\n</p>\r\n<p>\r\n	银鱼又名小眼银鱼，俗称面条鱼。江垭银鱼出产于江垭水库，这里万亩湖面烟波浩渺，碧波荡漾。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhZ2Kj3MhOZTLxJLmPGj-S3zIVns\" alt=\"\" />\r\n</p>\r\n江垭银鱼通体透明，小如银梭，长约7厘米，洁白如银、无鳞无刺，富含高蛋白，肉鲜味美，营养价值高，具有滋阴补肾、哺乳发奶、养颜保健之功效，是馈赠亲朋好友的上佳礼品和理想的旅游纪念休闲食品。'),(1413,526,1,'黄金浆果','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FmyWQj616WWNAdrSY8nPW66FyGO4\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界蓝莓采摘观光园，位于永定区尹家溪镇立功桥村，是目前湖南省内唯一一个蓝莓采摘园。蓝莓被称为水果之王,果实中含有丰富的营养成分,属高氨基酸、高锌、高钙、高铁、高铜、高维生素的营养保健果品，不仅具有良好的营养保健作用,还具有防止脑神经老化、强心、抗癌软化血管、增强人机体免疫等功能。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkjhPd079E9Vm110bCqiXg_exfYb\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界蓝莓果肉细腻，味道酸甜适口，被誉为“黄金浆果”，成熟期在6月-8月。\r\n</p>'),(1414,525,1,'果中之王','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpcaPCXybJNOvKacRWYq-iZHg-Xn\" alt=\"\" />\r\n</p>\r\n<p>\r\n	猕猴桃外国称中国鹅莓，民间称阳桃。《本草纲日》云：“其形如梨，其色如桃，因猕猴喜食，故叫猕猴桃。”张家界环境优美，盛产野生猕猴桃，有中华猕猴桃、毛花猕猴桃和草叶猕猴桃等5个品种，果实质地柔软，细嫩多汁，清香鲜美，营养极为丰富，被称为“果中之王”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiGf3Hk4Bfm7Ll41TLWwziE5O8Ry\" alt=\"\" />\r\n</p>\r\n<p>\r\n	永定区新桥镇郑家坪红心猕猴桃种植基地可参与采摘节活动。成熟期为8月-10月。\r\n</p>'),(1415,524,1,'八月炸','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FmRna51uf-f58LDjpYcP-4Ek1YX6\" alt=\"\" />\r\n</p>\r\n<p>\r\n	八月瓜又名“八月炸”，为木通和三叶木通的果实，因八月果熟开裂而得名。集中分布在张家界天子山、索溪峪、杨家界景区山麓谷地、林缘灌木丛中，果形似香蕉，富含糖、维生素以及人体必需氨基酸，果味香甜，乳白多汁，风味独特，为无污染的绿色食品，营养丰富，成熟期为8月-10月。此外，张家界三成农业发展有限公司对八月瓜进行深加工，如今已开发出果酒、果汁、果茶等系列产品。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Foa3jWj7GLwbdeePOfbaq1kOuMQk\" alt=\"\" />\r\n</p>\r\n<p>\r\n	其中，果茶已上市，成功创出了“九嘎公八月瓜”品牌。实现了在张家界，一年四季都可以吃到“八月瓜”的神话。\r\n</p>'),(1417,522,1,'菌类之王','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fg74Y3IPcnQdeKrlP6edoVmG79yI\" alt=\"\" />\r\n</p>\r\n<p>\r\n	枞菌，又名松菌，是一种纯天然、野生、名贵珍稀的食用菌。在张家界境内，武陵松生长比较广泛，悬崖峭壁、崇山峻岭到处都有它的身影。它生长在大松树下，肉厚，营养丰富，堪称菌类之王。6-9月是枞菌生长旺盛的时期，雨后天晴是采枞菌的最佳时机。枞菌含有蛋白质及多种维生素、微量元素和氨基酸。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuTYamAw-u4XEPcjuo5arbY2il2u\" alt=\"\" />\r\n</p>\r\n<p>\r\n	菌肉肥厚，香气浓郁，食味鲜美，为名贵佳肴，并有强身、益肠胃、理气化痰、止痛、驱虫功效,及调节人体新陈代谢、降低血压、胆固醇和治疗糖尿病的作用。\r\n</p>'),(1418,521,1,'笋类佳品','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fly46UVKG01ieah_m4ycRGnVNd5I\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界山竹笋采摘于风光秀丽的张家界高山丛林中，纯天然生长，鲜嫩金黄，竹质清香，是吸大自然之灵气生长而成的稀有之物。明朝李时珍《本草纲目》记载，“竹笋方而厚，性硬、脆，专蓄此笋，常食之，有延年益寿之功能”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqXNWwd-bFKTkeMSrr94kiIQM82_\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界山竹笋含有丰富的氨基酸、维生素，质嫩肉厚，色美味鲜，具有滋阴凉血、清热化痰、解渴除烦等功效。\r\n</p>'),(1419,520,1,'季节野菜','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuJaXO7dT2JpctrD_wB0UVEPqSZH\" alt=\"\" />\r\n</p>\r\n<p>\r\n	地木耳，又名地岩耳、地衣等，是真菌和藻类的结合体，一般生长在阴暗潮湿的地方，暗黑色，有点象泡软的黑木耳。富含蛋白质、多种维生素和磷、锌、钙等矿物质，是一种很好的低脂肪营养保健菜，能降脂减肥，同时，对目赤、夜盲、脱肛等病症也有一定疗效，具有补虚益气、滋养肝肾的作用。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnJU79sCPbdvAQlyr0VdFawWEO6C\" alt=\"\" />\r\n</p>\r\n<p>\r\n	地木耳适于做汤，也可凉拌或炖烧，地木耳炖肉、地木耳鸡蛋等都是张家界美食中的精品。\r\n</p>'),(1420,519,1,'土菜特产','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhdjfhBUerGUDShNtGQG55hKKfAl\" alt=\"\" />\r\n</p>\r\n<p>\r\n	洋荷，别名野姜、洋霍，为姜科，姜属多年生草本植物，栽培历史悠久，大朵为房前屋后零星种植。果黑红色，分为很多薄片，如笋，层层包裹，一般在夏末秋初采食，采食嫩芽、嫩茎和花穗，地下茎风味似姜，营养价值高，可凉拌或炒食，色彩艳丽，味道辣鲜香脆，经常食用，具有强身健体，防病怯病之功效。\r\n</p>'),(1421,518,1,'山菜之王','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkCyVr0_pjUqLOudT21CNKsOQoxU\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界蕨菜，享有\"山菜之王”美誉，根茎粗壮，富含淀粉，故名粉蕨或蕨粉，可做粉条、粉皮食用。其嫩苗似拳卷曲，故又称“拳头菜”。3-5月份多生长于张家界山间溪流阴凉处，食用时，一般用开水煮熟后，取出撕开，用清水浸泡一二天，每天换清水数次，泡去涩味，加油盐调料回锅炒熟食。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgwKxDHDkSZhz_t68oahVA2nYvbQ\" alt=\"\" />\r\n</p>\r\n<p>\r\n	厥菜有良好的清热解毒、杀菌清炎之功效；能扩张血管，降低血压；可补脾益气，强健机体，增强抗病能力。\r\n</p>'),(1424,270,1,'深山野菜','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fm4doadDhik06sVb9VK4yViKyJ0w\" alt=\"\" />\r\n</p>\r\n<p>\r\n	“鸭脚板”又称鸭儿芹、野蜀葵，属于伞形科多年生草本植物，因为它的叶片每一枝桠形状是分三瓣状，有些像鸭子的脚掌，所以才有了这个称呼。因张家界水质优良，鸭脚板生长十分普遍，其天生带有一种清香，采摘的时候会冒出浓酽的清香味，含有人体多种营养成分，有助于增强人体免疫功能，具有消炎、解活血、消肿的功效。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FudAa3ej7cs9AJJduS0vo7p4hIpS\" alt=\"\" />\r\n</p>\r\n<p>\r\n	作为纯天然的绿色食品，其味道鲜美、风味独特，张家界的鸭脚板以3-5月份最为新鲜，清香味美，粉嫩刮脆。\r\n</p>'),(1425,517,1,'开胃首选','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsO-yISlEsSk2Fqx8lrf2pavGj0Q\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界本地黄瓜以表皮带小刺者为最佳，属凉性食物，能祛除体内余热，具有祛热解毒的作用，此外还含有丰富的维生素E，可起到延年益寿，抗衰老的作用；黄瓜中的黄瓜酶，有很强的生物活性，能有效地促进机体的新陈代谢。用黄瓜捣汁涂擦皮肤，有润肤，舒展皱纹功效。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpzuQ16g_WJGbsKUbs6ysHDl_5rA\" alt=\"\" /></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">生吃脆中带麻，甘甜爽口，多汁柔嫩，熟食脆嫩清香，是不少人开胃的首选。</span>\r\n</p>'),(1426,516,1,'高山果蔬','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FscbqYletQF2nCS8I9M5PydTLD6T\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	白石萝卜产自张家界市桑植县白石乡，白石乡萝卜栽培历史十分悠久。据桑植县志记载：“明末清初，为萝卜栽培盛期。清末，萝卜生产逐渐衰落，民国时期有所发展，白石为全县主要蔬菜产区之一”。<span style=\"line-height:1.5;\">白石乡平均海拔1026米，山环水绕，这里冬寒夏凉、冰冻期长，独有的沙质土使得这里的萝卜无论是外观还是内在品质均优于长江流域同类产地产品。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fi_77E0mb2eMqDRyRvCdaDFk-mCN\" alt=\"\" /></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">近年来，白石萝卜的知名度逐渐上升，先后获得了农业部绿色产品认证，永强农业也跻身市级农业产业化龙头企业，作为桑植县乃至全市农业品牌产品，白石萝卜远销长沙、广州等省份以及出口港澳、海外。</span>\r\n</p>'),(1427,537,1,'土家腊味','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FriaHgV-NkwQBotrt18DQEZTcnwP\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	将肥瘦肉切成条状，与辣椒面、胡椒粉、食盐等佐料一起搅拌均匀，腌制12小时后进行灌装，每15厘米用线绳扎紧，灌装完成后在阴凉处悬挂7天，然后用香柏树枝进行烟熏，美味的土家香肠出炉了。\r\n</p>'),(1428,534,1,'娃娃鱼介绍','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fj3qhTVAUKQirAnZ3eEB1aV956Zz\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	“娃娃鱼”是大鲵的俗称，是世界上现存最大最珍贵的两栖动物，属国家二级保护动物，张家界是中国娃娃鱼的主要原产地，“张家界大鲵”荣获国家地理标志产品保护。\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiUyGYaPtPdUjFFs7F6qboGo2c4b\" alt=\"\" /></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">娃娃鱼具有丰富的食用价值、药用价值和美容价值，肉质洁白鲜嫩，肥而不腻，清淡而鲜美，富含18种氨基酸，17种不饱和脂肪酸和多不饱和脂肪酸，以及50多种对人体健康长寿有益的活性物质，娃娃鱼肉100g干物质中所含氨基酸总量为91.92％，必需氨基酸含量为39.69％，具有滋阴补肾、补血行气、补益疗虚、增强人体免疫力、提高智力、美容益肤、防癌健体、延年益寿等多种特殊功效，被誉为水中的“活人参”和“软黄金”。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fj_4Tlu4jMEmmunStSbVz2zzTWfu\" alt=\"\" /><br />\r\n</span> \r\n</p>'),(1429,534,2,'娃娃鱼营养价值','									<p class=\"MsoNormal\" align=\"left\">\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fv6-Q8m1I5lO8O-VF4QPNBPE3Vy6\" alt=\"\" />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	娃娃鱼鱼肉鲜美，味极鲜嫩，营养丰富。要是喝汤，河里的、高山的精华同时存在这鱼汤里，其味道和营养都属于极品。这鱼汤有抗衰老的作用，吃了可以延年益寿的，女士吃了还养颜，用娃娃鱼鱼头跟爪子来炖汤，是老年人最好的滋补品，可健胃理气、祛风除湿。<span></span> \r\n</p>\r\n									'),(1430,534,3,'娃娃鱼做法','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkSj2JgEDhYLy4wiGnf56Dxf9lFn\" alt=\"\" />\r\n</p>\r\n<p>\r\n	娃娃鱼的做法有很多种，可分为红烧娃娃鱼、浓汤娃娃鱼、清炖娃娃鱼、荷叶蒸娃娃鱼、虫草蒸娃娃鱼、鲵宝羹、八宝娃娃鱼、三扒大鲵等多种做法，可根据个人口味选择不同的做法。\r\n</p>\r\n									'),(1435,523,1,'瓜类良品','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fq7g-tX9mcMBSdw4ZRGwnMIOc4ua\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界牛奶西瓜主产于永定区后坪镇，曾有人以为它的甜味像牛奶，也曾有人以为它是牛奶浇灌长大。究竟因何为其名，只有在张家界吃了才知道。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnQg38KfvuszEnl8AVJdD1SnVVmk\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	牛奶西瓜对土壤、天气、温差、水分、种植技术等各方面的要求很高，张家界独特的气候条件，产出的牛奶西瓜品质优，色泽好，皮薄多汁，味甜爽口，口感上佳，富含糖、维生素、多种氨基酸以及少量的无机盐，具有清热解暑、生津止渴、利尿除烦的功效，成熟期为6月-8月。\r\n</p>'),(1436,541,1,'土家特色','<p>\r\n	在张家界，随处可见打着“正宗三下锅”或者“土家三下锅”字样的餐馆，“三下锅”俨然已成张家界最具特色和最富盛名的美食。在张家界，问三下锅，无人不知无人不晓。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fq2xn93HVU8dAPvhGUjmrDVLkjDb\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">相传明代嘉靖年间，朝廷征调湘鄂西土司兵上前线抗倭，恰好赶上年关，为不误军机，土司王下令提前—天过年，于是腊肉、豆腐、萝卜一锅煮，叫吃“合菜”，以后逐渐演变成“三下锅”。现在张家界的三下锅多为肥肠、猪肚、牛肚、羊肚、猪蹄或猪头肉等选其中二、三样或多样经过本地的土厨师加工成一锅煮。为了满足更多的喜好，三下锅选材更加广泛，增加了张家界特有的酸萝卜、霉豆腐、酸菜做开胃菜，菜品根据客人的喜好搭配，人数多，食材也多，种类不断增加，吃起来那叫一个过瘾。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FriS1Ozn029fp_aqMJyOuaYWz4p8\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	三下锅的吃法也分干锅与汤锅之分，干锅无汤，麻辣味重，不能吃辣的人最好好好考虑。三下锅虽好吃，但在张家界正规的酒店里面一般是见不到这道菜的，只有在当地人出入的一些小餐馆里才有的吃，才能吃出它的风味。三五个人坐着小木椅，围着一张小桌子，桌子中间挖个洞，放个炉子，炉子上架口小铁锅，锅内热气腾腾，吃得差不多了，再下千张或叶儿菜，真正的味儿就出来了。\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">三下五除二，简单明快添一锅，是武陵山脉简约生活的集中表达。邀三又唤五，呼啦随意风火吃，是土家人爽直性子的真实写照。</span>\r\n</p>'),(1437,546,1,'娃娃鱼产品','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqsVZAWLpW_sah3ZHO9bh2YF5yXa\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	娃娃鱼，我们都听过，但真正吃过的有几个？按道理来说，娃娃鱼是国家二级保护动物，是属于“可远观而不可亵玩焉”，更别说还要吃了它，能有机会一睹芳容就已经偷着乐了。其实不然，早在1996年中国娃娃鱼之父---张家界桑植县农民王国兴先生就成功研究繁殖娃娃鱼的技术了，目前流向市场的都是由野生娃娃鱼繁殖的后代，而吃娃娃鱼“子二代”是国家政策所允许的，只需要出售单位办理相关娃娃鱼的经营许可证即可。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fs9gIjyK0V7OxUolOLN-BAzw8c0k\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">&nbsp;“娃娃鱼”是大鲵的俗称，其肉质鲜嫩可口，富含18 种氨基酸，17 种不饱和脂肪酸和多不饱和脂肪酸，以及50 多种对人体健康长寿有益的活性物质，能改善人体血液循环，增强免疫力，延年益寿，被专家誉为“水中人参”和“软黄金”。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn9bi9PqNqls_0vOSFpPOC0T1KSB\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界金鲵生物科技有限公司与金驰生物科技有限公司经过科学研究，培养出可食用的子二代娃娃鱼，并开发出娃娃鱼汉堡、娃娃鱼面、大鲵熟食产品、大鲵保健酒等营养产品，同时利用提取大鲵低聚糖肽，开发仙鲵蔻系列护肤品。目前，娃娃鱼作为国内唯一从濒危到保护，再从合理利用促进保护的典型物种，开创了保护动物的新模式。</span>\r\n</p>'),(1442,547,1,'土家神茶','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsF72n-SDL8LJu4goiX0veseer57\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	茅岩莓，又名土家甘露茶、土家神茶，也俗称长寿藤。茅岩莓既有药品的疗效，又有食品的营养，是世界上黄酮成分含量最高、营养最丰富的野生植物。<br />\r\n<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FijSDRJ_RLoBwmXyBii_wFJRVTLy\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	茅岩莓主要分布在张家界茅岩河流域的崇山峻岭、云雾山中的红砂岩中，且蚊虫不吃、百病不生，具有这方水土特有的清新与淳朴。经科学鉴定，茅岩莓富含17种氨基酸和14种微量元素，表层含有一层天然植物蛋白霜，能增强人体免疫力，祛病强身，延年益寿。<br />\r\n<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkqNURVYZf95X_LIc6dvYWtmu42M\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	千百年来，当地土家族山民祖祖辈辈以此为茶，当地土家族人身体强健，古稀之年的健康老人众多（百岁老人之多，为全国地区之最），多少年来从无高血压、高血脂、肥胖症和癌症患者的出现，对此茶所含有的特殊香味及饮后能抗人体多种疾病和有益人体健康的神奇妙用，认为是神灵的恩赐，故名“神茶”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fvh6x16V-loJ_VF7fz9Z2pMAQbe-\" alt=\"\" />\r\n</p>\r\n<p>\r\n	茅岩莓运用了现代破壁工艺精制加工成茅岩莓等系列保健茶，其功效独特，用途广泛；\r\n</p>\r\n<p>\r\n	国际奥委会-丹徙•大山曾高度评价茅岩莓的诞生将为人类健康提供纯天然绿色通道，更能为人类的健康作出卓越的贡献。”\r\n</p>'),(1443,548,1,'健康饮品','<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn-ogrCyuiSBRbMLnK1q44GwwDeV\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	根据中国古书记载，杜仲的名称由来，是一位名为杜仲的人，发现喝了某种树的树皮煎出来的汤汁之后，身体变得十分轻盈，而且病痛消除，体力也恢复了。所以就将这种树称为杜仲树。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnqveiLYS8eDsfwdbS5qQvWBVI2v\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界是有名的“中国杜仲之乡”，杜仲又名丝连皮、扯丝皮、丝棉皮、玉丝皮、思仲等，属落叶乔木。杜仲是我国特有树种，经济价值很高，资源稀少，被我国有关部门定为国家二级珍贵保护树种。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fikp6M64MdEvsvRSvM9DY0vKgeDE\" alt=\"\" />\r\n</p>\r\n<p>\r\n	《本草汇言》记载：凡下焦之虚非杜仲不补，下焦之湿非杜仲不利。足胫之酸非杜仲不去，腰膝之痛非杜仲不除。杜仲为中国名贵中药材之一。<br />\r\n<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvirN2Epnbmn4OQvpF4I8KQuEGCs\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">杜仲茶精选优质杜仲鲜嫩叶、运用土家制茶工艺精制而成，富含十多种氨基酸、黄酮素、木脂素、维生素及人体所需的锌、硒等微量元素，具有养肝滋肾、双向调节血压、强筋骨、消除疲劳、提高身体免疫、抵御乙肝病毒、祛脂养颜、美体瘦腰的功效。因为杜仲所含成分可促进新陈代谢和热量消耗，而使体重下降，除此之外还有预防衰老、强身健体的作用，属于五大流传减肥秘药之一。可冲茶、泡酒，实乃真正的绿色健康饮品。</span>\r\n</p>'),(1444,549,1,'西莲云雾','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FmkCjF3xUFgugt2mSRAh4KJqRL7Q\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	西莲云雾名茶产自桑植县西莲乡，系革命老区贺帅故里，境内海拔800余米，雨量充沛、多云雾、富有机质，是为茗茶绝佳生长环境，西莲云雾因此得名。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvEPNaHbWqTPu710KtU6iWiDzqtx\" alt=\"\" />\r\n</p>\r\n<p>\r\n	西莲云雾采清明前后幼稚鲜叶为料，零污染环境与秘传工艺使其品质独特，具内质丰富、汤色绿亮、香气高长、滋味醇爽、叶底匀整、回味悠长之特点，是居家品饮馈赠亲友之上品。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnEZLsg1_j1GS5vcxYiG5LKqoalr\" alt=\"\" />\r\n</p>\r\n<p>\r\n	西莲红茶，色泽金黄，香味鲜醇，回味甘甜，以汤色红亮和 “冷后浑”为主要特点，系我国高品质的工夫红茶之一。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fox-wR2qKPM0oV1r4OqRMzGlZ6xC\" alt=\"\" /> \r\n</p>'),(1445,551,1,'上佳贡品','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhdS2vag8GJnqVQPYLrfvxTbKCIC\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	鱼泉贡米产自张家界武陵源中湖乡鱼泉峪村地，远离城镇，无污染源，数百年来为历代土司王敬献给朝廷的贡米。基地土壤富含硒元素，小气候特色突出，按照绿色食品(稻米)标准化生产技术生产、加工的鱼泉峪贡米为食药同源、营养保健、清香爽口的特种稻米。\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnW6EokuF9KZmGNnvt1qXVUazr1m\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">相传元朝时期，一土司王偶然食用鱼泉峪村的米饭，不但当时感到香甜可口，几天后仍留有余香，遂进贡给皇宫。皇帝御用后，称奇不已，要求鱼泉峪所产的稻米全部作为贡品进贡给皇宫，“鱼泉贡米”因此名扬天下。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fo-kmTIaWbxah-fgdp7aVzdIyDtv\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	现代医学技术证明，硒元素有抗癌之王美誉，是人类三大必补微量元素之一，它在人体中起着延缓衰老、排毒解毒、抗氧化、抗肿瘤、防治心脑血管疾病，提高免疫力等作用。鱼泉峪贡米高含硒元素，长期食用能促使消费者健康长寿。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqACietMNFOaRDAuJNDqSLDzfu4S\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FrA3TnXerw7QnfcHs8CpzszoQbdB\" alt=\"\" /> \r\n</p>'),(1453,552,1,'七星椒','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhO48fjOhB2IYk9QRH5E57mY9nU0\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	七星椒是张家界久负盛名的特产，因每个枝梢上大都长有七颗，又名“七姊妹”。<br />\r\n张家界种植七星椒有着悠久的历史。据考证，张家界七星椒的传统种植可以追溯到1200年以前，历史及其久远。适宜的气候，肥沃的土地，悠久的历史为七星椒的生产和发展奠定了牢靠扎实的基础。尤其值得一提的是，喜酸好辣的土家先民在七星椒的生产、加工、贮藏上自成一派，独具特色。王家坪镇、沅古坪镇等乡镇以堪称“土家秘笈”的七星椒配料、加工、储存工艺所生产的七星椒剁辣椒产品，色泽鲜红，口味独特，辣中带酸，酸中带麻，麻中带甜，不仅开胃健脾，而且还能祛风驱湿，一直以来都是人们不可或缺的“盘中最爱”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqeLEakxdLoblBI6pHO4pGO0-D6r\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界七星椒皮薄肉厚、辣味纯香、口感良好、营养丰富，富含大量维生素C、氨基酸、胡萝卜素、辣红素及钙、磷、铁等矿物质，而随着研究的不断深入，七星椒富含的抗癌之王----硒元素，使得七星椒成为健康养生食品中的精品。\r\n</p>'),(1454,552,2,'剁辣椒','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtlOelV7RftaJNy0lOE3P84QGAu2\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	张家界的剁辣椒，红彤彤的辣，酸溜溜的酸！！！\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">有一首张家界民歌《辣椒歌》，流唱全国。歌中唱道：“远方的客人莫见笑，张家界人最爱吃辣椒。” 的确，张家界民间有“无辣不成菜”的俗话，就像山西人爱在菜里放醋，闽南人喜欢放虾油一样，缺了辣椒就口味不佳。张家界人吃辣椒，除了一般的做菜、佐味外，还有剁辣椒、腌辣椒、泡辣椒、烧辣椒、白辣椒、豆豉辣椒、米粉辣椒、辣椒酱、辣椒油等等，不胜枚举。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqoZK9RapFBcUf487xPxEG50-32L\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	剁辣椒又名剁辣子，剁辣椒的制作并不复杂。不少本地人喜欢挑选那种红红的尖辣椒作为原料，选辣椒的时候一般喜欢选偏辣的那一种。把买来的辣椒洗干净，然后去蒂了放在阴凉处晾干生水，待辣椒表面干燥以后就可以剁了，剁红辣椒是细致活，剁时，菜刀不能抬得太高，下手不能太重，要用力均匀，恰到好处，否则辣椒片会飞出去。剁时还要特别注意不要让辣椒汁粘在手上，如粘上了，会辣上好一会儿，洗也洗不掉，火烧火燎，令人难忍。剁好后拌上盐、生姜片、大蒜头，就可以放进坛子里密封起来了，盐的多少很大程度上决定了以后的味道，盐少了就容易坏，盐多了也不会好吃。密封保存一个星期左右就可以取出来吃了。 \r\n</p>\r\n									'),(1455,553,1,'产品介绍','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiPzpF3hmrkgMND68QmdN10BZ5Rf\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">葛根粉，也称葛粉。葛根内富含黄酮类化合物和人体必须的铁、钙、铜、硒等矿物质，是老少皆宜的名贵滋补品，营养价值丰富，有“千年人参”之美誉。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界市葛根粉产自张家界自然保护区，生存环境优越，具有清热解毒、生津止渴、补肾健脾、益胃安神、清心明目、美容养颜及醒酒等功能。</span>\r\n</p>'),(1456,553,2,'生长环境','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuahWODLl7Jbua9lo3zlqpio9c-9\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界山高，气温适宜，是葛根生长的好地方。葛根一般生长在山坡草丛中或路旁及较阴湿的地方，以疏松肥沃、排水良好的土壤或砂壤土为好。葛根为豆科植物野葛的根，从泥土里挖出来后清洗干净，榨碎冲洗过滤沉淀为葛粉，就可以制作葛根粑。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">葛根为多年生藤本植物，藤长达数米，全株有黄褐色粗毛，块根肥厚，花期4-8个月。种子卵圆形而扁，赤褐色，有光泽，果期8-10个月。</span>\r\n</p>'),(1457,553,3,'做法用法','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpSNM4fC_mmsa3bZ22CZ4KwxI8Ku\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">新鲜葛根挖回家后要洗刷干净，留下那些含粉量低的细根切成片晒干，用于做葛根茶，可以治疗高血压、高血脂、高血糖。含粉量高的葛根在石头上锤扁，反复打碎，直到葛浆渗出，放入水中清洗，将汁水用筛浆法筛入缸里，葛根要反复捶打三次，打成齑粉。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlYiK2U4Wj8j5E_i2RiZcMtyXpVF\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">将葛根加入管中，能分离泥沙，加速葛粉沉淀，反复用清水漂洗三次，汁水开始成乳白色，完全过滤沉淀后做成葛饼，先风干一部分水分，再用纱布或者纸包裹悬挂风干，最后放在太阳下晒干，就是上好的葛粉。葛粉口感和藕粉类似，润肠通便。剩下的葛渣晒干可以引火，做柴火使用。老年人说，葛渣还可以纳鞋底。</span>\r\n</p>'),(1458,555,1,'山珍佳味','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FscqXtWF7We1RSG_dxQX9t9JksXw\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	“岩耳长得怪又怪哟，悬岩绝壁把家安，哥哥上山采岩耳，妹妹心哟蹦出怀......”说的就是一代代为绝壁而生的“土家蜘蛛人”穷途绝壁采摘岩耳的故事。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqteTiNf1Na59IjdXSDe_voMxk6L\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	也许你会好奇，这石崖岩耳能长成什么模样？是否也像天山雪莲那般晶莹透亮？《本草纲目》记载，“石耳，庐山亦多，状如地耳，山憎采曝魄远，洗去沙土，作茹，胜于木耳，佳品也。”其形状和木耳相似，但片张要薄，用手摸比木耳粗糙，颜色也与木耳有异，一面黑的像黑玉，一面又呈灰白色，是一种珍贵的药材，味鲜而滋补。“惟石耳味甘腴性平无毒，多食能润肌童颜。”\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtEUZ9XZOQCuvke3NO6b2ytM7cKZ\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">吃岩耳必须得法，先将干岩耳放在30-70℃的温水注浸泡七八个小时，待岩耳舒展以后，再用淘米水（也可以用碱水）轻轻揉搓，将沙与灰尘洗净，再滤干食用。岩耳炖鸡汤，其味鲜美，但必须等鸡肉基本炖熟以后，再放入岩耳，切忌同鸡肉一起炒炖。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlJr403sOe9__NXn8F7BF4pfCz9y\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	岩耳多产悬岩峭壁，在张家界，愈是陡的地方，岩耳形愈大，味愈鲜。攀走绝壁的采耳人，当地人称“岩耳客”，游客称“土家蜘蛛人”。猜岩耳者，悬荡于千仞绝壁之间。古今，会这项绝技的已寥寥无几了。\r\n</p>'),(1459,556,1,'蜜中极品','<p>\r\n	对于世世代代，祖祖辈辈居住在大山深处的土家人来说，虽然日子过得很清贫，但是空气明净，山青水绿。屋前屋后，地里田边，天天都是鸟语花香。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FrwEQbQUVakWv90t8X5pF4QbAqhT\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">有了鸟语，有了鲜花，当然就少补了在花丛中辛勤采蜜的野蜂了。到山中放羊，在山林里砍柴，在地里摘菜，在果树上采摘水果，身边总是“嗡嗡嗡”地飞舞着很多的野蜂。大人和小孩，基本上对于身前身后飞舞的野蜂，是无视的，该干嘛还是干嘛。你不理它，它也不会主动攻击你。何况四周土家的木楼上，几乎每家每户都有一些自制的蜂桶，一些技术好的山民，甚至养有十多桶野蜜蜂。一年仅取一次蜜，给家里的老人和女人享用。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fm6RxJ2qcRSbpwy9DVzdIYvUIev0\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">蜂蜜含有果糖、葡萄糖、蔗糖、有机酸及多种维生素，具有止咳润肺、补中缓急、美容养颜、润肠通便、保护心肝、促进睡眠、预防衰老等功效，备受人们喜爱。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界天然蜂蜜产自风景秀丽的张家界市自然保护区内，高海拔，无污染，采集山上原生态花蜜而成，饮用后唇齿留香，回味无穷，张家界天然蜂蜜不含蔗糖，极易结晶并且浓度高，一年仅取一次，保留了蜂蜜天然的营养活性，属蜜中极品。</span>\r\n</p>'),(1460,557,1,'十大名吃','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FoLsRuM0yBhOqa92h2CJi4pL4prq\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	魔芋又称作麻芋、鬼芋，我国早在2000多年前就开始了栽培魔芋，食用历史相当悠久，在中国西晋大文学家左思的名著《蜀都赋》中就有“以灰汁煮即成冻，以苦酒淹食，蜀人珍之”这样的记载。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fs39nCsFO6jwDpsl3sEUNDP1YQ-Y\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界山区海拔较高，雨量丰富，湿度较大，为魔芋的生长提供了良好的环境。出产的魔芋含有十六中氨基酸，十种矿物质微量元素和丰富的食物纤维，对防治结肠癌、乳腺癌有特效。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlsQ_mxI2JxYW2LcRQ4Fa_HdPfSH\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">魔芋低热、低脂、低糖，对肥胖症，高血压，糖尿病的人群可以说是一种上等的即饱口福、又治病健体的食品，此外魔芋还可以防治多种肠胃消化系统的常见慢性疾病，被人们誉为“魔力食品”，有“不想胖，吃魔芋；要想瘦，吃魔芋；要想肠胃好，还是吃魔芋“的说法。同时，魔芋也被联合国卫生组织确定为十大保健食品之一。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FglXoFoDrfoh1m7aaMmPNtyx5M8m\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">在张家界，由魔芋制成的魔芋干，选用武陵山区优质魔芋，采用湘西民间秘方，经过十几道工序，配以多种天然调料精制而成。闻之醇香，食之开胃，嚼之筋道爽口，是佐餐，下酒，休闲，馈赠亲友的上佳食品，在首届张家界国际旅游美食文化节上被评为张家界十大名小吃之一。</span>\r\n</p>'),(1461,558,1,'绿壳鸡蛋','<p>\r\n	<span style=\"line-height:1.5;\">张家界土鸡放养于高山密林，这里草木茂盛，空气清新，人迹罕至，泉水清甜，生态优良，养育着各种昆虫，花草，树木，鸡群喝的都是纯天然泉水，吃的是五谷杂粮。</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FoSUD-699Y_7OSV1sK3LJHSByWgH\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">土鸡更像一个天然的营养加工厂，在食用桔梗皮、花椒种、豆梗、玉米后，不仅不易被疫病传染，而且对微量元素、维生素、矿物质有极强的富集性，它大量吸收这些营养物质并转化到蛋中，蛋内的主要营养成分硒、锌、碘比普通鸡蛋高3-6倍，胆固醇含量低20% ，蛋白质含量高12.7%，同时还富含卵磷脂、脑磷脂、神经磷脂、高密度脂蛋白、维生素A、B、C、E以及微量元素，其中维生素E的含量比普通鸡蛋高2-3倍。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fr62mO1jUtr-dqLg6yh4FM4R4enH\" alt=\"\" />\r\n</p>\r\n<p>\r\n	绿壳鸡蛋属于高维生素、高微量元素、高氨基酸、低胆固醇、低脂肪的理想天然保健食品，已被国家绿色食品发展中心批准认定为“绿色食品’，被国家卫生部批准为”保健食品“，被誉为”鸡蛋中的人参“，更有国外友人称之为”东方神蛋“。\r\n</p>'),(1467,509,1,'航空','									<p>\r\n	您可以搭乘飞机进入张家界，这是进入张家界最快捷的一种方式，当然费用也比其他交通方式要贵。张家界荷花机场于1994年建成并投入使用，是目前国内的4D级旅游机场。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsHDWN1LXYfpfvkY0nQbbnYcwJHH\" alt=\"\" />\r\n</p>\r\n<p>\r\n	1997年经国务院批准设立航空口岸，开通张家界至香港、澳门航线，先后开通张家界至韩国首尔、釜山、日本福冈、大阪等国际客运包机。\r\n</p>\r\n									'),(1468,509,2,'铁路','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhbaoCmsUlkoHtbpx0ZhWYsCjmWO\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	火车是既经济又安全的交通工具，目前已与北京、上海、广州、深圳、长沙、南宁、贵阳、宜昌、湛江、遵义等多个大中城市开通了旅客列车。张家界至广州、长沙、南宁、北京等城市有始发车，乘坐较方便。\r\n</p>\r\n									'),(1469,509,3,'公路','<p>\r\n	张家界长途车站主要是发往周围景区以及郊县的长途汽车。多趟公交车都可抵达长途车站。<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpWyIi36PKSKQENVuSDpRsWelboM\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">市区至武陵源的汽车在汽车站每隔5分钟一趟，车程约1小时，票价5元；市区往张家界森林公园，在汽车站每隔15分钟一趟；市区至猛洞河，车程约2个多小时，票价15元左右；市区至茅岩河漂流，每天上午，在中商广场有专车前往。</span>\r\n</p>\r\n<p>\r\n	张家界至常德的高速公路已于05年12月26日正式通车。随着常张高速的开通，不仅使长沙至张家界的行程缩短到仅3小时，而且从张家界最大客源地之一的珠三角出发，驾车8小时就可到达张家界。常张高速公路与京广、京珠等高速相通，并和长常高速、207国道、319国道及1801省道相连，又与石长铁路、焦柳铁路、张家界荷花机场、常德桃源机场相接，为中外游客到张家界旅游观光构架起了最便捷的交通体系。\r\n</p>'),(1470,504,1,'多彩生物','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsTfTPNRwEh5KFpIVBXQr85HFtFl\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	珙桐是我国特有珍稀植物，为我国一级保护植物，张家界公园内的山谷中多有分布。珙桐为落叶大乔木，树干高大挺拔，叶卵形，先端渐尖，基部心形，有锯齿，花形似鸽子展翅，白色的大苞片似鸽子的翅膀，暗红色的头状花序如鸽子的头部，绿黄芭的柱头像鸽子的嘴喙，盛花时犹如满树群鸽栖息，被世界上誉称为“中国鸽子树”，为我国特产的珍贵园林观赏树种，生长于1000万年前的新生代第三世纪初期，因而又有“活化石”之称。&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">珙桐又名中国鸽子花，属珙桐科。这是一种非常稀有珍贵的古老林木，已列为国家重点保护树种。珙桐在张家界主要分布于腰子寨、天子山和琵琵界的山坡或谷地，金鞭溪沿路上也有不少。</span>\r\n</p>'),(1471,503,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">天门山，地处张家界市南郊，是四周绝壁的台地山原地貌。台地海拔龙虾花是张家界武陵源特有的植物，凤仙花科，草本，夏秋开花，龙紫杉又称红豆杉、赤柏松、常绿乔木地栽高达25米，是世界上濒临灭绝的天然珍稀抗癌植物，属国家一级保护植物。是集观赏和药用于一身的珍贵树种，从植株中提取的紫杉醇是世界公认的抗癌药，价格昂贵。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FofNQ39XtER6QuGxTyd7raRiySJC\" alt=\"\" />\r\n</p>\r\n<p>\r\n	因而，红豆杉也具有“生物黄金”之称。中药紫杉为红豆杉科植物东北红豆杉的枝和叶。1994年红豆杉被我国定为一级珍稀濒危保护植物，同时被全世界42个有红豆杉的国家称为“国宝”，联合国也明令禁止采伐，是名符其实的“植物大熊猫”。\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界是天然植物避难所，是“植物标本国”。红豆杉是世界上公认的濒临灭绝的天然珍稀抗癌植物，是第四纪冰川遗留下来的古老树种，在地球上已有250万年的历史。红豆杉中含有的紫杉醇，具有独特的抗癌机制和较高的抗癌活性，能阻止癌细胞的繁殖、抑制肿瘤细胞的迁移，被公认是当今天然药物领域中最重要的抗癌活性物质。</span>\r\n</p>'),(1472,502,1,'多彩生物','<p>\r\n	虾花有各种不同的颜色，花形如龙虾，有的还如对虾。根据花的大小不同，可分为米虾、河虾和龙虾等几种。开花时单苞独放的叫“独虾花”，成双成对的叫“对虾花”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpJDpAXnSvRkJVOmDKreJBHx-tI-\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">花的颜色丰富，有鲜红的，红如珊瑚；有金黄的，黄如琥珀；紫色的紫如葡萄。花在绿叶下面，花柄像一根青丝线，俗名叫“青丝吊龙虾”。虾头上有两根卷须。虾身有一道一道的花纹。花悬吊在叶子下面，争艳斗妍，微风中活蹦乱跳，如同活虾。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">凤仙花科，草本。广布于金鞭溪、沙刀沟等阴湿地。花的旗瓣和翼瓣组合似虾头，唇瓣组合囊状似虾身，花基部延长内弯似虾尾。小花称“米虾”，中花叫“河虾”，大花谓“对虾”，梗长者名“青丝吊龙虾”，花色金黄的为“黄金凤”。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">花金黄兼橙黄色者，为牯牛岭凤仙花和齿萼凤仙花；花色紫红者，为窄萼凤仙花；花紫色者，为睫毛凤仙花。均为蒴果，内藏种子，靠果壳的弹力传播。成熟时，轻轻一碰，“叭”的一声，果裂子飞，趣味盎然。</span>\r\n</p>'),(1473,500,1,'多彩生物','<p>\r\n	在1200米以上，最高海拔1518.6米，一年内温凉、湿润、多雾，没有明显的夏季。独特的自然条件，使天门山植物种类丰富，起源古老，地理成分复杂，特有种类多。天门山杜鹃、天门山马铃苣苔、湖南马蓝都是极具天门山特色的美丽植物。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FikJxrqusQANr7ONA9n7j2xtOZPI\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">马铃苣苔(学名:Oreocharis amabilis)为苦苣苔科马铃苣苔属下的一个种。</span><span style=\"line-height:1.5;\">多年生无茎草本。叶全部基生，具柄;叶片长圆形，长3-12厘米，宽1-3.5厘米，顶瑞钝，基部圆形，边缘具不规则圆齿，两面均被贴伏短柔毛，侧脉每边6-7条，下面明显，密被淡褐色绢状绵毛;叶柄长3-7厘米，与花序梗，花梗、苞片、小苞片均被锈色绢状绵毛。聚伞花序2次分枝，2一7条，每花序具3-8花;花序梗长7-18厘米，近无毛;苞片和小苞片均披针形，长7-10毫米，宽2-4毫米，顶端渐尖，近全缘。花萼5裂至近基部，裂片相等，线形，长达5.5毫米，宽1-2毫米，渐尖，外面被锈色绢状绵毛。花冠筒细筒状，长2-2.2厘米，裂片直立，披针状长圆形，长6-7毫米，顶端锐尖，无毛。雄蕊着生于花冠近基部，稍长于花冠筒。花柱稍扁，柱头2。</span>\r\n</p>'),(1475,499,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">马蓝，多年生草本，高达1米。根茎粗壮。茎基部稍木质化，略带方形，节膨大。单叶对生，叶片卯状椭圆形。2008年6月22日，黄宏全在天门山上偶然发现了这位神秘的“山中隐士”。跟踪调查发现，原来这种植物8到10年才开一次花，花期一过，植物从根至花全部死掉，仅通过留下的种子重新再孕育数年，很难被人发现。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjtaZxs5zTJKQsuhSf0_u3O36xti\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	花无梗，对生，组成腋生或顶生的穗状花序，长10-30厘米;苞片对生，叶状，长1.5-2.5厘米，具短柄，最下部的比花萼长。最下部小苞片比花萼稍短;花萼不等5深裂，裂片线形，后裂片较大;花冠堇、玫瑰红或白色，花冠圆筒形，顶端内弯，喉部扩大呈窄钟形，稍弯曲，不扭弯，花冠筒短圆柱形，喉窄钟形，长为花冠筒的两倍，支撑花柱的毛排列成2列，冠檐5裂，裂片等大，倒心形，旋转状排列;雄蕊2长2短，内藏;子房上半部被毛，每室有胚珠2粒，花柱无毛。<span style=\"line-height:1.5;\">蒴果棒状，上端稍大，长2-2.2厘米，稍具4棱。种子每室2粒，卵圆形，小基区以外散生稍明显的毛。单种属。花期11月。</span> \r\n</p>'),(1476,498,1,'多彩生物','<p>\r\n	红花龙胆，为龙胆科，多年生草本，茎具棱有分支，叶对生革质，花草生枝顶或腋生，淡紫红色，带有深紫色的条纹，全草药用，清热利胆，消炎止咳，花期10月。&nbsp;\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Ftr6XZPMoX2eFrs_Iiu_68QT68Zm\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">花单生茎顶，无花梗;花萼膜质，有时微带紫色;花冠淡红色，上部有紫色纵纹，筒状，上部稍开展;雄蕊着生于冠筒下部，花丝丝状，花药椭圆形;子房椭圆形。</span><span style=\"line-height:1.5;\">蒴果内藏或仅先端外露，淡褐色，长椭圆形，两端渐狭，果皮薄，种子淡褐色，近圆形，具翅。</span>\r\n</p>'),(1477,496,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">杜仲：全身都是宝。杜仲，俗称“丝棉树”，它是多年生阔叶乔木，干长而居，有高达数丈的，其叶是倒卵形。树皮可作药，花实苦涩，亦可入药。慈利杜仲的皮、果、叶均含有丰富的马来树胶，可提炼热塑性高、耐水性强、绝缘性好、黏着性足的优质硬胶，是电器、化工、电讯器材等工业的重要原料。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn6jXOkEaktcTkwzRSxujWmraT0R\" alt=\"\" />\r\n</p>\r\n<p>\r\n	它的皮作药用，能“益精气，坚筋骨”，对医治腰膝酸痛具有奇效。杜仲性味甘、微辛、性温，补肝肾，强筋骨，现代中医药学的研究，也证明了杜仲有强身壮骨作用。美国哈佛大学胡秀英教授把人参、杜仲并列为补品，并认为杜仲是目前世上最高质量的天然降压药物。\r\n</p>'),(1478,493,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">身高不足5厘米，像草，但的确是树，它生长在张家界桑植县的山地密林中、树干上，被称为“世界上最小的树木”，是张家界特有的物种。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpWQB9LOebDh3C_14gwRyYqr0Eis\" alt=\"\" />\r\n</p>\r\n<p>\r\n	花冠白色带淡紫色条纹或淡紫色，长3.5-4.8厘米，无毛；筒细漏斗状，长2.5-3.5厘米，口部直径1.2-1.5厘米；上唇长约4毫米，2浅裂，下唇长10毫米，3裂。雄蕊无毛，花丝着生于距花冠基部13-15毫米处，狭线形，长约12毫米，花药直径约1.2毫米，药隔背面突起长约0.8毫米；退化雄蕊3，无毛，中央的长约1毫米，侧生的狭线形，长约5毫米，弧状弯曲。花盘杯状，高2.5-4毫米，有尖齿。<span style=\"line-height:1.5;\">雌蕊长2-3.4厘米，无毛。蒴果线形，长5.5-9厘米，宽2-3毫米，无毛。种子纺锤形，长0.6-1毫米，毛长1.2-1.5毫米。花期7-10月。&nbsp;</span>\r\n</p>'),(1479,492,1,'多彩生物','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FubYb7cz2vGRTVAyzRsadi4FexzO\" alt=\"\" />\r\n</p>\r\n<p>\r\n	大鲵，是世界上现存最大的也是最珍贵的两栖类动物。中国大鲵生物科技馆坐落于张家界核心景区武陵源宝峰路，是全球首家融知识性、互动性、科研型、艺术性为一体的大鲵科技馆，采用先进的声、光、电互动多媒体展示手法，以世界罕见的张家界地貌的地质演变为背景，集科普、旅游、艺术于一体，揭开大鲵神秘的面纱，了解大鲵全面价值，2010年8月开馆后被湖南省科技厅授予“湖南省科普基地”。\r\n</p>\r\n									'),(1480,492,2,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">中国大鲵生物科技馆坐落于张家界核心景区武陵源区宝峰路。区间乘坐2路公交车抵达（武陵源标志门 - 御笔峰酒店 - 凯天国际酒店 - 武陵源汽车站 - 电信局 - 大华酒店 - 区人大 - 菜市场 - 宝峰大桥- 紫霞观 - 沙坪旅馆村- 大鲵科技馆 - 盛美达度假酒店 - 宝峰湖景区）公交全线行车8分钟</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1481,490,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">芙蓉龙化石产于桑植县芙蓉桥白族乡芙蓉桥村三叠世地层中，距今 240-230百万年。1970年，湖南省地质局405队在湘西北钾盐普查工作中，在桑植县芙蓉桥乡发现大量的恐龙化石。由于恐龙化石发掘地在芙蓉桥乡，因此命名为“芙蓉龙”。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgmruB7jLkmvSkz7C9fz-0IvPT3_\" alt=\"\" />\r\n</p>\r\n<p>\r\n	1981年湖南省地质博物馆组织专家对芙蓉龙化石进行大规模发掘，由中科院古脊椎动物与古人类研究所专家合作装架组成三具无牙芙蓉龙化石骨架个体，分别陈列在中科院古动物馆、北京自然博物馆和湖南省地质博物馆。经专家研究鉴定，芙蓉龙属脊椎动物槽齿目原始初龙类，种名“无牙芙蓉龙”，为恐龙原始始祖，被誉为”世界恐龙之父”。\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">桑植芙蓉化石保护区是全球目前唯一的无牙恐龙产地，在古生物演化上具有重大对比意义。该化石及其所赋存的地层岩性组合，为我国乃至全球地史和古生物研究提供了重要基础资料，具有极其重要的科学意义。&nbsp;</span>\r\n</p>'),(1482,490,2,'小贴士','<p>\r\n	<span style=\"line-height:1.5;\">此景点为免费景点。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1483,490,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市桑植县澧源镇文昌街；建议自驾。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1484,489,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">娃娃鱼又称大鲵，是世界上现存最大的也是最珍贵的两栖动物，被称为“游动的活化石”。它的叫声很像幼儿哭声，因此又称为“娃娃鱼”，是国家二类保护水生野生动物。经过了3亿5千万年的自然选择和进化演变，大鲵生存在张家界人迹罕至的山林溪流中。</span>\r\n</p>\r\n<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fpdg1rR2lRHknQquDH6qraoXXtMb\" alt=\"\" /><br />\r\n<p>\r\n	<span style=\"line-height:1.5;\">它的存在，是张家界拥有生物多样性及原生态地理环境的最有力证明。娃娃鱼生性喜静、怕光、畏声，性情较活泼，昼伏夜出，生活在水源洁净的地下溶洞中。娃娃鱼体重可达200公斤以上，寿命长达200岁。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界为娃娃鱼提供了得天独厚的生态环境，现如今还成立了张家界大鲵自然保护区，也是我国大鲵的集中分布区之一。</span>\r\n</p>'),(1485,489,2,'小贴士','<p>\r\n	<span style=\"line-height:1.5;\">在大鲵科技馆和宝峰湖景区许愿池可以欣赏到娃娃鱼。娃娃鱼生性不爱动如果你看见它待在哪里千万不要拿东西扔它。</span>\r\n</p>'),(1486,487,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">黑熊分布于八大公山等深山老林。又称为狗熊、熊瞎子或狗驼子，体毛坚硬而粗，富光泽，体高1.8米左右，体重达200公斤。性喜深山密林之中，居洞穴。孤独、倔强、胆大，敢与虎斗。外看笨拙，但行动灵活。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqRD0Uny3GOZAX6-BMQTNdwGsF7o\" alt=\"\" />\r\n</p>\r\n<p>\r\n	树上的果子，地里的笋子和蜂蛹，都是它们的美食。黑熊爱偷食蜂蜜，山民安放在岩壁凹处的蜂桶，成了它们时常光顾的好去处，嘴馋得任凭蜂群叮咬也贪吃不止，有时连蜂桶都给抱走了。\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">每到秋天，果实成熟时节，黑熊就爬到高高的树上去摘食野果子。吃一阵，扑通扑通使劲往地上摔，又乐滋滋地爬到树上接着吃，往返如是而乐此不疲。若是碰巧有人在树下歇息，猛然撞见这么一个黑乎乎的家伙掉下来，十有八九会吓出一身冷汗。一般情况下，黑熊不会主动袭击人，能够与人相安无事，和睦共处。</span>\r\n</p>'),(1487,487,2,'小贴士','<p>\r\n	<span style=\"line-height:1.5;\">如果你有幸遇上它最好别马山逃跑，也别上树它比你快的多，也别装死如果它饿了，它会···。最好慢慢向后倒退别发出声音和做怪异的动作惊到它。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1489,486,1,'多彩生物','<p>\r\n	在张家界有国家一级保护动物金雕，金雕属于鹰科，大型猛禽，以其突出的外观和敏捷有力的飞行而著名，一般生活于山谷或丘陵地区。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkEe_3PiEQiz7LOX-NKIodTTlYLc\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">金雕并非金色的雕，它全身的羽毛呈栗褐色，跟金色相距甚远。金雕体长近1米，体重6千克左右，是雕中最大的一种，它们的腿除脚趾外全被羽毛覆盖，看上去仪表堂堂。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">金雕飞行快捷，它有机智灵活的捕猎方式，一旦发现猎物，它们便直冲而下，抓住猎物后便扇动双翅，疾若闪电般飞向天空。</span><span style=\"line-height:1.5;\">金雕的巢都建在高处，如高树木的顶部、悬崖峭壁背风的凸岩上，因为这些地方人和其他动物很难接近。一对金雕占据的领域非常大，有近百平方千米，对接近它们巢的任何动物，它们都会以利爪相向。</span>\r\n</p>'),(1490,485,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">猕猴是猴科的一属，也称为猢狲。在张家界，猕猴特别多。按其毛色可分为大青猴、黄毛猴、金丝猴等，按照它们的生活习惯可概括为两种，即独猴和群猴。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuW3nfPeodgkIXDfohVNhwze6aFP\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">在张家界景区里面生活着2000多只野生的猕猴群，一般都生活在上山很少下山活动，在下雨的时候偶尔能看得见。</span><span style=\"line-height:1.5;\">在金鞭溪一带聚集着一群猕猴，当它们碰到陌生游人时，或瞪眼睛，做鬼脸，或抢食物，丢石头，甚至还调皮地做着各种滑稽动作，有时猴子还会向来往的游人招手致意。</span>\r\n</p>'),(1491,484,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">云豹属国家一级保护动物。张家界的云豹一般是栖息于八大公山等高山常绿林内，是大型猫科动物中体形最小的一种，它们只有一米左右长，体重在10到25千克之间。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Ft7fPsbRtRXkl5iKl9lnBCKjuAHN\" alt=\"\" />\r\n</p>\r\n<p>\r\n	云豹有较短而粗的四肢，几乎与身体一样长而且很粗的尾巴。头部略圆，突出的口鼻，爪子非常大。体色金黄色，并覆盖有大块的深色云状斑纹，因此称作云豹。<span style=\"line-height:1.5;\">它们的牙齿也与众不同，犬齿的长度比例在猫科动物中排名第一，因此它有“小剑齿虎”之称。</span>\r\n</p>'),(1492,483,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">在张家界武陵源景区，有一种鸡叫“背水鸡”。它的独特之处就在于善于背水，每次能背2千克多重的水。这种鸡的脖颈下面有一个水囊，平时像一只大扁豆，盛上水以后，便像一个鼓胀的小皮球。由于“包袱”沉重，走起路来一摇一摆的，显得有些笨重，但模样十分好看，非常惹人喜爱。这种鸡由于具有背水的本领，所以人们都叫它“森林中的骆驼”。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiP2dI53kpJ8WK2HNl4cvKd78dUh\" alt=\"\" />\r\n</p>\r\n<p>\r\n	背水鸡除了背水之外，还能飞、能跑、能唱歌，它喜欢在高山上和老林中栖息，每次下山背水，足够自己使用3天的。背水鸡毛呈金红色，鸡冠上凤头，鸡尾长，行动起来，流光溢彩，好看极了，具有鸡类“美女”之称。\r\n</p>'),(1493,482,1,'多彩生物','<p>\r\n	<span style=\"line-height:1.5;\">相思鸟，又叫红嘴玉、红嘴绿趔音。体长约14厘米。相思鸟生活在平原海拔2000米的山地，长栖居于常绿阔叶林、常绿和落叶混交林的灌丛或竹林中，很少在林缘活动。它们不仅活动于树丛下层，也常到中层或树冠觅食，偶尔到地面寻找食物。性喜结群或与其他鸟混群，雌雄形影不离，动作活波捷巧，不甚畏人。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtIg1vDtdtDT9fPVEYui--7D9D68\" alt=\"\" />\r\n</p>\r\n<p>\r\n	相传相思鸟成双入对，如有一只受伤，另一只会悲痛欲绝；如有一只被捕，另一只情愿前来送死，双双死在一起，以表示“天地合乃敢与君绝”之忠贞。所以这种鸟为中国和世界上许多国家的青年男女喂养，被视为忠贞爱情的相思鸟，以此象征夫妻和睦恩爱、白头到老。\r\n</p>'),(1494,482,2,'小贴士','<p>\r\n	<span style=\"line-height:1.5;\">情侣、夫妻过来旅游遇上它们不妨以自己的名字认养一只来象征着你们的爱情。</span>\r\n</p>'),(1501,515,1,'简介','<p>\r\n	<span style=\"line-height:1.5;\">万福温泉位于常张高速公路慈利东互通处，占地20 公顷，毗邻武陵源，融中华福文化与温泉沐浴养生文化于一体，是按国家4A 级景区标准精心构造的休闲、度假旅游风景区。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">万福温泉属天然自涌温泉，泉水清澈碧透，常年恒温35℃，富含多种有益人体的微量元素和矿物质，勤劳的土家族及当地居民在姑娘出嫁前一定要沐浴此温泉洁身，以示祥和、健康，俗有“泡温泉赐万福”之说。具有神奇魅力的48 个动、静露天温泉池和山顶温泉冲浪、温泉漂流等温泉娱乐项目，分别掩映在天然绿色植被中。</span>\r\n</p>'),(1502,515,2,'特色温泉池','<p>\r\n	<span style=\"line-height:1.5;\">石板泉：又叫地热带，石板泉采用高温温泉水在富含对人体有益微量元素的石板下潺潺流过。人躺在石板上，可改善血液循环，增强免疫力，对风湿病、关节炎、胃病有独到的疗效。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">香樟泉：水温39~41℃，此泉为静泡区主题泉，池边绿树成荫, 泉水从天然石缝潺潺流出，香樟花木气味芳香。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">八色汤：清热解毒汤，水温39~42℃，品名：甘草，性能：甘温，功效：清热、舒风、固本解表、抗病毒；养血安神汤，水温38~40℃，品名：当归，性能：干湿，功效：生血补心、扶虚益损等；强心益智汤，水温40~42℃，品名: 人参，功效：大补元气、生津安神、补脾益肺；美容健肤汤，水温39~41℃。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">滴翠泉：水温41.5~43.5℃，此为高温泉，池中烟雾蒸汽在上方树丛与树叶相遇后，形成水珠，滴于池中, 故名滴翠泉。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">醉荫泉：水温39~41℃，此泉为林中池，就象一片树叶飘落在林中，身在此泉可充分领略回归自然的感觉。</span>\r\n</p>\r\n<br />'),(1503,515,3,'交通线路','<p>\r\n	<span style=\"line-height:1.5;\">距 离：距武陵源核心景区约80 公里，距市区约86 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：武陵源汽车站乘坐至慈利班车，约需1.5 个小时到慈利东站。然后乘2 路公交车至万福温泉</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：武陵源区沿省道S306、常张高速公路至万福温泉</span>\r\n</p>'),(1504,515,4,'基础信息','<p>\r\n	<span style=\"line-height:1.5;\">地 址：张家界市慈利县常张高速慈利东互通处</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票价格：118 元</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">咨询电话：0744-3333333</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1505,514,1,'简介','<p>\r\n	<span style=\"line-height:1.5;\">江垭水库大坝的建立造就了山水交融的风光，既有浩淼的龙潭湾湖面，险峻的九渊关等峡谷，又有幽深如脱离尘世以外的五里溪、贝子溪等溪流峡谷，具有“山得水而活，水得山而幽；山得林则妍，林因山而茂；景随俗而丽，俗由景而魅”的特点。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">乘船游库区，“群山叠翠，高峡出平湖”的壮观景象展现在眼前。库区几乎没有任何人工点缀，看不到亭台楼阁，看不到绿瓦红墙，只有碧绿的湖水，洁净的泉水飞瀑，峭拔的高山峡谷和一望无际的竹木。湖面上停着几艘渔船，完全没有城市的喧嚣嘈杂。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">江垭湖沿岸的区域分布着九溪古城、江垭古镇、龙王洞、索水及溇水漂流等景区，它们既有原始质朴的乡村吊脚楼，又有历史悠久的古城古街，更有浓郁的土家族、苗族风情，集人文于一体，熔山水在一炉。</span>\r\n</p>'),(1506,514,2,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">距 离：江垭湖距离张家界市区约90 公里，距离武陵源核心景区约80 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：张家界市中心汽车站乘坐至江垭温泉专线车。武陵源区可在306 省道（百丈峡，黄龙洞等处）等候江垭温泉专线车</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：自张家界市区出发，沿途经武陵源风景区、黄龙洞、三官寺、张家界大峡谷、江垭古镇、江垭温泉度假村至江垭水库。（行车时间约100 分钟）</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1507,514,3,'温馨提示','<p>\r\n	<span style=\"line-height:1.5;\">江垭湖的平湖游和垂钓项目需要在江垭温泉度假村提前咨询和预订。</span>\r\n</p>'),(1508,511,1,'垂钓特色','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FmZwu8jMEA4aahPL6BQFqWqtJQIJ\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">好山出好水，好水有好鱼。响水洞之水来自巍峨的麻空山溶洞，一股清泉从绿树葱茏的山洞倾泻而出，长流不止，冬暖夏凉，一年四季都可垂钓。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsSz7wyDMHepxYOwXvs4yeSeh-Uz\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">响水洞垂钓园背后是著名的人文景观玉皇洞石窟，各种造型的石雕神像、石狮栩栩如生，垂钓之余可以攀登游览。响水洞垂钓园面对枫香岗田园风光，放眼望去，阡陌纵横，房舍棋布，炊烟袅袅，鸡犬相闻，桃李满枝，垂柳依依。垂钓园水面在微风的轻轻吹拂下，荡起了层层涟漪，加上在水边垂钓的人们，组成了一幅江南水乡的画卷。</span>\r\n</p>'),(1509,511,2,'垂钓乐趣','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fh2ZYBcWJqgcJtlaOiAskMNRLAG5\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">这里溶洞泉水生长的鱼，肉质特别鲜美。而且品种繁多，有草鱼、回鱼、鲶鱼、青鱼等等，分区垂钓，满足不同垂钓者的需求。如果你没有带钓具也没有关系，这里提供各种手竿、海竿供你选择。钓到的鱼你可以叫老板现场加工成美味佳肴，也可以带回家享用。</span><span style=\"line-height:1.5;\">&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">响水洞溪水蜿蜒回旋，形成一个自然的盆地，盆地内宛如自然空调，冬暖夏凉，冬不要烤火炉，夏不要蒲扇。 在这里男人垂钓，女人打牌，小孩抓鱼，一根根钓丝钓起一串串欢乐和喜悦；休闲小房子里，不时传来哈哈大笑，激起湖面波光粼粼。临近中午，有人大声吆喝“开饭喽”，垂钓休闲的人们纷纷走向饭桌，享受满桌味道纯正的农家菜，其乐融融。</span>\r\n</p>'),(1510,511,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">响水洞钓鱼园座落在张家界永定区枫香岗乡响水洞村，距市内6公里，距玉皇洞景区2公里，建议自驾车</span>\r\n</p>'),(1511,511,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">为了方便广大钓鱼爱好者，园内有中，高当钓鱼杆出售。自行保管好自己的鱼具及其他随身物品，以免丢失或损坏。四、在钓鱼的过程中，进行抛钩、起钩杆等活动时注意避让过往游人，以免鱼钩碰伤、划伤游人。</span>\r\n</p>'),(1518,510,1,'垂钓特色','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fl4dkzguQbFWWwTQ1e5t63aEcAXp\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	江垭因水驰名，江垭水库更令垂钓者心驰神往，被誉为“神仙的钓场、垂钓者的乐园”，每到垂钓季节，各路垂钓高手云集江垭“华山论剑”，一展英雄本色。每年都有垂钓高手在这里创造奇迹。\r\n</p>\r\n<p>\r\n	<span><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlSN0n6-tSjtboluKOirKyBhmPAP\" alt=\"\" /></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">人们常说，春来江水绿如蓝，江垭水库的江水却是一年四季清澈见底,能见度达八米。江垭水库的水质经过检测项项指标达到国家饮用水一类标准，比市场上的矿泉水、纯净水好得多！水库两岸座座青山，翠绿环绕，就象一座座垂直悬挂的绿毯。环库公路象一条长玉带把周围的农户全部串联起来，白墙红瓦倒映在水中，又是一道亮丽的风景线。</span>\r\n</p>'),(1519,508,1,'采摘乐趣','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fr_I9sJhy94YCNs6i5-cXeK5DEGq\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	张家界的蓝莓园，占地面积近800亩，是永定区近年来重点培育的绿色产业，也是目前湖南省内唯一一个可以去观光、亲手采摘蓝莓的地方。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjoSx3TzauLJAtWV0BHU_p1OGIkO\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">蓝莓成熟时间从6月初开始，成熟后蓝莓采摘园开始接纳游客，此轮采摘将一直持续到8月下旬。在这里可以尽情享受着远离喧嚣、藏身深山的生态农业观光时尚旅游的无限魅力。</span>\r\n</p>'),(1520,508,2,'蓝莓价值','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fpajt120Jbe58SmlrvNNDWI7FEc2\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">趁着假期或周末父母带着小孩，可以享受独具特色的蓝莓采摘乐趣,情侣们可以在这里亲近自然，品尝鲜嫩清爽的蓝莓，得到的不仅仅是味蕾的享受,更是回归田园的宁静与愉悦；带上你的爱人、家人、朋友都来采摘蓝莓吧，在这里收获快乐与健康。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtDd_EidF0pwgYWaTmGvWi12CSEa\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">蓝莓， 蓝莓被誉为“世界水果皇后”、“21世纪功能性保健浆果”，并被国际粮农组织列为人类五大健康食品之一市场价格每公斤200元——400元左右，经济、营养价值极高。</span>\r\n</p>'),(1521,508,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">张家界蓝莓采摘园（永定区尹家溪立功桥），市内乘桑植线路车即可到达</span>\r\n</p>'),(1522,508,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">摘蓝莓应注意防晒,由于蓝莓树不及人高，因此在采摘过程中一定要做好防晒措施，同时来采摘的时间尽量选在上午和傍晚，或者阴天，避开日光最毒的时候。</span>\r\n</p>'),(1523,506,1,'天门脚下','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjQnvQnq0kd2bBDIRI2nmORbduWv\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	天门山高尔夫球场位于张家界市永定区，球场依山傍水，自然环境十分优美，并与天门洞遥相呼应，是观赏天门洞的最佳位置，设计遵循自然、环保和可持续性的原则，很好的与自然地形想结合，同时融入当地的土家文化元素和天门洞独特的景观元素。\r\n</p>\r\n									'),(1524,506,2,'美景风光','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpyeF4QtF5lJaMvugY4CuMFPLd2t\" alt=\"\" />\r\n</p>\r\n<p>\r\n	站在天门山高尔夫球会所远眺，高尔夫球场与天门山融为一体，球场成为天门山的一部分，天门山成为球场的背景，在会所餐厅、出发广场等区域，都能欣赏到天门山美景，球场的每个球道都是不同角度的观赏点。球场18洞均以张家界各著名景点命名，每一个球洞都是一道风景，每一道风景都是一段人生。在这里，你不需要战胜别人，至需要战胜自己。“胜人者有力，自胜者强。”\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fnm0jWh9Cp_i8EqaYXpWTttDmTv4\" alt=\"\" />\r\n</p>\r\n									\r\n									'),(1525,506,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">地址： 张家界市永定区南庄坪办事处五子坡；</span><span style=\"line-height:1.5;\">建议的士或自驾车。</span>\r\n</p>'),(1526,506,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">开放时间：06:00-18:00</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">球童小费需另付，100元起。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">如果出现单人使用球车，需补球车差价110元/人。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">假日价为双休日及国家法定假日价格。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>相关信息</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：A.928元（18洞/人，平日） B.1288元（18洞/人，假日）</span>\r\n</p>'),(1531,505,1,'简介','<p>\r\n	<span style=\"line-height:1.5;\">山一座连一座，绵延数百里。水碧天一色，蜿蜒数百里。山与水在这里相亲相爱，于是拥有了一个诗意的名字——人潮溪。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">人潮溪乡位于桑植县境内，距离县城71 公里，与白石乡相连，这里钟灵毓秀，人杰地灵，山水奇绝，资源丰富，民风纯朴，是一块丰饶的宝地。境内山峦起伏，溪河纵横，山水交相辉映，资源十分丰富。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">这里有“百里画廊”之称的溇水峡谷平湖游览区。沿线山清水秀，风光旖旎，景色得天独厚，随处可见一片又一片连绵起伏的茂林修竹，一条又一条奔流激荡的清溪绿水。景区面积广、种类多、品味高、特色鲜明，集山、河、湖、岛、瀑之大成。棓子溪、五里溪峡谷幽深，以兼具幽、险、秀、野的原始风貌称胜，夹花湾、金藏河水花欢腾，是度假、休闲、垂钓的绝</span><span style=\"line-height:1.5;\">佳去处。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">这里有目前亚热带保存完好的原始森林，野生动植物种类繁多，林中有黄麂、猕猴、野猪、刺猪、拔毛鼠、五步蛇、山竹笋、粽叶、猫儿蕨、山胡椒、桂花树、黄杨木等珍稀动植物品种。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">这里是民歌之乡、山歌之源。土家族歌手向佐绒多次用山歌接待中央首长，年近90 高龄的向桂枝将山歌唱到了北京。其中的《冷水泡茶慢慢浓》、《太阳出来照白岩》、《木匠难修八角楼》、《去哒去哒又转来》等名曲都发源于人潮溪乡，文化底蕴十分浓</span><span style=\"line-height:1.5;\">厚。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">这里有长32 公里的库面资源，库区面积大，水质好，适合发展稀有名贵渔业。库区内有娃娃鱼、团鱼、锯把鱼、麻鲢、银鱼、岩骨鱼等近20 种，种类丰富，产量高。</span>\r\n</p>'),(1532,505,2,'交通线路','<p>\r\n	<span style=\"line-height:1.5;\">距 离：距武陵源核心景区约100 公里，距市区130 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：桑植汽车站乘坐至人潮溪乡班车</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自驾：可沿武陵源、赵家岗、竹叶坪、走马坪、人潮溪路线抵达，行程约为100 公里</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1533,505,3,'基础信息','<p>\r\n	<span style=\"line-height:1.5;\">地 址：张家界市桑植县人潮溪乡</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">电 话：0744-6593087（人潮溪乡政府）</span>\r\n</p>'),(1534,501,1,'简介','<p>\r\n	<span style=\"line-height:1.5;\">长茅山位于张家界市永定区尹家溪镇茅溪村，与沙堤乡、教字垭镇相邻，过去的长茅山，是一个穷山野岭、难以为生的落后山村。山上海拔虽然只有800米，相对高度却达到了500 多米，山高路陡、偏僻荒野。自2009 年开始，全村种植桃树3000 多亩，并配置了梨、李、柿、枣、瓜等20 多个其他品种，形成了村史介绍、农家乐园、生态观光、果园采摘、休闲娱乐、徒步健身等内容的旅游精品。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">如今的长茅山，两座青山夹着一条潺潺溪流，千亩桃花掩映着美丽的村庄。在道路两旁，栽种着不同花色的桃子树苗，每年“三月桃花始盛开”和“最是橙黄橘绿时”，姹紫嫣红的桃花与挂满枝头的桃子，都会引领着人们，向长茅山前进。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1535,501,2,'交通线路','<p>\r\n	<span style=\"line-height:1.5;\">距 离：距市区约26 公里，距武陵源核心景区约50 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：在张家界市中心汽车站坐市区至茅溪班车，到终点站下车，徒步1 小时或者搭摩托车15 元直接到达山顶万亩桃花林</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：可由市区驾车沿省道S228 及乡村公路至长茅山</span>\r\n</p>'),(1536,501,3,'基础信息','<p>\r\n	<span style=\"line-height:1.5;\">地 址：张家界市永定区尹家溪镇茅溪村（张桑公路前行12 公里）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票价格：免费</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">咨询电话：13517440259（黄书记）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">桃花花期：3 月15 日-4 月10 日</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">桃园果熟：6 月-9 月</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1541,495,1,'简介','<p>\r\n	<span style=\"line-height:1.5;\">丁家界，位于永定区三岔乡，知道这个地方的人并不多, 尘世中所有的喧嚣离这很远。界上的人家，过着云上的生活。层层梯田直上云端，高山流水，云海深处就是高耸入云的天门山。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">位于山顶的丁家界村，村子很有古老的味道，都是木制结构的房子，地势不平的地方还有吊脚楼。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2015 年2 月15 日至19 日，中央电视台春节联欢大型节目摄制组一行12 人，深入到张家界市永定区三岔乡丁家界村，拍摄以“北漂青年摄影师覃源回家过年”为主题的“覃氏家族百人团年饭”专题。其情浓浓、其境优美，将丁家界世外桃源般的生活向世人展现，为人们揭开了这里神秘的面纱。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">丁家界村所在三岔乡物产资源丰富，“龙虾花茶”、“青岩茗翠”、“三岔豆腐”等品牌享誉国内外。这里四季分明，空气新鲜无污染，人们热情好客，民风淳朴，是体验农家、休闲度假的好去处。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1542,495,2,'交通线路','<p>\r\n	<span style=\"line-height:1.5;\">距 离：距市区约45 公里，距武陵源核心景区约60 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：在张家界市中心汽车站乘坐至三岔乡班车</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：可由市区驾车沿永定大道、省道S306、省道S228 以及乡村公路至丁家界</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地 址：张家界市永定区三岔乡丁家界村</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票价格：免费</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">咨询电话：0744-8575030（三岔乡政府）</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1543,494,1,'简介','<p>\r\n	<span style=\"line-height:1.5;\">红河谷，原名山羊溪，位于永定区大坪镇，因溪中石头块大、且多呈红色，当地人又称之为红岩溪。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">红河谷坐落在堡子界林场的脚下，像一个养在深闺的土家妹子，自在地行走在群山之间。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">红，是红河谷的个性。那大块大块的红石头，染红了整个溪谷，那么坦荡，那么奔放，毫无保留。来到这里看到她的人，总忘不了那醉人的红色，那热烈的红色。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">绿，是红河谷的气质。整个红河谷隐藏在群山之间，植被茂盛，葱翠欲滴。置身谷间，不闻谷外之事。而当你走出溪谷，想一睹其容颜姿态，却已是不能。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">幽，是红河谷的境界。溪谷两岸浓荫蔽日，鸟叫蝉鸣不绝于耳，夏日寻幽避暑，红河谷将是首选。那清澈透凉的溪水，能荡涤远来客人身心的疲惫。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">红河谷，那个深闺里的红妹子，等你来揭开她的神秘面纱。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1544,494,2,'交通路线','<p class=\"MsoNormal\" align=\"left\">\r\n	距\r\n离：距市区约<span>26 </span>公里，距武陵源核心景区约<span>60 </span>公里\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">公共交通：张家界市中心汽车站乘坐大坪客运线路车到天门溪村龙门洞路口下</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">自驾、骑行：走省道</span><span style=\"line-height:1.5;\">S228 </span><span style=\"line-height:1.5;\">线，到达天门溪村龙门洞路口，全程路况好</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\"><strong>遇险求助电话：</strong></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">大坪镇政府：</span><span style=\"line-height:1.5;\">0744-8570618</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">大坪派出所：</span><span style=\"line-height:1.5;\">0744-8570148</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">田儿湾救援通道联系人：\r\n胡家玉</span><span style=\"line-height:1.5;\"> 13617445979</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">田家湾救援通道联系人：\r\n周余文</span><span style=\"line-height:1.5;\"> 15874452306</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">山羊溪点救援通道联系人：胡桂民</span><span style=\"line-height:1.5;\"> 13574466133</span>\r\n</p>'),(1545,494,3,'安全提示','<p>\r\n	<span style=\"line-height:1.5;\">该线路为具有一定危险性的户外线路，需有专业资质的户外俱乐部提供专人向导和专业装备，游客切莫孤身前往，注意自身安全。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1546,494,4,'特色景点','<p>\r\n	<span style=\"line-height:1.5;\">红石区：大块大块的溪底红石，是红河谷的标志性景观。露出水面的石头呈褚红色，浸水过后便成深红，隔远看，连溪水都成一片红色了。红河谷从龙门洞至山羊水库近8 公里，红石随处可见。溪谷中、下游段是观红石的绝佳地段。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">山羊溪水库：位于大坪镇柏树村曹湾，兴建于上世纪80 年代，主要经营水力发电。户外爱好者游玩红河谷，多以水库大坝为终点。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">红石潭：每遇落差，便会有深潭。红河谷内深潭遍布，潭水清澈凉爽，整块红石铺满潭底，让不少户外爱好者忍不住下水一游。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">后天门：在大坪镇视野开阔地，可欣赏到天门洞后山景色，看到后天门。</span>\r\n</p>'),(1559,491,1,'简介','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fg-TXCCJdRwpi_1oeXGL4-3Qp3kx\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">这是一片张家界地貌和喀斯特地貌富集区，森林草地覆盖率超过80%。其间，刀劈斧削的石柱刺破云霄，壁立千仞的石墙绵延数里，石墙有门户，通向深深不知几许的溶洞迷宫……<br />\r\n</span><span style=\"line-height:1.5;\">参照张家界地貌3亿8千万年的衍变史，槟榔谷堪称少年时代的张家界，是深藏亿万年沧海桑田变迁之地的地貌明珠。某种程度上，到了槟榔谷，就走进了张家界最纯净、最本初的历史。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fi0H6I4qFbxg2PZlfi7gZc60STXl\" alt=\"\" /> \r\n</p>\r\n									'),(1560,491,2,'户外攻略','<p>\r\n	<span style=\"line-height:1.5;\">【资源种类】张家界地貌、喀斯特天坑、地缝、溶洞、自生桥</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【户外项目】徒步山地穿越、攀岩、溯溪、探洞、水上抱石、定向越野、山地机动车、单车越野、骑行、野营等</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【海拔范围】250m至850m</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路难度】3星</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路强度】3星</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路风景】5星</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【装备要求】速干衣裤、冲锋衣、徒步鞋、登山杖、水袋、手套、头巾、野炊用具、露营装备、溶洞穿越照明灯具、头盔、药品等。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【交通指引】罗塔坪乡距市区约56km。自驾：约1小时30分钟，为市区公路+张花高速公路（茅岩河收费站下）+县道；骑行：市区公路+县道X019，全程59.8km，为沥青路面，道路平整；公交：张家界汽车西站（市民族中学对面）有市区至罗塔坪乡公交车，后包车或徒步进入线路首发地星之谷。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<h4>\r\n	<span style=\"line-height:1.5;\"><strong>&nbsp;行程</strong></span>\r\n</h4>\r\n<p>\r\n	<strong>DAY1</strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自驾或乘车至罗塔坪乡水田坪村，联系当地村民家中餐宿，或在星之谷烧烤、野营。星之谷为第二天行程出发点，可自驾或包车前往，谷中可停车。</span> \r\n</p>\r\n<p>\r\n	<strong>DAY2</strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">星之谷--槟榔孔--月之谷--天使之城（天堂门）--迷洞--星之谷</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]星之谷迷洞暗河出口处尽量不要搭帐篷，以免遭遇山洪灾害；野营的朋友出发时可将帐篷打包后寄存车上，也可穿过槟榔孔后寄存于水田坪村民家中。</span> \r\n</p>\r\n<p>\r\n	<strong>DAY3</strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">星之谷--驻马溪--草泥马峰--唐家坡</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]从星之谷到驻马溪可自驾、包车、骑行，1.8km沥青村道+2.6km乡道Y106抵达罗塔坪乡后左拐进入罗塔坪村村道，向四官峪村方向约800m机耕路可达驻马溪峡谷入口。驻马溪为张家界地貌峡谷，峡谷上行登山至唐家坡宿营。</span> \r\n</p>\r\n<p>\r\n	<strong>DAY4</strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">唐家坡--双峰岩--罗塔坪村--市区</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]从唐家坡沿黄土界山脊继续前行，过盐井村、一湾水村、可抵达寒溪沟；从双峰岩峡谷出来，可游览四官峪村、罗塔坪村的锅罗坑、寒溪沟，这些都是具有张家界地貌特征的峡谷区。</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FmhiPQeQDvSlu34sUIthZ5dIiMKK\" alt=\"\" /> \r\n</p>'),(1561,491,3,'安全提示','									<p>\r\n	<span style=\"line-height:1.5;\">该线路为具有一定危险性的户外线路，需有专业资质的户外俱乐部提供专人向导和专业装备，游客切莫孤身前往，注意自身安全。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>遇险求助电话</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">罗塔坪乡政府值班电话：0744-8543189</span>\r\n</p>\r\n									'),(1562,491,4,'餐饮推荐','									<p>\r\n	<span style=\"line-height:1.5;\">槟榔谷农家乐位于水田坪村岩旮子组，正处在X019公路线上，可容纳100人开餐，有4间客房8个床位，太阳能热水器、电视、无线网络。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">招牌菜：土鸡火锅、腊肉、野猪肉、竹鸡、四季蔬菜、野菜葱、米辣子、酸菜。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">预定热线：15387443719 覃桂林</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n									'),(1563,488,1,'简介','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fl_mEFRVTzZSq-WF-YcOFGPqk-Li\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">位于永定区东南边陲的石堰坪是一个土家族聚居、古村面貌和多样民族文化保存完好的传统村落，拥有规模庞大的明清古建筑群、金鸡洞古硝池遗址、宋代窑址皮垭里、僰人崖葬群、灰溪河穿心岩等历史遗存和自然奇观，还有“糊仓”、“扬叉舞”等土家农耕文化活动世代相传，堪称绝版的厚重历史文化和秀美自然景观承载着土家人浓浓的乡愁，是国内一块不可多得的土家文化保存完好的处女地。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlQ2w4SA3vbF286sdS82sb-bAJ59\" alt=\"\" /> \r\n</p>\r\n									'),(1564,488,2,'户外攻略','<p>\r\n	<span style=\"line-height:1.5;\">【资源种类】吊脚楼、古祠古庙、自然溪流、土家田园、原始森林、喀斯特溶洞、绝壁悬棺【户外项目】徒步山地穿越、单车越野、民俗采风、探洞、骑行、野营等</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【海拔范围】200m至800m</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路难度】1星</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路强度】2星</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路风景】5星</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【装备要求】速干衣裤、冲锋衣、徒步鞋、登山杖、水袋、手套、头巾、野炊用具、露营装备、溶洞穿越照明灯具、头盔、药品等</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【交通指引】王家坪镇石堰坪村距市区约58km。自驾约1小时38分钟，为市区公路+高等级公路S228（15km）+县道X016（38km）+县道X064（5km）；路况平整，适合骑行；公交:张家界中心汽车站有市区至沅古坪镇客班车，后可搭乘面的车到达目的地（至王家坪集镇5元，至马头溪桥4元，前往其余地点可商议包车）。</span> \r\n</p>\r\n<h4>\r\n	行程\r\n</h4>\r\n<p>\r\n	<strong>DAY1</strong>\r\n</p>\r\n<h3>\r\n	<span style=\"font-size:12px;font-weight:normal;line-height:1.5;\">市区--马头溪--关水坪--紫荆塔</span> \r\n</h3>\r\n<h3>\r\n	<span style=\"font-size:12px;font-weight:normal;line-height:1.5;\">[备注]马头溪至紫荆塔全程16km公路，尤其适合骑行，沿线土家民居、民俗荟萃，适合家访，午餐可联系当地村民，紫荆塔夜营。</span> \r\n</h3>\r\n<p>\r\n	<strong>DAY2</strong>\r\n</p>\r\n<h3>\r\n	<span style=\"font-size:12px;font-weight:normal;line-height:1.5;\">紫荆塔--砂子垭--石堰坪</span> \r\n</h3>\r\n<h3>\r\n	<span style=\"font-size:12px;font-weight:normal;line-height:1.5;\">[备注]该线路全长15km，为县乡公路，随山势起伏，海拔落差在500m左右，可选择徒步、骑行，砂子垭午餐，夜宿石堰坪村部。</span> \r\n</h3>\r\n<p>\r\n	<strong>DAY3</strong>\r\n</p>\r\n<h3>\r\n	<span style=\"font-size:12px;font-weight:normal;line-height:1.5;\">石堰坪--箱子岩--市区</span> \r\n</h3>\r\n<h3>\r\n	<span style=\"font-size:12px;font-weight:normal;line-height:1.5;\">[备注]石堰坪周边有洑溪柳廊、穿心岩等自然风光，古村落群集中在团龙岗，石堰坪，楼屋三个村民小组，商家河绝壁悬棺，又称“箱子岩”。</span> \r\n</h3>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fgt2rFt7ZzVOSwmwmYxfR5E9txHh\" alt=\"\" /> \r\n</p>'),(1565,488,3,'安全提示','									<p>\r\n	<span style=\"line-height:1.5;\">部分线路尚未正式开发，需要专人向导和专业设备。游客切莫孤身前往，注意自身安全。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">遇险求助电话：</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">王家坪镇政府值班电话：0744-8588077</span>\r\n</p>\r\n									'),(1569,481,1,'简介','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpP3uNccnnnN3VjNdQ3xXD15Tlqg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">七星山介于天门山、熊壁山、崇山三山之间，四座山均为四面刀削绝壁，海拔也均在1500米以上，构成了中国版图中最为大气壮观且呈垂直角度的孤峰地貌群落。由于七星山四围绝壁，鸟飞不渡、猿猴无攀，云遮雾障，被一些户外联盟称为“湖南最难登的山”，徒步七星山，也被驴友戏称为“绝壁上的探戈”，感受生与死的最近距离。为了安全保障，建议最好在当地向导的带领下徒步登山。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsmPIBvGyNXA1C-AQQXyUx7slau6\" alt=\"\" /> \r\n</p>'),(1570,481,2,'户外攻略','<p>\r\n	<span style=\"line-height:1.5;\">【资源种类】森林、高峰</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【户外项目】徒步、登山、露营、攀岩、体验、挑战、探险</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【海拔范围】399m至1528m</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路难度】4星</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路强度】4星</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路风景】4星</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【装备要求】露营装备、登山包、登山鞋、野炊用具、登山杖、防寒衣、路餐，扁带等保护装备、对讲机、急救药品等</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【交通指引】徒步进入：从市区5路、8路公交车至邢家巷，徒步经仙人溪大坝停车坪至仙人溪大坝，乘船至仙人溪村，继续徒步至犀牛潭村；乘车进入：包车从市区经竿子坪村，沿仙人溪至犀牛潭村部（16km）。回程：下山至大坪镇唐家村乘三轮车或面包车至大坪镇，班车或包车回市区（21km）。</span>\r\n</p>\r\n<h4>\r\n	行程\r\n</h4>\r\n<p>\r\n	<strong>DAY1</strong>\r\n</p>\r\n<p>\r\n	徒步或包车（16km）至犀牛潭村部，步行（2.3km）至犀牛潭最后一户人家——犀牛哥家（联系电话18207440168），安营扎寨。途中观小天门奇观。\r\n</p>\r\n<p>\r\n	<strong>DAY2</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">犀牛潭--天澜关--蚂蝗区--洗马池--绝壁群--绝壁栈道--婆婆洞--挖宝老人--天梯--雷窝凼</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]雨季注意防蚂蝗，绝壁栈道保护已脱落，需贴山壁缓慢移动；天梯所有抓手已脱落，有两处需要现场做保护，建议配置25m至30m扁带一条；线路景色壮美，但多悬崖绝壁，在拍照的同时注意自己脚下的安全；峡谷石头极易打滑，最好备用一双解放鞋。</span>\r\n</p>\r\n<p>\r\n	<strong>DAY3</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">雷窝凼--芦苇凼--古村落遗迹--唐家村--大坪镇</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]下山时，需从雷窝凼补足饮用水（古村落遗迹至唐家村路段，虽有水，但较为浑浊）；从古村落遗迹至唐家村下山路段的石板路注意防滑；从大坪返回市区有一个半小时的车程，均为盘山公路，坡陡弯急，开、乘车需注意安全</span>\r\n</p>\r\n<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnYxi3AWyd8mcmikSP-G3QpMZHQk\" alt=\"\" /><br />'),(1571,481,3,'安全提示','<p>\r\n	<span style=\"line-height:1.5;\">【安全提示】</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">该线路为具有一定危险的户外线路，需有专业资质的户外俱乐部提供专人向导和专业装备，游客切莫孤身前往，注意自身安全。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">遇险求助电话：</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">大坪镇政府值班电话0744-8570618</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">南庄坪街道办事处值班电话0744-8290316</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1576,480,1,'简介','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvfoXTTTmQG3IO8H05sG7CkMyKxL\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">崇山——仙人溪之旅，既可以被定义为休闲赏红叶、体验农家乐的经典线路，也可以被户外资深人士定义为接受更大挑战——七星山的必经之路。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhVveFZq0_uUteElx6igiifMWEFU\" alt=\"\" /> \r\n</p>\r\n									'),(1577,480,2,'户外攻略','<p>\r\n	<span style=\"line-height:1.5;\">【资源种类】溪流、河谷、张家界地貌</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【户外项目】徒步、自行车、溯溪、游泳、钓鱼、登山、攀岩、露营、摄影</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【海拔范围】最高海拔1164.7m，平均海拔800m</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路难度】3星</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路强度】2星</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路风景】4星</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【装备要求】露营装备、溯溪鞋、野炊用具、登山杖、防寒衣、灯具、自行车服、头盔、渔具、路餐、饮用水、药物</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【交通指引】该条线路设定崇山或仙人溪为起点均可。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">后坪镇距市区约18km。自驾或骑行，至崇山山顶；月50分钟，为市区公路+S306+乡间水泥路。值得一提的是，崇山山高坡陡、路长弯急、路况很好，是骑自行车的最佳场地；公交：张家界市汽车站坐后坪车，至八家河电站下次，由小路上山。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">仙人溪距市区约8.1km，乘8路公交到仙人溪站下车，步行10分钟进入仙人溪水库，转乘渡船或徒步进入仙人溪村村头。</span> \r\n</p>\r\n<h4>\r\n	行程\r\n</h4>\r\n<p>\r\n	<strong>DAY1</strong>\r\n</p>\r\n市区--自驾或乘车--后坪镇八家河电站--相公洞--连五间村--崇山山顶（扎营）<br />\r\n<p>\r\n	<strong>DAY2</strong>\r\n</p>\r\n崇山山顶--墨斗溃--二天门--犀牛潭--虾米溪--仙人溪水库--邢家巷社区--市区<br />\r\n[备注]1.因仙人溪河谷较为开阔，两旁植被稀少，所以日照较强时要做好防晒工作。<br />\r\n从仙人溪村往里走，因旅游开发尚未到位，目前当地暂时无法提供住宿等服务项目，想深入仙人溪景区需要配备齐全的户外装备。<br />\r\n<p>\r\n	部分地区潭水较深，进行游泳等活动时请注意安全。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuohYrxW40zQbREzpZ1QIC7OguE3\" alt=\"\" /> \r\n</p>'),(1578,480,3,'安全提示','									<p>\r\n	<span style=\"line-height:1.5;\">【安全提示】部分线路尚未正式开发，需要专人向导和专业设备。游客切莫孤身前往，注意自身安全。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>遇险求助电话</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">后坪镇政府值班电话：0744-8533008</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">官黎坪办事处值班电话：0744-8231615</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">后坪派出所：0744-8537396</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">连五间村村书记田开前：18374404578</span>\r\n</p>\r\n									'),(1579,480,4,'餐饮推荐','									<p>\r\n	<span style=\"line-height:1.5;\">【餐饮推荐】</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">崇山山势高，昼夜温差大，山顶种植的蔬菜特别可口。以下提供两家农庄的联系方式：</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">崇山农家乐饭庄;15874488989/13307445102</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">崇山客栈：13337243739</span>\r\n</p>\r\n									'),(1580,479,1,'简介','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Ftu2G-hSncjrHzb7lcFyUZcKpfho\" alt=\"\" />\r\n</p>\r\n<p>\r\n	清风峡在2009年前后被大批户外爱好者发现，在这里除了当地乡民就地取材、逐年垒砌而成的简易游道和一处似土家院落般的游客服务点和露营营地，几乎没有人工雕凿的痕迹。可以说是一处世外桃源。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fhji5V-Fh_GhcYOgN8H18vx9PGs4\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">清风峡谷沿途可见森林茂密、石峰林立，骏马奋蹄、屈子行吟、 指点江山、五指迎宾、金鞭直立等景点形态各异、活灵活现、栩栩如生，还有高山流水、鸳鸯洞穴、龙泉瀑布、千年古藤满绝壁、五色花树等绝景，这些风景在不同的角度观赏给人不同的感觉。真有：“横看成岭侧成峰，远近高低各不同”之感。</span> \r\n</p>\r\n									\r\n									'),(1581,479,2,'户外攻略','<p>\r\n	<span style=\"line-height:1.5;\">【资源种类】张家界地貌</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【户外项目】徒步、摄影、度假、攀岩、溯溪、露营、涉潭、探瀑等</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【海拔范围】470m至800m</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路难度】2星</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路强度】2星</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路风景】5星</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【装备要求】登山包、登山溯溪鞋、登山杖、防寒衣、路餐、对话机、露营装备（停车场和峡谷中段服务站可露营）等</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【交通指引】市区--武陵源中湖乡--杨家界景区门票站（大门左侧入峡谷）。中湖乡距市区约39km。自驾：约50分钟，为市区公路+省道S228+县道X020；骑行：市区公路+省道S228+县道X020，路况尚可；班车：从张家界中心汽车站乘坐中湖专线车直达中湖乡。抵达中湖乡后，步行10分钟或乘坐观光车3分钟，自驾约2分钟即可到杨家界景区门票站，再从其大门外左侧步行入清风峡。</span>\r\n</p>\r\n<h4>\r\n	<span style=\"line-height:1.5;\">行程</span>\r\n</h4>\r\n<p>\r\n	<strong>A.M</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">杨家界景区门票站（大门左侧）--1号营地（可停车、露营）--洗尘池--高山流水--2号营地（可露营、用餐）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]峡谷长3km，在铁索桥景点，可以溯溪而行。在高山流水景点，可稍作歇息并留影拍照。再往前步行30分钟就到了2号营地，即画里江山营地与中餐厅。</span>\r\n</p>\r\n<p>\r\n	<strong>P.M</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2号营地--五色花（春季）--千年滕王--龙泉瀑布--将军座--2号营地--杨家界景区门票站</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]清风峡整个行程往返约需4-5个小时，途中野人谷可以看到野猴，并有溯溪、登山、涉潭、探瀑等多种户外体验，在1、2号营地可以开展露营等营地休闲活动。</span>\r\n</p>\r\n<p>\r\n	<strong><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong>【联系方式】</strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界清风峡户外运动基地暨画里江山营地</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">15387449799（九九香）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">13297440222（兽兽）</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Frk-rsw2sBkfs46HbwEnR3oFOQP3\" alt=\"\" /> \r\n</p>'),(1582,479,3,'安全提示','<p>\r\n	<span style=\"line-height:1.5;\">【安全提示】该线路难度不大，满峡幽潭飞瀑峰林山泉，景色怡人。攀岩、溯降、瀑降等需有专业资质的户外俱乐部方可进行，游客及户外爱好者最好结伴而行，切莫孤身前往，定要注意安全。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>遇险求助电话：</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">中湖乡政府0744-5517003</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【其他】</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">峡谷属于户外人的天堂，起环境卫生靠大家自觉维护。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">因景区生态原野，沿路枯枝败叶较多，凡进景区者未到指定地点严禁抽烟生火，千万要注意防止火灾。</span>\r\n</p>'),(1583,478,1,'简介','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvnbixrIlXKPkIcHnHQrRyTwXrWu\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	八大公山自然保护区总面积2万公顷，分为天平山、杉木界、斗篷山三大林区，主峰斗篷山海拔1898.4米，为武陵山脉南麓之巅。气候属北亚热带山地湿润季风气候，夏秋季短，春冬季长，最冷月平均气温0.1℃，最热月平均气温22.8℃，相对湿度90%以上。区内群山起伏，山脉纵横交错，599座山峰星罗棋布，奇峰突起，溪流、瀑布众多。独特的地理环境，孕育和保存了亚热带最完整、面积最大原生型常绿阔叶林。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FrMEtL---DOU9NZcNb3MSwA7_vmA\" alt=\"\" /> \r\n</p>\r\n									'),(1584,478,2,'户外攻略','<p>\r\n	<span style=\"line-height:1.5;\">【资源种类】原生性森林、瀑布、山峦</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【户外项目】徒步山地穿越、溯溪、岩降、瀑降、水上抱石、定向越野、野营等</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【海拔范围】346m至2003m</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路难度】5星 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路强度】4星 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【线路风景】4星</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【装备要求】速干衣裤、冲锋衣、防寒衣、登山包、登山鞋、登山杖、水袋、手套、头巾、野炊用具、露营装备、头盔、药品</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【周边资源】贺龙故居、廖汉生故居、桑鹤土司结盟遗址、澧水源头七眼泉等。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">【交通指引】</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">1.进入：桑植汽车西站至芭茅溪乡（20元），然后租车进入辽竹湾村（穿越的起点）。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2.回程行程：</span>\r\n</p>\r\n<p>\r\n	<strong>DAY1</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">桑植县-芭茅溪-四门岩林场</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]桑植县至芭茅溪乡后中餐，推荐参观：圆梦饭庄（价格20-30元/人，联系电话：13974426576）。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">中餐后租车往辽竹湾，在辽竹湾村部下车后沿小路步行10km左右至四门岩林场，宿营四门岩林场场部。</span>\r\n</p>\r\n<p>\r\n	<strong>DAY2</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">四门岩林场-长坪-大顶坪</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">从四门岩林场出发，翻山至长坪峡谷入口，沿长坪沟溯溪而上，长坪午休，午休后顺干谷河床一路向上提升面，直达大顶坪宿营。</span>\r\n</p>\r\n<p>\r\n	<strong>DAY3</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">大顶坪-瞭望塔-小庄坪（天平山接待站）</span>\r\n</p>\r\n<p>\r\n	<strong>DAY4</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">小庄坪-连三台-毛垭半坡</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]沿山腰猎户小道环山至青龙湾，青龙湾对面有一小溪沟，顺沟而下可见三棵枯树，再往下见干枯河床，顺河床而下至漆水塔休息。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">顺峡谷而下行至毛垭半坡农户家宿营。全程无手机信号，需9小时。晚上可住宿当地农家，体验土家族的日常生活。</span>\r\n</p>\r\n<p>\r\n	<strong>DAY5</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">毛垭半坡-黄连台（李家河）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">早餐后从毛垭半坡至毛垭村，有小卖部补结，从毛垭村小路下山至黄连台村长宿营，全天行程需7小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">黄连台村村长杨志山15074408816 &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"line-height:1.5;\">书记刘伟华13407443072</span>\r\n</p>\r\n<p>\r\n	<strong>DAY6</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">黄连台-小溪口-天梯人家-七湾-芭茅溪</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">[备注]</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">至黄连台村部，沿溪边土公路往下到达小溪口，公路可达芭茅溪镇，公路旁小溪进山约40分钟山路到达天梯人家，此处可观60m瀑布。天梯人家徒步七湾下山到达天平山公路，可联系包车来接至芭茅溪，车费30元/车（司机张师傅13707446937）。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">下山后再沙园联系芭茅溪车主接至芭茅溪，原路返回桑植县。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fvs5Jm4IfV30TA_qi9CIaTDTXbeU\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1585,478,3,'安全提示','<p>\r\n	<span style=\"line-height:1.5;\">【安全提示】该线路需要扁带、对讲机、急救药品等公共装备方可成行，切勿孤身或私下结伴前往，需联系当地户外俱乐部提供专业向导和保护。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">此行程路途较长，而且进入谷底，手机信号不好，也有可能没有；又出于隐雷区，雨天建议慎重出行。谷中暂时没有小商品出售，进入景区要带足食物和饮水保持体力，可以带些高能量的食物，如巧克力、饼干、肉类等；景区中有许多飞禽走兽，请远离并保护它们，切勿伤害它们；山中雨雾笼罩，有时豁然开朗，横穿石壁，自然温服比较低，风力也会大些，请务必注意保暖，适当带些衣服，防止温度突变，引起身体不适。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">特别提醒：八大公山毒蛇（五步蛇）出没，要防止被毒蛇咬伤。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">遇险求助电话：八大公山乡政府0744-6730001</span>\r\n</p>'),(1586,475,1,'民俗风情','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiADZqjV1v0QQ-ui5bWup5HjP8lx\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	澧水河上，响起了喧嚣的锣鼓声，民间的龙舟船队已经整装待发，一声令下，如万箭齐发，奔向前方。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvUN4fCo07XGpwviZmNOdldQNtdC\" alt=\"\" /><span style=\"line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;摄影：代天胜</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">端午节龙舟赛，在中国南方水域很常见，但张家界土家族龙舟赛有着自己独特的文化特色。赛龙舟分“五龙”和“六龙”两个传统节日，“五龙”为迎龙日，“六龙”为送龙日，两个节日的时候是从农历五月初五算起，逢辰为“五龙”日，习惯称作龙头日，十二天轮转一次，依次算下去，算到六月的龙日为“六龙”。</span> \r\n</p>'),(1587,475,2,'民俗特色','																											<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjMs7oHi9t8c9aDYj82_XxDvhitH\" alt=\"\" />\r\n</p>\r\n<p>\r\n	龙舟赛前，首先由主办村寨请所有的船队高手吃一顿团圆饭，再给每条龙船上披红挂彩，以示吉祥顺利。\r\n</p>\r\n									\r\n									\r\n									'),(1588,473,1,'民俗风情','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn_J6nyQIziL6lcHIbVl5BUzXmBM\" alt=\"\" />\r\n</p>\r\n<p>\r\n	张家界人说起自己的元宵节，个个眉飞色舞，自豪之情溢于言表。民俗专家金克剑老先生介绍，清道光、同治年间的《永定县志》都有记载，元宵夜永定城区张灯结彩，演戏、玩花灯、舞龙灯、狮子灯、蚌壳灯通宵达旦。张家界闹元宵与别的地方还有一个不同，她是33个少数民族聚居区，以土家族为主。因此元宵节又主要是土家族的节日，是土家族（自称毕兹卡，本地人）的狂欢节。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fp71jXyJdkc5Z9RiVt4bisBlJh4E\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                                                                                                                    摄影：郑亚平\r\n</p>\r\n									'),(1589,473,2,'民俗特色','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjV0jDDqaxj7NQ9JCL808RKfUA-O\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span>摄影：郑亚平</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">有高花灯、花灯、龙灯、阳戏、茅古斯、傩戏、硬气功、草狮子、地狮子、打镏子……全都是国家级、省级或永定区的非物质文化遗产。</span>\r\n</p>'),(1590,433,1,'民俗风情','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvaDvCfSB9tWKg7CGjZSwn0Pp1J_\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	歌唱家宋祖英在维也纳金色大厅演唱桑植民歌马桑树儿搭灯台\r\n</p>\r\n<p>\r\n	桑植民歌是湖南民族文化中的枝奇葩，源远流长，浩如烟海，其特点是淳朴、真挚的情感而扣人心弦，曲调丰富多变，旋律优美动听。\r\n</p>\r\n									'),(1591,433,2,'民俗特色','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fmy2_oaTXjYnTfRZN2GrrNGnr0ea\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	世界级音乐家谭盾一直推介湘西民族音乐\r\n</p>\r\n<p>\r\n	桑植是个以土家族、白族、苗族为主体的少数民族县份。这些民族只有语言，没有文字，所创造的文化，多以口头代代相传，桑植民歌就是他们创作的口头文学与音乐的结合。2006年被列入第一批国家级非物质文化遗产名录。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgaNPxpvzc2nnmDzW8AOpLgDZnxi\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	&nbsp;歌手刘赛桑植人多次演唱桑植民歌\r\n</p>'),(1592,431,1,'民俗风情','<p>\r\n	<span style=\"line-height:1.5;\">在土家语里，\"西兰\"是铺盖的意思，\"卡普\"是花的意思，\"西兰卡普\"即土家族人的花铺盖。人们往往在\"花铺盖\"前冠以\"土\"字，以标示出这项民间工艺所包含的土家族民族特点。</span>\r\n</p>'),(1593,431,2,'民俗特色','<p>\r\n	<span style=\"line-height:1.5;\">土花铺盖是受到土家族人民的珍爱，视之为智慧、技艺的结晶，被称作\"土家之花\"。按照土家族习惯，过去土家姑娘出嫁时，都要在织布的机台上制作美丽的\"西兰卡普\"，即土花铺盖。</span>\r\n</p>'),(1594,430,1,'民俗风情','<p>\r\n	<span style=\"line-height:1.5;\">糊仓，土家族农耕习俗。主要流传在张家界市永定区以农事耕作为主的乡镇农村，是古代农耕文化延续的产物。</span>\r\n</p>\r\n<p>\r\n	“糊仓”的过程分为开秧门、吃盖碗肉和糊仓三部分。“开秧门”要举行仪式。由德高望重的老者面向东方起半个秧，再面朝南方起另一半，然后合拢一起，再由稻草捆扎，两半叉开，置于田坎上。接着还要念上一段祝福的话。“吃盖碗肉”就是吃早饭时，由领头人吃最大的一块腊肉，领导一天的劳动；“糊仓”是插秧的高潮，在快要结束时举行。\r\n</p>'),(1595,430,2,'民俗特色','									<p>\r\n	一丘秧田里，聚集了很多帮忙插秧的人，大家心里都憋着一股子劲，看谁插秧插得最快，插得慢的人往往会被插得快的人围住，最后关在秧田中间出不来，这叫“关猪笼”。这时候，插得快的人就会走上田埂，率先抓起泥团朝关了猪笼的人砸去。也有专门去砸主人一身稀泥巴的。主人身上被砸的稀泥越多，主人越高兴。到了最后，田野里就会上演一场你追我赶的游戏。土家人把它叫做“糊仓”。\r\n</p>\r\n<p>\r\n	“糊仓”意味着风调雨顺，农作物丰收。谁身上泥巴糊得越多，谁家的收成就越好，仓里储的粮食鼠不咬、虫不蛀，粒粒饱满，颗颗金黄。“糊仓”的民俗里透出的是美好希望和祝福。糊仓节是石堰坪村自定的节日，定为每年农历五月初一。\r\n</p>\r\n									'),(1596,428,1,'民俗风情','<p>\r\n	<span style=\"line-height:1.5;\">拜观音菩萨是张家界民俗之一。俗传观音菩萨农历二月十九日出生、九月十九日出家、六月十九日得道。民间和佛门在这三个日子都要举行纪念、享祀活动。观音又叫观自在、观世自在、观世音，俗称观音菩萨、观音大士。观音为阿弥陀佛左胁侍，西方三圣之一，又是汉化佛教四大菩萨之一，汉族民间奉其为大慈大悲观世音菩萨。</span>\r\n</p>'),(1597,428,2,'民俗特色','<p>\r\n	<span style=\"line-height:1.5;\">当地人每年都有在观世音菩萨生日这天上天门寺拜观音的习惯。据说拜观音前需净身、沐浴、食素三天。可以供花、供水果，绝不能供酒和肉食。每年的观音菩萨诞辰日，天门山朝拜的人从四面八方到来，可谓人山人海。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">&nbsp;“庙里不断香，灶里不熄火，路上不断人”这是1949年前香客们到天门山寺拜佛朝圣盛况的写实。直到今天，许多人仍虔诚地相信天门山的佛最灵验。</span>\r\n</p>'),(1598,427,1,'艺术精粹','									<p class=\"MsoNormal\" align=\"left\">\r\n	张家界世界地质公园博物馆位于张家界武陵源核心景区水绕四门。博物馆总占地面积<span>6000 </span>平方米，其中生态广场约<span>5000 </span>平方米，展厅约<span>500 </span>平方米、<span>5D </span>影厅约<span>500 </span>平方米。博物馆功能分区为：中央广场区、静态地质博物馆区、动态“张家界地貌”<span>5D</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	体验区、亲水文化休闲区。\r\n</p>\r\n									'),(1599,427,2,'主要功能','									<p class=\"MsoNormal\" align=\"left\">\r\n	博物馆担负的主要功能是科学地收藏张家界地貌有关的自然和生物标本，开展科学研究和挖掘学术价值。通过向社会开放，传播自然科学知识和地质地貌知识，全面、深入地展开对张家界地貌科普知识的传播、研究。同时也是吉首大学张家界学院、张家界航空技术学院教育基地。\r\n</p>\r\n									'),(1600,427,3,'艺术特色','									<p class=\"MsoNormal\" align=\"left\">\r\n	博物馆的开放填补了张家界旅游地质科普方面的空缺，同时也是国内外朋友参观张家界世界级的山水最权威、最系统的解说点。\r\n</p>\r\n									'),(1601,427,4,'交通路线','									<p class=\"MsoNormal\" align=\"left\">\r\n	距\r\n离：位于武陵源核心景区，距市区约<span>40 </span>公里\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	公共交通：武陵源汽车站乘坐武陵源至森林公园专线车，到森林公园门票站下车\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	自\r\n驾：可驾车沿省道<span>S306 </span>经峪园隧道到森林公园门票站\r\n</p>\r\n									'),(1602,427,5,'游览须知','									<p class=\"MsoNormal\" align=\"left\">\r\n	地\r\n址：武陵源核心景区内水绕四门\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	门票价格：<span>88 </span>元\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	电\r\n话：<span>0744-5518555</span>\r\n</p>\r\n									'),(1603,427,6,'拓展延伸','<p class=\"MsoNormal\" align=\"left\">\r\n	张家界地貌的形成成因：\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	1、滨海沉积\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	三亿八千万年前（地质史上称为泥盆纪中期），张家界一带为古海洋。大量陆源碎屑物流入当时还是滨海环境的张家界域内，经过亿万年的沉积压实，形成今天张家界地貌的母岩——石英砂岩。\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	2、变海为陆\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	约3.5—2.9 亿年前（泥盆纪晚期）的海西造山运动，使张家界变海为陆。砂岩层顶部含铁质层长期风化，形成红色铁质风化壳—俗称“铁帽”。\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	3、复为古海\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	约2.9—2 亿年前（二叠纪、三叠纪时期），地壳下降，使张家界再次变成海洋，沉积了石灰岩。这是张家界世界地质公园岩溶地貌的原岩。\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	4、复为陆地\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	约2 亿年前（三叠纪晚期）的印支运动，使张家界抬升为陆地。此前形成的石灰岩露出地表，张家界地区自此脱离海洋环境。\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	5、褶皱和断裂变形\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	约1.4-0.8 亿年前（侏罗纪、白垩纪）的燕山运动，使张家界的岩石发生褶皱和断裂变形，内部形成了许多节理和裂隙，为以后风化和流水切割提供了条件。\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	6、夷平面形成\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	约0.65—0.3 亿年前（古新世、渐新世），张家界域内地壳相对稳定，隆起的山体经风化剥蚀，削高填平，形成了海拔1200 米左右的湘西期夷平面。\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	7、剥夷面形成\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	约2300 万年—260 万年前（新近纪、第四纪初），喜马拉雅运动使张家界强烈抬升，河流在不断下切中受坚硬“铁帽”的阻挡，迫使下切转为侧蚀、拓宽作用，最终在“铁帽”层顶部形成了海拔800 米左右的“武陵源期剥夷面”。\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	8、地貌形成\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	近260 万年以来的新构造运动，使张家界地壳又一次抬升，尤其是近80 万年来，流水顺着节理和裂缝将石英砂岩切割成方山、平台、峰墙、峰林、残林等地貌景观。现在，张家界地区的地壳还在缓慢上升，张家界地貌景观仍在发育变化之中。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1604,425,1,'艺术精粹','									<p class=\"MsoNormal\" align=\"left\">\r\n	大庸府城位于张家界市中心解放路，是以展示张家界历史及土、白、苗、瑶、侗五大少数民族的非物质文化遗产为主的人文景点。大庸府城所在地是一块有着丰厚历史和人文资源的宝地，自明洪武三年（公元<span>1370 </span>年）始在此设大庸卫，一直到当代的大庸市政府、张家界永定区政府，先后经历了<span>4 </span>个朝代（时代）、<span>40 </span>个皇帝（元首）、<span>600 </span>多年漫长的演变史。\r\n</p>\r\n									'),(1605,425,2,'艺术特色','									<p class=\"MsoNormal\" align=\"left\">\r\n	大庸府城占地<span>45 </span>亩，建筑面积<span>6.5 </span>万平方米，由<span>8 </span>座楼群组成，建筑呈明清时期土、苗、白、瑶、侗等少数民族建筑之风格。整个府城从南至北呈阶梯状，府城中心为中央景区，从南门至中央景区依次有朝天楼、天街、“大庸府城”石碑、虎溪、虎乳铜雕、府城牌坊、大庸卫（府）轶官名录、照壁、府城门、虎錞香炉、府衙门、朝阳楼等<span>12 </span>个人文景点，均依<span style=\"line-height:1.5;\">古城旧制或传说而建，分别承载不同的文化内涵。</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	游走府城，您既可以看到土家吊脚楼的古朴大气、苗寨的神奇秀美、侗族风雨桥的浪漫多姿、瑶族盘王殿的神秘威严，还可以看到白族三坊一照壁的清幽绚丽、檐角飞扬的马头墙、精致秀美的雕花门窗，艺术而灵动的衔接古往今来，将民间淳朴又细腻的艺术风格发挥到极致。\r\n</p>\r\n<p>\r\n	中央景区内有“张家界多民族非物质文化遗产展示馆”和“张家界博物馆”两大主题展厅。\r\n</p>\r\n<p>\r\n	<p class=\"MsoNormal\" align=\"left\">\r\n		其中多民族非物质文化展示馆主要由土寨、苗寨、侗乡、瑶山、白馆组成，每个展厅用情景交融的方式将其特殊的文化、建筑、起居、服饰、饮食、歌舞、传说、戏剧、工艺、禁忌、丧葬等予以集中展示。其中桑植民歌、土家族摆手舞、苗家鼓舞、土家织锦、江永女书、滩\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\">\r\n		头年画、花瑶挑花等都已被列入中国首批“国家非物质文化遗产保护名录”。\r\n	</p>\r\n</p>\r\n									'),(1606,425,3,'特色功能','									<p class=\"MsoNormal\" align=\"left\">\r\n	大庸府城还拥有张家界市游客服务中心、星级酒店、民俗客栈、民俗风情酒吧街、特色商品购物街、美食街、休闲中心、超市及大型地下车库，完全满足了游客的多维需求。大庸府城与张家界雄奇的自然景观相映成辉，是张家界的新文化地理标志，是一道印象张家界的多民族文化盛宴，是国内外游客解读张家界的很重要的一站。\r\n</p>\r\n									'),(1607,425,4,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">距 离：位于市区，距武陵源核心景区约35 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：可在市区乘坐1、2、3、5、6、7、8 路公交车至大庸府城</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：可直接驾车至市区解放路到大庸府城</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1608,425,5,'游览须知','									<p class=\"MsoNormal\" align=\"left\">\r\n	地\r\n址：张家界市永定区解放路\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	门票价格：免费\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	电\r\n话：<span>0744-8288287</span>\r\n</p>\r\n									'),(1609,423,1,'艺术精粹','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkF3eSQPqMRLNcfxf_Owimoa0kVc\" alt=\"\" />\r\n</p>\r\n<p>\r\n	军声画院的砂石画，是张家界一大特色艺术。在近一千多平方米的展厅里，一幅幅用张家界特有的天然彩色砂石、植物所绘制而成的“军声砂石画”，为世人展示出灵山秀水和民土人情。透过这扇艺术之窗，您不仅仅可以看到一个更美的张家界，体会到土家人的聪明才智，更可带另一个“张家界”回家作为永久的珍藏。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fu-76AB1YVyP4l139ax-pitFpXkO\" alt=\"\" />\r\n</p>\r\n<p>\r\n	国家3A级旅游区，被外界称为“砂石画艺术宫殿”，位于张家界市区,距离武陵源风景名胜区约35公里，由现任院长、画家李军声于2001年创建，是展示新型绘画种类——砂石画的专题展馆。画家李军声凭籍扎实的美术功底，对绘画艺术进行了一次深入的回归性探索，以砂石、植物等自然材料为绘画创作原材料，创造了一种全新的绘画种类——砂石画。砂石画具有中国画的神韵，水彩画的清新，油画的凝重，工艺画的精巧，又有半浮雕的立体感，因其取材于自然而享有“绿色画种”和“环保画种”的美誉。\r\n</p>\r\n									\r\n									'),(1610,423,2,'艺术特色','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqYk7TdmfclQb2PzVJ9ZtQqcmvBf\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	军声画院本着“纯朴、自然、创新”的文化理念，将艺术与市场相结合，将文化与旅游相结合，走出了一条宽阔的路，不仅为张家界旅游做出了一定的贡献，而且在艺术界倍受好评。画院凭着良好的信誉和品质，独特的艺术思想和灵魂，赢得了硕果累累，相继被列为“湖南省57家民族工艺定点生产单位”、“湖南省民营企业500强” 及“湖南省首批文化产业示范基地”。\r\n</p>\r\n									'),(1611,423,3,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">馆内3000平方米的三层展厅内，陈列着一件件突破传统绘画手法的以砂石画为主的系列艺术作品，让人视觉受到冲击，极具艺术价值与收藏价值。游览时间2小时。</span>\r\n</p>'),(1612,422,1,'艺术精粹','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlnTy6PAcbCBIGNBSHbVnHYEe1MW\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	张家界作为世界级的旅游胜地，不仅拥有绝版的自然风光，而且还拥有独特的民族风情以及历史悠久的湖南土家文化，种种民间民俗文化丰富多彩。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FoaMTQr545DM_igi522_v7Xco-NV\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	近年来，随着张家界旅游从观光游向休闲文化游的转型，人们对民俗文化资源在旅游业中重要性的认识进一步加强，开发民俗文化资源已成为丰富旅游活动，提高旅游产品文化品位的重要措施。为了更好地挖掘、保护、传承张家界民俗传统文化，依托旅游发展民俗文化产业，2013年“国聪旅游民族文化艺术馆”应运而生。\r\n</p>\r\n									'),(1613,422,2,'文化特色','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjZfM3hckgd7oEo7TYSb3GsbueXD\" alt=\"\" />\r\n</p>\r\n<p>\r\n	国聪旅游民族文化艺术馆以弘扬大湘西土家文化为宗旨，是一家以湘西土家文化，民间艺术收藏，木雕石雕工艺，园林景观艺术，旅游文化艺术，农副产品开发，等收藏，展示，研究，交流，宣传土家历史文化，兼具古建筑设计、施工，民修古建筑装饰材料销售，古典红木家具销售的民间民俗文化艺术馆。它将成为传承保护大湘西历史文化以及发展文化旅游事业的先驱。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fg6eJtblllM4yRcwQC5lUUFCiHcl\" alt=\"\" />\r\n</p>\r\n									\r\n									'),(1614,422,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">湖南省张家界市永定区阳光酒店（张家界永定区永定大道2号）对面，出租车或2、7路公交车即到。</span>\r\n</p>'),(1615,422,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览时间大约为2小时。</span>\r\n</p>'),(1616,411,1,'艺术精粹','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FomvjjmA4gmkOlExQnrMz2YFNJ3v\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	全景区\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;\"></span><span style=\"line-height:1.5;\">张家界荷堂艺术馆位于张家界市荷花村，展馆面积1200多平方米，可同时举办不同类型、不同题材的展览，文化艺术讲座和论坛。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkmKQhgSrdwjT2naxmUUenqI5jF_\" alt=\"\" /> \r\n</p>'),(1617,411,2,'历史文化','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkRpnksT-n1r9Ys4gbEwqJvky8ak\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">荷堂艺术馆是永定区推进世界旅游精品建设和文化事业建设，按照国际化标准而着力打造的文化产业精品项目，其以中国当代名家书画作品展览、收藏为载体，以艺术创作、交流和艺术人才为培训目的，通过推介国内外艺术家的优秀作品，开展多元化的文化交流活动，来推进张家界文化艺术大发展大繁荣。艺术馆将通过社会免费开放的形式，为游客、市民和本土艺术家参观、作品展示和艺术交流提供平台。</span>\r\n</p>'),(1618,411,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">荷堂艺术馆位于张家界市荷花村S306省道南侧，距市中心约8公里，背靠天门山麓，前临澧水河畔，四周为万顷荷塘。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">乘车路线：张家界汽车站（火车站附近）乘坐后坪中巴车，荷堂艺术馆下。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnyvCGPAV30VmoYxid8aYoHqTDja\" alt=\"\" /> \r\n</p>'),(1619,411,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览时间大约为2小时 。【免费开放】， 周二至周日 ： 09:00~18:00 &nbsp;； 周一闭馆 &nbsp;； 节假日正常开放。</span>\r\n</p>'),(1620,409,1,'剧情介绍','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fid-YEtLRUfBw2cDbq3m8USr5VeV\" alt=\"\" />\r\n</p>\r\n<p>\r\n	魅力湘西剧场的舞台演出持续了十余年，在国内、港澳台、日韩、东南亚均拥有极高的知名度，为武陵源核心景区享誉盛名的大剧院。2012年龙年春晚的杂技歌舞节目《追爱》，便是出自魅力湘西剧场的作品。《追爱》原名叫《爬楼》，是魅力湘西剧场经久不衰的经典剧目。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fmqmd4sg54SobodEw3KXqFlIEdhB\" alt=\"\" />\r\n</p>\r\n<p>\r\n	而在魅力湘西剧场原址，《爬楼》的效果完全超越春晚舞台的表演。灯光亮出，少数民族的阿哥从观众席中突然跑出来，通过借力用力的配合，身手不凡的演员们，瞬间窜上数米高的阁楼，展现演员们极佳的弹跳力和柔韧性。舞台上的演员们更表现出极好的平衡，能几个人同时在一张双脚落地的梯子上作出惊险的动作，引得观众惊呼连连。\r\n</p>\r\n									'),(1621,409,2,'演出特色','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqUtss4NEC9MKj4ZLNYLuNbBIh22\" alt=\"\" />\r\n</p>\r\n<p>\r\n	魅力湘西的节目取材，全都来自于湘西的民间艺术和生活：《边城》中的翠翠，划着小船出场，动如雷震的苗鼓，敲出了大山的力量。人们熟悉的桑植民歌《郎在高山打一望》、《马桑树儿搭灯台》在舞台上有了全新的演绎；哭嫁也被搬上了舞台，让人们欣赏到濒临失传的《哭嫁歌》。原始的祭祀舞茅古斯，延续着湘西人原始的信仰；神秘可怖的湘西赶尸，转成了一个令人感伤落泪的故事。湘西侗寨的《合拢宴》，在节目尾声将气氛推向高潮，强烈的节奏感，牵动着每个观众的神经。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqURn4Q8T6DJNQU5NS-pnYPQALrz\" alt=\"\" />\r\n</p>\r\n<p>\r\n	室内演出之后，室外还有一场原生态的表演。苗族芦笙，侗族大歌，上刀山下火海，鬼谷神功轮番上阵，还有令人捧腹的互动游戏。\r\n</p>\r\n									'),(1622,409,3,'交通路线','<p>\r\n	<strong>联系方式</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">市中心汽车站乘“市区-武陵源”专线车直达</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">电话：0744-5667777</span>\r\n</p>\r\n&nbsp;<br />'),(1623,409,4,'观演须知','<p>\r\n	<strong>票价</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">挂牌价268元</span>\r\n</p>\r\n<p>\r\n	<strong>演出详情</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">时间：17：30,19：30，21:30</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地点：张家界魅力湘西国际文化广场</span>\r\n</p>'),(1624,407,1,'剧情介绍','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fu7M9_bKKXH87wSnVbOpyhCrX0xo\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                                                                                                                     摄影：李钢\r\n</p>\r\n<p>\r\n	《天门狐仙》是全世界首部以高山峡谷为背景和舞台的实景音乐剧演绎的是包括张家界内的大湘西流传的神话爱情故事。纵深5公里、海拔高差1100米的峡谷舞台，纳入了大自然的雄奇俊美。最高处是豁然悬廊的天门洞，山坡上建造了土家吊脚楼、飞瀑流泉、溜索、飞桥。这部音乐剧完全突破了人们熟知的舞台效果，融入了歌舞、武术、魔术、杂技等舞台艺术，宏大的灯光效果，营建出一个如梦如幻的世界。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsAdpLsXqVhIHvP2Ki1AWjpT7cfP\" alt=\"\" />\r\n</p>\r\n									'),(1625,407,2,'演出特色','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fpod9v8bax6MRpuRF0cixUCu_oEp\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span>                                                                           刘海哥                                                             </span><span>摄影：李东群</span>\r\n</p>\r\n<p>\r\n	《天门狐仙》又名《新刘海砍樵》。《刘海砍樵》是湖南著名的花鼓戏，主要流传在湘西一带。这出花鼓戏源起于神话传说，发源地在常德，讲述了樵夫刘海和千年狐狸精胡秀英的爱情故事。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqA_h0IeqVXqK14E7evas9PkUsEf\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span>                                                                              狐仙                                                              </span><span>摄影：李东群</span>\r\n</p>\r\n<p>\r\n	本剧通过对这个经典故事的重新构思、巧妙编排，并在音乐旋律的引领下，层层展开一幅幅绚丽优美、哀婉生动的舞台场景，重新讲述了以砍柴为生的青年樵夫刘海和在天门山中修炼千年的白狐仙之间曲折而浪漫的爱情经历。融入了桑植等地的民歌和土家的民风、民俗，在大型的真实场景中，表演的歌舞剧。以灯光和背景的大型营造以及歌曲独特风格为胜。\r\n</p>\r\n									'),(1626,407,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\"><strong>联系方式</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地点：天门山峡谷大剧场</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">电话：0744-8369999</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1627,407,4,'观演须知','<p>\r\n	<strong>票价</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">根据不同的座次，有238元、580元、880元。（演出基本不会受天气影响，每年冬季停演，时间大致在12月中下旬-次年中上旬</span>\r\n</p>\r\n<p>\r\n	<strong>演出详情</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">时间：每天20:15</span>\r\n</p>'),(1632,406,1,'剧情介绍','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlhfkwctRA6Rbe3fPtWgNVEX4hTa\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">《烟雨张家界》讲述了远古时代在烟雨缥缈的张家界大山深处，土家族英俊青年洛巴冲和苗族美丽姑娘阿依朵之间缠绵悱恻、美丽动人、荡气回肠的爱情故事。</span>\r\n</p>\r\n									'),(1633,406,2,'演出特色','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fr9rmi1X4iTDNljsAhtoKVprBmCn\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">《烟雨张家界》时长80分钟，10个选段围绕洛巴冲和阿依朵的爱情主线，展示了张家界地区历史悠久的农耕文化、巫傩文化、建筑文化、服饰文化、饮食文化、婚俗文化，其中桑植民歌、茅古斯舞蹈、土家织锦、打溜子、哭嫁等更是列入了国家非物质文化遗产名录。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtAFCAeEoagicOF-bcnFnwutDi3r\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">这台节目是中国首台3D视觉大型民俗文化演出，也是中国首个模拟实景剧场演出，利用现代声、光、电等科技设施，配合美妙而震撼的配乐以及逼真的超大视频特效，真人与视频的完美结合，演员在屏幕间来回穿梭，让人产生亦真亦幻、如烟似雨的感觉，向世人演绎魅力、神秘的张家界民族风情。整台大戏时长只有80分钟，穿越的却是张家界几千年的辉煌灿烂文化。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkS-B3nqCBHDrDzDhwPeGf7QMSaB\" alt=\"\" /> \r\n</p>\r\n									'),(1634,406,3,'交通路线','<p>\r\n	<strong>交通</strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自驾线路:长沙至张家界高速公路在阳和收费站下高速，到黄龙洞只有17公里。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公交线路:张家界火车站距黄龙洞48公里有直达武陵源的大巴，或者从火车站打的至张家界汽车站再转车到武陵源。</span> \r\n</p>\r\n<p>\r\n	<strong>联系方式</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">电话：400-883-1111 &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;0744-5628756</span>\r\n</p>'),(1635,406,4,'观演须知','									<p>\r\n	<strong>票价</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">挂牌价198元</span>\r\n</p>\r\n<p>\r\n	<strong>演出详情</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">时间：11:00、17:00，每场35分钟</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地点：哈利路亚音乐厅</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n									'),(1636,450,1,'红色经典','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiwWOx_z-0vsfsPROVtKo5r0JJ88\" alt=\"\" />\r\n</p>\r\n<p>\r\n	在张家界国家森林公园金鞭溪游览线上有一处路段被景区命名为红军路，很多游客走到此处疑惑不解，为何把这里立牌称为红军路，其实是因为在解放前夕，贺龙元帅曾经带领红二、六军从此地走过，为了纪念革命者的足迹，建立张家界红色旅游纪念地而设立了这段“红军路”。\r\n</p>\r\n									\r\n									'),(1637,450,2,'红色故事','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fmx2vRxwgvi8KJLqgJctGfVxyJY8\" alt=\"\" />\r\n</p>\r\n<p>\r\n	年轻人一定要重走红军路，以体会革命先烈“万水千山只等闲”的豪情。\r\n</p>\r\n									\r\n									'),(1638,450,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往森林公园的线路巴士。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">森林公园门票站进入：从老磨湾广场步行进入金鞭溪。</span>\r\n</p>'),(1639,450,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览金鞭溪全程，时间最少需3小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>相关信息</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：已含在武陵源核心景区门票内（票价248元/3天有效）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市武陵源区金鞭溪风景区</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1640,449,1,'红色经典','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkZiUGBQFKSiiTNKTaNupsmocs9w\" alt=\"\" />\r\n</p>\r\n<p>\r\n	茅垭，桑植县龙潭坪镇最偏远的一个行政小山村。全村分茅垭、苦竹溪、苦竹塔、文家界、谢家界，5个自然小组73户268人，生活在海拔1500米的崇山峻岭中。玉米、洋芋、红薯是村民的主要食粮。由于地处湘鄂交界，山势险要，易守难攻，大革命时期，这里曾是贺龙领导的红军主要活动地。\r\n</p>\r\n									'),(1641,449,2,'历史文化','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnGIAyCoVuQiPJzy9SiW2FRmH-p4\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	进村小路\r\n</p>\r\n<p>\r\n	从1930年8月到1935年11月，在该村苦竹塔组，红军成立四届茅垭乡苏维埃政府，村民杨云阶、文昌和、文昌杰、李宏才先后担任主席，陈卜然、陶春轩、刘远茂、龚伦常、石长生、田和尚、唐世文担任副主席。从此，红色的革命烈火在这块土地上越烧越旺，在这里，红军和群众亲如一家，患难共存，同抵外敌，谱写了一曲曲悲壮的英雄史歌，茅垭成为名副其实的红军村庄。\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">热情、纯朴、好客的茅垭人把红军部队当成自己的部队，把红军视为自己的亲人。茅垭人把最好的房间腾出来让红军住，留着最好的东西做给红军吃，红军和茅垭贫苦百姓成为一家人。苦竹溪住有杨曹两大家10多人，有杨青沛、杨青霸、张春生他们都参加了红军，杨青沛懂医术为红军伤病员治伤看病。</span>\r\n</p>'),(1642,449,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市桑植县龙潭坪镇。建议自驾前往。</span>\r\n</p>'),(1643,449,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">此景点为免费景点。</span>\r\n</p>'),(1644,448,1,'红色经典','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjezwMse-X7xIyoRvWy5KlsMsbZE\" alt=\"\" />\r\n</p>\r\n<p>\r\n	廖汉生故居位于桑植县樵子湾乡张家桥村三百墩组，始建于清光绪年间，为一间低矮的木房，小青瓦屋顶，六十年代，其弟廖章生修建了两间三柱四棋的木房，与廖汉生故居连为一体，1911年12月，廖汉生同志诞生在这里，并在这里度过了他的童年和青少年时期。廖汉生一九二九年参加湖南桑植苏区游击队。一九三三年加入党组织。土地革命战争时期，任乡苏维埃代表。湖北鹤峰第四游击大队副大队长，中国工农红军第三军政治部秘书，七师党委书记，九师政治委员，红二方面军政治部组织部部长，红二军团第四师、六师政治委员。参加了长征。\r\n</p>\r\n									\r\n									'),(1645,448,2,'历史文化','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fg5zZLcclkoWvs0nJe4hAnhFODbP\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	廖汉生 左五\r\n</p>\r\n<p>\r\n	抗日战争时期，廖汉生任八路军一二○师三五八旅七一六团副团长、政治委员，独立第二旅副政治委员、代旅长，中共襄南地委书记兼分区政治委员，江汉军区副政委委员、政治委员。解放战争时期，任晋北野战军副政治委员，晋绥军区第一纵队政治委员，第一野战军一军政治委员。中华人民共和国成立后，任第一兵团军政治委员兼青海省军区政治委员和中共青海省委书记，西北军区政治部主任、副政治委员，国防部副部长，军事学院院长，北京军区政治委员，军事科学院政治委员，南京军区、沈阳军区第一政治委员。一九五五年被授予中将军衔。是第一、二、三届国防委员会委员，第一、三、四、五届全国人民代表大会代表，第六届全国人民代表大会常务委员会副委员长，党第七次全国代表大会代表，第八届候补中央委员，第十一、十二届中央委员。\r\n</p>\r\n									\r\n									'),(1646,448,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市桑植县樵子湾乡张家桥村三百墩组。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">建议自驾前往。</span>\r\n</p>'),(1647,448,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">此景点为免费景点。</span>\r\n</p>'),(1648,447,1,'红色经典','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fq2WQj5HcbJCfHADWosmAuA9UymO\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	贺龙元帅和芭茅溪盐局旧址\r\n</p>\r\n<p>\r\n	芭茅溪盐局是贺龙元帅两把菜刀闹革命的地方，是红色旅游景之一。\r\n</p>\r\n<p>\r\n	1916年农历腊月二十八，青年贺龙响应蔡锷护国讨袁的号召，与12位穷苦兄弟首先拿搜刮民脂民膏的芭茅溪盐局开刀，一举端盐局后，回洪家关竖起了起义大旗。因为贺龙使用的武器是两把菜刀，从此“两把菜刀闹革命”成立贺龙元帅早期革命的代名词。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fk0TTjOFg-UAmMmByCKQsfGT-HIo\" alt=\"\" />\r\n</p>\r\n<p>\r\n	盐局旧址，50年代初撤除1扇2间，尚存3扇4间原房陈设。\r\n</p>\r\n									'),(1649,447,2,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市桑植县芭茅溪乡。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">建议自驾车前往。</span>\r\n</p>'),(1650,447,3,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">此景点为免费景点。在去八大公山的必经之地。</span>\r\n</p>'),(1651,442,1,'红色经典','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhS2QK3J2g0-cpCiJiP8VT5yLBGA\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">贺龙公园 是为纪念贺龙元帅转战天子山而建，坐落湖南省张家界市天子山上，在1200米的千层岩左侧，一般归类于石家檐游览线。公园内主要游览点有贺龙元帅铜像、兵器馆、贺龙元帅陈列馆、将军碑林（建设中）等。</span>\r\n</p>'),(1652,442,2,'人文景观','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fp0RoVCsjuYalbT6ZGO1u2XQ-HLi\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">屹立在“云青岩”上的贺龙铜像与大自然连为一体，形成独特的艺术风格。建铸于1986年，高6.5米,重9吨,为我国近百年塑造的最大的一尊铜像。铜像背负青山，面壁神堂湾，凝重、庄严、和谐地统一在青山绿水的画图中，逼真再现了贺龙元帅的鲜明形象：浓眉大眼，八字胡须，右手小臂提起，握着烟斗，微笑注视着家乡石峰峭壁。身旁一匹好征的战马，昂起头颅，似乎在等待主人扬鞭踏上征途。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqflsQVoIxBAGn1li11BqGgKqfop\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">1995年3月28日，江泽民总书记为公园题写园名。胡耀邦同志亦曾为云青崖题字。公园内有数处观景台，是天子山观景最集中的地方。</span>\r\n</p>'),(1653,442,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往武陵源的线路巴士。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">武陵源吴家峪门票站进入：乘景区环保车至天子山索道下站，乘缆车上山，或从卧龙岭步行上山。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">或者从杨家界索道、百龙天梯上山后，乘景区环保车前往。</span>\r\n</p>'),(1654,442,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">此景点在武陵源核心景区内天子山景区，门票已经包含在武陵源核心景区门票内（248元/人）。铜像背后的不远处，便是“云青岩”观景台，从这里可以观赏到“西海石林”。西海是名副其实的山的海洋，上千座山峰风起云涌般展现在眼底，鳞次栉比，巍峨壮观。这里还可以根据不同的季节和天气而欣赏到天子山的“四大奇观”-石林、日出、云瀑、冬雪。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1655,440,1,'红色经典','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FspART-_fi6rVEZSPgtpKdQT1f9F\" alt=\"\" />\r\n</p>\r\n<p>\r\n	贺龙元帅一生忠诚革命，军中大将且高风亮节，一直受到国人的爱戴。贺龙是张家界的骄傲，其故居位于桑植洪家关，目前得到较完整的恢复；故居斜对面，另建有贺龙纪念馆。前往桑植旅游，不可遗漏贺龙纪念馆。 \r\n</p>\r\n									\r\n									'),(1656,440,2,'建筑奇观','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsIo4rUgr-ff5GY8Q3CmHR7eYBoh\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	贺龙父母卧室\r\n</p>\r\n<p>\r\n	馆舍由八个展厅组成，陈列着两把菜刀闹革命、南昌起义、创建革命根据地、抗日战争、解放战争、国家体育事业奠基者、国家军队重要领导人、贺龙同志永远活在人民心中、中小学生思想教育展厅几大块内容。贺龙故居和纪念馆是全国爱国主义教育示范基地和革命传统教育基地。贺龙纪念馆展厅共辟贺龙同志及其任弼时、关向应等我军领导人的历史照片387张，文献文物190件。纪念馆于1993年3月筹建，1996年3月在纪念贺龙同志诞辰100周年时建成并对外开放，由时任中共中央总书记的江泽民同志题写馆名。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FrWplWy0688rf5cR0vs6I1aR1BaW\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	贺龙家厨房\r\n</p>\r\n									\r\n									'),(1657,440,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">从张家界中心汽车站乘车至桑植汽车站（西站），然后步行至附近的北门汽车站，在高架桥下，桥下有一些停泊的巴士，乘坐往洪家关方向的车，终点即是贺龙纪念馆和贺龙故居。返程的末班车在18:00发车。从桑植县返回张家界市区的班车最晚是17:00-18:00.</span>\r\n</p>'),(1658,440,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">此景点为免费景点，营业时间为淡季9:00-16:00，旺季8:00-17:00。</span>\r\n</p>'),(1659,439,1,'建筑特色','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvYOt4Iu3g2011tJADq1OfFH2txf\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">贺龙故居是一栋坐北朝南、四扇三间的普通木房，原是贺龙祖父贺良仕于清道光午间修建的，后为贺龙的父亲贺仕道所继承。贺龙和他的姐姐、妹妹、弟弟都出生在这里，并在这里度过了他们的童年和青少年时期。由于他们家人口多，贺龙父亲将这三间房用木板隔成六间。正中一间为堂屋，门首红底金字匾额上的“贺龙故居” 四个字为邓小平手书。</span>\r\n</p>\r\n<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn0984QXda6DadYCHtZC1gJNITe_\" alt=\"\" /><br />'),(1660,439,2,'民俗风情','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fo1TCP0CAf1oxpb2RmhWI86bih0m\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">堂屋前间过去是供奉家神和红白喜事用的厅堂，现在这里置放贺龙元帅身穿戎装的半身铜像。堂屋后面是退房，是贺龙姐姐和妹妹的卧室。左前间为火坑屋，冬天，全家人就在这间屋里生火取暖，来人来客，也都招呼在这里就坐。1916年春，贺龙联络20个人就在这火坑屋里商量过袭击芭茅溪盐税局的事。后面半间是贺龙父母亲的卧室。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">当地有个习俗，认为堂屋的左边为大(也叫东头，但它不是表示方位的)，应住长辈。客人来了，为表示尊敬，也请在这里就坐。堂屋右边前间是厨房，后面是贺龙和他弟弟贺文掌的卧室。凡卧室都陈列有木床、被褥、蚊帐、木箱、书桌、靠椅、灯盏架和女人做鞋用的竹篮、洗衣棒等各种常用器物。故居右边这栋房是骡马房，贺龙十多岁时为了谋生曾当过骡马客，在这里关养骡马。</span><span style=\"line-height:1.5;\">这是贺龙当年用过的马鞍、马鞭和斗笠。</span>\r\n</p>'),(1661,439,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">从张家界中心汽车站乘车至桑植汽车站（西站），然后步行至附近的北门汽车站，在高架桥下，桥下有一些停泊的巴士，乘坐往洪家关方向的车，终点即是贺龙纪念馆和贺龙故居。返程的末班车在18:00发车。从桑植县返回张家界市区的班车最晚是17:00-18:00.</span>\r\n</p>'),(1662,439,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">此景点为免费景点，营业时间为淡季9:00-16:00，旺季8:00-17:00。</span>\r\n</p>'),(1663,437,1,'红色历史','<p>\r\n	<span style=\"line-height:1.5;\">张家界烈士公园位于张家界市城区东面迎宾路旁回龙观, 占地面积200 亩。烈士公园修建于1985年，是湖南省第一批省级重点革命烈士纪念设施保护单位，湖南省首批爱国主义教育基地，湖南省第二批国防教育基地。2002 年实行免费对外开放。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界市永定区具有光荣的革命历史，在第二次国内革命战争时期，是中华苏维埃共和国湘鄂川黔省委和省军委的所在地。在长期的革命斗争中，涌现了无数的英雄儿女，永定区人民付出了惨重的代价，先后共认定了革命烈士2800 人，为昭烈士功勋于青史，垂英雄风范于后世，原大庸县委、县政府于1985 年作出修建烈士纪念塔的决定，烈士纪念塔于1985 年3 月3 日奠基动工兴建，10 月落成。</span>\r\n</p>'),(1664,437,2,'建筑特点','<p>\r\n	<span style=\"line-height:1.5;\">烈士纪念塔位于烈士公园山丘之顶，纪念塔正面刻有原全国人大常务委员会副委员会长廖汉生的题字“革命烈士纪念塔”，原中顾问委员会常务委员肖克的题词“革命烈士永垂不朽”。纪念塔背面刻有仿毛泽东主席题词“为人民而死，虽死犹荣”。塔座正面镌刻烈士碑文，其他三面分别刻有鸡公垭战役、百姓欢迎解放军入城和工农兵建设新大庸三幅浮雕。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">纪念塔背面建有六角纪念亭，亭内设烈士英名录石碑，上刻烈士英名1662人。纪念塔东南面立资助陵园修建的功德碑。纪念塔右侧有烈士公墓，安葬烈士遗骨15 人，老红军2 人。烈士陵园北面，有本籍抗日阵亡的陆军少将覃子斌将军墓。</span><span style=\"line-height:1.5;\">除了扫墓、瞻仰烈士英灵外，烈士公园树木葱郁，从山脚至山顶有一条石阶铺就的游道，因此这里也是人们健身、纳凉的极好选择。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1665,437,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">距 离：位于市区，距武陵源核心景区约35 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：可在市区乘坐1、7、10 路公交车至烈士公园站下车</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：可驾车沿市区迎宾路至烈士公园</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1666,437,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">建议穿合适的鞋，此经典免费。</span>\r\n</p>'),(1667,436,1,'建筑特色','<p>\r\n	<span style=\"line-height:1.5;\">湘鄂川黔苏区纪念馆占地面积约2800 平方米，东头是三堵矮墙围着的一栋木平房，三间小屋曾住过任弼时、贺龙、肖克，黑漆桌上的桐油灯作证，一代英杰曾在这里彻夜运筹。西头是可容纳200 余人的省委礼堂，当时省委扩大会和省直各部门的会议均在此召开，现已辟为将军馆，166 位在湘鄂川黔革命根据地留下英雄足迹的新中国将帅的照片挂满墙壁。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">纪念馆大门左右，一字排开11 间办公室，当年3 大机关13 个直属部门均在此办公。庭院正中是根据我国著名雕塑家潘鹤的作品《艰苦岁月》凿制而成的一尊双人石像，小红军抱着高过头顶的步枪，依偎在横吹短笛的老红军膝头，似在入神地倾听。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">雕像后面是纪念馆主楼，内设三个展室，陈列着223 幅大型图片和100 多件革命文物，再现了根据地的光辉斗争史，反映了根据地军民艰苦卓绝的革命历程和高尚情操。</span>\r\n</p>\r\n<br />'),(1668,436,2,'红色经典','<p>\r\n	<span style=\"line-height:1.5;\">1933 年11 月，由于王明“左”倾路线错误和敌人的大举进攻，湘鄂西革命根据地全部丧失，红三军在湘鄂西中央分局夏曦、贺龙、关向应的领导下不得不转战湘鄂川黔，建立了黔东根据地，为红二、六军团会师和湘鄂川黔根据地的形成创造了条件。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">1934 年10 月，红六军团与红三军在印江木黄胜利会师，由贺龙、任弼时、关向应统一指挥，发动湘西攻势，控制了永顺、大庸、桑植的大部，开辟了湘鄂川黔边根据地。11 月26 日，中共湘鄂川黔省委成立，任弼时为省委书记，贺龙、关向应、王震、肖克等为省委委员，同时成立省军区，贺龙为军区司令员，任弼时为政委。至此，以大庸为中心的湘鄂川</span><span style=\"line-height:1.5;\">黔革命根据地正式形成。</span>\r\n</p>'),(1669,436,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">距 离：位于市区，距武陵源核心景区约35 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：市区1、2、3、5、6、7、8、路公交车均可到纪念馆</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：可驾车沿解放路至纪念馆</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1670,436,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">地 址：张家界市永定区解放路179 号</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票价格：免费</span>\r\n</p>\r\n<br />'),(1671,429,1,'云雾茶乡','<p>\r\n	<span style=\"line-height:1.5;\">西莲乡位于桑植县的东北部，距县城约110 公里，与石门县交界。这里山清水秀，平均海拔800 多米，由于地理海拔高、位置偏远，历来就是盛产茶叶的好地方。这里的茶叶自明朝以来，就被历代朝廷列为贡茶，堪称高山云雾茶乡。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">“三鹤园”茶叶基地为西莲乡茶叶基地的代表，一眼望去，整齐的茶园，在茶叶出芽之时，茶农们辛勤劳作，实为一幅美丽画卷。“三鹤园”是个特殊的地方，冬天雪季降临，大地都披上银装的时候，“三鹤园”却见不到积雪。除去茶业基地，这里的自然景观也让人叹为观止。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1672,429,2,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">距 离：距武陵源核心景区约120 公里，距市区约140 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：桑植汽车站乘坐至西莲乡班车</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：可沿武陵源、赵家岗、竹叶坪、走马坪、人潮溪、西莲乡路线抵达，行程约为120 公里</span>\r\n</p>'),(1676,426,1,'醉美之乡','									<p>\r\n	<span style=\"line-height:1.5;\">在桑植县的东北部，离县城121 公里的地方，坐落着一个高山乡叫白石。白石乡东与石门县罗坪乡毗邻，南与人潮溪乡交界，西与长潭坪乡接壤，北与湖北鹤峰抵界，素有“桑植的青藏高原”之称，最高海拔为1571 米，平均海拔为1100 米。“巍巍白石壁千仞，茫茫南滩草万顷，涛涛林海翻绿浪，山山烟菜飘黄金”是对白石最好的写照。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">白石境内四面环山，层峦叠翠，郁郁葱葱，野花遍地，鸟语花香，森林覆盖率在90% 以上。夏季气候凉爽，空气清新，负氧离子丰富，是理想的“天然氧吧”。冬季千里冰封，万里雪飘，风景壮美。村落原始，民风淳朴。既有北方山水的粗犷特色，又具南国田园的柔美风情，是旅游观光、避暑度假和民俗体验的理想之地。2013 年，白石乡被农业部评为“中国美丽田园”。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">醉美高山云海：云海很美，但看过白石的云海后，你一定会改变对云海的想象。在这里，站在平均海拔1200 米的高山平原之上，白云就在你的脚下，你到哪儿，它跟着你，温柔可掬，甚至可以将它抓在手里、揽在怀里、贴在脸上。它又如大海般波涛汹涌，将白石周边的三道峡谷全部填满，仅露出几道高出峡谷的山尖，山风起，山尖随波涌动，就像在大海里飞翔的海鸥，时而隐在波谷，时而飘在浪尖。风停了，白白的、厚厚的云层又像一张巨大的棉被，从眼前直铺到天边，稳稳当当。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">醉美高山草滩：绿色南滩，中国南方最大的天然草场，春冬之时，南滩草场是雪的海洋，白茫茫一片，令人神往；夏秋之季，那绿意葱茏的草场和漫山遍野的蔬菜，美的让人乐不思归。放眼远眺，20 万亩绿色草场，一望无际，近万只羊，2000 头牛，在草场里惬意地觅食、撒欢，正如诗中所描绘的“天苍苍，野茫茫，风吹草低见牛羊”，如诗如画，让人沉醉。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">醉美高山菜园：每到秋天，一行行、一垅垅绿色的萝卜叶在连绵起伏的山头延伸，泥土中的白萝卜冒出地头，等待菜农采摘。地里，满是采收萝卜的菜农，一辆辆运送萝卜的车辆来回穿梭，成为白石高山一道美丽的风景。“白石萝卜”远销长沙、广州、深圳、香港等地，“桑</span><span style=\"line-height:1.5;\">植萝卜”也获得了国家地理标志产品。白石现有2 万亩高山蔬菜基地，一眼望不到边，不仅成了白石人致富的“香饽饽”、富民强乡的支柱产业，也成了桑植最美的高山菜园。</span>\r\n</p>\r\n									'),(1677,426,2,'交通路线','									<p>\r\n	<span style=\"line-height:1.5;\">距 离：距武陵源核心景区约180 公里，距市区约150 公里</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：桑植汽车站乘坐至白石乡的班车</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：可沿市区、瑞塔铺、刘家坪、芙蓉桥、马合口、官地坪、长潭坪、白石、南滩草场路线抵达，行程为148 公里。其中瑞塔铺－官地坪，县道柏油路基本平整，偶有小坎和小坑，基本不影响行车，35 公里；官地坪－长潭坪新修水泥路，所处山势险峻，行车小心，在长潭坪过轮渡，26 公里。长潭坪－白石，乡道水泥路，地势险峻，路窄，行车小心，35 公里；白石——南滩草场，路况较差，底盘低的车子无法前行，适合越野，10 公里。</span>\r\n</p>\r\n									'),(1678,426,3,'游览须知','									<p>\r\n	<span style=\"line-height:1.5;\">白石，位于桑植县境内的游览西线上，尚无旅游线路巴士，建议自驾车前往或在市区选择相关1日游。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：免费</span>\r\n</p>\r\n									'),(1679,412,1,'山寨特色','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FoW8hwB545nB39oZQnGR69Oo7lLl\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                                                                                                                   摄影：李东群\r\n</p>\r\n<p>\r\n	王家坪镇是张家界市最典型的土家族聚居区，不仅土家族占总人口的90%左右，更保存了多达2198栋的土家吊脚楼，19个极具特色的土家山寨，错落在奇山异水间，流传着多姿多彩、古朴浓郁的民俗风情。 \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FmCU57_Z_NQpKeW4k_lJQMr87Jzb\" alt=\"\" />\r\n</p>\r\n									\r\n									'),(1680,412,2,'民俗风情','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fr8sOlohdLVpYmhSmCDJ9bWVX-C1\" alt=\"\" />\r\n</p>\r\n<p>\r\n	在王家坪镇土家族聚居村落最为盛行的，是一年一度的“糊仓文化”。糊仓文化是一种区域性的农耕文化，最初属农事祭祀活动，经过长时间的继承和演变，成为土家人非常重视的年度喜事。\r\n</p>\r\n									\r\n									'),(1681,412,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">张家界市中心汽车站有往返王家坪镇的班车，发车时间8:00-10:00，每日两班，里程60余公里，车程2小时，价格15元；建议自驾前往。</span>\r\n</p>'),(1682,412,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">前往王家坪镇建议慎选农时，以芒种、夏至、秋收等节气为好，可以接触到土家人的田园劳作场景，寓劳于乐。盛夏之前的湘西山乡气候较为宜人,万物生长，一派生机，见欣欣向荣之感。</span>\r\n</p>'),(1683,410,1,'建筑风情','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqIHLaFi2CuUjLubdQZMjGMDM9fF\" alt=\"\" />\r\n</p>\r\n<p>\r\n	苦竹寨距九天洞5公里，为湘西北保存最为原始、完整的古镇，保留着典型的明代民居建筑群，具有良好的民俗开发基础。曾是澧水上游千帆林立的老码头、商人云集的古集市，梢公荡魂的逍遥窟、明清明代的万花筒。古老的风火墙、底矮的吊脚楼，向游人展示着土家人的聪明才智，一条条青石板、一声声咚咚喹，在诉说着古寨子过去的辉煌。\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n									'),(1684,410,2,'民俗风情','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiK9opjIV-ZT1AHMQxOssDi4sp_A\" alt=\"\" />\r\n</p>\r\n<p>\r\n	苦竹赛是澧水边的一座少数民族杂居的村落，保留了明清时的建筑特色，民风淳朴，村民能歌善舞，许多桑植民歌都是靠苦竹寨的歌手流传的。入夜的时候，苦竹寨亮起了盏盏红灯，这是村里的一个传统。当年红军夜行军过苦竹寨的时候，村里有个老婆婆怕红军夜黑路难走，便在家门口悬起了一盏红灯，其它村民争相效仿，苦竹寨成了一个灯的世界。这段军民鱼水情被写成了歌，传唱到现在，这就是《门前挂盏灯》。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fshs0DDljx75oMBPwIPVM8J9royF\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                                                                                                                    摄影：郑亚平\r\n</p>\r\n									'),(1685,410,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">苦竹寨距离九天洞2.5公里，市中心汽车站乘“市区-桑植”线路车至桑植县城，再转“桑植县城-瑞塔铺”的班车至终点瑞塔铺，然后前行一百米到丁字路口，有前往九天洞的三轮车，一个人8元；建议自驾车。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1686,410,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">九天洞，位于市区至桑植县的游览西线上，尚无旅游线路巴士，建议自驾车前往或在市区选择相关1日游。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">相关信息：门票98元/人。</span>\r\n</p>'),(1690,405,1,'历史文化','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fv-lUt-vk6EDRbuGa0a6CB6_K0rx\" alt=\"\" />\r\n</p>\r\n<p>\r\n	四十八寨，主要位于张家界市慈利县广福桥镇，地处慈利、桃源、石门三县交界地带，扼守武陵山脉重要咽喉，依托富饶的江汉平原，把持中国西部的战略纵深，历朝历代都是兵家必争之地。四十八寨周边方圆40 余平方公里范围内明清时期道教规模十分庞大，盛况空前。其道教与南岳衡山、湖北武当山成三足鼎立之势。清朝乾隆三十九年（公元 1774 年），爱新觉罗•弘历登临赐名“星德山”，并留下“南天门”、“毫无色相”、“惟道独尊”诸多墨痕。\r\n</p>\r\n									'),(1691,405,2,'建筑奇观','									<p>\r\n	四十八寨素有36 宫，72 殿，其历史文化遗存久远，而位于陡峭危崖上的星子宫堪称建筑史上的奇观，自公元1370 年始建至今已有600 余年历史，经多年战火和人为破坏而没有塌陷，其稳固程度令人惊叹。此外，在以星子宫为核心的2 平方公里范围内，罗列着象山、集云崖、舍身崖等大小景点四十八处，其自然景观密集程度令人惊叹。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkN66ygipUabXu6aB41tzI7i5p-h\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	步行石阶：山间的石阶或藏在草丛，任山里杂花野草掩埋，或费心修筑，宽阔平整。不管如何，它都是一条远离俗世的通道。拖着疲累的腿，登上九九八十一，又九九八十一级石阶，一仰头，山顶还在遥不可及的云边静候。\r\n</p>\r\n<p>\r\n	<div style=\"text-align:center;\">\r\n		<span style=\"line-height:1.5;\"></span><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fn-P_X5S5TbgprMM-OL7Ohgj2Qic\" alt=\"\" />\r\n	</div>\r\n	<div style=\"text-align:center;\">\r\n		<span style=\"line-height:1.5;\">四十八寨李自成屯兵遗址</span>\r\n	</div>\r\n</p>\r\n<p>\r\n	自然风景观赏区：兀立山巅崖峰的千年古松，虬枝苍劲，迎来送往，不卑不亢，可以让人大饱看松之眼福。 峰西舍身岩上的企人石，似鹅似人，根部与底座脱节，似飞欲坠。相传女娲补天，遗石至此。<br />\r\n星 子 宫：一座古色古香的道观。明初，道教名师张道会在星德山顶峰筑宫，明洪武三年（公元1370 年）建成，明末再度扩建。宫有3 重大殿，6 间偏殿，两厢有火工殿、寝宿殿、问事殿、三生殿及观星坛、望月楼、百子堂等建筑群。\r\n</p>\r\n									'),(1692,405,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">距 离：距武陵源核心景区约99 公里，距市区约105 公里。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公共交通：武陵源汽车站乘坐至慈利线路车，再转“慈利县-广福桥镇”客运车至广福桥镇，包车前往太平三组。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：武陵源区沿省道S306、常张高速公路在慈利东下高速，再由慈利东至广福桥镇。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1693,405,4,'基础信息','<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">地 址：张家界市慈利县广福桥镇</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">门票价格：免费</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">咨询电话：0744-3570001（广福桥镇政府） &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"line-height:1.5;\">0744-3577133（广福桥镇派出所）</span>\r\n</p>'),(1694,405,5,'游览须知','<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">该线路为具有一定危险性的户外线路，需有专业资质的户外俱乐部提供专人向导和专业装备，游客切莫孤身前往，注意自身安全。</span>\r\n</p>'),(1695,379,1,'古寨风情','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FiF0C8vAJAtFQtJ6XGLBBZjmBMJY\" alt=\"\" />\r\n</p>\r\n<p>\r\n	袁家寨子位于张家界武陵源核心景区，是袁家界风景区范围内的一个是私人建筑，当地地主的寨子，被保存下来，成为旅游一大景点。还保留土家族的建筑特色，屋檐角往外斜飞，屋檐下挂着一串串玉米跟辣椒，土家人民热情好客，喜欢用自家酿制的美酒招待贵客，喝酒前还必须唱歌祝酒。所以进门就看到一张摆满酒的小方桌，阿妹祝酒完毕，游客每人一杯。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuwV8qpy7WIs2Kd1yTXiHa2dFQR6\" alt=\"\" />\r\n</p>\r\n									'),(1696,379,2,'古寨特色','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgN-USDL13nKrNgYz-gJuVaewKH5\" alt=\"\" />\r\n</p>\r\n<p>\r\n	寨内有土家的三大特色之前就有耳闻“赶尸、哭嫁、下蛊”，至今都是未解之谜。西兰卡普——土家族的一大特色， 是出嫁的女子用各色彩线一手一手编织而成的，工艺精美，费时很长。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fip6kkZ4STSDsypbR0LD9evLZjG5\" alt=\"\" />\r\n</p>\r\n									'),(1697,379,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">1、从火车站乘坐张家界--武陵源班线汽车（时间约40分钟），到达武陵源汽车站，乘1路至门票站，购买公园门票后进入核心景区，可选择乘坐百龙电梯（需另付费）上山或经金鞭溪千里相会来爬山到达景区。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2、从飞机场搭乘4路公共汽车（时间约6分钟）到汽车站。乘坐张家界——武陵源班线汽车（时间约40分钟），到达武陵源汽车站，乘1路至门票站，购买公园门票后进入核心景区，可选择乘坐百龙电梯（需另付费）上山或经金鞭溪千里相会来爬山到达景区。</span>\r\n</p>\r\n&nbsp;<br />'),(1698,379,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">张家界唯一一处园中园景点，不设门票优惠政策，学生可享受半价，1.2米以下儿童免费参观。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">相关信息：门票85元。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1699,368,1,'建筑奇观','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fq8XLAjMdgQviEw31eL1OC37oLra\" alt=\"\" />\r\n</p>\r\n<p>\r\n	田家大院也称老院子，原名紫荆堂，始建于清雍正初年，是田氏族人的祖居，其建筑风格为四井封火墙式土家建筑，融土家园林、土家吊脚楼于一体，是典型的毕兹卡（土家族）民居。也是整个湘西乃至全国幸存下来，保存最为完好的土家古宅，堪称土家建筑的活化石，被誉为“土家第一宅”，他的外表看起来朴实无华，其实它神奇得很，可以说是藏而不露，深隐玄机。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtQnSA1TYB4EjnnSrwe38_4wPa5V\" alt=\"\" />\r\n</p>\r\n<p>\r\n	江南名联“世上几百年旧家，莫非积德；天下第一件好事，还是读书”在田家大院及其适用。晚晴的抗法保台将领土家军主帅刘明灯、民国总理熊希龄、曾任孙中山先生保镖的南北大侠杜心武、地质专家田奇隽、国务院前总理朱镕基等名人都曾在田家大院修习。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqANimIffKaPnCJwe-JHnuHQseGZ\" alt=\"\" />\r\n</p>\r\n<p>\r\n	学堂内有一幅朱镕基总理的手迹碑刻。众所周知，朱镕基总理书法功力深厚，但题字寥寥，仅在自己认为非常重要的场合才会挥毫。这幅字是他多年以后回到田家大院参观留下的一首七绝，十足珍贵。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlS5_omdHo2hicmNbHMUr-eIn-uB\" alt=\"\" />\r\n</p>\r\n									'),(1700,368,2,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">位于城区永定大道彭家巷，鹭鸶湾大桥东端，市内乘1、2、7、9路公交车直达田家大院（老院子）。</span>\r\n</p>'),(1701,368,3,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年</span><span style=\"line-height:1.5;\">证购老人票。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">开放时间：8:00-17:00</span>\r\n</p>'),(1702,334,1,'历史文化','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fk2qJaV-4jiIqg1fuKmTEcMspHhS\" alt=\"\" />\r\n</p>\r\n<p>\r\n	普光禅寺，又名白羊古刹，坐落于湖南张家界永定区城东，前有天门山，后又福德山（即今子午台），为一处古建筑群景观，现存普光寺、武庙与文昌祠等建筑，是佛教与道教的联合建筑体，融合了元、明、清三个朝代的建筑风格。寺庙建于明永乐十一年，比北京故宫（天安门）早七年，比湖北武当山金殿早四年，比四川峨眉山的仙峰寺与报国寺早建一百多年，历史悠久，名声远播，有“江南名刹”之称，具有很高的历史价值与观赏价值，被列为第五批湖南省重点文化保护单位。\r\n</p>\r\n									'),(1703,334,2,'建筑美景','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvZ7ay2FcKtKCehF6Z82hk8_FYDZ\" alt=\"\" />\r\n</p>\r\n<p>\r\n	普光禅寺整个建筑采用传统斗拱和藻井结构，设计精巧、宏伟壮观，由大山门、二山门、钟鼓楼、大雄宝殿、罗汉殿、观音殿、玉皇阁、高贞观、武庙、牌坊等组成一个庞大的古建筑群，具有宋、元、明、清各朝的建筑风格和特点，同时融佛教文化、道教文化、儒家文化于一体，普光禅寺是我国古代人民超凡智慧的结晶，在建筑和宗教方面都有较高的研究价值。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Flf7zSqWd-6hAPjOV7UiSmoSzBek\" alt=\"\" />\r\n</p>\r\n<p>\r\n	普光禅寺整个普光寺形成了集寺、观、祠、坊、阁为一体和格局，这在国内实不多见。有人参观普光寺后即兴题写了一幅对联：“寺、观、阁、祠、坊一体，儒、释、道、文、武合流”。这十四个字是对普光禅寺个性特点的最好总结。\r\n</p>\r\n									'),(1704,334,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">乘4路、5路、6路、7路可以直达；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">普光禅寺位于张家界市区解放路（人民广场附近），在张家界市区的游客步行即可，也可坐出租车前往。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1705,334,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览时间在1个小时左右。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">相关信息：门票20元。</span>\r\n</p>'),(1706,332,1,'历史文化','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqDJlfltFlg5Oic-mnaF5gtVKLRW\" alt=\"\" />\r\n</p>\r\n<p>\r\n	紫霞观位于张家界市武陵源区宝峰路，坐落在环境优美的景区中心，是武陵源风景名胜区内的主要人文景观与宗教文化旅游相结合的精品旅游景点，国家“AA”级旅游景区，张家界市道教协会会址所在地。藏有著名书法家的题词、碑刻及教祖讲经说法渡化图等宗教文物。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FmAEdCINtkG9VCXQWeUy2pQ2itVv\" alt=\"\" />\r\n</p>\r\n<p>\r\n	紫霞观始建于明朝嘉靖年间，近500年的历史，在战乱时期收到不同程度的破坏。紫霞观是1992年因旅游事业的发展，教徒的信教，经政府的批准，由各地的善男信女共同捐资，当地政府拨款扩建起来的。道观共有大小殿庙30余座，计5殿、3宫、2亭、2庙、1阁、八仙山、仙人足迹石、中华同心锁等景点。中国著名书法家沈鹏先生为紫霞观题写了观名，观内有国内外著名书法家的题词、碑刻等宗教文物，以及老子教祖讲经说法渡化图一百余幅。\r\n</p>\r\n									'),(1707,332,2,'建筑美景','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fv5OyYzffxcYlzocM5YuhOyTCGFU\" alt=\"\" />\r\n</p>\r\n<p>\r\n	紫霞观观内巍峨的殿宇建筑群落金碧辉煌，雕梁画栋，布局严谨，供奉道教尊神。宗教建筑有老子出关亭、三清殿、慈航殿、祖师坛、关圣殿、玉皇殿等5殿、3宫、2亭、2庙、1阁，大小殿庙约30余座。现建有八仙山、仙人足迹石、南天门、中华同心锁等景点。景区内巍峨的建筑群落与亭林楼阁错落有致、环境幽美。终年香火鼎盛、朝拜者络绎不绝，因其确属一方不可多得的风水宝地，观内环境优美，交通便利，而享有“湘西第一观”的美誉。\r\n</p>\r\n									'),(1708,332,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">1、从武陵源汽车站搭乘1路公交车（1元/人）到天子街下，然后搭乘4路公交车到紫霞观大门下，打的5元左右；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2、从张家界市汽车站搭乘至武陵源中巴车（10元/人）到武陵源汽车站下，接下来参照上面步骤进行，全程打的100元/车左右；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">3、从火车站广场搭乘公交车（1元/人）到张家界市汽车站下，接下来参照上面步骤2进行，全程打的（120元/车左右）。</span>\r\n</p>'),(1709,332,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">观内烧香属自费项目，不要听信他人花言巧语的游说。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票价格20元/人，对身高不足1.2米的儿童、残疾人和70周岁以上老年人凭有效证件实行免票；对身高1.2米以上的学生、现役军人、烈士直系家属、城乡低保户及60周岁至70周岁的老年人均凭有效证件实行优惠票。</span>\r\n</p>'),(1710,268,1,'寺庙奇观','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fh-BgiYy-HNDJW1-GjinMeUfvmNK\" alt=\"\" />\r\n</p>\r\n<p>\r\n	天门山寺始建于明朝，历经儒、道、佛三教的融会变通，建寺以来就香火鼎盛，信士遍及辰、永、澧、常，是湘西地区的佛教中心，天门山也由此得名“湘西第一神山”。复建后的天门山寺占地两万余平方米，是目前湖南省境内海拔最高的佛教建筑群。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvRxHP97mt1D_0n44arymRtRoPaC\" alt=\"\" />\r\n</p>\r\n<p>\r\n	寺庙坐落山窝，视野开阔，极目东南，庄严端丽，尽显天界佛国雄姿。\r\n</p>\r\n									'),(1711,268,2,'天界佛国','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtXXRJHWFeN2e2BWmQ5JFn2GbugE\" alt=\"\" />\r\n</p>\r\n<p>\r\n	“承天地之灵气，接山水之精华，佛祖舍利给众生带来庇佑，也使天门奇景结无上佛缘。”天门山寺方丈怀梵法师引领着众人走进一个“天界佛国”，以最华丽的方式展现着湘西地区的佛教文化。1997年，李娜剃度受戒，法号释昌圣，从此遁迹空门。2009年有释迦摩尼故乡尼泊尔蓝毗尼迎请的释迦摩尼舍利正式安防在天门山寺，天门山寺成为湖南省内唯一一座拥有佛舍利的寺庙。\r\n</p>\r\n									'),(1712,268,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在市区可乘5、6、10路等公交至市区天门山索道下站，乘缆车至天门山国家森林公园。</span>\r\n</p>'),(1713,268,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">该景点位于张家界天门山国家森林内。乘索道终点下，山顶线路分东、西线，任走一条都会途经此地。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">相关信息：天门山门票258元（含往返索道、天门山寺门票）</span>\r\n</p>'),(1714,264,1,'溪布特色','									<p>\r\n	“民族风情步行街”溪布街，依武陵源索溪河而建，是国内唯一集水上酒吧街、民族风情街、特色小吃街等为一体的复合型旅游商业步行街，被誉为“不可复制的文化旅游藏品”。溪布，土家语叫做“西兰卡普”，原本是一种精美的土家族手工织锦，曾是土司王进献给皇室的贡品；而溪布街寓意为“布满小溪的街道，充满快乐的地方”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvRjnuSIXeM2cu2gEtAo0BFWg2-s\" alt=\"\" />\r\n</p>\r\n<p>\r\n	溪布街位于张家界武陵源区景区核心，武陵源区政府对面，北靠武陵源区第一大道——武陵大道，东邻张家界大剧院，西接前往宝峰湖要道——宝峰桥，南向沿400米索溪河岸布局，地理位置得天独厚，是通往武陵源所有重要景区的核心。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvZ549d4Xs69TaD77iL4AdCXql_Z\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n									'),(1715,264,2,'民族风情','									<p>\r\n	溪布街由张家界金瑞旅游发展有限公司投资4亿余元人民币开发，项目占地面积约100亩，总建筑面积约7万平方米，项目建筑风格以湘西土风苗韵为基调，以传统土家民居为蓝本，依托索溪河自然水景元素，营造房中有院、院中有街、韵味十足的集市盛景，并融汇符合时代审美的时尚休闲元素，以“最原生态、最自然、最和谐”的开发理念进行规划建设，是全国唯一集水上酒吧街、中华名特小吃街、湘西民俗购物精品街、休闲客栈、创意工坊等为一体的大型的特色文化街区。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fkh0aTqpi7q3s8wrtZHF-cY6-kXV\" alt=\"\" />\r\n</p>\r\n<p>\r\n	其整体规划以街巷与院落结合布局，力求浪漫、飘逸和通透，建筑风格融合土家族和苗族传统艺术语言，体现出古朴大气和原始自然的独特气质，是夜宿武陵源的游人不可不去的休闲之地。\r\n</p>\r\n									'),(1716,264,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在武陵源区乘2、3路公交车前往。</span>\r\n</p>\r\n&nbsp;<br />'),(1717,264,4,'游览须知','<p>\r\n	&nbsp;<span style=\"line-height:1.5;\">溪布街交通十分方便，一般可步行到达，是武陵源区晚间休闲娱乐的最佳之地。</span>\r\n</p>'),(1718,256,1,'历史文化','									<p>\r\n	玉皇洞位于张家界城西约7公里的麻空山南壁，为清代本地乡绅李京开独资捐修。李京开又名叫李五瑞，别名九洞乐，号识破生。他生于乾隆七年（公元1742），道光乙酉年（1825年）去世，享年83岁。李先生家道殷实，富甲一方。少时勤奋读书，颇有才华，数十年奔波仕途，但屡试不第，官场无望，于是看破红尘，另寻安慰，请工匠，耗巨资，凭借麻空山天然溶洞，按上、中、下方位布局，巧妙构思，开凿成“天堂”、“人间”、“地府”三层共八个洞。另外，在离此约3公里的峰泉洞另凿一洞，故有“九乐洞”别名。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpY3U3mhsuAECof63YLCrWwEnRtT\" alt=\"\" />\r\n</p>\r\n<p>\r\n	玉皇洞石窟是一雕塑造像甚多，工艺亦较精美的清代艺术石窟。石窟上层“天堂”为雷电洞；“人间”居中，自东向西依次排列为玉金、毫笔、墨池、虎龙、狮子、孔圣六洞；下怪为因果洞，即所谓“地府”。从“天堂”到“人间”，自“人间”至“地府”，上下三层距离分别为60米和70米，其间由宽不盈尺的曲径于悬崖峭壁上相沟连，气势十分险峻。\r\n</p>\r\n									'),(1719,256,2,'石窟奇观','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuIylv7hbVaDzmxOMArBdF8a-23J\" alt=\"\" />\r\n</p>\r\n<p>\r\n	玉皇洞8个石窟，反映的内容十分丰富，且各具特色，虽杂而不乱。它将石刻、雕塑、文学、书法等艺术溶铸于一炉，是古代劳动人民智慧的结晶。1959年，湖南省政府将玉皇洞正式列为重点文物保护单位。玉皇洞石窟风景区集观光、休闲与宗教活动于一体，景点内相应地开设了珍石文化区（天然石景陈列，考古石器、石雕艺术欣赏）、园林休闲区（画眉园、斗鸡场、重檐亭、水景、密林）、盆花、苗木观赏区，是永定区难得的历史人文景观，是张家界精品旅游景点。\r\n</p>\r\n									'),(1720,256,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">从市区打车往玉皇石窟20元左右。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">乘坐公交车11路在枫香岗乡下。票价3元。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1721,256,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">该景点属于免费景点，该线路为具有一定危险的户外线路，游客切莫孤身前往建议聘请专业向导。</span>\r\n</p>'),(1722,247,1,'观风情园','									<p>\r\n	张家界土家风情园座落在湖南张家界市城区，交通便利。占地80余亩,总投资近亿元,是以湘西人的土司城堡为基础，以最完整的土家山寨旧址原貌为风格，于1997年原址修复后向游客开放，是与张家界自然山水交相辉映的一颗璀璨的文化明珠，是土家人朝拜、祭祀、祈祷和节庆的“圣地”，这里保存着土家族千年奇风异俗，园中的土司城堡、九重天世袭堂、土家山寨汇集了整个大湘西土家人历史的精粹，是集土家族的农耕文化、兵战文化、土司文化、建筑文化、民俗风情为一体的民族文化景点,开园以来接待了以国务院前总理朱镕基为代表的30多位党和国家领导人，中央电视台、省级电视台多次对风情园进行报道，2003年被列入了国家AAAA级景区，现每年接待中外游客60多万人次，构成了“山上观风景、山下看风情”行程精品线路。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fq9vgCeOHXmK5YBYavBvNCi-_Zjj\" alt=\"\" />\r\n</p>\r\n<p>\r\n	土家风情园是原土司城所在地，土司城建于清庸正八年（公元1730年）。土家风情园占地达120余亩，建筑群主要有走马转角楼和吊脚楼民居群两部分。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FmYvOFBWRDnaJEwvHa_hXV4QOxqm\" alt=\"\" />\r\n</p>\r\n<p>\r\n	九重天吊脚楼为全木质结构，飞檐翘角，土家族民居里面的建筑元素在这里都可以找到，这里也是目前国内最大、最壮观的吊脚楼，有民间艺人设计建筑，高48米，九重十二层，层叠有致，从侧面看尤其壮丽。\r\n</p>\r\n									'),(1723,247,2,'民俗风情','									<p>\r\n	民风民俗展示主要有土家婚俗、头饰服饰、蜡染、织锦、银匠、石匠以及奇石根雕，珍贵文物展等，还有整台原汁原味的土家文艺节目，如表现湖南土家原始劳作与生活情形的茅古斯舞、铜铃舞、摆手舞、打溜子等表演。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FslWN7I1OlrmtlST1KnI7dn0p5D8\" alt=\"\" />\r\n</p>\r\n<p>\r\n	这里是集土家族农耕文化、兵战文化、土司历史、建筑艺术、工艺美术、表演艺术、民俗风情及饮食起居为一体的大型民族文化景点，有醉人的土家拦门酒，神秘的土家祭祖活动，土家历史文物展示，土家民俗表演，还有头饰服装、蜡染、织锦、银器、石器以及奇石根雕珍贵文物展等。拥有“九重天”、“千人茅古斯”两项世界吉尼斯之最纪录。\r\n</p>\r\n									'),(1724,247,3,'推荐理由','<p>\r\n	<span style=\"line-height:1.5;\">园内建筑多为木石结构，雕梁画栋，金壁辉煌，飞檐翘角，玲珑雅致。代表作有土司城堡、摆手堂、湖南土家山寨、垕王宫等。特别是冲天楼，依山造阁，气势恢弘，楼高48米，九重十二层，曲径回复，独具匠心。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1725,247,4,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">到张家界土家风情园，可以先坐火车到张家界火车站下，然后市内驱车5分钟到,即市南庄坪五子坡.车费不贵，公交5路、10路、1路可达。包车去最多也不过20元。游客可以选择在离开张家界之前游玩土家风情园，既不用担心赶车或者飞机，又可以给自己在张家界的行程画上一个完美的句号！</span>\r\n</p>'),(1726,247,5,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">票价：成人票120元，儿童1.2米以下免费，70岁以上老人持老年</span><span style=\"line-height:1.5;\">证购老人票。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">开放时间：8:00-17:00</span>\r\n</p>'),(1731,381,1,'历史传说','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FvzpWdxLAdpYIIELCT4ho5iWAS5T\" alt=\"\" />\r\n</p>\r\n<p>\r\n	赵家垭，相传因赵匡胤早年到过此地，与这里山水有过不解之缘而得名。与离县城28公里。暗河溶洞位于赵家垭水库底部，是一个外河流入型的水底大溶洞，洞高30—80m，宽道5—25m，全长1550m，洞穴廊道呈峡谷状态，走向由南向北穿山而过，落差80余米，形成于高山峰丛喀斯特峡谷流域，深切河床渗漏石灰岩断层层里裂隙，向含水层集中循环，再经溶蚀、侵蚀崩塌作用，形成与原河道平行的地下通道，是喀斯特区过境河的伏流河。暗河发育史应该与当地的喀斯特化过程同步，是研究当地峰丛喀斯特发育阶段和形成年代的重要走廊，过境河流在峰丛喀斯特及喀斯特形成与发育过程中的作用与意义，有待科学家作出深入研究。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Ft0EmyJbrzVDZ2f6z27g_WfbYlQD\" alt=\"\" />\r\n</p>\r\n									'),(1732,381,2,'风光美景','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fv5THxyQMGqDN-isyGimgrZVHjbF\" alt=\"\" />\r\n</p>\r\n<p>\r\n	赵家垭水库建成后，载流了这过境河的入口水源，从而把暗河运行轨迹形成的各类奇异岩溶地貌型态全都显露出来，有溶痕、流痕、波痕、窝穴、天锅、槽穴、暗竖井、洞顶穹窿、孔窗、岩底坎、岩边槽、岩角、磨坑、沉积砂砾石等无奇不有，洞壁沉积物有石莲、石须、石幔、石瀑布、石壁流等气势磅礴、洞穴水文景观有水池、水潭、溶泉、流水瀑、滴水瀑等，神秘莫测。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhBeM1esLIqP_ZLkINSx4yJBXTIK\" alt=\"\" />\r\n</p>\r\n									'),(1733,381,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">自驾路线：</span><span style=\"line-height:1.5;\">张家界火车站——永定路——常张高速公路——慈利县——三溶村——终点</span>\r\n</p>'),(1734,381,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">1.开放时间7:30-18：00</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2.注意出行安全，防止发生滑到、摔伤、碰伤等意外事故。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">3.走路要慢，把脚抬高一些，防绊倒。观景时，要停止脚步，看完后再往前走，切莫边看边走。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">相关信息：门票111元。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1747,353,1,'溶洞奇观','<p>\r\n	黄龙洞是张家界武陵源风景名胜中著名的溶洞景点，因享有“世界溶洞奇观”“世界溶洞全能冠军”“中国最美旅游溶洞”等顶级荣誉而名震全球。现已探明洞底总面积10万平方米；洞体共分四层，洞中有洞、洞中有山、山中有洞、洞中有河。经中外地质专家考察认为：黄龙洞规模之大、内容之全、景色之美，包含了溶洞学的所有内容。黄龙洞以其庞大的立体结构洞穴空间、丰富的溶洞景观、水陆兼备的游览观光线路独步天下。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlVwJyql4tHzhDZQqbaThz84uj4p\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	世界自然遗产、世界地质公园、首批国家5a级旅游区张家界武陵源的组成部分。2005年被评选为“中国最美的旅游溶洞”。位于湖南省张家界市核心景区武陵源风景名胜区内，距离张家界市城区及张家界荷花机场、张家界火车站、张家界市中心汽车站36公里，距常张高速阳和出口20公里，有高等级公路张清公路连接张家界市城区和景区，省道s306直达景区，属典型的喀斯特岩溶地貌。1983年，黄龙洞由当地8个青年民兵发现。1984年10月1日正式向社会开放。自开放至今，已接待中外游客1000万人次。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FoVjTY-IkCxe1-S4AALChMJOxNVw\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;摄影：向金次\r\n</p>'),(1748,353,2,'风光美景','																		<p>\r\n	黄龙洞现已探明的洞底总面积10万平方米，全长7.5公里，垂直高度140米。洞体共分四层，整个洞内洞中有洞，洞中有河，石笋、石柱、石钟乳各种洞穴奇观琳琅满目，美不胜收。据专家考证，大约3.8亿年前，黄龙洞地区是一片汪洋大海，沉积了可溶性强的石灰岩和白云岩地层，经过漫长年代开始孕育洞穴，直到6500万年前地壳抬升，出现了干溶洞，然后经岩溶和水流作用，便形成了今日地下奇观。中国地质部70多位专家的考察结论则是：黄龙洞规模之多、内容之全、景色之美几乎包揽了《洞穴学》的全部内容，是世界溶洞的“全能冠军”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhyU6wL_5jP1mFfRQsBtU2_COVlX\" alt=\"\" />\r\n</p>\r\n<p>\r\n	黄龙洞石笋、石柱、石钟乳等各种洞穴奇观遍布，琳琅满目，美不胜收。水陆兼备的游览动线等，构成国内外颇有特色的游览洞穴。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgDft7v0_uW28jtaT0pvJXftaWMb\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">人类文化已进入崇尚王牌、追求极品的时代。被列入世界自然遗产的武陵源风景区，无疑是中华自然风光的一块王牌，而被国家旅游局列为全国35个王牌景点之一的黄龙洞，当之无愧成了这一王牌景区的核心。这颗深藏地下亿年的明珠，伴随着八十年代初张家界的对外开放而得以蛮声海内外。</span><span style=\"line-height:1.5;\"></span> \r\n</p>\r\n									\r\n									'),(1749,353,3,'推荐旅游','<p>\r\n	<span style=\"line-height:1.5;\">黄龙洞以立体的洞穴结构，庞大的洞穴空间，宽阔的龙宫厅及数以万计的石笋，高大的洞穴瀑布，水陆兼备的游览线等优势构成了国内外颇有特色的游览洞穴，洞内有1库、2河、3潭、4瀑、13大厅、98廊，以及几十座山峰，上千个白玉池和近万根石笋。由石灰质溶液凝结而成的石钟乳、石笋、石柱、石花、石幔、石枝、石管、石珍珠、石珊瑚等遍布其中，无所不奇，无奇不有，仿佛一座神奇的地下“魔宫”。黄龙洞现已开放有龙舞厅、响水河、天仙瀑、天柱街、龙宫等6大游览区，主要景观有定海神针、万年雪松、龙王宝座、火箭升空、花果山、天仙瀑布、海螺吹天、双门迎宾、沧海桑田、黄土高坡等100多个。</span>\r\n</p>'),(1750,353,4,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">可在武陵源区乘1路公交车前往。</span>\r\n</p>'),(1751,353,5,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览景区最少需3小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">黄龙洞外为生态广场，洞内为单一游览线，入口与出口不同，可以一路跟随景区导览员游玩。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：100元</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市武陵源区索溪峪镇河口村</span>\r\n</p>'),(1752,344,1,'宝峰概况','<p>\r\n	<span style=\"line-height:1.5;\">宝峰山，又名“宝凤山”，它立于武陵源宝峰湖的源头，背靠宝峰湖，出至百丈峡，与天门山对峙，海拔高1200 多米。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">宝峰山三面悬崖万丈，异常惊险，仅一面有一条山径可上峰顶。在攀爬的过程中，可以领略张家界地貌的地质之美，以及深山的苍莽之感。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1753,344,2,'云霄之上','<p>\r\n	<span style=\"line-height:1.5;\">登至山顶，可</span><span style=\"line-height:1.5;\">以看到一处古刹寺庙“宝峰寺”。寺庙为宋朝所建，清代咸丰年间曾经维修过。解放前，此庙虽小，但是由于许愿很灵，香火很旺，名气很大。寺外一块石板悬空伸出，名叫“舍身岩”，岩石下面万丈深渊，踏上去令人毛骨悚然。舍身岩的缝隙里长有几株铁梅，虽时愈千年，仍傲然挺立。整个庙宇悬立在绝壁之上，倚在残垣断壁中，可以俯瞰周边的山山水水，其中知名的有宝峰湖。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">山不在高，有仙则灵。如今的宝峰山，已经成为张家界旅游爱好者户外活动露营的理想之地。</span>\r\n</p>'),(1754,344,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">公共交通：张家界中心汽车站有市区至武陵源区班车，在协合乡插旗峪下车。乘车或骑行到达插旗峪后，再前进6km 即到达苏家组，从苏家组上宝峰山最便捷。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">自 驾：可直接驾车到宝峰山，约37 分钟，为市区公路+ 省道S306。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">骑 行：市区公路+ 省道S306，全程约27km，路况尚可。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1755,344,4,'游览须知','<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">地\r\n址：张家界市武陵源区</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">门票价格：免费</span>\r\n</p>'),(1756,344,5,'安全提示','<p>\r\n	<span style=\"line-height:1.5;\">遇险求助电话：4006009958（张家界蓝天救援队）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">该线路难度适中，雨雪天不建议上山。山路蜿蜒多岔口，路旁</span><span style=\"line-height:1.5;\">草木茂盛，需有专业资质的户外俱乐部提供专人向导和专业装</span><span style=\"line-height:1.5;\">备，游客切莫孤身前往，注意自身安全。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1757,344,6,'附近旅游','<p>\r\n	<span style=\"line-height:1.5;\">白 虎 堂：白虎堂在武陵源区索溪峪镇，距宝峰山15.8 公里，自驾需33 分钟。这里水质清洌，且石峰林立，谷深涧幽，是户外游泳的绝佳之地。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">宝 峰 寺：宝峰寺门额上刻有“宝峰”、“胜武当”，门两边刻有对联“庙貌更新昭下士，神灵常显护群生”。内有菩萨与香炉，常有善男信女登临朝拜。寺外有一眼泉水，至今长流不绝，其味甘甜，相传这是一股神水，喝了可以替人祛病，永保平安。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1758,337,1,'激流回旋','<p>\r\n	张家界激流回旋漂流位于世界地质公园、世界自然遗产核心景区。武陵源门票站旁，高峡平湖一索溪湖下。重新包装后的激流回旋使整个游程增至1.5小时，集平湖游、参观奥运比赛训练基地、索溪自然漂流于一体，是世界水上活动的代表作，又是张家界国际旅游精品游览线，是您游张家界的必选。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Ft-NhpGI3kZvvwx5UxH1MP5yfz4Z\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">泛舟戏水、尽享自然一平湖游</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">潺潺的金鞭溪水顺流而下，与矿洞溪、楠竹溪、天子溪水汇入索溪。索溪湖两岸奇峰耸立，怪石林立，郁郁葱葱，形成了奇特的高峡平湖风光。漫步湖畔，扑面而来，湛蓝湛蓝的碧水瑶池，晶莹透亮;山在水里，水在山中，人在湖上，相映成趣;身在其中，水天一色，天人合一，如此和谐;怪不得江苏才女苏叶游览索溪风景以后，深情地留下这样的诗句：“相去千万里，心随月色归。来生甘作石，嫁与索溪水”。泛舟湖中，“小桥、流水、人家”尽收眼底，世界一流、国家二级保护动物一娃娃鱼及湖中小鱼，自由翱翔;进入湖里，看流泉，过飞瀑，踩花船，与土家阿妹对山歌;湖中戏水，打水仗，体验土家茅古斯泼水节。一步一景，趣味无穷，世俗凡尘的无限疲倦，在这群山环抱、碧水蓝天之间，荡涤的干干净净，无影无踪……</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fi8TFI6DEZBunCD_tdpM3VFGACT1\" alt=\"\" /></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">浪花飞溅、高潮不断一索溪自然漂流&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">放舟湖下，带给您的是另一番风景、另一种及至。激流险滩，浪花飞溅，飞舟而下，一路欢笑，一路尖叫，快乐无限;激流回旋，冠军瑶篮，激浪在奥运健儿的赛道上，徜徉在冠军的摇篮里，与浪共舞，乐在其中;自然漂流，有惊无险，在诗与画的于地间，在山与水的怀抱里，在粗犷的索溪号子下，让你充分享受激流与回旋带来的无限快感，奥运体验，高潮不断，带给您的是终生难忘的新奇体验。惊险、刺激、安全、方便，是您洗去旅途疲惫，放松、放飞心情的绝妙去处。1.5小时的游程，亲密相伴，使您的张家界之旅更加完美，余味无穷，不在遗憾，看山张家界，玩水激流回旋。</span> \r\n</p>'),(1759,337,2,'玩转激流','<p>\r\n	冠军摇篮一参观奥运比赛场地&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2002年，激流回旋的创始人，国际激流、漂流协会主席西班牙拉蒙博士来到这里，优质的水源、良好的气候、适宜的水温、十分安全，令他兴奋不已，这位西班牙人抑制不住冲动，亲手按国际标准，设计了张家界激流回旋。建成后成为“世界一流、亚洲最佳”的世界比赛场地。2003年，这里成功举办了“五城会”和全国激流回旋比赛，继而成为2008年北京奥运健儿指定比赛基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgnFy_LCSCmgCo_KG2jU88Uo3pIS\" alt=\"\" /></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2004年，张家界激流回旋正式对外开放，陈至立等党和国家领导人来此观察，国家体育总局局长袁伟民参观后称赞道：“奥运健儿摇篮，时尚漂流胜地”。</span>\r\n</p>'),(1760,337,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">1.张家界中心汽车站：乘“张家界市区—武陵源”的线路巴士至终点站。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2.武陵源汽车站：可乘坐1和2路至标志门的公交车至终点站，也可以步行到达。</span>\r\n</p>'),(1761,337,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">1.着装。衣服穿比较薄容易干的衣服或带一套备用衣，鞋子穿带鞋带的不易掉的。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2.身上不要携带任何物品。漂流时身上钱包、手机、钥匙等物品都要放置岸上，不要随身携带。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">3.漂流过程中注意事项。穿好救生衣，系好安全绳，漂流过程中一旦发生落水不要惊慌，漂流水流一般不会很深，另外救生衣的浮力会保证你不会沉入水中，及时让队友把你拉上皮筏即可。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">4.关于夏日防晒。漂流季节一般都比较热，太阳会很毒辣，所以平时晒太阳比较少的你尽量涂上防晒霜， 这样可以避免被晒伤。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：118元。</span>\r\n</p>'),(1762,335,1,'峰林峡谷','									<p>\r\n	“湖南天泉山国家森林公园”即湖南张家界“永定区猪石头国有林场”。2009年12月4日被省政府批准为省级森林公园，2011年4月8日又被国家林业局批准为国家森林公园. 天泉山国家森林公园位于永定区西北部，东与罗水、桥头乡交界，南与温塘镇相连，西与青安坪乡相依，北与桑植县瑞塔埔白族乡接壤。属武陵山脉，海拔平均在800 米左右, 森林蓄积量15万立方米，森林覆盖率达87.4%，它的诞生对保护生物多样性，加快我市西线旅游建设和生态文明建设，促进当地经济发展都将产生不可估量的作用。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fi8JYFiHUXLzggiQ9sjKS6OPb1ha\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	湖南天泉山森林公园属武陵山脉，境内群峰竞秀，沟谷纵横，山坡陡峭，整个地形东西窄、南北长、中间低的狭长地形。天泉山主峰海拔为1264.1米，公园最低海拔为270米。一般海拔在800米左右。地貌类型为中山地貌。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fv1p5jCg7oDhEC_PhSjYafqJXOWP\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	                                                                  穿越龙凤梯田                                                                  摄影：代天胜\r\n</p>\r\n<p>\r\n	园内风景资源独特，景观资源丰富、质量高，天然次生林覆盖率高，环境质量优越，气候宜人，历史文化底蕴深厚，民俗风味浓郁。\r\n</p>\r\n									'),(1763,335,2,'优质资源','																		<p>\r\n	该园自然环境优美，动植物资源丰富，在湖南省森林公园中，特别是高海拔森林公园中，其丰富的动植物资源、众多的保护物种和稀有种属在湖南省境内首屈一指。该园地文景观十分奇特，特别是近20处各类岩石形成的景观，拟人状物，形态逼真。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fm5kwBMLQbN9y15Jz3X1j_nOjLts\" alt=\"\" />\r\n</p>\r\n<p>\r\n	根据《中国森林公园风景资源质量等级评定》公园森林风景资源质量等级为一级。有茅岩河景区、七年寨景区、中山景区、祝融景区、覃垕王园林景区。公园瀑布、溪流多，空气负离子含量高，适合生态旅游。园内旅游资源丰富，旅游商品众多，开发条件良好。\r\n</p>\r\n									\r\n									'),(1764,335,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">建议自驾。</span>\r\n</p>'),(1765,335,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">1.严禁在园区内野外用火。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2.不要孤身前往，注意安全。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">3.园区内路窄弯多，进出车辆须谨慎驾驶。</span>\r\n</p>\r\n<p>\r\n	<strong>相关信息</strong>\r\n</p>\r\n<p>\r\n	地址：张家界永定区猪石头国有林场\r\n</p>'),(1766,333,1,'道教圣地','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FokitaYXLiwjKRmdC6Klvk7b04Km\" alt=\"\" />\r\n</p>\r\n<p>\r\n	五雷山，原名雷岳，海拔一千米，主峰金顶分出数脉，呈辐射状伸延，有如淮南子天文训中的地维，因庙宇出现“雷扫其殿，钟鼓自鸣，尘埃自净”的奇迹，故更名为五雷山。山上气候宜人，环境优美，植被茂密，沟壑纵横，空气清新，自然景观独特，是有名的旅游避暑胜地。同时五雷山更是著名的道教圣地，素有“楚南第一胜境”之称。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Flcq8dvCLeRn62-X6UEH3XjTW4r2\" alt=\"\" />\r\n</p>\r\n<p>\r\n	它与湖北武当山齐名，俗话说“北武当，南五雷”，二者如兄弟联袂，名闻遐迩，被尊为“中国南武当”。其道教殿宇之多被《三湘之最》誉为湖南最大的道教文化群落。据史书记载，五雷山道教“始于唐、盛于明”。相传西域净乐国太子曾选中此地，垒石室苦修，“得道高升”，这就是著名的真武帝君。嗣后，唐代李靖慕名上山草创道观。元末翰林国史编修张兑辞官不做，归隐五雷山，在五雷山扩修殿宇，弘扬道教文化，并亲题“楚南名山推第一”，从此五雷山名声大振，所建殿宇“旁魄百里，列县俱瞻”。 到了明代，常德荣定王、澧州华阳王对五雷山进行大规模扩修改建，建筑面积达5000余平方米，有36宫，72殿。其建筑为石墙铁瓦构筑，随山脊沟壑纵横陈列，绵延15华里，奇险深幽，玄妙超然，独具一格，蜚声南北。明神宗得知后，封五雷山为“洞天福地”，道教信徒遍及鄂西南、湘西北两省十八县。\r\n</p>\r\n									'),(1767,333,2,'民俗风情','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpZSNuQ-6rOHqZ6aNkp-NOKFv86x\" alt=\"\" />\r\n</p>\r\n<p>\r\n	每年古历“三月三”（7天）、“八月十五”（15天）、“九月初九”为朝圣节，信徒昼夜朝拜，人山人海，锣鼓喧天，炮声如雷，热闹非凡，形成了五雷山盛大的传统庙会，有“朝不朝，一年十万八千到”的壮观景象。时至今日，三大传统庙会代代传承，年年举行。 \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fu0C8KlF-BH2j685EfzLHp-sVDzV\" alt=\"\" />\r\n</p>\r\n<p>\r\n	五雷仙山神秘、神奇、神圣，古朴而幽深，空灵而厚重，它是大湘西东大门户一颗璀璨的明珠。\r\n</p>\r\n									'),(1768,333,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在张家界慈利县建议自驾车前往；</span>\r\n</p>'),(1769,333,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览景区最少需4小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">可参照景区地图及标识牌，沿景区游道上山，欣赏沿途美景。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">相关信息：门票68元（单程游览观光车15元，不包含在门票中）。</span>\r\n</p>'),(1770,331,1,'娄江漂流','									<p>\r\n	澧水河有两条最大的支流。一条是茅岩河支流，一条是有着中国三大古道之称的排道支流－溇江！这里是湘西近现代史上最风云的一条河道。在当地还流传一句话“了解溇江，就了解湘西”之说！我们中国有三条最出名的古道，这三条古道在不同历史时期传承着中国的物质文明和精神文明！第一条古道：丝绸之路（由中原到西域）；第二条古道：茶马古道（由江苏、湘川、云贵到西藏或东南亚）；第三条古道：排道（由湘西八大公山、雪峰山、经溇江、入洞庭、出长江）。湘西石氏家族掌握湘西溇江排运的经济命运长达四百年的时间，传承15代18代扛把子。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fm1P5fesB2dpHkrq5P3eHFEYW88g\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	溇江漂流，地处张家市江垭镇，距核心景区武陵源仅30多公里（车程40分钟），交通便利。公司现有漂流船只120余条，可一次性接待1000人的大型团队，随到随漂，便于您的安排行程。随着上游水量调节坝的建成，游客将在娄江享受到更为惊险、刺激的漂流乐趣。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjN-9LI4t6xj-ycSdB_2olllvuPw\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	在土家族历史上，深受后人爱戴的土家族首领便是元末明初的向王天子向大坤，着名景区天子山便因向王天子在那儿率众起义而得名。今天，在我们溇江河，也有一处因向王天子而命名的景点：天子妃子洗衣滩。漂流的第一个险滩边据说曾是当年向王天子的妃子们为征战的英勇男儿捶洗衣服的地方。现在还有很多土家妇女、土家姑娘在这里洗衣服哩。\r\n</p>\r\n									'),(1771,331,2,'历史传说','<p>\r\n	<span style=\"line-height:1.5;\">漂过一个险滩之后，河流的左岸是一堵石壁，呈弧形，上部延伸出来的地方有一根细长细长的藤蔓垂挂着，传说这是姜子牙当年垂钓的地方，我们叫它姜太公钓鱼台。</span>\r\n</p>\r\n<p>\r\n	来到这里以后，河道千回百转，但见两岸青山叠翠，山的走势蜿蜒曲折，抬头向上望去，四周的青山便形成了一个标准的圆形井沿，身在其中，坐井观天的感觉很强烈，真是山外有山，天外有天呀。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fpl8ea1s7pTczGa737HzBqi6kXz4\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	这不仅是一段移动的风景，更是一段流动的历史，从东汉“娄中蛮”，到唐朝兴国寺，到宋代索口寨，到明朝九溪卫，历史用大青石砌成厚厚的册页，在这里作着长久的陈列。“南北大侠”杜心五就从此地出山，从名重一时的“孙中山保镖”到技绝武林的“自然门大师”，都为悠悠娄江留下了一部永远无法复制的传奇。\r\n</p>'),(1772,331,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">建议自驾车。</span>\r\n</p>'),(1773,331,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">娄江漂流，时间约2小时，位于武陵源区至慈利县的游览东线上，尚无旅游线路巴士，建议自驾车前往或在市区选择相关1日游。</span>\r\n</p>\r\n<p>\r\n	<strong>相关信息</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：148元</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市慈利县江垭镇</span>\r\n</p>'),(1774,321,1,'风光美景','<p>\r\n	<span style=\"line-height:1.5;\">茅岩河平湖游位于茅岩河上段，起点位于九天洞下的苦竹河，经青安坪，然后到终点站鱼潭电站。河床平均宽30—50 米，水深5-7 米，清澈见底。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">乘船赏两岸风光，沿途绝壁雄踞、怪石重叠，有猴子捞月、血门沟、印花墙、晾衣篙等景点，土家英雄覃垕王的故事贯穿在整个游览途中，游览时间约为1 小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">茅岩河平湖游，不是三峡胜似三峡。千百年来，这里是一处神秘的禁地，岁月如尘，轻掩着一部古老的传说。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1775,321,2,'交通路线','<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">公共交通：桑植县汽车站乘至九天洞客运专线车，在九天洞购买平湖游门票后乘船游览</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">自 驾：可沿省道S306、县道X019 至茅岩河平湖游起点</span>\r\n</p>'),(1776,321,3,'游览须知','<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">距\r\n离：距武陵源核心景区约</span><span style=\"line-height:1.5;\">65 </span><span style=\"line-height:1.5;\">公里，距市区约</span><span style=\"line-height:1.5;\">35 </span><span style=\"line-height:1.5;\">公里</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">地 址：张家界市桑植县苦竹河</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">门票价格：78 元</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">电 话：0744-6237279（桑植县旅游局）</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1777,302,1,'漂流奇观','<p>\r\n	<span style=\"line-height:1.5;\">茅岩河是澧水上游50多公里长的一段河。茅岩河是一幅多彩多姿、生动活泼的水国画，是一帧竞奇斗艳、婀娜多娇的青山图。清代光绪《永定县乡士志》载曰：“澧自苦竹河入境，重滩叠濑，其著者八十有奇，浪头河大多奇险”。</span>\r\n</p>\r\n<p>\r\n	澧水在湖南湘、资、沅、澧四大水系中，排名老幺，而性情十分刚烈，凶猛异常。吞噬生命，扫荡原野，曾使多少人家破人亡。因而人们一度谈澧色变，称它为“魔水”、“鬼河”。特别是当它发疯时，人们几乎用尽了一切诅咒的字眼咒骂它，渐渐地忘却了它的美丽。直到茅岩河漂流的兴起，澧水美丽的面孔才重新走进人们的视野。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtcXc1gDyV7Rkubd0TUV5BZqU9oL\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;摄影：代天胜\r\n</p>\r\n<p>\r\n	从湖北发源的澧水，在连绵不断的山峡里喧泄奔腾，到永定区温塘镇后，在较为开阔的盆谷里，绕着一架青山盘缠歇息。造物主就是这么奇妙，澧水长途奔泄的余威还未荡平，河两岸的群山突然收缩，山哥山弟齐心协力拼死挤压澧水，不甘被挤扁的澧水，在山峡里，同巍巍大山展开了搏斗，于是造就了茅岩河之奇观。\r\n</p>'),(1778,302,2,'风光美景','																		<p>\r\n	河水更为凶悍强劲，昂头撤蹄，湍湍东去。一滩连一滩，一浪挽一浪。百里河流百里滩，百里流水百里浪。茅岩河水无情地撞击着河中根基深厚的礁石，尽管撞击得头破血流，断肠裂肺，粉身碎骨，依然无怨无悔，从不停歇地演绎着生命的壮观。被撕碎的生命化成点点银珠抛上天空，继而又落回永不消逝的躯体上，织就了一幅生生不息的王母娘娘洒降甘露图。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkTyUMHcY-cJxfsFQH322550yldM\" alt=\"\" />\r\n</p>\r\n<p>\r\n	而被河水冲撞了千百年仍然傲然屹立的礁石，撕破了河水刚刚要向人展示玉脂般的肌肤，又被汹涌而至的河水淹没，再次击起满天银花。前进与阻挡相搏发生的巨大轰鸣，在河谷里一次又一次的回响，在群山中一次又一次地震荡。若乘坐皮筏或小舟在河上漂流，一种无以言状的享受游走在每根神经。人时而被软若无骨的河水抛上浪尖，时而又被它牵扯着跌入峰谷。会不由自主地发出享受大自然赋予的刺激的无险惊叫。尖叫声与浪涛声汇成了一支欢乐的乐章。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fg0WiuZcHKOAiXc96Prnux2457yQ\" alt=\"\" />\r\n</p>\r\n<p>\r\n	茅岩河漂流这是继张家界国家森林公园开放以后开辟的又一个旅游新景区，坐落在市区以西35公里处。1986年，茅岩河在全国首创橡皮舟漂流旅游项目。茅岩河全长50多公里，两岸全是悬崖峭壁，河段多险滩、急流、瀑布、古木，有\"百里画廊\"之美誉。乘橡皮舟顺水而下，时而急，惊心动魄，时而缓，优哉游哉。一路赏夹岸奇山异峰，又可领略水上激情，二者可兼得，其中茅岩瀑布和温泉最为壮观和最具特色。\r\n</p>\r\n									\r\n									'),(1779,302,3,'推荐理由','<p>\r\n	茅岩河是历史的河、在茅岩河的起点──温塘的议事坡下曾掘出汉代古墓，我们的先人在汉代时，就享受着茅岩河的恩赐，在这美丽的山水间勤劳耕作，繁衍生息。创造了一幕幕历史的文明。土家族英雄覃后为反抗元、明封建王朝的残酷剥削和压迫，率领土家儿女在此浴血奋战，留下了许多可歌可泣的故事和覃后王古堡，覃后洞，七年寨等许多供人凭吊的古迹。&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">茅岩河流淌着历史，呤唱着历史。温塘至今古风依旧，土家吊脚楼鳞次栉比，古渡舰公依然斗笠蓑衣，呤唱着古老的船歌接送南来北往的客人。电影《边城》翠翠家的木楼，屋后的古渡摄拍于此。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">茅岩河是澧水的上游一段，这里有橡皮舟漂流的旅游项目，游人乘橡皮船顺水漂流，既可以欣赏美丽的峡谷风光，又可领略到急流险滩的惊险与刺激。茅岩河的漂流段在张家界城西20公里青鱼潭往北的一段，这里虽然滩险流急，但河床坡度小，水位落差不大，险滩一般仅有20余米长，而且险滩与险滩间的距离又很长，所以在这里漂流是有惊无险的。　　&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">茅岩河的主要景点有茅岩滩、温塘温泉、麻阳古渡、茅岩河峡、水洞子瀑布等。河的两岸青山绿水，风光如画，故又称“百里画廊”。</span>\r\n</p>'),(1780,302,4,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">市中心汽车站乘“市区-温塘”线路车前往茅岩河景区，建议自驾车。</span>\r\n</p>'),(1781,302,5,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">平湖游约2小时，漂流约3小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">茅岩河平湖游与漂流，位于市区至桑植县的游览西线上，尚无旅游线路巴士，建议自驾车前往或在市区选择相关1日游。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>相关信息</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：A. 78元（平湖游） B.160元（漂流）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界桑植县苦竹寨（平湖游起点）/温塘镇热水坑（漂流起点）</span>\r\n</p>'),(1782,298,1,'空中圣湖','<p>\r\n	<span style=\"line-height:1.5;\">宝峰湖主要景点由宝峰湖和鹰窝寨两大块组成。其中宝峰湖、奇峰飞瀑、鹰窝寨、一线天被称为武陵源“四绝”。是武陵源风景名胜中的精品景点。</span>\r\n</p>\r\n<p>\r\n	它是一座罕见的高峡平湖，四面青山，一泓碧水，风光旖旎，是山水风景杰作。宝峰湖水深72米，长2.5米公里，以其秀丽的湖光水色与幽野的洞天情趣成为武陵源水景风光的代表之作，电视剧《西游记》中花果山水帘洞外景就拍摄于“奇峰飞瀑”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Flfp8oTOS6qnnJPgJKb85yDKKTWI\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	湖中有两座叠翠小岛，近岸奇峰屹立，峰回水转。泛舟漫游，只见一湖绿水半湖倒影，充满诗情画意。“云梯万丈上天台，高峡平湖一鉴开，王母瑶池金扶，浣沙仙女下凡来”游宝峰湖，你就会欣赏到这样的诗情画意。群峰拥抱的宝峰湖，长约2.,5公里，湖犹如一面宝镜，四面青山，一泓碧水，荡桨温游，格外惬意。坐在船上，环顾四周，千山耸翠，俯视水中，倒影慢移，碧水照相馆得群峰绿，人面桃花水映红。真是静极了，美极了！\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fh41JxerkxSa6TE2_vaHPkTIDSiP\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	神秘的宝峰寺\r\n</p>\r\n<p>\r\n	韩剧《九天洞》拍摄的湖水就是宝峰湖。在湖中漫游，还可以见到湖心岛上的一些佳景，如“仙女照镜”、“高峡平湖”、“金蟾含月”也各有特色。鹰窝寨为宝峰公园一景，进公园后西向登山数百石级，头顶的石峰裂缝如线，入口处有古城门雉堞，尽处有宝峰古寺，香火旺盛。崖上石径直上峰顶，相传旧社会有匪首如鹰盘踞山顶小寨。一线天是宝峰公园的一大绝景。峡谷长200余米，高100余米，平均宽度不足两米，中有小溪，溪畔石级盘旋而上，清幽无比，曲奥无穷。该游览线还有一系列景点，著名的鹰窝寨位于宝峰湖西南一绝壁之上。早年是著名的土匪寨子，电视剧《乌龙山剿匪记》就拍摄于此。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1783,298,2,'风光美景','									<p>\r\n	地质与地貌：武陵源景区内的巨厚的石英砂岩，产状平缓，使岩层不能沿层面薄弱部位滑塌，覆盖在志留系柔性的页岩之上。重力作用，使得刚性的石英砂岩垂直节理发育，在水流强烈的侵蚀作用下，岩层不但解体、崩塌，流水搬运，残留在原地的便形成雄、奇、险、秀、幽、旷等千奇百怪的峰林，是武陵源风景区的主体。流水侵蚀地貌，在这里一个突出的表现是由于地壳上升，溪流向下切割作用加大，来不及将河流拓宽，而使河谷形成隘谷、峡谷。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtaBh9VNAGHWCKbl04Jp89QUZTFE\" alt=\"\" />\r\n</p>\r\n<p>\r\n	河的谷底极窜成线形，两壁陡峻。咱们宝峰湖就是这种地貌。当你泛舟湖上，湖水那纤尘不染、婀娜多姿，悠然自得的特点使你感觉超然于世，进入忘我的境界。两岸陡峻秀丽的景色，又使你留恋人生，感叹造物主的神奇。身边猛然响起阿哥阿妹动情的对唱，又会把你带回湖上，让你感受到这人世间最纯朴的真爱。这就是为什么众多的年青人，也有不少中老年人，到了张家界，非到宝峰湖来不可，真谛，也许就在这里。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkpPziHLA_4DLqrQ_m23OHVVUQg6\" alt=\"\" />\r\n</p>\r\n<p>\r\n	宝峰湖是世界自然遗产、世界地质公园、首批国家5A级旅游区组成部分。湖中有两座叠翠小岛，群山拥抱，奇峰屹立，一湖绿水碧波，充满诗情画意。泛舟湖上环顾四周，千山耸翠，水中倒影慢移，此一既静且美之绝景，着实他处难寻。在湖中漫游，还可以偶然间邂逅俊俏淳朴的土家阿哥、阿妹的原生态山野民歌对唱，让人远离尘世喧嚣，回归宁静自然。\r\n</p>\r\n									'),(1784,298,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在武陵源区，乘2路公交车前往。</span>\r\n</p>'),(1785,298,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览景区，最少需2小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">宝峰湖为环形游览线，进入景区后，一般沿右手方向开始进行游览。</span>\r\n</p>'),(1786,290,1,'门洞奇观','<p>\r\n	张家界天门山国家森林公园有着深厚的文化底蕴，有着\"武陵之魂\"之美誉，更有湘西第一神山的美誉，它兼峰、石、泉、溪、云、林于一体，集雄、奇、秀、险、幽于一身，被誉为空中原始花园。我们乘坐天门山索道，前往游览天门山：观天门洞、99道弯公路奇观（通天大道）、鬼谷栈道、鬼谷兵盘、云梦仙顶、天门山寺、灵泉院、木石之恋等著名景点。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpmV25i36h7mbDZmHN8wH-uPiHw8\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	因“世界海拔最高的天然穿山溶洞”【天门洞】而得名，惊险刺激的天门山特技飞行表演就曾多次在这里举行。可搭乘“世界上最长的高山观光索道”【天门索道】，从市中心直达山顶，让人得享“一步达天界”的惊奇乐趣。天门山顶自然环境原生态，被人称为【空中花园】。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjmUuum-CtrNR8c1loDsXo7Lh3AH\" alt=\"\" /><span style=\"line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp;图片由天门山提供/摄影李纲</span>\r\n</p>'),(1787,290,2,'风光美景','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuCBrX9OIDGhEf5cFIKfaPLw6Idv\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;图片由天门山提供/摄影李东群</span>\r\n</p>\r\n<p>\r\n	待到雨前雨后，悬于鬼谷洞上侧，立于峭壁之上的【鬼谷栈道】，雾气盘绕，山间云海翻腾，抬腿似生雾，迈步如踏云，仿佛人间天界。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqaA1osYPmIOIX3-8qtxNYQ6lKgr\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	除自然美景之外，全长10.77公里的天门山“盘山公路”，也同样世界知名。盘山公路平均每米造价为1万人民币，号称中国公路最高，海拔高度有200米一路提升至1300米，期间180度发夹弯多达99处，符合“天有九重，云有九霄”之意。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fj-30vSa3HKDop4i-9w0pAaWVjtF\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	天门山寺是张家界天门山的新传奇，2009年6月8日，佛舍利子安家天门寺，此枚释迦牟尼佛舍利不仅是湖南省首枚，也是中国目前仅有的五枚之一。寺庙建筑古雅清幽，奇巧恢弘，视野开阔，极目东南，众山皆小，确有一山独尊的气概，是湘西的佛教中心。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fh-BgiYy-HNDJW1-GjinMeUfvmNK\" alt=\"\" />\r\n</p>\r\n<p>\r\n	每年的3月19日、6月19日、9月19日被张家界人定位观音娘娘的生辰，每年的这个时候张家界人就会一起上天门山的天门山寺祈祷，有信仰的您如果也真好是这天来到天门就一定要和大家一起祈祷哟。 &nbsp;\r\n</p>'),(1788,290,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在市区可乘5、6、10路等公交至市区天门山索道下站，乘缆车至天门山国家森林公园。</span>\r\n</p>'),(1789,290,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览景区至少需5小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">在市区可乘5、6、10路等公交至天门山索道下站，乘缆车至索道上站，游览山顶。山顶为环形游览线，分东、西两线。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">游完山顶，乘缆车至索道中站，转乘景区环保车至天门洞游览。随后，乘车返回索道中站，乘缆车下山。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>相关信息</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：258元</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市区官黎坪天门山索道下站（纬地酒店对面）</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1790,280,1,'田园之韵','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fhxy2aoM8G_NZPidH8z6aMs6QJCb\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                                                                                                                     摄影：代天胜\r\n</p>\r\n<p>\r\n	龙凤梯田始建于春秋战国时期，形成至今已有上千年的历史，是龙凤村历代先民的劳动结晶，是稻作文化的历史遗存，是人类物质文化发展的见证者，是一块躲在深山里的旅游胜地。这里山地连绵，层峦叠嶂，阡陌纵横，气势磅礴，从高处远望，万亩梯田，层层相间，梯田线条变幻无穷，有的舒缓流畅，有的蜿蜒曲折，梯田随季节变化，呈万千气象。眺望那山、那云海、那梯田、那山寨一应俱全，与日月共辉。不仅是观赏和拍摄日出、日落、云海、梯田最佳的地方，还是具有海中之寨的绝美仙境。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpXEo1_KhZ9u5jZA2YjwGj4xdEd7\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                                                                                                                     摄影：代天胜\r\n</p>\r\n<p>\r\n	龙凤梯田位于张家界市西北部，属永定区罗水乡管辖，与天泉山国家森林公园背靠着背紧紧相连。上亿万年前，在云贵高原的强烈造山运动作用下与武陵山脉相撞击而形成龙凤梯田原型、龙凤山脉。其梯田规模宏大、历史悠久，相传为明初土家族农民起义军领袖覃垕率领数万大军开垦而来，距今已七百多年了，堪舆广西龙胜的龙脊梯田媲美。\r\n</p>\r\n									'),(1791,280,2,'最美田园','									<p style=\"text-align:center;\">\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fle6vF7EUkF8rFVHaeKwYRu0lzH9\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                                                                                                                    摄影：代天胜\r\n</p>\r\n<p>\r\n	去龙凤村观梯田美景的最佳时节应是春耕与秋收两季，站在天泉山的抱儿岩垭口可以俯瞰龙凤村全貌，为最佳观景之处。龙凤村至今还算是个比较原始的村落，修了一条不足4米宽的毛坯土公路，若要留宿山上欣赏日出日落美景，可以住在老百姓的农家乐，也可以自带帐篷露营，不过，山上可供扎帐露营的地方还真不少，大大小小的自然草坪有好几十处，水源洁净、充沛又方便，任由你挑选。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkPz1j_EOOlzNfOED3yaHntPAHIi\" alt=\"\" />\r\n</p>\r\n<p>\r\n	龙凤山下的沟谷中就是永定区特色文化乡镇--罗水乡政府所在地，每年的古历\"六月六\"（覃垕晒皮）都会在乡政府边上一个名叫南梁寨的村子里，举办一次有中国戏剧“活化石”之称的土家族茅古斯舞风情表演，以纪念农民起义军领袖覃垕，游客可以随时参加六月六活动。\r\n</p>\r\n									'),(1792,280,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">1.从张家界出发：张家界市内—三家馆乡—温塘镇—天泉山公园—龙凤村—罗水乡（或桥头乡）--教子垭镇—尹家溪镇—市内。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">2.从桑植出发：桑植县城、老火电厂后面出发，经过老牙槽、永定青安平五家湾、天泉山国家森林公园直达关门岩\"龙凤梯田\" 观景台。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1793,280,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">建议自驾游，全程来回130公里，2个小时。</span>\r\n</p>'),(1798,269,1,'最美峡谷','																		<p>\r\n	金鞭溪 是张家界的黄金旅游区，全长5700米，溪水发源于土地垭，从张家界林场场部到水绕四门，蜿转曲折，幽静异常，随山而移，穿行在峰峦幽谷云间，迤逦延伸于鸟语花香之中。因溪水流经金鞭岩而得名。金鞭溪全长6.5公里，自西向东贯通森林公园，两岸奇峰屏列，风光如画，嬉戏的鸟兽、古奇的树木、悠然的游鱼、景色显得异常幽静。金鞭溪所经之地被誉为“世界上最美丽的峡谷”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkU8uu9cTRowMzpmGtTCLIbqkrt_\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">                                                                                                                                                     摄影：汤东涛</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">主要景致有醉罗汉、神鹰护鞭、金鞭岩、花果山、水帘洞、劈山救母、千里相会、楠木坪、水绕四门等。其中，金鞭岩拔地而起380多米，整块岩石为石英砂构成，在阳光的照耀下反射出熠熠金光，使得这根“石鞭”被冠以“金”的定语。金鞭岩身边一山峰状若护雏的老鹰，威风凛凛，日夜守护着“金鞭”，故而被称为神鹰，这片景得名“神鹰护鞭”。</span>\r\n</p>\r\n									\r\n									'),(1799,269,2,'风光美景','																		<p>\r\n	一块巨大的岩石从当中裂开，取名为“沉香救母”。这缘于一个古老的传说：一个叫沉香的孩子，他的母亲因触犯天条被压在了山脚。小沉香为救母亲，苦练本领，最终用利斧劈开山峰，救出了母亲。“花果山”、“水廉洞”是美猴王孙悟空的天下，在《西游记》里早已为人们所熟悉。总之，这一段景色犹如世外桃源，不能不令人流连忘返。水绕四门为金鞭溪与矿洞溪、龙尾溪汇合处，溪谷宽阔，御案峰奇秀迷人，万岁牌传说神秘。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fh83lD10qw5V1FK4NiCS6d76YUe4\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                                                                                                                     摄影：韦伟群\r\n</p>\r\n<p>\r\n	金鞭溪穿行在峰峦幽谷间，把张家界的山水发挥到了淋漓尽致，溪水明净，跌宕多姿，小鱼游弋其中。溪畔花草鲜美，鸟鸣莺啼，人沿清溪行，胜似画中游。不仅有“千年长旱不断流，万年连雨水碧青”这样的美誉；著名文学家沈从文先生赞誉它是“张家界的少女”，著名画家吴冠中先生曾赞美她是“一片充满着童话般的世界”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fv3fxxiQTcsXxR9uGoiFTUsK8adR\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                                                                                                                     摄影：宋志光\r\n</p>\r\n<p>\r\n	金鞭溪自然环境原生态，动植物种类多样，主要分布常绿和落叶阔叶林，而主要树种为楠木，更有国家一级保护植物，珙桐和水杉。金鞭溪沿溪遍布着一种形状怪异的花，它的形状特别像龙虾，故名龙虾花。龙虾花是一种珍贵的草本植物，大小不同，颜色各异，有红色、蓝色、黄色等。溪里有一种“娃娃鱼”，世界上现存最大的也是最珍贵的两栖动物，属国家二级保护动物。\r\n</p>\r\n									\r\n									'),(1800,269,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往森林公园的线路巴士。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">森林公园门票站进入：从老磨湾广场步行进入金鞭溪。</span>\r\n</p>'),(1801,269,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览金鞭溪全程，时间最少需3小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">金鞭溪属于山下游览线，1日游推荐线路为：市区-森林公园门票站-徒步金鞭溪-水绕四门-景区环保车-十里画廊-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需6小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>相关信息</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：已含在武陵源核心景区门票内（票价248元/4天有效）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市武陵源区金鞭溪风景区</span>\r\n</p>'),(1802,259,1,'石寨遗址','<p>\r\n	<span style=\"line-height:1.5;\">鹞子寨位于武陵源核心景区东南面，海拔1050米，相传明清和民国年间，这里还是当地百姓躲避兵匪的地方，现仍存有石寨遗址，据说鹞子都难以飞过，山形又如同一只展翅欲飞的鹞子，所以取名叫鹞子寨。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">鹞子寨距森林公园门票站仅200米，是武陵源核心景区内面积最大，线路最长，景点最多的游览区。以险、奇、雄、野而著称，是户外徒步的最佳之地，需要充沛的体力。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fh4Rz1EhWfWrfcmpFxk4p-7FQfse\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">鹞子寨的入口是张家界国家森林公园大氧吧广场。往鹞子寨方向的游人都是性情中人，或只身前往，或三五成群。有的时候，群山之中，甚至只有你一个人。但是对于户外探险爱好者来说，这无疑是一个优势，远离拥挤的人潮、鼎沸的人声，近距离倾听自然的脉动。</span>\r\n</p>'),(1803,259,2,'风光美景','<p>\r\n	<span style=\"line-height:1.5;\">从入口到百应谷，是一段平路，分为梅花路、军乐路、脚印路三段，每一段都有自己独特的符号。军乐路上是一个个约一米长的琴键，或高或低，如音符般点缀在路旁。原生态的游道两旁，丛林茂密，树木擎天，即便是艳阳高照的夏日，也只有几处阳光细线般洒落。沿途的路标，在张家界的所有景点里也是一个独创，它不仅标有景点名，还有海拔高程。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">如果够幸运，你可以看到黄鼠狼、锦鸡等野生动物穿梭在丛林中。记者去的时候，正值初秋，有幸领略到了“万绿丛中一点红”的佳景。若是深秋，整个鹞子寨，一层红艳，一层深绿，层层叠叠，相间而生，那景色，岂一个“美”字了得？</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FoOlpGhPmw9U8dua3nVNHNFQKxdu\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">鹞子寨之“险”，让您领略无限风光在众峰，体验胆颤心惊、回味无穷的惊险经历，“凌空惊魂”等景点，将是您人生旅途难以忘怀的亮点。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">鹞子寨之“奇”，让您感叹造物主的生气和伟大，惟妙惟肖的“梭镖峰”，能上能下的“天桥”等景点，为您增添新的阅历和记载。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">鹞子寨之“雄”，让您饱尝一览众山小豪迈，“万笋争春”，“将军阅兵”等景点，带您体验万里江山尽收眼底的霸气。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">鹞子寨之“野”，让您远离城市的喧嚣，清泉石上流，山花四季香，猴戏鸟语蝉鸣， “花香溪”、“万花争妍”等景点，给您徒步的享乐，花草的芬香，天簌的声音。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgZ-DlmgGaw70nrntxLAg4rwim16\" alt=\"\" />\r\n</p>\r\n<p>\r\n	从百应谷到寨顶，约莫20分钟。天晴的时候，寨顶偶尔可见老鹰掠过的身影，那就是鹞子。到了寨顶再顺着环寨游道绕一圈要30来分钟，沿途可以看到老鹰嘴、梭镖岩、指点江山、凌空惊魂等景点。每一处景点的石碑，都是由中国人民解放军将军们亲笔题写，其中“鹞子寨”三字就是上将张万年亲自题写。\r\n</p>'),(1804,259,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往森林公园的线路巴士。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">森林公园门票站进入：步行200米至鹞子寨山门。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1805,259,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览鹞子寨，时间最少需4小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">因鹞子寨为独立的山峰，游览时间较长，1日游推荐线路为：市区-森林公园门票站-鹞子寨-骆驼峰-进入金鞭溪-水绕四门-景区环保车-十里画廊-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需8小时。</span>\r\n</p>\r\n<p>\r\n	<strong>相关信息</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：已含在武陵源核心景区门票内（票价248元/3天有效）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市武陵源区鹞子寨风景区</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n<p>\r\n	<br />\r\n</p>'),(1810,243,1,'峰林奇观','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FplbF2wnJG5_nhyWWTsbQ9Qxowhp\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;摄影者：文绍军\r\n</p>\r\n<p>\r\n	天子山位于武陵源核心景区东北面，海拔1200多米，景区内石峰林立，沟壑纵横，被誉为“扩大的盆景，缩小的仙境”。相传，土家民族英雄“向王天子”，带领土家族人反对朝廷暴政。在此与前来围剿的官兵殊死搏斗，兵败后宁死不屈，跳下了神堂湾。人们为了念这位土家族英雄，而取名“天子山”。即使后来清王朝采用各种压抑措施。强令取消“天子”二字。但土家族人民却一直称“天子山”。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FoWnbWzwdTSWhBZkSnrmJpW7NloF\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;摄影者：赵敏\r\n</p>\r\n<p>\r\n	因景区内峰林耸立如海，故被游人赞为“峰林之王”。那些遗世而独立的石峰，屹立在岁月的长河中，以至于每一座孤峰都拥有着生命，向您无言地倾诉着他们积攒下的无数故事。不会腾云的我们，只能在路可以相连的山壁上行走。而那些雄壮的山体，也许再过十万载，又演变成了新的峰林。\r\n</p>'),(1811,243,2,'风光美景','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fq2_UxpU8oB4qX7MBQE4dOvC0jEQ\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	                                                                                                                                 摄影者：孙建华\r\n</p>\r\n<p>\r\n	天子山还有“云雾、月夜、霞日、冬雪”四大奇观。天子山最多见的云雾奇观，景象万千，有云雾、云海、云涛、云瀑、云彩等。久雨初晴之后或霏霏细雨之中，先是朦胧大雾，继而形成白云，环绕飘浮。群峰在无边无际的云海中时隐时现，使人仿佛置身于蓬莱仙境之中。霞日，晴天的早晨，一轮红日在朵朵红云的衬托下，从奇山异峰中冉冉升起。千万座石塔，婷婷玉立，披上一层红霞，十分迷人。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkwsYEMrrY-C-ndeYwWAo8NR-KJL\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	                                                                                                                                      摄影者：孙建华\r\n</p>\r\n<p>\r\n	冬雪，入冬，天子山银装素裹，青松和峰顶被大雪覆盖。此时的天子山冰柱倒立，冰花四散，只有一些山鹿、野猪，才不为银白世界所征服。\r\n</p>\r\n									'),(1812,243,3,'交通路线','									<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往武陵源的线路巴士。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">武陵源吴家峪门票站进入：乘景区环保车至天子山索道下站，乘缆车上山，或从卧龙岭步行上山。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">或者从杨家界索道、百龙天梯上山后，乘景区环保车前往。</span>\r\n</p>\r\n									'),(1813,243,4,'游览须知','									<p>\r\n	<span style=\"line-height:1.5;\">武陵源核心景区游览路线可简要形容为山下游览路线、山上游览路线；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">天子山属于山上游览线，可作为游览的起点，1日游推荐线路为：武陵源区-吴家峪门票站-景区环保车-天子山索道上山-天子山-景区环保车-杨家界-景区环保车-袁家界-百龙天梯下山-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需7小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">天子山常规游览多在贺龙公园游览区（时间约2小时）。如果时间允许，体力充沛，可选择去茶盘塔游览区（时间约1.5小时）、老屋场游览区游览（时间约3小时）。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">老屋场云海是最直接推荐的拍摄地，云雾缭绕，仙气逼人。从丁香榕路口至老屋场，尚有4公里余山路，可乘当地面包车、三轮车前往。若是选择徒步，要考虑此道是一路下坡，路上布满石子，晴天三尺土，雨天一身泥。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>相关信息</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：已含在武陵源核心景区门票内（票价248元/3天有效）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市武陵源区天子山风景区</span>\r\n</p>\r\n									'),(1814,242,1,'凌空观景','									<p>\r\n	俗话说“不上黄石寨，枉到张家界”。黄石寨，位于张家界市的武陵源，是国家5条精品游览线之一。相传汉朝张良，看破红尘，辞官不做，隐居江湖。在云游这里时，被官兵围困。后来得师父黄石公的帮助脱险，因而把这里叫作黄石寨。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpiKralRUNQpjpxW7oW55XytcaqD\" alt=\"\" />\r\n</p>\r\n									'),(1815,242,2,'美景风光','									<p>\r\n	黄石寨海拔1200米，占地面积250亩，为张家界森林公园最大、 最集中的观景台，主要观景点有20余处，故有“不上黄石寨，枉到张家界”之说。有南北两条步行登寨游道，还有后山车道自老磨湾通通寨后卡门，系砂石路面；从南面登寨，沿途有天书宝匣、定海神针、南天门、南天一柱、摘星台、天桥遗墩、六奇阁等绝佳景点。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlhMCZyTqki6-1RjdmnOD9sFij9B\" alt=\"\" />\r\n</p>\r\n<p>\r\n	游人这样评价黄石寨：五步称奇，七步叫绝；十步之外，目瞪口呆。黄石寨这里的每一座岩峰都是一件古老的艺术品，它们如剑、如针、如龙首、如老龟、如现代化都市的摩天大厦……曾有诗曰：“千峰竟秀，万木葱笼，琳琅满目，美不胜数。虽非大上，不似人间，借以喻此，不为过也。”\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fs5KXH8YjT70Rr0CikaC7tpcX2eJ\" alt=\"\" />\r\n</p>\r\n									'),(1816,242,3,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往森林公园的线路巴士。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">森林公园门票站进入：从老磨湾广场乘景区环保车至黄石寨索道下站，乘缆车上山。或从登山游道步行上山（时间约1.5小时）。</span>\r\n</p>'),(1817,242,4,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">武陵源核心景区游览路线可简要形容为山下游览路线、山上游览路线；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">黄石寨归属于山下游览线，1日游推荐线路为：市区-森林公园门票站-老磨湾广场-黄石寨索道上山-黄石寨-黄石寨索道下山-徒步金鞭溪-水绕四门-景区环保车-十里画廊-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需7小时。</span><span style=\"line-height:1.5;\">如果选择步行上下山，时间增加约3小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">黄石寨山顶为环形游道，有小环线（1.5小时）、大环线（3小时）之分。</span>\r\n</p>\r\n<p>\r\n	<strong>相关信息</strong>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：已含在武陵源核心景区门票内（票价248元/3天有效）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市武陵源区黄石寨风景区</span>\r\n</p>'),(1818,240,1,'山水画卷','									<p>\r\n	十里画廊，位于武陵源核心景区中部，来回全长10里。峡谷两旁千峰耸立，林木葱笼，野花飘香；奇峰异石，千姿百态，像一幅巨大的山水画卷，并排悬挂在千韧绝壁之上，使秀美绝伦的自然奇观溶进仙师画工的水墨丹青之中；人行其间，三步一景，如在画中遨游，故有“十里画廊”之美誉。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjJo1xFoU8DSc5IL5d0cQOD62j12\" alt=\"\" />\r\n</p>\r\n<p>\r\n	十里画廊是张家界国家森林公园景区内的旅游精华，在这条长达十余里的山谷两侧，有着丰富的自然景观，人行其间如在画中。沟旁黛峰屏列，山上的岩石形成了200来尊似人似物、似鸟似兽的石景造型，其中“孔雀开屏”、“采药老人”、“寿星迎宾”、“猛虎啸天”等最为著名。\r\n</p>\r\n									'),(1819,240,2,'风光美景','									<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FsIIkGyu5rh_ysS4CZkIhPHJgI28\" alt=\"\" />\r\n</p>\r\n<p>\r\n	十里画廊是核心景区内，目前唯一可乘交通工具全程游览的景区，观光电车不仅是游客朋友以车代步、轻松观景的美妙体验载体，更是游览十里画廊最佳的观景方式，改变了游客欣赏风景的角度和视野，被誉为“张家界最美的流动风景线”。 \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgIPnzGj3enSuUE81SvTp_71iRIT\" alt=\"\" />\r\n</p>\r\n<p>\r\n	                                                               星海采药                                                         摄影者：贾长江\r\n</p>\r\n									'),(1820,240,3,'推荐理由','<p>\r\n	<span style=\"line-height:1.5;\">有人说，十里画廊是在大自然有无穷灵感的时候，恣意描绘出的一幅神奇绝美的画卷！诗人黎盛鸣游毕赋诗:“奇峰争起闹长空,百态千姿造化功。秀谷清溪十里路,游人多少画廊中。”雕塑家钱绍武称其为“一条天然雕塑杰作的群像陈列长廊”。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">十里画廊内可谓步步有景，处处如画。在这条长达十余里的山谷两侧，有着丰富的自然景观，人行其间如在画中。沟旁黛峰屏列，山上的岩石形成了200来尊似人似物、似鸟似兽的石景造型，沿途主要景点有寿星迎宾、采药老人、猛虎啸天、夫妻抱子、转阁楼、三姐妹等。</span>\r\n</p>'),(1821,240,4,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往森林公园、武陵源的线路巴士。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">武陵源吴家峪门票站进入：乘景区环保车至十里画廊。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">森林公园门票站进入：步行至金鞭溪尽头水绕四门停车场，乘景区环保车至十里画廊。</span>\r\n</p>'),(1822,240,5,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">游览十里画廊，时间约1.5小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">武陵源核心景区游览路线可简要形容为山下游览路线、山上游览路线；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">十里画廊属于山下游览线，1日游推荐线路为：市区-森林公园门票站-徒步金鞭溪-水绕四门-景区环保车-十里画廊-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需6小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">此外，穿过十里画廊，沿尽头卧龙岭游道，可徒步登上天子山。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong>相关信息</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：已含在武陵源核心景区门票内（票价248元/3天有效）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市武陵源区十里画廊风景区</span>\r\n</p>'),(1823,238,1,'峰林奇观','<p>\r\n	<span style=\"line-height:1.5;\">袁家界位于武陵源核心景区中心位置，平均海拔1074米，为一方山台地，背依岩峰山峦，面临幽谷群峰，自东向西延伸，是武陵源核心景区的风景集中地。袁家界名称相传来源于后唐时期，黄巢起义失败后，有一名姓“袁”的义军将士，为躲避朝廷追捕，便来到了这远离人世的深山野岭隐居生活，并以自己的姓氏为这里命名，起名“袁家界”。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpyLiZYH6FyI1jN5YADmNMPOsAK2\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 峰林 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 摄影者：张建国\r\n</p>\r\n<p>\r\n	袁家界面积约1200公顷，平均海拔1074米，它东邻金鞭溪、远眺鹞子寨；南望黄石寨，连接天波府；西通天子山；北距索溪峪。它位于张家界国家森林公园北部，是镶嵌在武陵源核心景区的一颗明珠。袁家界位于杉刀沟北麓，是以石英岩为主构成的一座巨大而较平缓的山岳。袁家界是张家界公园又一处风景集中地。自金鞭溪紫草潭左入杉刀沟可上袁家界；顺袁家界台地边沿，绕荒径小道亦可下至金鞭溪千里相会处；亦可从袁家界去天子山、杨家寨。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fva9CQrMeDOYgJYz0Yaoqx9XRVI8\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">袁家界景区虽然在地域上隶属于中国第一个国家森林公园——张家界国家森林公园，但从地理位置上来说，却被众星捧月般地位居张家界武陵源风景名胜区的核心，与世界上最美丽的峡谷金鞭溪一衣带水，东接索溪峪，南眺鹞子寨，西望黄石寨，北靠天子山，总面积约1200公顷，平均海拔1074米。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">站在袁家界景区的观景台上，三千奇峰尽收眼底，雄险秀野，神态各异。峡谷深处，千百根石峰石柱奇伟突立。峻峭之石，如英武将帅；嵯峨之峰，似勇猛壮士。有声有色，若静若动，形象逼真，呼之欲出。</span>\r\n</p>'),(1824,238,2,'美景风光','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FmWQKqqbkn3NOhJsOHioo0_T-XeD\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 摄影者：宋志光\r\n</p>\r\n<p>\r\n	袁家界景色之美有“张家界后花园”之称，后来因美国科幻大片《阿凡达》在此取景而名声大噪。来到袁家界，一步一景美妙绝伦，原来，电影中的潘多拉星球真的存在。悬浮山的原型“南天一柱”现被更名为 “哈利路亚山”，“潘多拉太远，张家界很近”的广告词更是脍炙人口，传播甚广。<span style=\"line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp;</span><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Flzn5ANzewaen07hQw8_5pHbaAWX\" alt=\"\" />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;摄影者：陈明明\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">景区内的代表性景点有：百龙天梯，世界上最高、载重量最大、运行速度最快的户外观光电梯；天下第一桥，桥面宽两米，厚四米，跨度为二十多米，高度为三百多米，是世界是迄今为止所发现落差最高的天然石桥。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),(1825,238,3,'推荐理由','<p>\r\n	<span style=\"line-height:1.5;\">最具特色的百龙天梯，垂直高差335米，运行高度326米，由154米山体内坚井和172米贴山钢结构井架等组成，采用三台双层全暴露观光电梯并列分体运行，每台次载客50人次，运行速度3米/秒，三台同时运行每小时往返量达4000人次。百龙天梯的修建实现“山上游，山下住”的目标，提供了交通保障，将袁家界天下第一桥、迷魂台、天悬白练以及乌龙寨、杨家界等绝世美景从幕后推向了前台，并将天子山、袁家界、金鞭溪等精品旅游线路连为一体，解决了多年困扰景区的交通瓶颈问题。</span>\r\n</p>'),(1826,238,4,'交通路线','<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往森林公园、武陵源的线路巴士。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">武陵源吴家峪门票站进入：乘景区环保车至百龙天梯下站，乘电梯上山。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">森林公园门票站进入：步行至金鞭溪千里相会景点处，从乱窜坡游道步行上山。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">或者从杨家界索道、天子山索道上山后，乘景区环保车前往。</span>\r\n</p>'),(1827,238,5,'游览须知','<p>\r\n	<span style=\"line-height:1.5;\">武陵源核心景区游览路线可简要形容为山下游览路线、山上游览路线；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">袁家界属于山上游览线，可作为游览的起点，1日游推荐线路为：武陵源区-吴家峪门票站-景区环保车-百龙天梯上山-袁家界-景区环保车-杨家界-景区环保车-天子山-天子山索道下山-景区环保车-吴家峪门票站-武陵源区，整线游览时间至少需7小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">袁家界为半环形游览线，迷魂台停车场进入景区-沿游道步行-天下第一桥停车场处景区，游览时间约1.5小时。</span>\r\n</p>'),(1840,232,1,'地质公园','																		<p>\r\n	<span style=\"line-height:1.5;\">“中国首批世界自然遗产、世界首批地质公园、国家首批5A级旅游景区”【武陵源核心景区】为是来张家界游人的必玩景区，因独特的“张家界石英砂岩峰林地貌”而闻名于世，由张家界国家森林公园、天子山风景区、索溪峪风景区（部分）共同组成，门票为通票制，下辖7个分景区，分别为【杨家界】、【天子山】、【袁家界】、【十里画廊】、【金鞭溪】、【黄石寨】、【鹞子寨】，自然风光各不相同。</span>\r\n</p>\r\n<p>\r\n	奇山异水张家界，举世闻名，是湖南核心旅游景点，还有世界溶洞之冠的黄龙洞，神秘幽深。有峰林之称的天子山，不到黄石寨，枉到张家界，还有世界上最美丽的峡谷金鞭溪。\r\n</p>\r\n									\r\n									'),(1841,232,2,'美景风光','<p>\r\n	上亿年前的地壳运动，大自然的鬼斧神工，造就了张家界不可复制的神奇和美丽。于是，无数游过张家界的人赞叹：人生不到张家界，访尽千山也枉然，人生不到张家界 百岁何能称老翁？张家界是“扩大的盆景.缩小的仙境”，随着四季的气象不同变幻莫测，是大自然鬼斧神功美的让人心动。张家界集桂林之秀、黄山之奇、华山之险、泰山之雄于一体，藏赂、桥、洞、湖、瀑于一身，有“扩大的盆景、缩小的仙山”之美称。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FhCqiJlWLEP_z2Wn6rLaLgvS4zpc\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">杨家界峰墙</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界国家森林公园位于湖南省西北部张家界市境内，是1982年由国务院委托国家计委批准成立的中国第一个国家森林公园，1992年12月因奇特的石英砂岩大峰林被联合国列入《世界自然遗产名录》，2004年2月被列入世界地质公园。</span><span style=\"line-height:1.5;\">公园自然风光以峰称奇、以谷显幽、以林见秀。</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fmz6Xu92uBYWvSJ0UrAYgih8SeHV\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;天子山卧龙岭 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;摄影者：刘君\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公园属于中亚热带气候区，高山峡谷，茂密的森林，使公园形成了独特的森林小气候——冬暖夏凉，年平均气温12.8℃，夏天平均最高气温16.8℃，冬天平均最低气温9.6℃，公园空气清新，含负氧离子高，身临其中，人体感觉特别舒适，是休闲避暑的理想之地。</span><img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Ft_uXCnP7jx7AQC7wls53oowhZyD\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">袁家界</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">绝美自然景观。张家界地处于云贵高原隆起区与洞庭湖沉降区之间，既受隆起的影响，又受沉降的牵制 ,加上地表水切割强烈和岩溶地貌极其发育，形成了当今这种高低悬殊、奇峰林立、万石峥嵘、溪谷纵横的自然奇观。是户外爱好者的旅行天堂。</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtKK03KxQiXejL4U5svHBs7sa-MA\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	十里画廊\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">独特的人文景观。土家族、白族、苗族、侗族和瑶族等少数民族占整个张家界市的96%。具有浓烈的民族风情，是人文摄影爱好者的必去之地。</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FkvEZVkSvMUjRvCF8LXblYyMcdLA\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;金鞭溪 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;摄影者： 汤东涛\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">及其优良的气候条件。四季分明，冬 长夏短。每年3月起，气温日渐升高，春意盎然，兽鸟活跃，山峦田野，充满着生机与活力，同时此时降雨较多，可看到云海。到了6月下旬，武陵源开始进入夏季，直至9月上旬。武陵源夏季的平均气温在22至26摄氏度之间，最高时也不过32摄氏度左右。此时，许多地区酷热难熬，而这里却一派清凉，是避暑消夏的理想去处之一。冬季景区山顶与市区截然不同，张家界虽然地处南方，但还是有雪景的出现！是冬季摄影的好去处！</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FuGkbhel39KhJP0A-fiev7y_XCWe\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	黄石寨日出\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">张家界以岩称奇。这里三千座奇峰拔地而起，形态各异，有的似玉柱神鞭，立地顶天；有的像铜墙铁壁，巍然屹立；有的如晃板垒卵，摇摇欲坠；有的若盆景古董，玲珑剔透......神奇而又真实，迷离而又实在，不是艺术创造胜以艺术创造，令人叹为观止，张家界奇独特的风景，丰富的旅游资源，唯美的自然风光，无一不向世人展示着它雄伟壮丽。</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FskpOR80jt2ikzzKosPBg6W1W_1M\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;鹞子寨 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;摄影者：郑亚平\r\n</p>'),(1842,232,3,'推荐理由','																		<p>\r\n	<span style=\"line-height:1.5;\">其间有奇峰3000多座，这些石峰如人如兽、如器如物，形象逼真，气势壮观。峰间峡谷，溪流潺潺，浓荫蔽日。有“三千奇峰，八百秀水”之美称。公园不仅自然风光壮美绝伦，而且森林植物和野生动物资源极为丰富，森林覆盖率达98％，是一座巨大的生物宝库和天然氧吧，被称为“自然博物馆和天然植物园”。草木禽兽与奇山异水，同生共荣，形成完美的自然生态系统。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">境内峰奇岩险，谷深涧幽，水秀林碧，云缭雾绕。集雄、奇、幽、野、秀于一体，汇峰、谷、壑、林、水为一色。张家界国家森林公园主要景点有：金鞭溪，杨家界景区，乌龙寨，鹞子寨景区，天子山景区。</span>\r\n</p>\r\n									\r\n									'),(1843,232,4,'交通路线','																		<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往森林公园、武陵源的线路巴士，每十分钟发班。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">公园交通十分方便，距市城区、飞机场、火车站仅28公里。景区内已建成58公里的国际标准游道、安全舒适的索道和开通了环保车。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n									\r\n									'),(1844,232,5,'游览须知','																		<p>\r\n	<span style=\"line-height:1.5;\">游览景区一般为2到3天。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">景区有5个门票站，其中森林公园门票站、吴家峪门票站（武陵源标志门）是游人最常的选择。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">景区游览路线可简要形容为山下游览路线、山上游览路线；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">山下游览路线为：森林公园门票站-大氧吧广场-黄石寨-金鞭溪-水绕四门-十里画廊-吴家峪门票站。山上游览路线为：袁家界-杨家界-天子山。另有鹞子寨，可选择单独游览。</span>\r\n</p>\r\n									\r\n									'),(1845,232,6,'相关信息','																		<p>\r\n	<span style=\"line-height:1.5;\">门票：A.248元（含保险，4天有效） B.301元（含保险，7天有效）</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">地址：张家界市武陵源区</span>\r\n</p>\r\n									\r\n									'),(1849,408,1,'古城介绍','<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FnegLuFe8UGs6Q951DLOLmZaT8di\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	在湘西北清清溇水河畔，在巍巍群山中的北山脚下有座三面环水的独立山城，它就是风景秀丽，历史悠久的名城——九溪卫古城。据明隆庆《岳州府志》载“九溪在慈利西北九十里，以喝堡等八溪会索溪”故名( 即淋溪、撒珠溪、输赢溪、倍子溪、人潮溪、南渡溪、柳杨溪、冷水溪、索溪九条溪汇合于此)。古人对它的自然风貌及地理形势作了这样记述：“九溪拥山面溪，笔峰（大尖山）峙其东，麻阜（茅花界）供其西，溇水环其南，驼峰耸其北，东抵洞庭，西通巴蜀，岳郡之锁钥，诸夷之襟喉也”。明洪武二十三年( 公元1390 年) 在九溪设卫建城。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FpX4hpDO4co9jx4L_mDxlaXdCCeS\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	九溪卫古城位于慈利县江垭镇境内，距慈利县城45 公里。这里植被茂密，物种丰富，空气清新宜人，是一处观光、休闲的好去处。\r\n</p>'),(1850,408,2,'交通路线','<p>\r\n	距 离：距武陵源核心景区约40 公里，距市区约70 公里\r\n</p>\r\n<p>\r\n	公共交通：可由武陵源汽车站乘坐江垭专线车，在江垭乘坐至九溪卫的班车\r\n</p>\r\n<p>\r\n	自 驾：武陵源区沿省道S306、常张高速公路在慈利东下高速，再由慈利东至九溪卫\r\n</p>\r\n<div>\r\n	<br />\r\n</div>'),(1851,408,3,'基础信息','<p>\r\n	地 址：张家界市慈利县江垭镇\r\n</p>\r\n<p>\r\n	门票价格：免费\r\n</p>\r\n<p>\r\n	咨询电话：13574436562（九溪村书记）\r\n</p>'),(1856,394,1,'溶洞奇观','									<p>\r\n	九天洞因洞内天生有九个自然天窗与外界相通而得名，享有“世界奇穴之冠”、“亚洲第一大洞”等桂冠。洞内总面积达250多万平方米，分上、中、下三层，最下层低于地表面400多米。\r\n</p>\r\n<p>\r\n	九天洞内空气清新，冬暖夏凉。经初步探明洞内有40个大厅、3条落差悬殊的阴河、12条瀑布、5座自生桥、10余座洞中山，此外，还有不可胜数的五光十色的钟乳石群，堆珍叠玉、千姿百态。九天洞被誉为中国溶洞奇葩。在众多迷人的景观中尤以九星山、玉宫、水晶宫、九天玄女宫、寿星宫五大奇。九天洞有大、奇、美、幽四大特色，堪称四绝。 \r\n</p>\r\n									'),(1857,394,2,'风光美景','									<p>\r\n	1995年，被定为“国际溶洞协会探险基地”（全国仅二家），并被吸收为国际溶洞协会会员（全国仅三家）。经考察，发现洞内有古树化石和其它溶洞极少见的岩溶物质，不仅是难得的自然景观，而且有极高的科研价值。\r\n</p>\r\n<p>\r\n	洞内的36支洞交错相连，内有30余座大厅、10余座洞中山、6方千丘田、5座自生桥、3段阴河、3个天然湖、12瀑、3井等景。洞中石林密布，钟乳悬浮，石笋、石柱、石幔、石花、石人、石兽等千姿百态，五彩缤纷，琳琅满目。其中九星山玉柱、九天玄女宫和寿星宫三大奇观堪称景绝盖世，令人惊叹。\r\n</p>\r\n									'),(1858,394,3,'交通路线','									 市中心汽车站乘“市区-桑植”线路车至桑植县城，再转“桑植县城-瑞塔铺”的班车至终点瑞塔铺，然后前行一百米到丁字路口，有前往九天洞的三轮车，一个人8元；建议自驾车。<br />\r\n									'),(1859,394,4,'游览须知','									游览景区最少需3小时。<br />\r\n九天洞，位于市区至桑植县的游览西线上，尚无旅游线路巴士，建议自驾车前往或在市区选择相关1日游。<br />\r\n相关信息<br />\r\n门票：75元<br />\r\n									'),(1860,600,1,'简介','									<p class=\"MsoNormal\" align=\"left\">\r\n	长茅山位于张家界市永定区尹家溪镇茅溪村，与沙堤乡、教字垭镇相邻，过去的长茅山，是一个穷山野岭、难以为生的落后山村。山上海拔虽然只有<span>800</span>米，相对高度却达到了<span>500 </span>多米，山高路陡、偏僻荒野。自<span>2009 </span>年开始，全村种植桃树<span>3000 </span>多亩，并配置了梨、李、柿、枣、瓜等<span>20 </span>多个其他品种，形成了村史介绍、农家乐园、生态观光、果园采摘、休闲娱乐、徒步健身等内容的旅游精品。\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	如今的长茅山，两座青山夹着一条潺潺溪流，千亩桃花掩映着美丽的村庄。在道路两旁，栽种着不同花色的桃子树苗，每年“三月桃花始盛开”和“最是橙黄橘绿时”，姹紫嫣红的桃花与挂满枝头的桃子，都会引领着人们，向长茅山前进。\r\n</p>\r\n									'),(1861,600,2,'交通线路','									<p class=\"MsoNormal\" align=\"left\">\r\n	距\r\n离：距市区约<span>26 </span>公里，距武陵源核心景区约<span>50 </span>公里\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	公共交通：在张家界市中心汽车站坐市区至茅溪班车，到终点站下车，徒步<span>1 </span>小时或者搭摩托车<span>15 </span>元直接到达山顶万亩桃花林\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	自\r\n驾：可由市区驾车沿省道<span>S228 </span>及乡村公路至长茅山\r\n</p>\r\n									'),(1862,600,3,'基础信息','									<p class=\"MsoNormal\" align=\"left\">\r\n	地\r\n址：张家界市永定区尹家溪镇茅溪村（张桑公路前行<span>12 </span>公里）\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	门票价格：免费\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	咨询电话：<span>13517440259</span>（黄书记）\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	桃花花期：<span>3 </span>月<span>15 </span>日<span>-4 </span>月<span>10 </span>日\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	桃园果熟：<span>6 </span>月<span>-9 </span>月\r\n</p>\r\n									'),(1863,387,1,'峡谷奇观','																																				<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtDTEpxLPSEqmu3vZlraR7Bf3aVh\" alt=\"\" />\r\n</p>\r\n<p>\r\n	苦竹河两岸以岩溶地貌为主，又有砂崖峰林地貌，据地质部门考察，苦竹河、温塘、罗塔坪一带为北东构造，由青安坪 —杨家湾向斜构造组成。背斜核部出露寒武系娄关山群，向斜核部是三迭纪大治组，为海相碳酸岩建造，岩层主要有灰岩、白去岩、泥灰岩，两岸岩溶洞穴以千计。而罗塔坪乡驻马溪、双峰崖、寒溪沟等地，岩层为志留系泥盆纪石英砂岩，经长期的风雨剥蚀和溪水的深切，有张家界石英砂岩峰林景观。\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(1864,387,2,'风光美景','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqIF6wgzNz4m5N1l6BvGQZTXscW1\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	乘船赏“百里画廊”苦竹河两岸风光，及游轮上土家阿哥阿妹的歌舞表演。沿途绝壁雄居、怪石重叠，有猴子捞月、血门沟、印花墙、凉衣篙等景点，土家英雄覃厚王的故事贯穿在整个游览途中。免费参观苦竹寨红军村，苦竹寨建于唐宋，曾经是一个商业码头，非常繁荣。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlUU2s1VX5mJqqegHq9ZBFdFdbCu\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	                                                                                                                                                      摄影：郑亚平\r\n</p>\r\n<p>\r\n	千百年来，这里是一处神秘的禁地，岁月如尘，轻掩着一部古老的传说。\r\n</p>\r\n<p>\r\n	迫近她，宛如闯进了隔世的梦培，纤尘不染的意境里，她会让你体会到什么是人间仙境，什么叫苍凉凝重。扑面而来的，不仅仅是风景……\r\n</p>\r\n									\r\n									'),(1865,387,3,'交通路线','									<p>\r\n	<span style=\"line-height:1.5;\">建议自驾车；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">苦竹河大峡谷距张家界市城区5公里，距茅岩河漂流下水码头39公里，距茅岩河漂流上岸码头18公里。</span>\r\n</p>\r\n									'),(1866,387,4,'游览须知','									<p>\r\n	<span style=\"line-height:1.5;\">建议安排一日游。</span>\r\n</p>\r\n									'),(1867,375,1,'溶洞奇观','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fpd12LrILGLbDedXjwu1BkjmPzb1\" alt=\"\" />\r\n</p>\r\n<p>\r\n	黄龙洞、龙王洞、九天洞是张家界三大溶洞。黄龙洞恢弘壮阔，九天洞原始野性，龙王洞最为神秘。龙王洞是在1979年当地采药山民从溶洞上方的“天窗”不慎掉进洞内，这个巨大的溶洞才为世人所知。\r\n</p>\r\n									\r\n									'),(1868,375,2,'风光美景','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FgSdB2I4elCsyjokxu19Kxj11glF\" alt=\"\" />\r\n</p>\r\n<p>\r\n	现已探知:此洞为特大石灰岩溶洞，年龄达3亿8000万年，全长33公里，现在已开发4.5公里，平均高度50米，宽80米，有58个厅台，28条石走廊，15处珍珠瀑布，3条地下阴河，湖泊2个，水花池45处，洞中石笋、石钟乳、石幔、石柱、石花遍布，琳琅满目。\"龙宫\"大厅有四根巨型钟乳石柱撑开一片洞天，喻为龙王洞的\"镇海宝柱\"，最大的一根周长达40米，高32米，堪称\"世界之最\"，已经申报吉尼斯世界记录，誉为\"天下第一柱\"之称，顶天立地、气势磅礴，令人叹为观止。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fsyl8Pqx_y7bEV7uuZk-w1vOsE-Q\" alt=\"\" />\r\n</p>\r\n<p>\r\n	龙王洞属张家界旅游核心景点之一，被溶洞专家称赞为\"世界溶洞奇葩\"，是中国最大、最古老的溶洞之一，具有极高的科研价值和旅游开发的价值。被学者誉为\"神秘、神奇、神得不可思议的地心之门\"。 \r\n</p>\r\n									\r\n									'),(1869,375,3,'交通路线','									<p>\r\n	<span style=\"line-height:1.5;\">张家界市中心汽车站搭乘前往慈利或江垭的班车，在306省道龙王洞标示处下车，车费8元。</span>\r\n</p>\r\n									'),(1870,375,4,'游览须知','									<p>\r\n	<span style=\"line-height:1.5;\">位于世界自然遗产武陵源风景区向东30公里与慈利县之间主干公路58公里南侧10米处，距张家界市城区约60公里左右，省道S306经过景区，交通极为便利。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：78元</span>\r\n</p>\r\n									'),(1871,365,1,'风光美景','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fk6DiK5IF4Hk8fJ-ynZfO7hLxmSz\" alt=\"\" />\r\n</p>\r\n<p>\r\n	老道湾位于张家界市城区以南，天门山、七星山北面的山谷，离市中心7公里，总面积9千余亩，因相传春秋战国时期，纵横术道人鬼谷子在此练功修行，收徒传道而得名。谷内绿树叠翠，山水交错，峡谷狭窄，森林覆盖率达到99%，珍稀树种红豆杉群落以及珍禽异兽在游道旁随处可见。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fq3m6K7EYvOsg3HuC2BN0svXfG6b\" alt=\"\" />\r\n</p>\r\n<p>\r\n	老道湾一期试验型特色温馨树屋，共30多间房间，面积大小不一无一雷同，有单门别户，有阁楼小户型，更有楼中楼别墅大套间。适宜独行浪迹天涯者、年青二人情侣、夫妻、家庭团队多人等入住休闲度假。\r\n</p>\r\n									\r\n									'),(1872,365,2,'趣味体验','																		<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fv6wRDEWsi1bKqk2rY7U8Dd_NuNn\" alt=\"\" />\r\n</p>\r\n<p>\r\n	老道湾景区分三道风景区，西线为仙女峡谷，七潭七瀑；中线为历史上繁华的千年古道，三十六绞，体现红色文化；东线景色为鬼谷峡，现已开发十八潭，体现鬼谷文化。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FtStmPNTdqh_sMfbv9TWhOpyTNiy\" alt=\"\" />\r\n</p>\r\n<p>\r\n	滑索、滑道、水上乐园是来这里必须体验的旅游项目。想体验凌空飞渡，离开地面看看全新视角的张家界风光，可以尝试全国第一长的高山无动力滑索（全长800米，凌空高度超百米，最高速度超15米每秒）。\r\n</p>\r\n									\r\n									'),(1873,365,3,'交通路线','									<p>\r\n	<span style=\"line-height:1.5;\">景区尚未开通公交车，可以在张家界中心汽车出站口对面乘坐至老道湾直达车，票价5元。市区打车至老道湾平均一趟30-50元</span>\r\n</p>\r\n									'),(1874,365,4,'游览须知','									<p>\r\n	<span style=\"line-height:1.5;\">滑索太过刺激建议小孩在大人的陪同下游玩，不建议老人游玩。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">票价108元，1.3米以下儿童，70岁以上老人免票。</span>\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n									'),(1875,275,1,'峰墙奇观','									<p>\r\n	<span style=\"line-height:1.5;\">杨家界景区东连袁家界，北邻天子山，总面积34平方公里，除了峰墙奇观，还有石柱、古藤、飞瀑、五色花等自然美景和乌龙寨等人文风景。由于山势险峻，交通不畅，普通游客难以企及，多年来只在户外驴友中享有盛名，峰墙奇观多半处于雪藏状态。</span><span style=\"line-height:1.5;\">而2014年刚投入运行的“空中走廊”杨家界索道，向世人尽早揭开了杨家界的面纱，展示了杨家界峰墙的稀缺之美、原始之美、形体之美和文化之美，将过去的遥不可及将变得触手可及。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FrnOXcvz2PxK1mX7vxI2hZJ2s8My\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">10年（1998年至2008年）前的杨家界还是一个被游人遗忘的角落的话，10年（1998年至2008年）后的今天，她作为张家界与天子山的姐妹山，就已经走出闺阁，走出武陵源，已让很多来自五湖四海的朋友亲睹了她的芳容。杨家界风景区同张家界一样，一开始也是“养在深闺人未识”，可刚一露头角，便是“一举成名天下知”。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">湘潭大学教授羊春秋老先生游览了杨家界后，盛赞道：“清秀、壮丽、险特、雄奇，使人为之倾倒，为之拍案惊奇。” 山西大学副教授周征松说：“杨家界新发现的又一个神奇世界。这里山势壮丽，奇峰林立，俊俏无比。”自从1991年3月12日《人民日报》海外版与同年3月23日《人民日报》国内版刊登武陵源新发现杨家界旅游风景区的消息以后，不久便有100多家报刊、电台与电视台报道，因此不久在北京、上海、广州与香港便掀起了“杨家界热”。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fp27Va0uj3Vj0j-kw03rb0DK8wiP\" alt=\"\" />\r\n</p>\r\n<p>\r\n	环球国际社《今日中国》杂志社摄影部主任王新民、华艺出版社梁希毅、《工人日报》记者何碧宝、湖南省人民政府对台办主任王习年以及美过国几位朋友，到杨家界以后，一进山就被这里的秀丽风光迷住了，一连几天住在山洞里欣赏自然风光。他们异口同声赞扬：“杨家界自然风光美不胜收，叫人留连忘返！”\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">杨家界风景，同张家界一样，主要是原始风光自然美。它张家界、天子山、索溪峪比较，除地质地貌与一部分山峰基本相同以外，又有其独特之处，不似张家界，一部分景观又胜似张家界。</span>\r\n</p>\r\n									'),(1876,275,2,'美景风光','									<p>\r\n	<span style=\"line-height:1.5;\">游玩杨家界，可谓野趣横生。这里的乌龙寨曾是土匪盘踞的山寨，建于断崖深谷旁，地势险要。进入乌龙寨有“过三关”之说，第一道关“一步难行”，那是一段陡峭的石级，攀登时往往要手足并用。第二关“谁能不低头”，这里的路需要钻洞绕石，小心低头弯腰才能通过。第三关“一线天”，又叫“瘦身关”，需要侧着身子，才能慢慢地从这两块巨石间穿过。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">著名景点天波府是近距离观赏“峰墙奇观”绝佳之地，那相对平行而立的十余道石墙，皆高矮参差不齐，气势恢宏，巍峨壮观，宛如造物主铸就的“自然长城”。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FrAzj1n72w6sjBpLq3b8vhIMCMUQ\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">除了这几处特别有灵气的景点以外，杨家界山水也是很美的。这里供有景点 杨家界风景区110处，既有造型独特的山峰与危崖，又有流翠滚玉一般溪流与飞瀑：既有以前窝藏土匪的“歪嘴岩”，又有奇绝难攀的“鬼愁寨”：还有“马蹄岩”、“猿愁攀”、“天波府”、“姐妹湾”等许多奇景，真是写不尽，看不完。</span>\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FqTO-N4hso8A6EltP_v2yzqD_PSo\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">这里还要特别提到“宗保峰”、“六郎峰”、“佘太君”、“杨家界”，这些名字是什么意思？原来这杨家界的一些山名，都是杨家将子孙后代为纪念杨家将而取的地名，一千多年来，一直沿袭未改，所以来杨家界游览，不仅可以饱览这里的原始风光，同时还可以替杨家寻祖问根，继承并发扬爱国忠良的遗风。</span>\r\n</p>\r\n									'),(1877,275,3,'交通路线','									<p>\r\n	<span style=\"line-height:1.5;\">在张家界市中心汽车站（火车站旁50米）就可搭乘去往森林公园、武陵源的线路巴士。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">森林公园门票站进入：老磨湾广场步行至黄石寨索道下站，从索道站左手旁公路上山步行至龙凤庵，搭乘景区环保车至杨家界索道下站，可乘缆车或步行上山（约2小时） 。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">武陵源吴家峪门票站进入：乘景区环保车至天子山索道下站或百龙天梯下站，乘缆车或电梯上山。或者从卧龙岭或乱窜坡步行上山。上山后，乘景区环保车前往。</span>\r\n</p>\r\n									'),(1878,275,4,'游览须知','									<p>\r\n	<span style=\"line-height:1.5;\">武陵源核心景区游览路线可简要形容为山下游览路线、山上游览路线；</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">杨家界属于山上游览线，可作为游览的起点，1日游推荐线路为：市区-森林公园门票站-老磨湾广场-龙凤庵-杨家界索道上山-杨家界-袁家界-天子山-天子山索道下山-吴家峪门票站-武陵源区，整线游览时间至少需7小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">杨家界常规游览区域在乌龙寨、天波府一线（时间约2小时）。如果体力充沛，可选择去一步登天、空中走廊等（时间约2小时）。</span>\r\n</p>\r\n									'),(1887,359,1,'峡谷奇观','																																				<p>\r\n	相传张家界大峡谷原来有两个名字，一个叫做烂船峡，是因为从我们所处的位置上行一点五公里，就是神泉溪，整个大峡谷和南方红旗渠的水流都来源于这里。传说以前从泉眼中涌出过很多烂了的船板，当地人们又无法知晓烂船板从何而来，所以这里得名“烂船峡”。这个名字来源，有些地老天荒，海枯石烂的意蕴。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FjSMg8msueSEiYwYcMeJsG6AufKP\" alt=\"\" />\r\n</p>\r\n<p>\r\n	另外一个名字叫做乱泉峡，是指峡谷中的两面石壁，溪泉众多，满峡飞流。这个名称来源，突出了我们张家界大峡谷风景区的特色，那就是峡谷中的飞瀑神泉比比皆是，一路游览下来，让人目不暇接。我们今天的游览行程是沿着面前的一线天绝壁拾级而下，然后顺着溪流的流向一路前行，一直到达神泉湖码头。\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(1888,359,2,'风光美景','																																				<p>\r\n	在张家界，大峡谷就像是一位仙女，远离尘世，追求着天道的和谐与自身的完美。置身于此，薄雾飞天，天地只有一线宽，惊心动魄。观光滑道如仙女之彩带，飘落在大峡谷山间。玻璃桥，飞跃峡谷，惊险与刺激，大峡谷的自然神奇和谐天成。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fj-PeoUvvcMk6WeKU9R1Bp3PO4te\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	湖水\r\n</p>\r\n<p>\r\n	张家界大峡谷集山、水、洞、湖于一身，被誉为“张家界地貌的博物馆”！峡谷内栈道绵延数里，沿途飞瀑神泉比比皆是，溪水常年弥漫着一层薄雾，宛如世外桃源。其间植被繁茂，动物繁多，猴群欢跃，百鸟争鸣，鸳鸯戏水，令人目不暇接。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/Fm7MWEodaFAFAulMD-Ez9yFI7Atu\" alt=\"\" />\r\n</p>\r\n<p>\r\n	谷内独具特点的长约600米的诺克里娱乐滑道，可缓解游客旅途疲劳。世界最高、最长的玻璃桥——张家界大峡谷玻璃桥就在这里，还能一享极限蹦极的惊险刺激。\r\n</p>\r\n<p>\r\n	<img src=\"http://7xr35w.com2.z0.glb.qiniucdn.com/FlTiT_T59hdyqr-3bxvetBODy7cE\" alt=\"\" />\r\n</p>\r\n<p>\r\n	主要景点有：一线天天梯栈道三叠游道平台吴王坡石壁裂张家界大峡谷缝全家福妇唱夫随千年古藤石上森林慈航普渡；天仙水瀑布曲径幽林闯王亭南方红张家界大峡谷旗渠天河观景台；翠潭揽瀑一路平安桥隔音石竹仙泉仙人洗面；蝴蝶泉瀑布怪谜洞佛手遮天烂船岩张家界大峡谷土匪洞等景点。\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n									\r\n									\r\n									\r\n									'),(1889,359,3,'交通路线','																											<p>\r\n	<span style=\"line-height:1.5;\">在武陵源区汽车站乘坐去往“大峡谷”的线路巴士。</span>\r\n</p>\r\n									\r\n									\r\n									'),(1890,359,4,'游览须知','																											<p>\r\n	<span style=\"line-height:1.5;\">游览景区最少需4小时。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">乘线路巴士上山，从门票站景区进入，内为单一游览线，先从栈道下山，后漫步峡谷之中，最后乘游船出景区。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">门票：121元（含保险）</span>\r\n</p>\r\n									\r\n									\r\n									');

/*Table structure for table `security_bq` */

DROP TABLE IF EXISTS `security_bq`;

CREATE TABLE `security_bq` (
  `id` int(32) unsigned NOT NULL AUTO_INCREMENT COMMENT 'PK',
  `name` varchar(100) DEFAULT NULL COMMENT '菜单名称',
  `code` varchar(50) DEFAULT NULL COMMENT '菜单编码',
  `description` varchar(500) DEFAULT NULL COMMENT '菜单描述',
  `orderlist` int(11) DEFAULT NULL COMMENT '排序号',
  `img` varchar(200) DEFAULT NULL COMMENT '图片地址',
  `url` varchar(150) DEFAULT NULL COMMENT '跳转url',
  `parent_id` int(111) DEFAULT NULL COMMENT '父节点',
  `type` int(50) DEFAULT NULL COMMENT '菜单类型',
  `status` int(4) DEFAULT NULL COMMENT '状态',
  `column_type` varchar(50) DEFAULT NULL,
  `page_type` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `security_bq` */

/*Table structure for table `security_menu` */

DROP TABLE IF EXISTS `security_menu`;

CREATE TABLE `security_menu` (
  `id` int(32) unsigned NOT NULL AUTO_INCREMENT COMMENT 'PK',
  `name` varchar(100) DEFAULT NULL COMMENT '菜单名称',
  `code` varchar(50) DEFAULT NULL COMMENT '菜单编码',
  `description` varchar(500) DEFAULT NULL COMMENT '菜单描述',
  `orderlist` int(11) DEFAULT NULL COMMENT '排序号',
  `img` varchar(200) DEFAULT NULL COMMENT '图片地址',
  `url` varchar(150) DEFAULT NULL COMMENT '跳转url',
  `parent_id` int(111) DEFAULT NULL COMMENT '父节点',
  `type` int(50) DEFAULT NULL COMMENT '菜单类型',
  `status` int(4) DEFAULT NULL COMMENT '状态',
  `column_type` varchar(50) DEFAULT NULL,
  `page_type` varchar(50) DEFAULT NULL,
  `can_search` varchar(4) DEFAULT NULL COMMENT '是否支持搜索',
  `banner_id` int(11) DEFAULT NULL COMMENT '栏目ID',
  PRIMARY KEY (`id`)
